import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";import{o as i,c as r,d as t}from"./app.d6b42b88.js";var a="/blog/assets/1-\uCEF4\uD478\uD130-\uAD6C\uC131\uC694\uC18C.ac70aa69.png",s="/blog/assets/1-\uCEF4\uD4E8\uD130\uC640-\uD558\uB4DC\uC6E8\uC5B4-\uAD6C\uC131\uC694\uC18C.8d0d5e6b.png";const e={},n=t('<p>\uD574\uB2F9\uD398\uC774\uC9C0\uB294 2022\uB144 3\uC6D4 \uB178\uC158\uC5D0\uC11C \uB9C8\uC774\uADF8\uB808\uC774\uC158 \uB418\uC5C8\uC2B5\uB2C8\uB2E4.<br> \uBC11\uC5D0 \uC788\uB294 \uADF8\uB9BC\uB4E4\uC740 \uAC1C\uC778\uC801\uC778 \uC774\uD574\uB97C \uB3D5\uAE30 \uC704\uD574 \uADF8\uB9B0 \uADF8\uB9BC\uC73C\uB85C \uC2E4\uC81C \uC6D0\uB9AC\uC640 \uB9DE\uC9C0 \uC54A\uC744 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="\u110F\u1165\u11B7\u1111\u1172\u1110\u1165-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7\u110B\u1174-4\u1100\u1161\u110C\u1175-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169" tabindex="-1"><a class="header-anchor" href="#\u110F\u1165\u11B7\u1111\u1172\u1110\u1165-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7\u110B\u1174-4\u1100\u1161\u110C\u1175-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169" aria-hidden="true">#</a> \uCEF4\uD4E8\uD130 \uC2DC\uC2A4\uD15C\uC758 4\uAC00\uC9C0 \uAD6C\uC131\uC694\uC18C</h2><ul><li>\uD558\uB4DC\uC6E8\uC5B4 : \uAE30\uBCF8 \uC5F0\uC0B0\uC744 \uD558\uB294 \uC790\uC6D0\uC744 \uC81C\uACF5\uD558\uB294 \uC7A5\uCE58 <ul><li>CPU, \uBA54\uBAA8\uB9AC, I/O \uC7A5\uCE58</li></ul></li><li>\uC751\uC6A9 \uD504\uB85C\uADF8\uB7A8 : \uC6F9\uBE0C\uB77C\uC6B0\uC800, \uC5D1\uC140 \uAC19\uC740 \uC560\uD50C\uB9AC\uCF00\uC774\uC158</li><li>\uC0AC\uC6A9\uC790 : \uC694\uCCAD\uC744 \uD558\uB294 \uC8FC\uCCB4, \uC0AC\uC6A9\uD558\uB294 \uC0AC\uB78C\uBFD0\uC544\uB2C8\uB77C \uAE30\uACC4, \uCEF4\uD4E8\uD130\uAC00 \uB420 \uC218\uB3C4 \uC788\uB2E4.</li><li>\uC6B4\uC601\uCCB4\uC81C : \uC751\uC6A9\uD504\uB85C\uADF8\uB7A8\uAC04\uC758 \uD558\uB4DC\uC6E8\uC5B4 \uC0AC\uC6A9\uC744 \uC81C\uC5B4, \uC870\uC815\uD558\uB294 \uC5ED\uD560</li></ul><p><img src="'+a+'" alt="\uCEF4\uD4E8\uD130 \uAD6C\uC131\uC694\uC18C" loading="lazy"><br> \uC2DC\uC2A4\uD15C \uAD6C\uC131: \uC0AC\uC6A9\uC790(user) -&gt; \uC751\uC6A9\uD504\uB85C\uADF8\uB7A8 -&gt; \uC6B4\uC601\uCCB4\uC81C -&gt; \uD558\uB4DC\uC6E8\uC5B4</p><h2 id="\u110F\u1165\u11B7\u1111\u1172\u1110\u1165-\u1112\u1161\u1103\u1173\u110B\u1170\u110B\u1165" tabindex="-1"><a class="header-anchor" href="#\u110F\u1165\u11B7\u1111\u1172\u1110\u1165-\u1112\u1161\u1103\u1173\u110B\u1170\u110B\u1165" aria-hidden="true">#</a> \uCEF4\uD4E8\uD130 \uD558\uB4DC\uC6E8\uC5B4</h2><p>\uCEF4\uD4E8\uD130 \uD558\uB4DC\uC6E8\uC5B4\uB294 \uD06C\uAC8C CPU(\uD504\uB85C\uC138\uC11C), \uBA54\uBAA8\uB9AC(\uAE30\uC5B5\uC7A5\uCE58), \uC8FC\uBCC0\uC7A5\uCE58\uB85C \uAD6C\uC131\uB418\uACE0 \uC774\uB4E4\uC740 \uC2DC\uC2A4\uD15C \uBC84\uC2A4\uB85C \uC5F0\uACB0\uD55C\uB2E4.</p><p><strong>CPU</strong></p><ul><li>CPU\uB294 \uCEF4\uD4E8\uD130 \uD558\uB4DC\uC6E8\uC5B4 \uAD6C\uC131\uC694\uC18C \uC911 \uC6B4\uC601\uCCB4\uC81C\uC640 \uAC00\uC7A5 \uBC00\uC811\uD55C \uBD80\uBD84\uC73C\uB85C \uCEF4\uD4E8\uD130\uC758 \uBAA8\uB4E0 \uC7A5\uCE58\uC758 \uB3D9\uC791\uC744 \uC81C\uC5B4\uD558\uACE0 \uB3D9\uC791\uC744 \uC218\uD589\uD55C\uB2E4.</li><li>CPU\uB294 \uC81C\uC5B4\uC7A5\uCE58,\uC5F0\uC0B0\uC7A5\uCE58,\uB808\uC9C0\uC2A4\uD130\uB85C \uAD6C\uC131\uB418\uBA70 \uB0B4\uBD80 \uBC84\uC2A4\uB85C \uC5F0\uACB0\uB418\uC5B4 \uC788\uB2E4. \uBCF4\uD1B5\uC740 \uC81C\uC5B4\uC640 \uB370\uC774\uD130 \uCC98\uB9AC\uB97C \uB2F4\uB2F9\uD55C\uB2E4.</li><li>\uB808\uC9C0\uC2A4\uD130\uB294 \uC0AC\uC6A9\uC790\uC758 \uC815\uBCF4 \uBCC0\uACBD \uAC00\uB2A5\uC5EC\uBD80\uC5D0 \uB530\uB77C, \uC0AC\uC6A9\uC790 \uAC00\uC2DC \uB808\uC9C0\uC2A4\uD130(\uC811\uADFC\uAC00\uB2A5\uD55C \uB370\uC774\uD130\uC640 \uC8FC\uC18C \uB4F1)\uC640 \uC0AC\uC6A9\uC790 \uBD88\uAC00\uC2DC\uB808\uC9C0\uC2A4\uD130(CPU\uC758 \uC0C1\uD0DC\uC640 \uC81C\uC5B4\uB97C \uAD00\uB9AC)\uB85C \uAD6C\uBD84\uD560 \uC218 \uC788\uB2E4. \uADF8\uC678\uC5D0\uB3C4 \uC6A9\uB3C4\uC5D0 \uB530\uB77C, \uC800\uC7A5\uD558\uB294 \uC815\uBCF4\uC5D0 \uB530\uB77C \uBD84\uB958\uD560 \uC218 \uC788\uB2E4.</li></ul><p><strong>\uBA54\uBAA8\uB9AC</strong><br> \uC5EC\uB7EC \uACC4\uCE35\uC758 \uBA54\uBAA8\uB9AC\uB97C \uC5F0\uACB0\uD558\uC5EC \uBE44\uC6A9, \uC18D\uB3C4, \uC6A9\uB7C9, \uC811\uADFC\uC2DC\uAC04 \uB4F1\uC744 \uC0C1\uD638 \uBCF4\uC644\uD55C \uACC4\uCE35\uC801 \uBA54\uBAA8\uB9AC \uAD6C\uC870\uB97C \uBA54\uBAA8\uB9AC \uACC4\uCE35 \uAD6C\uC870\uB77C \uD558\uBA70 <mark>\uD558\uB4DC\uB514\uC2A4\uD06C -&gt; \uBA54\uC778 \uBA54\uBAA8\uB9AC -&gt; \uCE90\uC2DC -&gt; \uB808\uC9C0\uC2A4\uD130 (\uC18D\uB3C4,\uAC00\uACA9 \uB192\uACE0, \uC6A9\uB7C9 \uB0AE\uACE0)</mark> \uC774\uC640 \uAC19\uC740 \uAD6C\uC870\uB85C \uAD6C\uC131\uB418\uC5B4 \uC788\uB2E4</p><ul><li>\uB808\uC9C0\uC2A4\uD130 : CPU \uB0B4\uBD80\uC5D0 \uC788\uC73C\uBA70 CPU\uAC00 \uC0AC\uC6A9\uD560 \uB370\uC774\uD130\uB97C \uBCF4\uAD00\uD558\uB294 \uAC00\uC7A5 \uBE60\uB978 \uBA54\uBAA8\uB9AC\uC774\uB2E4.</li><li>\uCE90\uC2DC : CPU\uC758 \uB0B4\uBD80\uB098 \uC678\uBD80\uC5D0 \uC788\uC73C\uBA70 \uCC98\uB9AC\uC18D\uB3C4\uAC00 \uBE60\uB978 CPU\uC640 \uC0C1\uB300\uC801\uC73C\uB85C \uB290\uB9B0 \uBA54\uC778\uBA54\uBAA8\uB9AC\uC758 \uC18D\uB3C4\uCC28\uC774\uB97C \uBCF4\uC644\uD558\uB294 \uACE0\uC18D \uBC84\uD37C\uB2E4. \uBA54\uC778\uBA54\uBAA8\uB9AC\uC5D0\uC11C \uB370\uC774\uD130\uB97C \uBE14\uB85D \uB2E8\uC704(\uC800\uC18D)\uB85C \uAC00\uC838\uC640 CPU\uC5D0 \uC6CC\uB4DC \uB2E8\uC704(\uACE0\uC18D)\uB85C \uC804\uC1A1</li><li>\uBA54\uC778 \uBA54\uBAA8\uB9AC(\uC8FC\uAE30\uC5B5\uC7A5\uCE58) : CPU \uC678\uBD80\uC5D0 \uC788\uC73C\uBA70 CPU\uC5D0\uC11C \uC989\uAC01\uC801\uC73C\uB85C \uC218\uD589\uD560 \uD504\uB85C\uADF8\uB7A8\uACFC \uB370\uC774\uD130\uB97C \uC800\uC7A5\uD558\uAC70\uB098, \uCC98\uB9AC\uD55C \uACB0\uACFC\uB97C \uC800\uC7A5\uD55C\uB2E4. \uB610\uD55C \uC785\uCD9C\uB825 \uC7A5\uCE58\uB3C4 \uBA54\uC778 \uBA54\uBAA8\uB9AC\uC5D0\uC11C \uB370\uC774\uD130\uB97C \uBC1B\uAC70\uB098 \uC800\uC7A5\uD55C\uB2E4.</li><li>\uBCF4\uC870\uAE30\uC5B5\uC7A5\uCE58(\uC678\uBD80\uAE30\uC5B5\uC7A5\uCE58) : \uC8FC\uBCC0\uC7A5\uCE58 \uC911 \uD504\uB85C\uADF8\uB7A8\uACFC \uB370\uC774\uD130\uB97C \uC800\uC7A5\uD558\uB294 \uD558\uB4DC\uC6E8\uC5B4\uB85C \uC18D\uB3C4\uB294 \uB290\uB9AC\uB098 \uC6A9\uB7C9\uC774 \uD06C\uB2E4. \uC790\uAE30\uB514\uC2A4\uD06C, \uAD11\uB514\uC2A4\uD06C,\uC790\uAE30\uD14C\uC774\uD504 \uB4F1\uC774 \uC788\uB2E4.</li></ul><div class="custom-container info"><p class="custom-container-title">\uB300\uB7B5\uC801\uC778 \uCEF4\uD4E8\uD130 \uC5F0\uC0B0 \uCC98\uB9AC\uC18D\uB3C4</p><ul><li>L1 \uCE90\uC2DC \uCC38\uC870 : 0.5ns</li><li>L2 \uCE90\uC2DC \uCC38\uC870 : 7ns</li><li>\uC8FC \uBA54\uBAA8\uB9AC \uCC38\uC870 : 100ns</li><li>\uB514\uC2A4\uD06C \uD0D0\uC0C9 : 10,000,000 = 10ms</li></ul><p>L1, L2.. \uCE90\uC2DC : CPU\uC5D0 \uC0AC\uC6A9\uB418\uB294 \uCE90\uC2DC \uBA54\uBAA8\uB9AC\uB85C \uC5ED\uD560 \uAC19\uC73C\uB098 \uC18D\uB3C4\uC640 \uD06C\uAE30 \uB530\uB77C \uBD84\uB958\uD55C \uAC83(\uB0AE\uC744\uC218\uB85D \uC791\uACE0 \uBE60\uB984)</p><p>\uCD94\uAC00(\uC2A4\uD130\uB514) : \uCE90\uC2DC\uB294 \uC2DC\uAC04\uC9C0\uC5ED\uC131\uC5D0\uC11C \uC774\uC810\uC744 \uC5BB\uB294\uAC74 \uD655\uC2E4\uD558\uB2E4.(\uC6D0\uB860\uC801\uC73C\uB85C, \uCD5C\uADFC \uC811\uADFC\uD55C\uAC70 \uB2C8\uAE4C) \uACF5\uAC04\uC9C0\uC5ED\uC131\uC758 \uC774\uC810\uC744 \uC5BB\uB294\uB2E4 \uB9D0\uD558\uB294 \uC774\uC720\uB294 \uBE14\uB85D\uC774\uB77C\uB294 \uAC1C\uB150\uC744 \uC4F0\uACE0 \uD55C \uBE14\uB85D \uC5D1\uC138\uC2A4\uD560\uB54C \uD574\uB2F9 \uBE14\uB85D\uC744 \uC804\uCCB4\uAC00\uC838\uC624\uAE30 \uB54C\uBB38\uC5D0 \uACF5\uAC04\uC9C0\uC5ED\uC131\uC744 \uD65C\uC6A9\uD55C\uB2E4.</p><p>\uC2DC\uAC04\uC9C0\uC5ED\uC131, \uACF5\uAC04\uC9C0\uC5ED\uC131\uC740 \uCEF4\uD4E8\uD130 \uC5D1\uC138\uC2A4\uC758 \uD2B9\uC131\uC774\uACE0 \uCE90\uC2DC\uAC00 \uC774\uB7F0 \uD2B9\uC131\uC744 \uC774\uC6A9\uD55C\uB2E4,</p></div><p><strong>\uC2DC\uC2A4\uD15C \uBC84\uC2A4</strong></p><p>\uC2DC\uC2A4\uD15C \uBC84\uC2A4\uB294 \uD558\uB4DC\uC6E8\uC5B4\uB97C \uBB3C\uB9AC\uC801\uC73C\uB85C \uC5F0\uACB0\uD558\uC5EC \uC11C\uB85C \uB370\uC774\uD130\uB97C \uC8FC\uACE0\uBC1B\uC744 \uC218 \uC788\uAC8C \uD558\uB294 \uD1B5\uB85C\uC774\uB2E4. \uCEF4\uD4E8\uD130 \uB0B4\uBD80\uC758 \uB2E4\uC591\uD55C \uC2E0\uD638(\uB370\uC774\uD130 \uC785\uCD9C\uB825 \uC2E0\uD638, \uD504\uB85C\uC138\uC11C \uC0C1\uD0DC \uC2E0\uD638, \uC778\uD130\uB7FD\uD2B8 \uC694\uAD6C\uC640 \uD5C8\uAC00 \uC2E0\uD638,\uD074\uB85D \uC2E0\uD638 \uB4F1)\uB97C \uC2DC\uC2A4\uD15C \uBC84\uC2A4\uB85C \uC804\uB2EC\uD55C\uB2E4. \uC2DC\uC2A4\uD15C \uBC84\uC2A4\uB294 \uAE30\uB2A5\uC5D0 \uB530\uB77C \uB370\uC774\uD130 \uBC84\uC2A4, \uC8FC\uC18C\uBC84\uC2A4, \uC81C\uC5B4\uBC84\uC2A4\uB85C \uAD6C\uBD84\uD55C\uB2E4.</p><ul><li>\uB370\uC774\uD130 \uBC84\uC2A4 : CPU\uC640 \uBA54\uC778\uBA54\uBAA8\uB9AC, \uC8FC\uBCC0\uC7A5\uCE58 \uC0AC\uC774\uC5D0\uC11C \uB370\uC774\uD130\uB97C \uC804\uC1A1\uD55C\uB2E4. \uB370\uC774\uD130 \uBC84\uC2A4\uB97C \uAD6C\uC131\uD558\uB294 \uBC30\uC120 \uC218\uB294 CPU\uAC00 \uD55C \uBC88\uC5D0 \uC804\uC1A1\uD560 \uC218 \uC788\uB294 \uBE44\uD2B8 \uC218\uB97C \uACB0\uC815\uD558\uB294\uB370 \uC774\uB97C \uC6CC\uB4DC\uB77C\uACE0 \uD55C\uB2E4.</li><li>\uC8FC\uC18C \uBC84\uC2A4 : CPU\uAC00 \uC2DC\uC2A4\uD15C\uC758 \uAD6C\uC131 \uC694\uC18C\uB97C \uC2DD\uBCC4\uD558\uB294 \uC8FC\uC18C \uC815\uBCF4\uB97C \uC804\uC1A1\uD55C\uB2E4. \uC8FC\uC18C \uBC84\uC2A4\uB97C \uAD6C\uC131\uD558\uB294 \uBC30\uC120 \uC218\uB294 CPU\uC640 \uC811\uC18D\uD560 \uC218 \uC788\uB294 \uBA54\uC778\uBA54\uBAA8\uB9AC\uC758 \uCD5C\uB300 \uC6A9\uB7C9\uC744 \uACB0\uC815\uD55C\uB2E4.</li><li>\uC81C\uC5B4\uBC84\uC2A4 : CPU\uAC00 \uC2DC\uC2A4\uD15C\uC758 \uAD6C\uC131 \uC694\uC18C\uB97C \uC81C\uC5B4\uD558\uB294\uB370 \uC0AC\uC6A9\uD55C\uB2E4. \uC81C\uC5B4 \uC2E0\uD638\uB85C \uC5F0\uC0B0\uC7A5\uCE58\uC758 \uC5F0\uC0B0 \uC885\uB958\uC640 \uBA54\uC778 \uBA54\uBAA8\uB9AC\uC758 \uC77D\uAE30\uB098 \uC4F0\uAE30 \uB3D9\uC791\uC744 \uACB0\uC815\uD55C\uB2E4.</li></ul><p><strong>\uC8FC\uBCC0\uC7A5\uCE58</strong><br> \uC8FC\uBCC0\uC7A5\uCE58\uB294 CPU\uC640 \uBA54\uC778\uBA54\uBAA8\uB9AC\uB97C \uC81C\uC678\uD55C \uB098\uBA38\uC9C0 \uD558\uB4DC\uC6E8\uC5B4 \uAD6C\uC131\uC694\uC18C\uC774\uB2E4. \uB2E8\uC21C\uD788 \uC785\uCD9C\uB825\uC7A5\uCE58\uB77C\uACE0\uB3C4 \uD558\uB294\uB370, \uD06C\uAC8C \uC785\uB825\uC7A5\uCE58 , \uCD9C\uB825\uC7A5\uCE58, \uC800\uC7A5\uC7A5\uCE58\uB85C \uAD6C\uBD84\uB41C\uB2E4.</p><ul><li>\uC785\uB825\uC7A5\uCE58 : \uCEF4\uD4E8\uD130\uC5D0\uC11C \uCC98\uB9AC\uD560 \uB370\uC774\uD130\uB97C \uC678\uBD80\uC5D0\uC11C \uC785\uB825\uD558\uB294 \uC7A5\uCE58</li><li>\uCD9C\uB825\uC7A5\uCE58 : \uCEF4\uD4E8\uD130\uC5D0\uC11C \uCC98\uB9AC\uD55C \uB370\uC774\uD130\uB97C \uC678\uBD80\uB85C \uB0B4\uBCF4\uB0B4\uB294 \uC7A5\uCE58</li><li>\uC800\uC7A5\uC7A5\uCE58 : \uBA54\uC778\uBA54\uBAA8\uB9AC\uC640 \uB2EC\uB9AC \uAC70\uC758 \uC601\uAD6C\uC801\uC73C\uB85C \uB370\uC774\uD130\uB97C \uC800\uC7A5\uD558\uB294 \uC7A5\uCE58,</li></ul><p><img src="'+s+'" alt="\uCEF4\uD4E8\uD130+\uD558\uB4DC\uC6E8\uC5B4 \uAD6C\uC131\uC694\uC18C" loading="lazy"></p><h2 id="\u110F\u1165\u11B7\u1111\u1172\u1110\u1165-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7\u110B\u1174-\u1103\u1169\u11BC\u110C\u1161\u11A8" tabindex="-1"><a class="header-anchor" href="#\u110F\u1165\u11B7\u1111\u1172\u1110\u1165-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7\u110B\u1174-\u1103\u1169\u11BC\u110C\u1161\u11A8" aria-hidden="true">#</a> \uCEF4\uD4E8\uD130 \uC2DC\uC2A4\uD15C\uC758 \uB3D9\uC791</h2><p>\uCEF4\uD4E8\uD130 \uC2DC\uC2A4\uD15C\uC73C\uB85C \uC791\uC5C5\uC744 \uCC98\uB9AC \uD560 \uB54C\uB294 \uB2E4\uC74C\uC21C\uC11C\uC5D0 \uB530\uB77C \uB3D9\uC791\uD558\uBA70 \uC81C\uC5B4\uC7A5\uCE58\uAC00 \uC774 \uB3D9\uC791\uC744 \uC81C\uC5B4\uD55C\uB2E4.</p><ol><li>\uC785\uB825\uC7A5\uCE58\uB85C \uC815\uBCF4\uB97C \uC785\uB825\uBC1B\uC544 \uBA54\uBAA8\uB9AC\uC5D0 \uC800\uC7A5\uD55C\uB2E4.</li><li>\uBA54\uBAA8\uB9AC\uC5D0 \uC800\uC7A5\uD55C \uC815\uBCF4\uB97C \uD504\uB85C\uADF8\uB7A8 \uC81C\uC5B4\uC5D0 \uB530\uB77C \uC778\uCD9C\uD558\uC5EC \uC5F0\uC0B0\uC7A5\uCE58\uC5D0\uC11C \uCC98\uB9AC\uD55C\uB2E4.</li><li>\uCC98\uB9AC\uD55C \uC815\uBCF4\uB97C \uCD9C\uB825\uC7A5\uCE58\uC5D0 \uD45C\uC2DC\uD558\uAC70\uB098 \uBCF4\uC870 \uAE30\uC5B5\uC7A5\uCE58\uC5D0 \uC800\uC7A5\uD55C\uB2E4.</li></ol><p>\uC785\uB825\uC7A5\uCE58\uB85C \uCEF4\uD4E8\uD130\uC5D0 \uC720\uC785\uB418\uB294 \uC815\uBCF4\uB294 \uBA85\uB839\uC5B4\uC640 \uB370\uC774\uD130\uB85C \uBD84\uB958\uD55C\uB2E4. \uBA85\uB839\uC5B4\uB294 \uC2E4\uD589\uD560 \uC0B0\uC220.\uB17C\uB9AC \uC5F0\uC0B0\uC758 \uB3D9\uC791\uC744 \uBA85\uC2DC\uD558\uB294 \uBB38\uC7A5\uC73C\uB85C \uC5B4\uB5A4 \uC791\uC5C5\uC744 \uC218\uD589\uD558\uB294 \uBA85\uB839\uC5B4 \uC9D1\uD569\uC774 \uD504\uB85C\uADF8\uB7A8\uC774\uB2E4.<br> \uD504\uB85C\uADF8\uB7A8\uC740 \uCEF4\uD30C\uC77C\uB7EC \uB4F1\uC744 \uC774\uC6A9\uD558\uC5EC 0\uACFC1\uB85C \uC774\uC9C4\uD654\uB41C \uAE30\uACC4 \uBA85\uB839\uC5B4\uB85C \uBCC0\uD658\uD574\uC57C \uCEF4\uD4E8\uD130\uAC00 \uC774\uD574\uD560 \uC218 \uC788\uB2E4.</p><p><strong>\uBA85\uB839\uC5B4 \uAD6C\uC870\uC640 \uC2E4\uD589</strong></p><ul><li>\uBA85\uB839\uC5B4\uB294 \uC5F0\uC0B0\uBD80\uD638\uC640 \uBA85\uB839\uC5B4\uAC00 \uCC98\uB9AC\uD560 \uB370\uC774\uD130, \uB370\uC774\uD130\uAC00 \uC800\uC7A5\uB41C \uB808\uC9C0\uC2A4\uD130\uB098 \uBA54\uBAA8\uB9AC \uC8FC\uC18C\uC778 \uD53C\uC5F0\uC0B0\uC790\uB85C \uAD6C\uC131\uB41C\uB2E4. \uC5F0\uC0B0\uBD80\uD638\uB294 \uD2B9\uBCC4\uD55C \uACBD\uC6B0\uAC00 \uC544\uB2C8\uBA74 \uD55C\uAC1C\uC774\uB098 \uD53C\uC5F0\uC0B0\uC790\uB294 \uC5EC\uB7EC\uAC1C \uC77C \uC218\uB3C4 \uC788\uB2E4.</li><li>\uD53C\uC5F0\uC0B0\uC790\uB294 \uC5F0\uC0B0\uD560 \uB370\uC774\uD130 \uC815\uBCF4\uB97C \uC800\uC7A5\uD55C\uB2E4. \uB370\uC774\uD130\uB294 \uB808\uC9C0\uC2A4\uD130\uB098 \uBA54\uBAA8\uB9AC, \uC785\uCD9C\uB825 \uC7A5\uCE58 \uB4F1\uC5D0 \uC704\uCE58 \uD560 \uC218 \uC788\uB2E4. \uBCF4\uD1B5 <strong>\uB370\uC774\uD130 \uC790\uCCB4\uBCF4\uB2E4\uB294 \uB370\uC774\uD130 \uC704\uCE58\uB97C \uC800\uC7A5</strong>\uD55C\uB2E4.</li><li>\uBA85\uB839\uC5B4\uB294 \uC2E4\uD589\uC804\uC5D0 \uBA54\uC778\uBA54\uBAA8\uB9AC\uC5D0 \uC800\uC7A5\uD558\uBA70 \uD55C \uBC88\uC5D0 \uD558\uB098\uC529 CPU\uC5D0\uC11C \uC21C\uCC28\uC801\uC73C\uB85C \uC804\uC1A1\uD558\uC5EC \uD574\uC11D.\uC2E4\uD589\uD55C\uB2E4.</li><li>\uBA85\uB839\uC5B4\uB294 \uBA85\uB839\uC5B4 \uC778\uCD9C\uACFC \uBA85\uB839\uC5B4 \uC2E4\uD589 \uC8FC\uAE30\uC758 \uBC18\uBCF5 \uCC98\uB9AC\uB85C \uC2E4\uD589\uD55C\uB2E4. \uBA85\uB839\uC5B4 \uC2E4\uD589 \uC0AC\uC774\uD074\uC740 \uC778\uCD9C\uC0AC\uC774\uD074, \uAC04\uC811 \uC0AC\uC774\uD074, \uC2E4\uD574 \uC0AC\uC774\uD074, \uC778\uD130\uB7FD\uD2B8 \uC0AC\uC774\uD074\uB85C \uAD6C\uC131\uB41C\uB2E4. <ul><li>\uC778\uCD9C \uC0AC\uC774\uD074 : \uBA85\uB839\uC5B4 \uC2E4\uD589 \uC0AC\uC774\uD074\uC758 \uCCAB\uBC88\uC9F8 \uB2E8\uACC4\uC774\uB2E4. \uC778\uCD9C \uC0AC\uC774\uD074\uC740 \uBA54\uBAA8\uB9AC\uC5D0\uC11C \uBA85\uB839\uC5B4\uB97C \uC77D\uC5B4 \uBA85\uB839\uC5B4 \uB808\uC9C0\uC2A4\uD130\uC5D0 \uC800\uC7A5\uD558\uACE0, \uB2E4\uC74C \uBA85\uB839\uC5B4\uB97C \uC2E4\uD589\uD558\uB824\uACE0 \uD504\uB85C\uADF8\uB7A8 \uCE74\uC6B4\uD130\uB97C \uC99D\uAC00\uC2DC\uD0A8\uB2E4.</li><li>\uC2E4\uD589 \uC0AC\uC774\uD074 : \uC778\uCD9C\uD55C \uBA85\uB839\uC5B4\uB97C \uD574\uB3C5\uD558\uACE0 \uADF8 \uACB0\uACFC\uC5D0 \uB530\uB77C \uC81C\uC5B4\uC2E0\uD638\uB97C \uBC1C\uC0DD\uC2DC\uCF1C \uBA85\uB839\uC5B4\uB97C \uC2E4\uD589\uD55C\uB2E4. \uC774 \uB2E8\uACC4\uC5D0\uC11C \uC18C\uBE44\uB418\uB294 \uC2DC\uAC04\uC744 \uC2E4\uD589 \uC2DC\uAC04\uC774\uB77C\uACE0 \uD55C\uB2E4.</li><li>\uAC04\uC811 \uC0AC\uC774\uD074 : \uC9C1\uC811 \uC8FC\uC18C \uC9C0\uC815 \uBC29\uBC95\uC744 \uC0AC\uC6A9\uD558\uB294 \uC2E4\uD589 \uC0AC\uC774\uD074\uC740 \uBA85\uB839\uC5B4\uB97C \uC989\uC2DC \uC218\uD589\uD558\uC9C0\uB9CC, \uAC04\uC811 \uC8FC\uC18C \uC9C0\uC815 \uBC29\uBC95\uC744 \uC0AC\uC6A9\uD558\uB294 \uC2E4\uD589 \uC0AC\uC774\uD074\uC740 \uBA85\uB839\uC5B4\uB97C \uC218\uD589\uD558\uAE30 \uC804\uC5D0 \uC2E4\uC81C \uB370\uC774\uD130\uAC00 \uC800\uC7A5\uB41C \uC8FC\uAE30\uC5B5\uC7A5\uCE58\uC758 \uC8FC\uC18C\uC778 \uC720\uD6A8 \uC8FC\uC18C\uB97C \uD55C \uBC88 \uB354 \uC77D\uC5B4 \uC628\uB2E4.</li><li>\uC778\uD130\uB7FD\uD2B8 \uC0AC\uC774\uD074 : \uC778\uD130\uB7FD\uD2B8\uB780 cpu\uAC00 \uD504\uB85C\uADF8\uB7A8\uC744 \uC218\uD589\uD558\uB294 \uB3D9\uC548 \uCEF4\uD4E8\uD130 \uC2DC\uC2A4\uD15C\uC758 \uB0B4\uBD80\uB098 \uC678\uBD80\uC5D0\uC11C \uBC1C\uC0DD\uD558\uB294 \uC608\uAE30\uCE58 \uBABB\uD55C \uC0AC\uAC74\uC744 \uC758\uBBF8\uD55C\uB2E4. CPU\uB294 \uC2E4\uD589\uC0AC\uC774\uD06C\uB97C \uC644\uB8CC\uD55C \uD6C4 \uC778\uD130\uB7FD\uD2B8 \uC694\uAD6C\uAC00 \uC788\uB294\uC9C0 \uAC80\uC0AC\uD55C\uB2E4. \uC778\uD130\uB7FD\uD2B8 \uC694\uAD6C\uAC00 \uC5C6\uC73C\uBA74 \uB2E4\uC74C \uBA85\uB839\uC5B4\uB97C \uC778\uCD9C\uD558\uACE0, \uC778\uD130\uB7FD\uD2B8 \uC694\uAD6C\uAC00 \uC788\uC73C\uBA74 \uC7AC \uC218\uD589\uC911\uC778 \uD504\uB85C\uADF8\uB7A8\uC758 \uC8FC\uC18C(PC)\uAC12\uC744 \uD2B9\uC815\uC7A5\uC18C\uC5D0 \uC800\uC7A5\uD558\uACE0 \uC778\uD130\uB7FD\uD2B8 \uCC98\uB9AC\uB97C \uC644\uB8CC\uD558\uBA74 \uC911\uB2E8\uB41C \uD504\uB85C\uADF8\uB7A8\uC73C\uB85C \uBCF5\uADC0\uD558\uC5EC \uACC4\uC18D \uC218\uD589\uD55C\uB2E4.</li></ul></li></ul>',23),o=[n];function p(c,_){return i(),r("div",null,o)}var g=l(e,[["render",p],["__file","ch1-\uCEF4\uD4E8\uD130-\uC2DC\uC2A4\uD15C-\uAD6C\uC131.html.vue"]]);export{g as default};
