import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as p,d as i}from"./app.d6b42b88.js";var l="/blog/assets/10-map-reduce.a606e237.png";const r={},t=i('<h2 id="\u1106\u1162\u11B8\u1105\u1175\u1103\u1172\u1109\u1173\u110B\u116A-\u1107\u116E\u11AB\u1109\u1161\u11AB-\u1111\u1161\u110B\u1175\u11AF-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1106\u1162\u11B8\u1105\u1175\u1103\u1172\u1109\u1173\u110B\u116A-\u1107\u116E\u11AB\u1109\u1161\u11AB-\u1111\u1161\u110B\u1175\u11AF-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7" aria-hidden="true">#</a> \uB9F5\uB9AC\uB4C0\uC2A4\uC640 \uBD84\uC0B0 \uD30C\uC77C \uC2DC\uC2A4\uD15C</h2><p><strong>\uB9F5\uB9AC\uB4C0\uC2A4\uB294 \uC720\uB2C9\uC2A4 \uB3C4\uAD6C\uC640 \uBE44\uC2B7\uD55C \uBA74\uC774 \uC788\uC9C0\uB9CC \uC218\uCC9C \uB300\uC758 \uC7A5\uBE44\uB85C \uBD84\uC0B0\uD574\uC11C \uC2E4\uD589\uC774 \uAC00\uB2A5\uD558\uB2E4\uB294 \uC810\uC774\uC11C \uCC28\uC774\uAC00 \uC788\uB2E4.</strong> \uB9F5\uB9AC\uB4C0\uC2A4 \uC791\uC5C5\uB3C4 \uC720\uB2C9\uC2A4\uC640 \uB9C8\uCC2C\uAC00\uC9C0\uB85C \uC785\uB825\uC744 \uC218\uC815\uD558\uC9C0 \uC54A\uAE30 \uB54C\uBB38\uC5D0 \uCD9C\uB825\uC744 \uC0DD\uC0B0\uD558\uB294 \uAC83 \uC678\uC5D0 \uB2E4\uB978 \uBD80\uC218 \uD6A8\uACFC\uB294 \uC5C6\uB2E4.</p><p>\uC720\uB2C9\uC2A4\uC5D0\uC11C\uB294 stdin, stdout\uC73C\uB85C \uC8FC\uACE0\uBC1B\uB358 \uC785\uCD9C\uB825\uC744 \uB9F5 \uB9AC\uB4C0\uC2A4\uC5D0\uC11C\uB294 \uBD84\uC0B0 \uD30C\uC77C \uC2DC\uC2A4\uD15C \uC0C1\uC758 \uD30C\uC77C \uD615\uD0DC\uB85C \uB2E4\uB8E8\uAC8C \uB41C\uB2E4. \uD558\uB461 \uB9F5\uB9AC\uB4C0\uC2A4 \uAD6C\uD604\uC5D0\uC11C \uC774 \uD30C\uC77C \uC2DC\uC2A4\uD15C\uC740 HDFS(Hadoop Distribute File System)\uC774\uB77C\uACE0 \uD55C\uB2E4.</p><p>HDFS\uB294 \uBE44\uACF5\uC720 \uC6D0\uCE59\uC744 \uAE30\uBC18\uC73C\uB85C \uD558\uB294\uB370 NAS\uC640 SAN \uC544\uD0A4\uD14D\uCC98\uC5D0\uC11C \uC0AC\uC6A9\uD558\uB294 \uACF5\uC720 \uB514\uC2A4\uD06C \uBC29\uC2DD\uACFC\uB294 \uBC18\uB300\uB2E4. \uACF5\uC720 \uB514\uC2A4\uD06C \uC800\uC7A5\uC18C\uB294 \uC911\uC559 \uC9D1\uC911 \uC800\uC7A5 \uC7A5\uCE58\uB97C \uC0AC\uC6A9\uD558\uB294 \uB370 \uB9DE\uCDA4\uD615 \uD558\uB4DC\uC6E8\uC5B4\uB97C \uC0AC\uC6A9\uD558\uAC70\uB098 \uD30C\uC774\uBC84 \uCC44\uB110\uACFC \uAC19\uC740 \uD2B9\uBCC4\uD55C \uB124\uD2B8\uC6CC\uD06C \uC778\uD504\uB77C\uB97C \uC0AC\uC6A9\uD558\uAE30\uB3C4 \uD55C\uB2E4. \uBC18\uBA74 \uBE44\uACF5\uC720 \uBC29\uC2DD\uC740 \uD2B9\uBCC4\uD55C \uD558\uB4DC\uC6E8\uC5B4\uAC00 \uD544\uC694 \uC5C6\uACE0, \uC77C\uBC18\uC801\uC778 \uB124\uD2B8\uC6CC\uD06C\uC5D0 \uC5F0\uACB0\uB41C \uCEF4\uD4E8\uD130\uBA74 \uCDA9\uBD84\uD558\uB2E4.</p><p>HDFS\uB294 \uAC01 \uC7A5\uBE44\uC5D0\uC11C \uC2E4\uD589\uB418\uB294 \uB370\uBAAC \uD504\uB85C\uC138\uC2A4\uB85C \uAD6C\uC131\uB41C\uB2E4. \uB2E4\uB978 \uB178\uB4DC\uAC00 \uD574\uB2F9 \uC7A5\uBE44\uC5D0 \uC800\uC7A5\uB41C \uD30C\uC77C\uC5D0 \uC811\uADFC \uAC00\uB2A5\uD558\uAC8C\uB054 \uB124\uD2B8\uC6CC\uD06C \uC11C\uBE44\uC2A4\uB97C \uC81C\uACF5\uD55C\uB2E4. \uB124\uC784 \uB178\uB4DC(NameNode)\uB77C\uACE0 \uBD80\uB974\uB294 \uC911\uC559 \uC11C\uBC84\uB294 \uD2B9\uC815 \uD30C\uC77C \uBE14\uB85D\uC774 \uC5B4\uB5A4 \uC7A5\uBE44\uC5D0 \uC800\uC7A5\uB410\uB294\uC9C0 \uCD94\uC801\uD55C\uB2E4. \uB530\uB77C\uC11C HDFS\uB294 \uAC1C\uB150\uC801\uC73C\uB85C\uB294 \uB9E4\uC6B0 \uD070 \uD558\uB098\uC758 \uD30C\uC77C \uC2DC\uC2A4\uD15C\uC774\uACE0 \uB370\uBAAC\uC774 \uC2E4\uD589 \uC911\uC778 \uBAA8\uB4E0 \uC7A5\uBE44\uC758 \uB514\uC2A4\uD06C\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC788\uB2E4. \uC7A5\uBE44\uAC00 \uC8FD\uAC70\uB098 \uB514\uC2A4\uD06C\uAC00 \uC2E4\uD328\uD558\uB294 \uACBD\uC6B0\uC5D0 \uB300\uBE44\uD558\uAE30 \uC704\uD574 \uD30C\uC77C \uBE14\uB85D\uC740 \uC5EC\uB7EC \uC7A5\uBE44\uC5D0 \uBCF5\uC81C\uB41C\uB2E4.</p><p>HDFS\uB294 \uD655\uC7A5\uC131\uC774 \uB6F0\uC5B4\uB098\uB2E4. \uC774\uB97C \uC774\uC6A9\uD55C \uB370\uC774\uD130 \uC800\uC7A5\uACFC \uC811\uADFC \uBE44\uC6A9\uC740 \uBC94\uC6A9 \uD558\uB4DC\uC6E8\uC5B4\uC640 \uC624\uD508\uC18C\uC2A4 \uC18C\uD504\uD2B8\uC6E8\uC5B4\uB97C \uC0AC\uC6A9\uD558\uAE30 \uB54C\uBB38\uC5D0 \uB3D9\uAE09 \uC6A9\uB7C9\uC758 \uC804\uC6A9 \uC800\uC7A5\uC18C \uC7A5\uCE58\uB97C \uC0AC\uC6A9\uD558\uB294 \uBE44\uC6A9\uBCF4\uB2E4 \uD6E8\uC52C \uC800\uB834\uD558\uB2E4. \uADF8\uB807\uAE30 \uB54C\uBB38\uC5D0 \uC774\uB7F0 \uB300\uADDC\uBAA8 \uD655\uC7A5\uC774 \uAC00\uB2A5\uD558\uB2E4.</p><h3 id="_1-\u1106\u1162\u11B8\u1105\u1175\u1103\u1172\u1109\u1173-\u110C\u1161\u11A8\u110B\u1165\u11B8-\u1109\u1175\u11AF\u1112\u1162\u11BC\u1112\u1161\u1100\u1175" tabindex="-1"><a class="header-anchor" href="#_1-\u1106\u1162\u11B8\u1105\u1175\u1103\u1172\u1109\u1173-\u110C\u1161\u11A8\u110B\u1165\u11B8-\u1109\u1175\u11AF\u1112\u1162\u11BC\u1112\u1161\u1100\u1175" aria-hidden="true">#</a> 1. \uB9F5\uB9AC\uB4C0\uC2A4 \uC791\uC5C5 \uC2E4\uD589\uD558\uAE30</h3><p>\uB9F5\uB9AC\uB4C0\uC2A4\uB294 HDFS \uAC19\uC740 \uBD84\uC0B0 \uD30C\uC77C \uC2DC\uC2A4\uD15C \uC704\uC5D0\uC11C \uB300\uC6A9\uB7C9 \uB370\uC774\uD130\uC14B\uC744 \uCC98\uB9AC\uD558\uB294 \uCF54\uB4DC\uB97C \uC791\uC131\uD558\uB294 \uD504\uB85C\uADF8\uB798\uBC0D \uD504\uB808\uC784\uC6CC\uD06C.</p><ol><li><p>\uC785\uB825 \uD30C\uC77C\uC744 \uC77D\uB294\uB2E4. \uB808\uCF54\uB4DC\uB85C \uCABC\uAC20\uB2E4.</p></li><li><p>\uAC01 \uC785\uB825 \uB808\uCF54\uB4DC\uB9C8\uB2E4 \uB9E4\uD37C \uD568\uC218\uB97C \uD638\uCD9C\uD574 \uD0A4\uC640 \uAC12\uC744 \uCD94\uCD9C\uD55C\uB2E4.(\uC0AC\uC6A9\uC790 \uC791\uC131 \uCF54\uB4DC)</p></li><li><p>\uD0A4\uB97C \uAE30\uC900\uC73C\uB85C \uD0A4-\uAC12 \uC30D\uC744 \uBAA8\uB450 \uC815\uB82C\uD55C\uB2E4.</p></li><li><p>\uC815\uB82C\uB41C \uD0A4-\uAC12 \uC30D \uC804\uCCB4\uB97C \uB300\uC0C1\uC73C\uB85C \uB9AC\uB4C0\uC2A4 \uD568\uC218\uB97C \uD638\uCD9C\uD55C\uB2E4.(\uC0AC\uC6A9\uC790 \uC791\uC131 \uCF54\uB4DC)</p></li></ol><p>\uB9F5\uB9AC\uB4C0\uC2A4 \uC791\uC5C5\uC744 \uC0DD\uC131\uD558\uB824\uBA74 \uB2E4\uC74C\uACFC \uAC19\uC774 \uB3D9\uC791\uD558\uB294 \uB9E4\uD37C\uC640 \uB9AC\uB4C0\uC11C\uB77C\uB294 \uB450 \uAC00\uC9C0 \uCF5C\uBC31 \uD568\uC218\uB97C \uAD6C\uD604\uD574\uC57C \uD55C\uB2E4.</p><p>\uB9E4\uD37C(Mapper)</p><p>\uBAA8\uB4E0 \uC785\uB825 \uB808\uCF54\uB4DC\uB9C8\uB2E4 \uD55C \uBC88\uC529\uB9CC \uD638\uCD9C\uB41C\uB2E4. \uC785\uB825 \uB808\uCF54\uB4DC\uB85C\uBD80\uD130 \uD0A4\uC640 \uAC12\uC744 \uCD94\uCD9C\uD558\uB294 \uC791\uC5C5\uC774\uB2E4. \uAC01 \uC785\uB825\uC73C\uB85C\uBD80\uD130 \uC0DD\uC131\uD558\uB294 \uD0A4-\uAC12 \uC30D\uC740 \uBE48 \uC30D\uC744 \uD3EC\uD568\uD574 \uC6D0\uD558\uB294 \uB9CC\uD07C \uC0DD\uC131 \uAC00\uB2A5\uD558\uB2E4. \uB2E4\uC74C \uB808\uCF54\uB4DC\uAE4C\uC9C0 \uC0C1\uD0DC\uB97C \uC720\uC9C0\uD558\uC9C0 \uC54A\uAE30 \uB54C\uBB38\uC5D0 \uAC01 \uB808\uCF54\uB4DC\uB97C \uB3C5\uB9BD\uC801\uC73C\uB85C \uCC98\uB9AC\uD55C\uB2E4.</p><p>\uB9AC\uB4C0\uC11C(Reducer)</p><p>\uB9E4\uD37C\uAC00 \uC0DD\uC0B0\uD55C \uD0A4-\uAC12 \uC30D\uC744 \uBC1B\uC544 \uAC19\uC740 \uD0A4\uB97C \uAC00\uC9C4 \uB808\uCF54\uB4DC\uB97C \uBAA8\uC73C\uACE0 \uD574\uB2F9 \uAC12\uC758 \uC9D1\uD569\uC744 \uBC18\uBCF5\uD574 \uB9AC\uB4C0\uC11C \uD568\uC218\uB97C \uD638\uCD9C\uD55C\uB2E4. \uB9AC\uB4C0\uC11C\uB294 \uCD9C\uB825 \uB808\uCF54\uB4DC\uB97C \uC0DD\uC0B0\uD55C\uB2E4.</p><h3 id="_2-\u1106\u1162\u11B8\u1105\u1175\u1103\u1172\u1109\u1173\u110B\u1174-\u1107\u116E\u11AB\u1109\u1161\u11AB-\u1109\u1175\u11AF\u1112\u1162\u11BC" tabindex="-1"><a class="header-anchor" href="#_2-\u1106\u1162\u11B8\u1105\u1175\u1103\u1172\u1109\u1173\u110B\u1174-\u1107\u116E\u11AB\u1109\u1161\u11AB-\u1109\u1175\u11AF\u1112\u1162\u11BC" aria-hidden="true">#</a> 2. \uB9F5\uB9AC\uB4C0\uC2A4\uC758 \uBD84\uC0B0 \uC2E4\uD589</h3><p><strong>\uB9F5\uB9AC\uB4C0\uC2A4\uB294 \uBCD1\uB82C\uB85C \uC218\uD589\uD558\uB294 \uCF54\uB4DC\uB97C \uC9C1\uC811 \uC791\uC131\uD558\uC9C0 \uC54A\uACE0\uB3C4 \uC5EC\uB7EC \uC7A5\uBE44\uC5D0\uC11C \uB3D9\uC2DC\uC5D0 \uCC98\uB9AC\uAC00 \uAC00\uB2A5\uD558\uB2E4.</strong> \uB9E4\uD37C\uC640 \uB9AC\uB4C0\uC11C\uB294 \uD55C \uBC88\uC5D0 \uD558\uB098\uC758 \uB808\uCF54\uB4DC\uB9CC \uCC98\uB9AC\uD558\uACE0 \uC785\uB825\uC774 \uC5B4\uB514\uC11C \uC624\uB294\uC9C0 \uCD9C\uB825\uC774 \uC5B4\uB514\uB85C \uAC00\uB294\uC9C0 \uC2E0\uACBD \uC4F0\uC9C0 \uC54A\uB294\uB2E4. \uB9F5\uB9AC\uB4C0\uC2A4 \uD504\uB808\uC784\uC6CC\uD06C\uAC00 \uC7A5\uBE44 \uAC04\uC5D0 \uB370\uC774\uD130\uAC00 \uC774\uB3D9\uD558\uB294 \uBCF5\uC7A1\uD55C \uBD80\uBD84\uC744 \uCC98\uB9AC\uD558\uAE30 \uB54C\uBB38\uC774\uB2E4.</p><p>\uBCD1\uB82C \uC2E4\uD589\uC740 \uD30C\uD2F0\uC154\uB2DD\uC744 \uAE30\uBC18\uC73C\uB85C \uD55C\uB2E4. \uC791\uC5C5 \uC785\uB825\uC73C\uB85C HDFS\uC0C1\uC758 \uB514\uB809\uD130\uB9AC\uB97C \uC0AC\uC6A9\uD558\uB294 \uAC83\uC774 \uC77C\uBC18\uC801\uC774\uACE0, \uAC01 \uD30C\uC77C \uB610\uB294 \uD30C\uC77C \uBE14\uB85D\uC744 \uB3C5\uB9BD\uB41C \uB9F5 \uD0DC\uC2A4\uD06C\uC5D0\uC11C \uCC98\uB9AC\uD560 \uB3C5\uB9BD \uD30C\uD2F0\uC158\uC73C\uB85C \uAC04\uC8FC\uD55C\uB2E4.</p><ol><li><p>\uB9E4\uD37C\uAC00 \uC785\uB825 \uD30C\uC77C\uC744 \uC77D\uC5B4\uC11C \uC815\uB82C\uB41C \uCD9C\uB825 \uD30C\uC77C\uC744 \uAE30\uB85D\uD558\uAE30\uB97C \uC644\uB8CC\uD558\uBA74</p></li><li><p>\uB9F5\uB9AC\uB4C0\uC2A4 \uC2A4\uCF00\uC904\uB7EC\uB294 \uB9E4\uD37C\uC5D0\uC11C \uCD9C\uB825 \uD30C\uC77C\uC744 \uAC00\uC838\uC62C \uC218 \uC788\uB2E4\uACE0 \uB9AC\uB4C0\uC11C\uC5D0\uAC8C \uC54C\uB824\uC900\uB2E4.</p></li><li><p>\uB9AC\uB4C0\uC11C\uB294 \uAC01 \uB9E4\uD37C\uC640 \uC5F0\uACB0\uD574\uC11C \uB9AC\uB4C0\uC11C\uAC00 \uB2F4\uB2F9\uD558\uB294 \uD30C\uD2F0\uC158\uC5D0 \uD574\uB2F9\uD558\uB294 \uC815\uB82C\uB41C \uD0A4-\uAC12 \uC30D \uD30C\uC77C\uC744 \uB2E4\uC6B4\uB85C\uB4DC\uD55C\uB2E4. \uB9AC\uB4C0\uC11C\uB97C \uAE30\uC900\uC73C\uB85C \uD30C\uD2F0\uC154\uB2DD \uD558\uACE0 \uC815\uB82C\uD55C \uB4A4 \uB9E4\uD37C\uB85C\uBD80\uD130 \uB370\uC774\uD130 \uD30C\uD2F0\uC158\uC744 \uBCF5\uC0AC\uD558\uB294 \uACFC\uC815\uC744 \uC154\uD50C\uC774\uB77C \uD55C\uB2E4.</p></li><li><p>\uB9AC\uB4C0\uC2A4 \uD0DC\uC2A4\uD06C\uB294 \uB9E4\uD37C\uB85C\uBD80\uD130 \uD30C\uC77C\uC744 \uAC00\uC838\uC640 \uC815\uB82C\uB41C \uC21C\uC11C\uB97C \uC720\uC9C0\uD558\uBA74\uC11C \uBCD1\uD569\uD55C\uB2E4. \uBCD1\uD569\uB41C \uC774\uD6C4 \uB9AC\uB4C0\uC11C\uC758 \uC785\uB825\uC73C\uB85C \uB4E4\uC5B4\uAC08 \uB54C\uB294 \uC11C\uB85C \uC778\uC811\uD558\uAC8C \uB41C\uB2E4.</p></li></ol><p>\uB9AC\uB4C0\uC11C\uB294 \uD0A4\uC640 \uBC18\uBCF5\uC790(iterator)\uB97C \uC778\uC790\uB85C \uD638\uCD9C\uD558\uB294\uB370 \uC774 \uBC18\uBCF5\uC790\uB85C \uC804\uB2EC\uB41C \uD0A4\uC640 \uB3D9\uC77C\uD55C \uD0A4\uB97C \uAC00\uC9C4 \uB808\uCF54\uB4DC\uB97C \uBAA8\uB450 \uD6D1\uC744 \uC218 \uC788\uB2E4. \uB9AC\uB4C0\uC11C\uB294 \uC784\uC758\uC758 \uB85C\uC9C1\uC744 \uC0AC\uC6A9\uD574\uC11C \uC774 \uB808\uCF54\uB4DC\uB4E4\uC744 \uCC98\uB9AC\uD558\uACE0 \uC5EC\uB7EC \uCD9C\uB825 \uB808\uCF54\uB4DC\uB97C \uC0DD\uC131\uD560 \uC218 \uC788\uB2E4. \uC774 \uCD9C\uB825 \uB808\uCF54\uB4DC\uB294 \uBD84\uC0B0 \uD30C\uC77C \uC2DC\uC2A4\uD15C\uC5D0 \uD30C\uC77C\uB85C \uAE30\uB85D\uB41C\uB2E4.<br><img src="'+l+'" alt="map-reduce" loading="lazy"></p><h3 id="_3-\u1106\u1162\u11B8\u1105\u1175\u1103\u1172\u1109\u1173-\u110B\u116F\u110F\u1173\u1111\u1173\u11AF\u1105\u1169" tabindex="-1"><a class="header-anchor" href="#_3-\u1106\u1162\u11B8\u1105\u1175\u1103\u1172\u1109\u1173-\u110B\u116F\u110F\u1173\u1111\u1173\u11AF\u1105\u1169" aria-hidden="true">#</a> 3. \uB9F5\uB9AC\uB4C0\uC2A4 \uC6CC\uD06C\uD50C\uB85C</h3><p>\uB9F5\uB9AC\uB4C0\uC2A4 \uC791\uC5C5 \uD558\uB098\uB85C \uD574\uACB0\uD560 \uC218 \uC788\uB294 \uBB38\uC81C\uC758 \uBC94\uC704\uB294 \uC81C\uD55C\uC801\uC774\uAE30 \uB54C\uBB38\uC5D0 \uB9F5\uB9AC\uB4C0\uC2A4 \uC791\uC5C5\uC744 \uC5F0\uACB0\uD574 \uC6CC\uD06C\uD50C\uB85C\uB85C \uAD6C\uC131\uD558\uB294 \uBC29\uC2DD\uC744 \uC77C\uBC18\uC801\uC73C\uB85C \uC0AC\uC6A9\uD55C\uB2E4. \uB9F5\uB9AC\uB4C0\uC2A4 \uC791\uC5C5 \uD558\uB098\uC758 \uCD9C\uB825\uC744 \uB2E4\uB978 \uB9F5\uB9AC\uB4C0\uC2A4 \uC791\uC5C5\uC758 \uC785\uB825\uC73C\uB85C \uC0AC\uC6A9\uD558\uB294 \uC2DD\uC774\uB2E4. \uD504\uB808\uC784\uC6CC\uD06C\uAC00 \uC9C1\uC811 \uC81C\uACF5\uD558\uC9C0 \uC54A\uAE30 \uB54C\uBB38\uC5D0 \uCCAB \uBC88\uC9F8 \uC791\uC5C5\uC740 HDFS\uC0C1\uC5D0 \uC9C0\uC815\uB41C \uB514\uB809\uD130\uB9AC\uC5D0 \uCD9C\uB825\uD558\uB3C4\uB85D \uC124\uC815\uD558\uACE0 \uB450 \uBC88\uC9F8 \uC791\uC5C5\uC740 \uD574\uB2F9 \uB514\uB809\uD130\uB9AC\uB97C \uC785\uB825\uC73C\uB85C \uC0AC\uC6A9\uD558\uB3C4\uB85D \uC124\uC815\uD574\uC57C \uD55C\uB2E4. \uB450 \uC791\uC5C5\uC740 \uC644\uC804\uD788 \uB3C5\uB9BD\uC801\uC73C\uB85C \uB3D9\uC791\uD55C\uB2E4.</p><h3 id="_4-\u1105\u1175\u1103\u1172\u1109\u1173-\u1109\u1161\u110B\u1175\u1103\u1173-\u110C\u1169\u110B\u1175\u11AB\u1100\u116A-\u1100\u1173\u1105\u116E\u11B8\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#_4-\u1105\u1175\u1103\u1172\u1109\u1173-\u1109\u1161\u110B\u1175\u1103\u1173-\u110C\u1169\u110B\u1175\u11AB\u1100\u116A-\u1100\u1173\u1105\u116E\u11B8\u1112\u116A" aria-hidden="true">#</a> 4. \uB9AC\uB4C0\uC2A4 \uC0AC\uC774\uB4DC \uC870\uC778\uACFC \uADF8\uB8F9\uD654</h3><p>\uB9F5\uB9AC\uB4C0\uC2A4\uC5D0\uB294 \uC801\uC5B4\uB3C4 \uC77C\uBC18\uC801\uC73C\uB85C \uC774\uC57C\uAE30\uD558\uB294 \uC0C9\uC778 \uAC1C\uB150\uC774 \uC5C6\uB2E4. \uC785\uB825 \uD30C\uC77C \uC804\uCCB4 \uB0B4\uC6A9\uC744 \uC77D\uB294\uB370 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C \uC774 \uC5F0\uC0B0\uC744 full table scan\uC774\uB77C \uBD80\uB978\uB2E4. \uC801\uC740 \uC218\uC758 \uB808\uCF54\uB4DC\uB9CC \uC77D\uACE0 \uC2F6\uC744 \uB54C full table scan\uC744 \uC0AC\uC6A9\uD55C\uB2E4\uBA74 \uC0C9\uC778 \uD0D0\uC0C9\uC5D0 \uBE44\uD574 \uBE44\uC6A9\uC774 \uD130\uBB34\uB2C8\uC5C6\uAC8C \uB9CE\uC774 \uB4E0\uB2E4. \uADF8\uB7EC\uB098 \uBD84\uC11D \uC9C8\uC758\uB294 \uB300\uB7C9\uC758 \uB808\uCF54\uB4DC\uB97C \uB300\uC0C1\uC73C\uB85C \uC9D1\uACC4 \uC5F0\uC0B0\uC744 \uD558\uB294 \uAC83\uC774 \uC77C\uBC18\uC801\uC774\uB2E4. \uC774\uB7F0 \uACBD\uC6B0 \uC785\uB825 \uC804\uCCB4\uB97C \uC2A4\uCE94\uD558\uB294 \uAC74 \uC0C1\uB2F9\uC774 \uD569\uB9AC\uC801\uC774\uB2E4. \uC5EC\uB7EC \uC7A5\uBE44\uC5D0 \uAC78\uCCD0 \uBCD1\uB82C \uCC98\uB9AC\uAC00 \uAC00\uB2A5\uD55C \uACBD\uC6B0\uB294 \uD2B9\uD788 \uADF8\uB807\uB2E4. \uC77C\uAD04 \uCC98\uB9AC \uB9E5\uB77D\uC5D0\uC11C \uC870\uC778\uC740 \uB370\uC774\uD130\uC14B \uB0B4 \uBAA8\uB4E0 \uC5F0\uAD00 \uAD00\uACC4\uB97C \uB2E4\uB8EC\uB2E4\uB294 \uB73B\uC774\uB2E4. \uC608\uB97C \uB4E4\uBA74 \uD2B9\uC815 \uC0AC\uC6A9\uC790\uC758 \uB370\uC774\uD130 \uB9CC\uC744 \uCC3E\uB294 \uAC83\uC774 \uC544\uB2C8\uB77C \uBAA8\uB4E0 \uC0AC\uC6A9\uC790 \uB370\uC774\uD130\uB97C \uB3D9\uC2DC\uC5D0 \uCC98\uB9AC\uD55C\uB2E4. \uC77C\uAD04\uCC98\uB9AC\uC5D0\uC11C \uD558\uB098\uC758 \uC774\uBCA4\uD2B8 \uD14C\uC774\uBE14\uACFC \uC5EC\uB7EC \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0 \uC788\uB294 \uC720\uC800 \uD14C\uC774\uBE14\uC744 \uC870\uC778\uD560 \uB54C \uAC00\uC7A5 \uAC04\uB2E8\uD558\uAC8C \uAD6C\uD604\uD558\uB294 \uBC29\uBC95\uC740 \uD558\uB098\uC529 \uD65C\uB3D9 \uC774\uBCA4\uD2B8\uB97C \uD6D1\uC73C\uBA74\uC11C \uB098\uC624\uB294 \uBAA8\uB4E0 \uC0AC\uC6A9\uC790 ID\uB9C8\uB2E4 \uC6D0\uACA9 \uC11C\uBC84\uC5D0 \uC788\uB294 \uC0AC\uC6A9\uC790 DB\uC5D0 \uC694\uCCAD\uC744 \uBCF4\uB0B4\uB294 \uAC83\uC774\uB2E4. \uC548\uB420 \uAC74 \uC5C6\uC9C0\uB9CC \uC131\uB2A5\uC774 \uC88B\uC9C0 \uC54A\uB2E4. \uC77C\uAD04 \uCC98\uB9AC\uC5D0\uC11C \uCC98\uB9AC\uB7C9\uC744 \uB192\uC774\uAE30 \uC704\uD574\uC11C\uB294 \uAC00\uB2A5\uD55C \uD55C \uD55C \uC7A5\uBE44 \uB0B4\uC5D0\uC11C \uC5F0\uC0B0\uC744 \uC218\uD589\uD574\uC57C \uD55C\uB2E4. \uCC98\uB9AC\uD560 \uBAA8\uB4E0 \uB808\uCF54\uB4DC\uB97C \uB124\uD2B8\uC6CC\uD06C\uB97C \uD1B5\uD574 \uC784\uC758 \uC811\uADFC \uC694\uCCAD\uC744 \uD558\uB294 \uAC83\uC740 \uB108\uBB34 \uB290\uB9AC\uB2E4. \uB610\uD55C \uC6D0\uACA9 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC9C8\uC758\uD55C\uB2E4\uB294 \uAC74 \uC77C\uAD04 \uCC98\uB9AC\uAC00 \uBE44\uACB0\uC815\uC801\uC774\uB77C\uB294 \uB73B\uC774\uB2E4. \uC88B\uC740 \uBC29\uBC95\uC740 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uC0AC\uBCF8\uC744 \uAC00\uC838\uC640 \uBD84\uC0B0 \uD30C\uC77C \uC2DC\uC2A4\uD15C\uC5D0 \uB123\uB294 \uBC29\uBC95\uC774\uB2E4. \uADF8\uB7EC\uBA74 \uC0AC\uC6A9\uC790 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uAC00 \uAC19\uC740 HDFS\uC0C1\uC5D0 \uC874\uC7AC\uD558\uACE0 \uB9F5\uB9AC\uB4C0\uC2A4\uB97C \uC0AC\uC6A9\uD574 \uC5F0\uAD00\uB41C \uB808\uCF54\uB4DC\uB07C\uB9AC \uBAA8\uB450 \uAC19\uC740 \uC7A5\uC18C\uB85C \uBAA8\uC544 \uD6A8\uC728\uC801\uC73C\uB85C \uCC98\uB9AC\uAC00 \uAC00\uB2A5\uD558\uB2E4.</p><ul><li>\uC815\uB82C \uBCD1\uD569 \uC870\uC778 \uC54C\uACE0\uB9AC\uC998</li></ul><p>\uC870\uC778\uD560 \uAC01 \uC785\uB825\uC740 \uC870\uC778 \uD0A4\uB97C \uCD94\uCD9C\uD558\uB294 \uB9E4\uD37C\uB97C \uD1B5\uACFC\uD55C\uB2E4. \uB9F5\uB9AC\uB4C0\uC2A4 \uD504\uB808\uC784\uC6CC\uD06C\uC5D0\uC11C \uD0A4\uB85C \uB9E4\uD37C\uC758 \uCD9C\uB825\uC744 <strong>\uD30C\uD2F0\uC154\uB2DD</strong>\uD574 \uD0A4-\uAC12 \uC30D\uC73C\uB85C <strong>\uC815\uB82C</strong>\uD55C\uB2E4\uBA74 \uAC19\uC740 \uC0AC\uC6A9\uC790\uC758 \uC870\uC778\uD560 \uAC01 \uAC12\uC740 \uB9AC\uB4C0\uC11C\uC758 \uC785\uB825\uC73C\uB85C \uC11C\uB85C \uC778\uC811\uD574\uC11C \uB4E4\uC5B4\uAC04\uB2E4. \uB9E4\uD37C \uCD9C\uB825\uC774 \uD0A4\uB85C \uC815\uB82C\uB41C \uD6C4\uC5D0 \uB9AC\uB4C0\uC11C\uAC00 \uC870\uC778\uC758 \uC591\uCE21\uC744 \uC815\uB82C\uB41C \uB808\uCF54\uB4DC \uBAA9\uB85D\uC744 \uBCD1\uD569\uD558\uAE30 \uB54C\uBB38\uC774\uB2E4. \uC989 \uC870\uC778 \uD0A4\uB97C \uCD94\uCD9C\uD558\uB294 \uB9E4\uD37C\uB97C \uD1B5\uACFC\uD55C \uD6C4 \uD30C\uD2F0\uC154\uB2DD, \uC815\uB82C, \uBCD1\uD569 \uACFC\uC815\uC744 \uB9C8\uCE58\uBA74 \uAC19\uC740 \uD0A4\uB97C \uAC00\uC9C0\uB294 \uBAA8\uB4E0 \uB808\uCF54\uB4DC\uB294 \uD558\uB098\uC758 \uB9AC\uB4C0\uC11C\uC5D0\uC11C \uD638\uCD9C\uB41C\uB2E4. \uC774 \uB9AC\uB4C0\uC11C \uD568\uC218\uC5D0\uC11C \uBCD1\uD569\uB41C \uB808\uCF54\uB4DC\uB97C \uCD9C\uB825\uD560 \uC218 \uC788\uB2E4. \uC774\uB807\uAC8C \uD568\uC73C\uB85C\uC368 \uB9AC\uB4C0\uC11C\uB294 \uD2B9\uC815 \uB370\uC774\uD130 ID\uC758 \uBAA8\uB4E0 \uB808\uCF54\uB4DC\uB97C \uD55C \uBC88\uC5D0 \uCC98\uB9AC\uD558\uBBC0\uB85C \uD55C \uBC88\uC5D0 \uD558\uB098\uC758 ID\uC640 \uAD00\uB828\uB41C \uC815\uBCF4\uB9CC \uBA54\uBAA8\uB9AC\uC5D0 \uC720\uC9C0\uD558\uBA74 \uB418\uACE0 \uB124\uD2B8\uC6CC\uD06C\uB85C \uC544\uBB34 \uC694\uCCAD\uB3C4 \uBCF4\uB0BC \uD544\uC694\uAC00 \uC5C6\uB2E4.</p><ul><li>\uAC19\uC740 \uACF3\uC73C\uB85C \uC5F0\uAD00\uB41C \uB370\uC774\uD130 \uAC00\uC838\uC624\uAE30</li></ul><p>\uC704 \uC54C\uACE0\uB9AC\uC998\uC5D0\uC11C \uD2B9\uC815 ID\uC870\uC778 \uC5F0\uC0B0 \uC2DC \uD544\uC694\uD55C \uBAA8\uB4E0 \uB370\uC774\uD130\uB97C \uD55C \uACF3\uC5D0 \uBAA8\uC740\uB2E4. \uB9E4\uD37C\uAC00 \uB9AC\uB4C0\uC11C\uB85C &#39;\uBA54\uC2DC\uC9C0 \uC804\uC1A1&#39;\uC2DC \uD0A4\uB294 \uAC12\uC744 \uBCF4\uB0BC \uBAA9\uC801\uC9C0\uC758 \uC8FC\uC18C \uC5ED\uD560\uC744 \uD55C\uB2E4. \uD0A4\uAC00 IP\uC8FC\uC18C\uB098 \uD3EC\uD2B8\uBC88\uD638 \uAC19\uC740 \uC2E4\uC81C \uB124\uD2B8\uC6CC\uD06C \uC8FC\uC18C\uAC00 \uC544\uB2C8\uB77C \uC77C\uBC18 \uBB38\uC790\uC5F4\uC77C \uBFD0\uC774\uC9C0\uB9CC \uC8FC\uC18C\uCC98\uB7FC \uB3D9\uC791\uD55C\uB2E4. \uAC19\uC740 \uD0A4\uB97C \uAC00\uC9C4 \uD0A4-\uAC12 \uC30D\uC740 \uBAA8\uB450 \uAC19\uC740 \uBAA9\uC801\uC9C0\uB85C \uBC30\uB2EC\uB41C\uB2E4.(\uAC19\uC740 \uB9AC\uB4C0\uC11C\uB97C \uD638\uCD9C\uD55C\uB2E4) \uB9F5\uB9AC\uB4C0\uC2A4 \uD504\uB85C\uADF8\uB798\uBC0D \uBAA8\uB378\uC740 \uC62C\uBC14\uB978 \uC7A5\uBE44\uB85C \uB370\uC774\uD130\uB97C \uBAA8\uC73C\uB294 \uC5F0\uC0B0\uC758 \uBB3C\uB9AC\uC801 \uB124\uD2B8\uC6CC\uD06C \uD1B5\uC2E0 \uCE21\uBA74\uACFC \uBC1B\uC740 \uB370\uC774\uD130\uB97C \uCC98\uB9AC\uD558\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uB85C\uC9C1\uC744 \uBD84\uB9AC\uD55C\uB2E4. \uB9F5\uB9AC\uB4C0\uC2A4\uB294 \uBAA8\uB4E0 \uB124\uD2B8\uC6CC\uD06C \uD1B5\uC2E0\uC744 \uC9C1\uC811 \uAD00\uB9AC\uD558\uAE30 \uB54C\uBB38\uC5D0 \uD2B9\uC815 \uC7A5\uBE44\uAC00 \uC8FD\uB294 \uAC83\uACFC \uAC19\uC774 \uBD80\uBD84\uC801\uC73C\uB85C \uC2E4\uD328\uAC00 \uBC1C\uC0DD\uD558\uB354\uB77C\uB3C4 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uCF54\uB4DC \uB2E8\uC5D0\uC11C \uACE0\uBBFC\uD560 \uD544\uC694\uAC00 \uC5C6\uB2E4.</p><ul><li>\uADF8\uB8F9\uD654</li></ul><p>\uC870 \uC778 \uC678\uC5D0 \uAC19\uC740 \uACF3\uC73C\uB85C \uAD00\uB828 \uB370\uC774\uD130\uB97C \uBAA8\uC73C\uB294 \uC77C\uBC18\uC801\uC778 \uC0AC\uC6A9 \uC720\uD615\uC740 GROUP BY\uC808\uACFC \uAC19\uC774 \uD2B9\uC815 \uD0A4\uB85C \uB808\uCF54\uB4DC\uB97C \uADF8\uB8F9\uD654\uD558\uB294 \uAC83\uC774\uB2E4. \uB9E4\uD37C\uAC00 \uD0A4-\uAC12 \uC30D\uC744 \uC0DD\uC131\uD560 \uB54C \uADF8\uB8F9\uD654\uD560 \uB300\uC0C1\uC744 \uD0A4\uB85C \uD558\uC5EC \uAC19\uC740 \uD0A4\uB97C \uAC00\uC9C4 \uBAA8\uB4E0 \uB808\uCF54\uB4DC\uB97C \uAC19\uC740 \uB9AC\uB4C0\uC11C\uB85C \uBAA8\uC740\uB2E4. \uB9F5 \uB9AC\uB4C0\uC2A4\uC5D0\uC11C \uADF8\uB8F9\uD654\uC640 \uC870\uC778\uC758 \uAD6C\uD604\uC740 \uC0C1\uB2F9\uD788 \uC720\uC0AC\uD558\uB2E4. \uADF8\uB8F9\uD654\uB97C \uC0AC\uC6A9\uD558\uB294 \uC608\uB294 \uAC01 \uADF8\uB8F9\uC758 \uB808\uCF54\uB4DC \uC218\uB97C \uCE74\uC6B4\uD2B8\uD558\uAC70\uB098, \uD2B9\uC815 \uD544\uB4DC \uB0B4 \uBAA8\uB4E0 \uAC12\uC744 \uB354\uD558\uAC70\uB098, \uC5B4\uB5A4 \uB7AD\uD0B9 \uD568\uC218\uB97C \uC2E4\uD589\uD588\uC744 \uB54C \uC0C1\uC704 K\uAC1C\uC758 \uB808\uCF54\uB4DC \uACE0\uB974\uAE30 \uB4F1\uC774 \uC788\uB2E4.</p><ul><li>\uC3E0\uB9BC \uB2E4\uB8E8\uAE30</li></ul><p>\uBD88\uADE0\uD615\uD55C \uD65C\uC131 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uB808\uCF54\uB4DC\uB97C \uB9B0\uCE58\uD540 \uAC1D\uCCB4, \uD56B \uD0A4 \uB77C \uD55C\uB2E4. \uC720\uBA85\uC778\uC0AC \uD55C \uC0AC\uB78C\uC5D0 \uAD00\uB828\uB41C \uBAA8\uB4E0 \uD65C\uB3D9\uC5D0 \uB300\uD574\uC11C \uD558\uB098\uC758 \uB9AC\uB4C0\uC11C\uC5D0 \uBAA8\uC740\uB2E4\uBA74 \uC0C1\uB2F9\uD55C \uC3E0\uB9BC \uD604\uC0C1\uC774 \uC0DD\uAE34\uB2E4. \uC774\uB7F0 \uD604\uC0C1\uC744 \uD56B\uC2A4\uD31F\uC774\uB77C \uD55C\uB2E4. \uD55C \uB9AC\uB4C0\uC11C\uAC00 \uB2E4\uB978 \uB9AC\uB4C0\uC11C\uBCF4\uB2E4 \uC5C4\uCCAD\uB098\uAC8C \uB9CE\uC740 \uB808\uCF54\uB4DC\uB97C \uCC98\uB9AC\uD574\uC57C \uD55C\uB2E4\uB294 \uB73B\uC774\uB2E4.</p><p>\uC870\uC778 \uC785\uB825\uC5D0 \uD56B \uD0A4\uAC00 \uC874\uC7AC\uD558\uB294 \uACBD\uC6B0 \uD56B\uC2A4\uD31F\uC744 \uC644\uD654\uD560 \uBA87 \uAC00\uC9C0 \uC544\uC774\uB514\uC5B4\uB294 \uC3E0\uB9B0 \uC870\uC778(skewed join) \uBA54\uC11C\uB4DC\uB97C \uD1B5\uD574 \uD56B\uD0A4\uB97C \uACB0\uC815\uD55C \uD6C4 \uD56B \uD0A4\uB97C \uC5EC\uB7EC \uB9AC\uB4C0\uC11C\uC5D0 \uD37C\uB728\uB824\uC11C \uCC98\uB9AC\uD558\uAC8C \uD558\uB294 \uBC29\uBC95\uC774\uB2E4. \uB2E4\uB978 \uC870\uC778 \uC785\uB825\uC744 \uC5EC\uB7EC \uB9AC\uB4C0\uC11C\uB85C \uBCF5\uC81C\uD558\uB294 \uBE44\uC6A9\uC774 \uB4E4\uC9C0\uB9CC \uBCD1\uB82C\uD654 \uD6A8\uACFC\uAC00 \uD6E8\uC52C \uD06C\uB2E4. \uD639\uC740 \uD14C\uC774\uBE14 \uBA54\uD0C0 \uB370\uC774\uD130\uC5D0 \uD56B\uD0A4\uB97C \uBA85\uC2DC\uC801\uC73C\uB85C \uC9C0\uC815\uD558\uACE0 \uD56B \uD0A4\uC640 \uAD00\uB828\uB41C \uB808\uCF54\uB4DC\uB97C \uB098\uBA38\uC9C0 \uD0A4\uC640\uB294 \uBCC4\uB3C4 \uD30C\uC77C\uC5D0 \uC800\uC7A5 \uD6C4 \uD574\uB2F9 \uD14C\uC774\uBE14\uC5D0\uC11C \uC870\uC778\uD560 \uB54C \uB9F5 \uC0AC\uC774\uB4DC \uC870\uC778\uC744 \uC0AC\uC6A9\uD574 \uCC98\uB9AC\uD558\uB294 \uAC83\uC774\uB2E4.(\uB2E4\uC74C \uC8FC\uC81C)</p><h3 id="_5-\u1106\u1162\u11B8-\u1109\u1161\u110B\u1175\u1103\u1173-\u110C\u1169\u110B\u1175\u11AB" tabindex="-1"><a class="header-anchor" href="#_5-\u1106\u1162\u11B8-\u1109\u1161\u110B\u1175\u1103\u1173-\u110C\u1169\u110B\u1175\u11AB" aria-hidden="true">#</a> 5. \uB9F5 \uC0AC\uC774\uB4DC \uC870\uC778</h3><p>\uC704\uC758 \uB0B4\uC6A9\uC5D0\uC11C \uB2E4\uB8EC \uB9AC\uB4C0\uC2A4 \uC0AC\uC774\uB4DC \uC870\uC778\uC740 \uC2E4\uC81C \uC870\uC778 \uB85C\uC9C1\uC744 \uB9AC\uB4C0\uC11C\uC5D0\uC11C \uC218\uD589\uD558\uAE30 \uB54C\uBB38\uC5D0 \uADF8\uB807\uAC8C \uBD88\uB9B0\uB2E4. \uD574\uB2F9 \uBC29\uBC95\uC740 \uC785\uB825 \uB370\uC774\uD130\uC758 \uC18D\uC131\uACFC \uAD6C\uC870\uAC00 \uBB34\uC5C7\uC774\uB4E0 \uB9E4\uD37C\uAC00 \uB370\uC774\uD130\uB97C \uC870\uC778\uD560 \uC900\uBE44\uB97C \uD560 \uC218 \uC788\uB2E4\uB294 \uC7A5\uC810\uC774 \uC788\uC9C0\uB9CC \uC815\uB82C \uD6C4 \uB9AC\uB4C0\uC11C\uB85C \uBCF5\uC0AC \uD55C \uB4A4 \uB9AC\uB4C0\uC11C \uC785\uB825\uC744 \uBCD1\uD569\uD558\uB294 \uBAA8\uB4E0 \uACFC\uC815\uC5D0\uC11C \uB4DC\uB294 \uBE44\uC6A9\uC774 \uC0C1\uB2F9\uD788 \uD06C \uB2E4\uB294 \uB2E8\uC810\uC774 \uC788\uB2E4. \uADF8\uB7EC\uB098 \uC785\uB825 \uB370\uC774\uD130\uC5D0 \uB300\uD574 \uD2B9\uC815\uD55C \uAC00\uC815\uC774 \uAC00\uB2A5\uD558\uB2E4\uBA74 \uB9F5 \uC0AC\uC774\uB4DC \uC870\uC778(map-side-join)\uC744 \uC0AC\uC6A9\uD574 \uC870\uC778\uC744 \uB354 \uBE60\uB974\uAC8C \uC218\uD589\uD560 \uC218 \uC788\uB2E4.</p><ul><li><p>\uBE0C\uB85C\uB4DC \uCE90\uC2A4\uD2B8 \uD574\uC2DC \uC870\uC778<br> \uC870\uC778\uD560 \uC785\uB825 \uB450 \uAC1C \uC911 \uC911 \uD558\uB098\uAC00 \uC0C1\uB300\uC801\uC73C\uB85C \uC791\uB2E4\uBA74 \uD30C\uD2F0\uC154\uB2DD \uD558\uC9C0 \uC54A\uACE0 \uD574\uC2DC \uD14C\uC774\uBE14\uC5D0 \uBAA8\uB450 \uC801\uC7AC\uD560 \uC218 \uC788\uB2E4. \uD070 \uC870\uC778 \uC785\uB825\uC758 \uAC01 \uD30C\uD2F0\uC158\uC5D0\uC11C \uB9E4\uD37C\uB97C \uC2DC\uC791\uD560 \uB54C \uAC01 \uB9E4\uD37C\uC5D0 \uC791\uC740 \uC785\uB825\uC73C\uB85C \uB9CC\uB4E4\uC5B4\uC9C4 \uD574\uC2DC \uD14C\uC774\uBE14\uC744 \uC801\uC7AC\uD558\uACE0 \uD070 \uC785\uB825\uC5D0\uC11C \uD55C \uBC88\uC5D0 \uD558\uB098\uC529 \uB808\uCF54\uB4DC\uB97C \uC2A4\uCE94\uD558\uBA74\uC11C \uAC01 \uB808\uCF54\uB4DC\uAC00 \uD574\uC2DC \uD14C\uC774\uBE14\uC5D0 \uC874\uC7AC\uD558\uB294\uC9C0 \uC9C8\uC758\uD55C\uB2E4.</p></li><li><p>\uD30C\uD2F0\uC158 \uD574\uC2DC \uC870\uC778<br> \uC870\uC778 \uC785\uB825 \uB450 \uAC1C\uB97C \uAC19\uC740 \uBC29\uC2DD\uC73C\uB85C \uD30C\uD2F0\uC154\uB2DD\uD558\uBA74(\uAC19\uC740 \uD0A4\uC640 \uAC19\uC740 \uD574\uC2DC \uD568\uC218\uC640 \uAC19\uC740 \uD30C\uD2F0\uC158 \uC218\uB97C \uC0AC\uC6A9) \uD574\uC2DC \uD14C\uC774\uBE14 \uBC29\uC2DD\uC744 \uAC01 \uD30C\uD2F0\uC158 \uBCC4\uB85C \uB3C5\uB9BD\uC801\uC73C\uB85C \uC0AC\uC6A9\uD560 \uC218 \uC788\uB2E4.</p></li></ul><h2 id="_6-\u110C\u116E\u11BC\u1100\u1161\u11AB-\u1109\u1161\u11BC\u1110\u1162-\u1100\u116E\u110E\u1166\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#_6-\u110C\u116E\u11BC\u1100\u1161\u11AB-\u1109\u1161\u11BC\u1110\u1162-\u1100\u116E\u110E\u1166\u1112\u116A" aria-hidden="true">#</a> 6. \uC911\uAC04 \uC0C1\uD0DC \uAD6C\uCCB4\uD654</h2><p>\uBAA8\uB4E0 \uB9F5\uB9AC\uB4C0\uC2A4 \uC791\uC5C5\uC740 \uB2E4\uB978 \uC791\uC5C5\uACFC \uBAA8\uB450 \uB3C5\uB9BD\uC801\uC774\uB2E4. \uC791\uC5C5\uC774 \uC678\uBD80 \uC138\uACC4\uC640 \uB9CC\uB098\uB294 \uC8FC\uC694 \uC811\uC810\uC740 \uBD84\uC0B0 \uD30C\uC77C \uC2DC\uC2A4\uD15C \uC0C1\uC758 \uC785\uB825\uACFC \uCD9C\uB825 \uB514\uB809\uD130\uB9AC\uB2E4. \uCCAB \uBC88\uC9F8 \uC791\uC5C5\uC758 \uCD9C\uB825\uC744 \uB450 \uBC88\uC9F8 \uC791\uC5C5\uC758 \uC791\uC5C5\uC758 \uC785\uB825\uC73C\uB85C \uC0AC\uC6A9\uD558\uB824\uBA74 \uAC19\uAC8C \uC124\uC815\uD574\uC57C \uD55C\uB2E4. \uADF8\uB9AC\uACE0 \uC678\uBD80 \uC6CC\uD06C\uD50C\uB85C \uC2A4\uCF00\uC904\uB7EC\uC5D0\uC11C \uBC18\uB4DC\uC2DC \uCCAB \uBC88\uC9F8 \uC791\uC5C5\uC744 \uC644\uB8CC\uD55C \uD6C4\uC5D0 \uB450 \uBC88\uC9F8 \uC791\uC5C5\uC744 \uC218\uD589\uD574\uC57C \uD55C\uB2E4.\u2019 \uC774\uB54C \uD55C \uC791\uC5C5\uC758 \uCD9C\uB825\uC774 \uB2E8\uC9C0 \uB2E4\uB978 \uD2B9\uC815 \uC791\uC5C5\uC758 \uC785\uB825\uC73C\uB85C\uB9CC \uC0AC\uC6A9\uB420 \uB54C \uBD84\uC0B0 \uD30C\uC77C \uC2DC\uC2A4\uD15C \uC0C1\uC5D0 \uC788\uB294 \uD30C\uC77C\uB4E4\uC740 \uB2E8\uC21C\uD788 \uD55C \uC791\uC5C5\uC5D0\uC11C \uB2E4\uB978 \uC791\uC5C5\uC73C\uB85C \uB370\uC774\uD130\uB97C \uC62E\uAE30\uB294 \uC218\uB2E8, \uC911\uAC04 \uC0C1\uD0DC\uB2E4. \uC911\uAC04 \uC0C1\uD0DC\uB97C \uD30C\uC77C\uB85C \uAE30\uB85D\uD558\uB294 \uACFC\uC815\uC744 \uAD6C\uCCB4\uD654(materialization)\uB77C \uD55C\uB2E4.</p><p>\uC911\uAC04 \uC0C1\uD0DC\uB97C \uC644\uC804\uD788 \uAD6C\uCCB4\uD654\uD558\uB294 \uB9F5\uB9AC\uB4C0\uC2A4 \uC811\uADFC\uBC95\uC740 \uC720\uB2C9\uC2A4 \uD30C\uC774\uD504\uC5D0 \uBE44\uD574 \uC5EC\uB7EC \uB2E8\uC810\uC774 \uC788\uB2E4.</p><ol><li><p>\uC785\uB825\uC744 \uC0DD\uC131\uD558\uB294 \uBAA8\uB4E0 \uC120\uD589 \uC791\uC5C5\uC774 \uC644\uB8CC\uB410\uC744 \uB54C\uB9CC \uC2DC\uC791 \uAC00\uB2A5\uD558\uB2E4.</p></li><li><p>\uB9E4\uD37C\uB294 \uC885\uC885 \uC911\uBCF5\uB418\uAE30\uB3C4 \uD55C\uB2E4.</p></li><li><p>\uBD84\uC0B0 \uD30C\uC77C \uC2DC\uC2A4\uD15C\uC5D0\uC11C \uC911\uAC04 \uC0C1\uD0DC\uB97C \uC800\uC7A5\uD558\uB294 \uAC83\uC740 \uC911\uAC04 \uC0C1\uD0DC \uD30C\uC77C\uB4E4\uC774 \uC5EC\uB7EC \uC7A5\uBE44\uC5D0 \uAC78\uCCD0 \uBCF5\uC81C\uB410\uB2E4\uB294 \uC758\uBBF8\uB2E4. \uC784\uC2DC \uB370\uC774\uD130\uC5D0\uAC8C \uACFC\uC789 \uC870\uCE58\uC774\uB2E4.</p></li></ol><h2 id="\u1103\u1166\u110B\u1175\u1110\u1165-\u1111\u1173\u11AF\u1105\u1169-\u110B\u1166\u11AB\u110C\u1175\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1103\u1166\u110B\u1175\u1110\u1165-\u1111\u1173\u11AF\u1105\u1169-\u110B\u1166\u11AB\u110C\u1175\u11AB" aria-hidden="true">#</a> \uB370\uC774\uD130 \uD50C\uB85C \uC5D4\uC9C4</h2><p>\uB9F5\uB9AC\uB4C0\uC2A4\uC758 \uC704\uC640 \uAC19\uC740 \uBB38\uC81C\uB97C \uD574\uACB0\uD558\uAE30 \uC704\uD574 \uBD84\uC0B0 \uC77C\uAD04\uCC98\uB9AC \uC5F0\uC0B0\uC744 \uC218\uD589\uD558\uB294 \uC5D4\uC9C4\uC774 \uC0C8\uB86D\uAC8C \uBA87 \uAC00\uC9C0 \uAC1C\uBC1C\uB418\uC5C8\uB2E4. \uC2A4\uD30C\uD06C \uD14C\uC988, \uD50C\uB9C1\uD06C \uB4F1\uC758 \uC5D4\uC9C4\uC774 \uB098\uC654\uB2E4. \uC704 \uC5D4\uC9C4\uB4E4\uC758 \uACF5\uD1B5\uC810\uC740 \uC804\uCCB4 \uC6CC\uD06C\uD50C\uB85C\uB97C \uB3C5\uB9BD\uB41C \uD558\uC704 \uC791\uC5C5\uC73C\uB85C \uB098\uB204\uC9C0 \uC54A\uACE0 \uC791\uC5C5 \uD558\uB098\uB85C\uC11C \uB2E4\uB8EC\uB2E4\uB294 \uC810\uC774\uB2E4. \uC774 \uC5D4\uC9C4\uB4E4\uC740 \uC5EC\uB7EC \uCC98\uB9AC \uB2E8\uACC4\uB97C \uD1B5\uD574 \uB370\uC774\uD130 \uD750\uB984\uC744 \uBA85\uC2DC\uC801\uC73C\uB85C \uBAA8\uB378\uB9C1\uD558\uAE30 \uB54C\uBB38\uC5D0 \uB370\uC774\uD130 \uD50C\uB85C \uC5D4\uC9C4\uC774\uB77C \uBD88\uB9B0\uB2E4. \uB370\uC774\uD130 \uD50C\uB85C \uC5D4\uC9C4\uC740 \uC785\uB825\uC744 \uD30C\uD2F0\uC154\uB2DD\uD574 \uBCD1\uB82C\uD654 \uD55C\uB2E4. \uD55C \uD568\uC218\uC758 \uCD9C\uB825\uC744 \uB2E4\uB978 \uD568\uC218\uC758 \uC785\uB825\uC73C\uB85C \uC0AC\uC6A9\uD558\uAE30 \uC704\uD574 \uB124\uD2B8\uC6CC\uD06C\uB97C \uD1B5\uD574 \uBCF5\uC0AC\uD55C\uB2E4.</p><p>\uB9F5\uB9AC\uB4C0\uC2A4\uC640 \uB2EC\uB9AC \uC774 \uD568\uC218\uB4E4\uC740 \uB9F5\uACFC \uB9AC\uB4C0\uC2A4\uB97C \uBC88\uAC08\uC544 \uC218\uD589\uD558\uB294 \uC2DD\uC758 \uADDC\uCE59\uC744 \uC9C0\uD0AC \uD544\uC694\uAC00 \uC5C6\uACE0 \uB354 \uC720\uC5F0\uD55C \uBC29\uBC95\uC73C\uB85C \uD568\uC218\uB4E4\uC744 \uC870\uD569\uD560 \uC218 \uC788\uB2E4. (\uB9F5 : \uB370\uC774\uD130 \uCD94\uCD9C\uD574\uC11C \uBD84\uBC30\uD55C\uB2E4. \uB9AC\uB4C0\uC2A4 : \uCD9C\uB825\uBB3C \uACC4\uC0B0)</p>',42),d=[t];function h(n,s){return e(),p("div",null,d)}var u=a(r,[["render",h],["__file","ch10-batch-processing-2.html.vue"]]);export{u as default};
