import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as r,d as h}from"./app.d6b42b88.js";var i="/blog/assets/11-cdc.ecccf946.png";const d={},p=h('<h2 id="\u1100\u1162\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#\u1100\u1162\u110B\u116D" aria-hidden="true">#</a> \uAC1C\uC694</h2><p>\uC55E\uC120 10\uC7A5\uC5D0\uC11C\uB294 \uC77C\uAD04\uCC98\uB9AC\uB97C \uC0B4\uD3B4\uBCF4\uC558\uB2E4. \uC77C\uAD04\uCC98\uB9AC\uB780 \uC785\uB825\uC73C\uB85C \uD30C\uC77C \uC9D1\uD569\uC744 \uC77D\uACE0 \uCD9C\uB825\uC73C\uB85C \uC0C8\uB85C\uC6B4 \uD30C\uC77C \uC9D1\uD569\uC744 \uC0DD\uC131\uD558\uB294 \uAE30\uC220\uC774\uB2E4. \uCD9C\uB825\uC740 \uD30C\uC0DD \uB370\uC774\uD130\uC774\uB2E4. \uC77C\uAD04\uCC98\uB9AC\uB294 \uD544\uC694\uD558\uB2E4\uBA74 \uB2E4\uC2DC \uC218\uD589\uD574\uC11C \uC7AC\uC0DD\uC131\uC774 \uAC00\uB2A5\uD55C \uB370\uC774\uD130 \uC14B\uC774\uBA70, \uAC80\uC0C9 \uC0C9\uC778\uC774\uB098 \uCD94\uCC9C \uC2DC\uC2A4\uD15C, \uBD84\uC11D \uB4F1\uC5D0\uC11C \uC0AC\uC6A9\uB41C\uB2E4. \uADF8\uB7EC\uB098 \uC77C\uAD04\uCC98\uB9AC\uB294 \uC785\uB825\uC744 \uC0AC\uC804\uC5D0 \uC54C\uB824\uC9C4 \uC720\uD55C\uD55C \uD06C\uAE30\uB85C \uAC00\uC815\uD55C\uB2E4\uB294 \uAC00\uC815\uC774 \uC788\uC5B4\uC57C \uD55C\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uB9F5\uB9AC\uB4C0\uC2A4\uC758 \uD575\uC2EC\uC778 \uC815\uB82C \uC5F0\uC0B0\uC758 \uACBD\uC6B0 \uCD9C\uB825\uC744 \uC2DC\uC791\uD558\uAE30 \uC804\uC5D0 \uC804\uCCB4 \uC785\uB825\uC744 \uB2E4 \uC77D\uC5B4\uC57C \uD55C\uB2E4. \uC870\uAE30 \uCD9C\uB825\uC744 \uC2DC\uC791\uD560 \uC218 \uC5C6\uB2E4.</p><p>\uADF8\uB7EC\uB098 \uC2E4\uC81C \uB370\uC774\uD130\uB294 \uC624\uB298\uB3C4 \uC0DD\uC0B0\uB418\uACE0 \uB0B4\uC77C\uB3C4 \uC0DD\uC0B0\uB41C\uB2E4. \uB370\uC774\uD130 \uC14B\uC740 \uC808\uB300 &#39;\uC644\uB8CC&#39;\uB418\uC9C0 \uC54A\uB294\uB2E4. \uC77C\uAD04\uCC98\uB9AC \uD504\uB85C\uC138\uC11C\uB294 \uC791\uC5C5\uC744 \uC218\uD589\uD558\uAE30 \uC704\uD574 \uC778\uC704\uC801\uC73C\uB85C \uC77C\uC815 \uAE30\uAC04\uC529 \uB370\uC774\uD130 \uCCAD\uD06C\uB97C \uB098\uB220\uC57C \uD55C\uB2E4. \uD558\uB8E8\uAC00 \uB05D\uB098\uB294 \uC2DC\uC810\uC5D0 \uD558\uB8E8 \uB370\uC774\uD130\uB098 \uB9E4\uC2DC\uAC04\uC774 \uB05D\uB0A0 \uB54C \uD574\uB2F9 \uC2DC\uAC04\uC758 \uB370\uC774\uD130\uB97C \uCC98\uB9AC\uD558\uB294 \uC2DD\uC73C\uB85C \uB9D0\uC774\uB2E4.</p><p>\uC77C\uAD04\uCC98\uB9AC\uC758 \uBB38\uC81C\uC810\uC740 \uC785\uB825\uC758 \uBCC0\uD654\uAC00 \uC2DC\uAC04\uC774 \uC9C0\uB098\uC57C \uBC18\uC601\uC774 \uB41C\uB2E4\uB294 \uAC83\uC778\uB370, \uB2F9\uC5F0\uD788 \uC774 \uBC29\uBC95\uC744 \uC0AC\uC6A9\uD558\uBA74 \uC0AC\uC6A9\uC790\uAC00 \uB290\uB07C\uAE30\uC5D0 \uB108\uBB34 \uB290\uB9AC\uB2E4. \uC774\uB7F0 \uC9C0\uCCB4\uB97C \uC904\uC774\uAE30 \uC704\uD574 <strong>\uACE0\uC815\uB41C \uC2DC\uAC04 \uC870\uAC01\uC774\uB77C\uB294 \uAC1C\uB150\uC744 \uC644\uC804\uD788 \uBC84\uB9AC\uACE0 \uB2E8\uC21C\uD788 \uC774\uBCA4\uD2B8\uAC00 \uBC1C\uC0DD\uD560 \uB54C\uB9C8\uB2E4 \uCC98\uB9AC\uD558\uB294 \uAC1C\uB150\uC774 \uC2A4\uD2B8\uB9BC \uCC98\uB9AC</strong>\uC758 \uAE30\uBCF8 \uAC1C\uB150\uC774\uB2E4. \uC77C\uBC18\uC801\uC73C\uB85C \uC2A4\uD2B8\uB9BC\uC740 \uC2DC\uAC04 \uD750\uB984\uC5D0 \uB530\uB77C \uC810\uC9C4\uC801\uC73C\uB85C \uC0DD\uC0B0\uB41C \uB370\uC774\uD130\uB97C \uC77C\uCEEB\uB294\uB2E4. \uC774\uBC88 \uC7A5\uC5D0\uC11C\uB294 \uB370\uC774\uD130 \uAD00\uB9AC \uBA54\uCEE4\uB2C8\uC998\uC778 \uC774\uBCA4\uD2B8 \uC2A4\uD2B8\uB9BC\uC744 \uB2E4\uB8E8\uAC8C \uB418\uB294\uB370 <strong>\uC77C\uAD04\uCC98\uB9AC\uC758 \uB370\uC774\uD130\uB294 \uC785\uB825\uC774 \uD55C\uC815\uC801\uC73C\uB85C \uC774\uBCA4\uD2B8 \uC2A4\uD2B8\uB9BC \uB370\uC774\uD130\uB294 \uC810\uC9C4\uC801\uC73C\uB85C \uCC98\uB9AC\uB41C\uB2E4</strong></p><p>\uC774\uBCA4\uD2B8\uB294 \uD2B9\uC815 \uC2DC\uC810\uC5D0 \uBC1C\uC0DD\uD55C \uC0AC\uAC74\uC744 \uAE30\uB85D\uD55C \uB808\uCF54\uB4DC\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uC6F9\uD398\uC774\uC9C0\uB97C \uBCF4\uAC70\uB098 \uC0C1\uD488\uC744 \uAD6C\uC785\uD558\uB294 \uC77C \uAC19\uC740 \uC0AC\uC6A9\uC790\uAC00 \uCDE8\uD55C \uD589\uB3D9\uC774 \uC774\uBCA4\uD2B8\uAC00 \uB420 \uC218 \uC788\uB2E4. \uC2A4\uD2B8\uB9BC \uC2DC\uC2A4\uD15C\uC5D0\uC11C\uB294 \uB300\uAC1C \uD1A0\uD53D\uC774\uB098 \uC2A4\uD2B8\uB9BC\uC774\uB77C\uB294 \uB2E8\uC5B4\uB85C \uAD00\uB828 \uC774\uBCA4\uD2B8\uB97C \uBB36\uB294\uB2E4. \uC77C\uAD04\uCC98\uB9AC\uC5D0\uC11C \uD30C\uC77C\uC744 \uD55C \uBC88 \uAE30\uB85D\uD558\uBA74 \uC5EC\uB7EC \uC791\uC5C5\uC5D0\uC11C \uC77D\uC744 \uC218 \uC788\uC5C8\uB4EF\uC774 \uC2A4\uD2B8\uB9AC\uBC0D\uC5D0\uC11C\uB3C4 \uC774\uC640 \uBE44\uC2B7\uD558\uB2E4 \uC0DD\uC0B0\uC790(producer)\uAC00 \uC774\uBCA4\uD2B8\uB97C \uD55C \uBC88 \uB9CC\uB4E4\uBA74 \uD574\uB2F9 \uC774\uBCA4\uD2B8\uB97C \uBCF5\uC218\uC758 \uC18C\uBE44\uC790(consumer)\uAC00 \uCC98\uB9AC\uD560 \uC218 \uC788\uB2E4.</p><p>\uC774\uB860\uC0C1\uC73C\uB85C\uB294 \uD30C\uC77C\uC774\uB098 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uAC00 \uC788\uC73C\uBA74 \uC0DD\uC0B0\uC790\uC640 \uC18C\uBE44\uC790\uB97C \uC5F0\uACB0\uD558\uAE30\uC5D0 \uCDA9\uBD84\uD558\uB2E4. \uC0DD\uC0B0\uC790\uB294 \uB370\uC774\uD130 \uC2A4\uD1A0\uC5B4\uC5D0 \uAE30\uB85D\uD558\uACE0 \uC18C\uBE44\uC790\uB294 \uC8FC\uAE30\uC801\uC73C\uB85C \uD3F4\uB9C1 \uD574 \uB9C8\uC9C0\uB9C9\uC73C\uB85C \uCC98\uB9AC\uD55C \uC774\uBCA4\uD2B8 \uC774\uD6C4\uC5D0 \uC0C8\uB85C \uBC1C\uC0DD\uD55C \uC774\uBCA4\uD2B8\uAC00 \uC788\uB294\uC9C0 \uD655\uC778\uD558\uB294 \uBC29\uBC95\uC774\uB2E4. \uADF8\uB7EC\uB098 \uC9C0\uC5F0 \uC2DC\uAC04\uC774 \uB0AE\uC73C\uBA74\uC11C \uC9C0\uC18D\uD574\uC11C \uCC98\uB9AC\uD558\uB294 \uBC29\uC2DD\uC744 \uC9C0\uD5A5\uD560 \uB54C \uB370\uC774\uD130 \uC2A4\uD1A0\uC5B4\uB97C \uC774\uB7F0 \uC6A9\uB3C4\uC5D0 \uB9DE\uAC8C \uC124\uACC4\uD558\uC9C0 \uC54A\uC558\uB2E4\uBA74 \uD3F4\uB9C1 \uBC29\uC2DD\uC740 \uBE44\uC6A9\uC774 \uD06C\uB2E4. \uD3F4\uB9C1\uC774 \uC7A6\uC744\uC218\uB85D \uC0C8\uB85C\uC6B4 \uC774\uBCA4\uD2B8 \uBC18\uD658 \uC694\uCCAD \uBE44\uC728\uC774 \uB0AE\uC544\uC838\uC11C \uC624\uBC84\uD5E4\uB4DC\uAC00 \uCEE4\uC9C4\uB2E4. \uC624\uD788\uB824 \uC0C8\uB85C\uC6B4 \uC774\uBCA4\uD2B8\uAC00 \uB098\uD0C0\uB0A0 \uB54C\uB9C8\uB2E4 \uC18C\uBE44\uC790\uC5D0\uAC8C \uC54C\uB9AC\uB294 \uD3B8\uC774 \uB0AE\uB2E4. \uADF8\uB7EC\uB098 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB294 \uC804\uD1B5\uC801\uC73C\uB85C \uC54C\uB9BC \uBA54\uCEE4\uB2C8\uC998\uC744 \uAC15\uB825\uD558\uAC8C \uC9C0\uC6D0\uD558\uC9C0 \uC54A\uB294\uB2E4. \uBCF4\uD1B5 \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uB294 \uD2B8\uB9AC\uAC70 \uAE30\uB2A5\uC774 \uC788\uC9C0\uB9CC \uAE30\uB2A5\uC774 \uC81C\uD55C\uC801\uC774\uACE0 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB97C \uC124\uACC4\uD55C \uC774\uD6C4\uC5D0 \uB3C4\uC785\uD55C \uAC1C\uB150\uC774\uB2E4. \uB300\uC2E0 <strong>\uC774\uBCA4\uD2B8 \uC54C\uB9BC \uC804\uB2EC \uBAA9\uC801\uC73C\uB85C \uAC1C\uBC1C\uB41C \uD2B9\uBCC4\uD55C \uB3C4\uAD6C\uB4E4\uC774 \uC788\uB2E4.</strong></p><h2 id="\u1106\u1166\u1109\u1175\u110C\u1175\u11BC-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1106\u1166\u1109\u1175\u110C\u1175\u11BC-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7" aria-hidden="true">#</a> \uBA54\uC2DC\uC9D5 \uC2DC\uC2A4\uD15C</h2><p><strong>\uC0C8\uB85C\uC6B4 \uC774\uBCA4\uD2B8\uC5D0 \uB300\uD574 \uC18C\uBE44\uC790\uC5D0\uAC8C \uC54C\uB824\uC8FC\uAE30 \uC704\uD574 \uC4F0\uC774\uB294 \uC77C\uBC18\uC801\uC778 \uBC29\uBC95\uC740 \uBA54\uC2DC\uC9D5 \uC2DC\uC2A4\uD15C\uC744 \uC0AC\uC6A9\uD558\uB294 \uAC83\uC774\uB2E4.</strong> \uC0DD\uC0B0\uC790\uB294 \uC774\uBCA4\uD2B8\uB97C \uD3EC\uD568\uD55C \uBA54\uC2DC\uC9C0\uB97C \uC804\uC1A1\uD55C\uB2E4. \uADF8\uB9AC\uACE0 \uBA54\uC2DC\uC9C0\uB294 \uC18C\uBE44\uC790\uC5D0\uAC8C \uC804\uB2EC\uB41C\uB2E4. \uBA54\uC138\uC9D5 \uC2DC\uC2A4\uD15C\uC740 \uB2E4\uC218\uC758 \uC0DD\uC0B0\uC790 \uB178\uB4DC\uAC00 \uAC19\uC740 \uD1A0\uD53D\uC73C\uB85C \uBA54\uC2DC\uC9C0\uB97C \uC804\uC1A1\uD560 \uC218 \uC788\uACE0 \uB2E4\uC218\uC758 \uC18C\uBE44\uC790 \uB178\uB4DC\uAC00 \uD1A0\uD53D \uD558\uB098\uC5D0\uC11C \uBA54\uC2DC\uC9C0\uB97C \uBC1B\uC544\uAC08 \uC218 \uC788\uB2E4.</p><p>\uBC1C\uD589/\uAD6C\uB3C5(publish/subscribe) \uBAA8\uB378\uC5D0\uC11C\uB294 \uC5EC\uB7EC \uC2DC\uC2A4\uD15C\uB4E4\uC774 \uB2E4\uC591\uD55C \uC811\uADFC\uBC95\uC744 \uC0AC\uC6A9\uD55C\uB2E4. \uBAA8\uB4E0 \uBAA9\uC801\uC5D0 \uBD80\uD569\uD558\uB294 \uD558\uB098\uC758 \uC815\uB2F5\uC740 \uC5C6\uAE30 \uB54C\uBB38\uC5D0 \uC544\uB798 \uB450 \uAC00\uC9C0 \uC9C8\uBB38\uC774 \uC2DC\uC2A4\uD15C\uC744 \uAD6C\uBCC4\uD558\uB294 \uB370 \uC0C1\uB2F9\uD788 \uB3C4\uC6C0\uC774 \uB41C\uB2E4.</p><ol><li><p>\uC0DD\uC0B0\uC790\uAC00 \uC18C\uBE44\uC790\uAC00 \uBA54\uC2DC\uC9C0\uB97C \uCC98\uB9AC\uD558\uB294 \uC18D\uB3C4\uBCF4\uB2E4 \uBE60\uB974\uAC8C \uBA54\uC2DC\uC9C0\uB97C \uC804\uC1A1\uD55C\uB2E4\uBA74 \uC5B4\uB5BB\uAC8C \uB420\uAE4C? \uC2DC\uC2A4\uD15C\uC740 \uBA54\uC2DC\uC9C0\uB97C \uBC84\uB9AC\uAC70\uB098 \uD050\uC5D0 \uBC84\uD37C\uB9C1 \uD558\uAC70\uB098 \uD750\uB984\uC744 \uC81C\uC5B4\uD558\uC5EC \uC0DD\uC0B0\uC790\uAC00 \uBA54\uC2DC\uC9C0\uB97C \uB354 \uBCF4\uB0B4\uC9C0 \uBABB\uD558\uAC8C \uB9C9\uB294\uB2E4.</p></li><li><p>\uB178\uB4DC\uAC00 \uC8FD\uAC70\uB098 \uC77C\uC2DC\uC801\uC73C\uB85C \uC624\uD504\uB77C\uC778\uC774 \uB41C\uB2E4\uBA74 \uC5B4\uB5BB\uAC8C \uB420\uAE4C? \uC190\uC2E4\uB418\uB294 \uBA54\uC2DC\uC9C0\uAC00 \uC0DD\uAE38\uAE4C? \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB97C \uC0AC\uC6A9\uD560 \uB54C\uCC98\uB7FC \uC9C0\uC18D\uC131\uC744 \uAC16\uCD94\uB824\uBA74 \uB514\uC2A4\uD06C\uC5D0 \uAE30\uB85D\uD558\uAC70\uB098 \uBCF5\uC81C \uBCF8 \uC0DD\uC131\uC744 \uD574\uC57C \uD55C\uB2E4. \uADF8\uB807\uAE30 \uB54C\uBB38\uC5D0 \uBE44\uC6A9\uC774 \uB4E0\uB2E4. \uB54C\uB85C\uB294 \uBA54\uC2DC\uC9C0\uB97C \uC783\uC5B4\uB3C4 \uAD1C\uCC2E\uB2E4\uBA74 \uCC98\uB9AC\uB7C9\uC744 \uB192\uC774\uACE0 \uC9C0\uC5F0\uC2DC\uAC04\uC744 \uB0AE\uCD9C \uC218 \uC788\uB2E4.</p></li></ol><p>\uBA54\uC2DC\uC9C0\uC758 \uC720\uC2E4\uC744 \uD5C8\uC6A9\uD558\uC9C0 \uB9D0\uC9C0\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0 \uB530\uB77C \uC0C1\uB2F9\uD788 \uB2E4\uB974\uB2E4. \uC8FC\uAE30\uC801\uC73C\uB85C \uC0DD\uC131\uB418\uB294 \uC13C\uC11C \uAC12\uC740 \uAC00\uB054 \uB370\uC774\uD130\uAC00 \uB204\uB77D\uB418\uB354\uB77C\uB3C4 \uD070 \uBB38\uC81C\uAC00 \uC5C6\uC744 \uC218 \uC788\uC9C0\uB9CC \uC774\uBCA4\uD2B8 \uC218\uB97C \uC138\uB294 \uACBD\uC6B0 \uBA54\uC2DC\uC9C0\uAC00 \uC720\uC2E4\uB410\uB2E4\uB294 \uAC83\uC740 \uCE74\uC6B4\uD130\uAC00 \uC798\uBABB\uB410\uB2E4\uB294 \uC758\uBBF8\uC774\uAE30 \uB54C\uBB38\uC5D0 \uBA54\uC2DC\uC9C0\uB97C \uC2E0\uB8B0\uC131 \uC788\uAC8C \uC804\uC1A1\uD558\uB294 \uC77C\uC740 \uB9E4\uC6B0 \uC911\uC694\uD558\uB2E4.</p><h2 id="\u1106\u1166\u1109\u1175\u110C\u1175-\u1107\u1173\u1105\u1169\u110F\u1165" tabindex="-1"><a class="header-anchor" href="#\u1106\u1166\u1109\u1175\u110C\u1175-\u1107\u1173\u1105\u1169\u110F\u1165" aria-hidden="true">#</a> \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4</h2><p>\uC911\uAC04 \uB178\uB4DC\uB97C \uD1B5\uD558\uC9C0 \uC54A\uACE0 \uC0DD\uC0B0\uC790\uC640 \uC18C\uBE44\uC790\uAC00 \uC9C1\uC811 \uD1B5\uC2E0\uD558\uB294 \uC9C1\uC811 \uBA54\uC2DC\uC9D5 \uC2DC\uC2A4\uD15C\uC740 \uC124\uACC4 \uC0C1\uD669\uC5D0\uC11C\uB294 \uC798 \uB3D9\uC791\uD558\uC9C0\uB9CC \uC77C\uBC18\uC801\uC73C\uB85C \uBA54\uC2DC\uC9C0\uAC00 \uC720\uC2E4\uB420 \uC218 \uC788\uB294 \uAC00\uB2A5\uC131\uC744 \uACE0\uB824\uD574\uC11C \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uCF54\uB4DC\uB97C \uC791\uC131\uD574\uC57C \uD55C\uB2E4. \uD504\uB85C\uD1A0\uCF5C\uC774 \uB124\uD2B8\uC6CC\uD06C \uC0C1\uC5D0\uC11C \uD328\uD0B7 \uC720\uC2E4\uC744 \uAC10\uC9C0\uD558\uACE0 \uC7AC\uC804\uC1A1\uC744 \uD558\uB354\uB77C\uB3C4 \uC18C\uBE44\uC790\uAC00 \uC624\uD504\uB77C\uC778\uC774\uB77C\uBA74 \uBA54\uC2DC\uC9C0\uB97C \uC804\uB2EC\uD558\uC9C0 \uBABB\uD558\uB294 \uC0C1\uD0DC\uC5D0 \uC788\uB294 \uB3D9\uC548 \uC804\uC1A1\uB41C \uBA54\uC2DC\uC9C0\uB294 \uC783\uC5B4\uBC84\uB9B4 \uC218 \uC788\uB2E4.</p><p>\uC9C1\uC811 \uBA54\uC2DC\uC9D5 \uC2DC\uC2A4\uD15C\uC758 \uB300\uC548\uC73C\uB85C \uC0AC\uC6A9\uB418\uB294 \uBC29\uBC95\uC740 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4(\uBA54\uC2DC\uC9C0 \uD050)\uC744 \uD1B5\uD574 \uBA54\uC2DC\uC9C0\uB97C \uBCF4\uB0B4\uB294 \uAC83\uC774\uB2E4. \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC740 \uADFC\uBCF8\uC801\uC73C\uB85C \uBA54\uC2DC\uC9C0 \uC2A4\uD2B8\uB9BC\uC744 \uCC98\uB9AC\uD558\uB294 \uB370 \uCD5C\uC801\uD654\uB41C \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uC77C\uC885\uC774\uB2E4. \uC11C\uBC84\uB85C \uAD6C\uB3D9\uB418\uACE0 \uC0DD\uC0B0\uC790\uC640 \uC18C\uBE44\uC790\uB294 \uC11C\uBC84\uC758 \uD074\uB77C\uC774\uC5B8\uD2B8\uB85C \uC811\uC18D\uD55C\uB2E4. <strong>\uBE0C\uB85C\uCEE4\uC5D0 \uB370\uC774\uD130\uAC00 \uBAA8\uC774\uAE30 \uB54C\uBB38\uC5D0 \uC774 \uC2DC\uC2A4\uD15C\uC740 \uD074\uB77C\uC774\uC5B8\uD2B8\uC758 \uC0C1\uD0DC \uBCC0\uACBD\uC5D0 \uC27D\uAC8C \uB300\uCC98\uD560 \uC218 \uC788\uB2E4. \uC9C0\uC18D\uC131 \uBB38\uC81C\uAC00 \uBE0C\uB85C\uCEE4\uC73C\uB85C \uC62E\uACA8\uAC14\uAE30 \uB54C\uBB38\uC774\uB2E4. \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC740 \uBA54\uC2DC\uC9C0\uB97C \uBA54\uBAA8\uB9AC\uC5D0\uB9CC \uBCF4\uAD00\uD558\uAC70\uB098 \uC7A5\uC560\uB85C \uC911\uB2E8\uB410\uC744 \uB54C\uB3C4 \uBA54\uC2DC\uC9C0\uB97C \uC783\uC5B4\uBC84\uB9AC\uC9C0 \uC54A\uAE30 \uC704\uD574 \uB514\uC2A4\uD06C\uC5D0 \uAE30\uB85D\uD55C\uB2E4.</strong> \uC18C\uBE44 \uC18D\uB3C4\uAC00 \uB290\uB9B0 \uC18C\uBE44\uC790\uAC00 \uC788\uC73C\uBA74 \uC77C\uBC18\uC801\uC73C\uB85C \uBE0C\uB85C\uCEE4\uC740 \uD050\uAC00 \uC81C\uD55C \uC5C6\uC774 \uACC4\uC18D \uB298\uC5B4\uB098\uAC8C \uD55C\uB2E4. \uC774\uB7F0 \uC124\uC815\uC740 \uC120\uD0DD \uAC00\uB2A5\uD560 \uC218\uB3C4 \uC788\uB2E4.</p><p>\uB610\uD55C \uD050 \uB300\uAE30\uB97C \uD558\uBA74 \uC18C\uBE44\uC790\uB294 \uC77C\uBC18\uC801\uC73C\uB85C \uBE44\uB3D9\uAE30\uB85C \uB3D9\uC791\uD55C\uB2E4. \uC0DD\uC0B0\uC790\uAC00 \uBA54\uC2DC\uC9C0\uB97C \uBCF4\uB0BC \uB54C \uC0DD\uC0B0\uC790\uB294 \uBA54\uC2DC\uC9C0\uB97C \uBC84\uD37C\uC5D0 \uB123\uC5C8\uB294\uC9C0 \uB9CC \uD655\uC778\uD558\uACE0 \uCC98\uB9AC\uD558\uAE30\uAE4C\uC9C0 \uAE30\uB2E4\uB9AC\uC9C0 \uC54A\uB294\uB2E4.</p><h3 id="_1-\u1107\u1169\u11A8\u1109\u116E-\u1109\u1169\u1107\u1175\u110C\u1161" tabindex="-1"><a class="header-anchor" href="#_1-\u1107\u1169\u11A8\u1109\u116E-\u1109\u1169\u1107\u1175\u110C\u1161" aria-hidden="true">#</a> 1. \uBCF5\uC218 \uC18C\uBE44\uC790</h3><p>\uBCF5\uC218 \uC18C\uBE44\uC790\uAC00 \uAC19\uC740 \uD1A0\uD53D\uC5D0\uC11C \uBA54\uC2DC\uC9C0\uB97C \uC77D\uC744 \uB54C \uC0AC\uC6A9\uD558\uB294 \uB450 \uAC00\uC9C0 \uC8FC\uC694 \uD328\uD134\uC774 \uC788\uB2E4.</p><ol><li><p>\uB85C\uB4DC \uBC38\uB7F0\uC2F1 : \uAC01 \uBA54\uC2DC\uC9C0\uB294 \uC18C\uBE44\uC790 \uC911 \uD558\uB098\uB85C \uC804\uB2EC\uB41C\uB2E4. \uC18C\uBE44\uC790\uB294 \uD574\uB2F9 \uD1A0\uD53D\uC758 \uBA54\uC2DC\uC9C0\uB97C \uCC98\uB9AC\uD558\uB294 \uC791\uC5C5\uC744 \uACF5\uC720\uD55C\uB2E4.</p></li><li><p>\uD32C \uC544\uC6C3 : \uAC01 \uBA54\uC2DC\uC9C0\uB294 \uBAA8\uB4E0 \uC18C\uBE44\uC790\uC5D0\uAC8C \uC804\uB2EC\uB41C\uB2E4. \uC5EC\uB7EC \uB3C5\uB9BD\uC801\uC778 \uC18C\uBE44\uC790\uAC00 \uBE0C\uB85C\uB4DC \uCE90\uC2A4\uD305\uB41C \uB3D9\uC77C\uD55C \uBA54\uC2DC\uC9C0\uB97C \uC11C\uB85C \uAC04\uC12D \uC5C6\uC774 \uCCAD\uCDE8\uD560 \uC218 \uC788\uB2E4. \uAC19\uC740 \uC785\uB825 \uD30C\uC77C\uC744 \uC77D\uC5B4 \uC5EC\uB7EC \uB2E4\uB978 \uC77C\uAD04 \uCC98\uB9AC \uC791\uC5C5\uC5D0\uC11C \uC0AC\uC6A9\uD558\uB294 \uAC83\uACFC \uB3D9\uC77C\uD558\uB2E4.</p></li></ol><p>\uC774 \uB450 \uAC00\uC9C0 \uD328\uD134\uC740 \uD568\uAED8 \uC0AC\uC6A9 \uAC00\uB2A5\uD558\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uB450 \uAC1C\uC758 \uC18C\uBE44\uC790 \uADF8\uB8F9\uC5D0\uC11C \uD558\uB098\uC758 \uD1A0\uD53D\uC744 \uAD6C\uB3C5\uD558\uACE0 \uAC01 \uADF8\uB8F9\uC740 \uBAA8\uB4E0 \uBA54\uC2DC\uC9C0\uB97C \uBC1B\uC9C0\uB9CC \uADF8\uB8F9 \uB0B4\uC5D0\uC11C\uB294 \uAC01 \uBA54\uC2DC\uC9C0\uB97C \uD558\uB098\uC758 \uB178\uB4DC\uB9CC \uBC1B\uAC8C \uD558\uB294 \uC2DD\uC774\uB2E4</p><h3 id="_2-\u1112\u116A\u11A8\u110B\u1175\u11AB-\u110B\u1173\u11BC\u1103\u1161\u11B8\u1100\u116A-\u110C\u1162\u110C\u1165\u11AB\u1109\u1169\u11BC" tabindex="-1"><a class="header-anchor" href="#_2-\u1112\u116A\u11A8\u110B\u1175\u11AB-\u110B\u1173\u11BC\u1103\u1161\u11B8\u1100\u116A-\u110C\u1162\u110C\u1165\u11AB\u1109\u1169\u11BC" aria-hidden="true">#</a> 2. \uD655\uC778 \uC751\uB2F5\uACFC \uC7AC\uC804\uC1A1</h3><p>\uC18C\uBE44\uC790\uB294 \uC5B8\uC81C\uB77C\uB3C4 \uC7A5\uC560\uAC00 \uBC1C\uC0DD\uD560 \uC218 \uC788\uB2E4. \uBA54\uC2DC\uC9C0\uB97C \uC783\uC5B4\uBC84\uB9AC\uC9C0 \uC54A\uAE30 \uC704\uD574 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC740 \uD655\uC778 \uC751\uB2F5\uC744 \uC0AC\uC6A9\uD55C\uB2E4. \uBA54\uC2DC\uC9C0 \uCC98\uB9AC\uAC00 \uB05D\uB0AC\uC744 \uB54C \uBE0C\uB85C\uCEE4\uC774 \uBA54\uC2DC\uC9C0\uB97C \uD050\uC5D0\uC11C \uC81C\uAC70\uD560 \uC218 \uC788\uAC8C \uBA85\uC2DC\uC801\uC73C\uB85C \uC54C\uB824\uC57C \uD55C\uB2E4. \uBE0C\uB85C\uCEE4\uC774 \uD655\uC778 \uC751\uB2F5\uC744 \uBC1B\uAE30 \uC804\uC5D0 \uD074\uB77C\uC774\uC5B8\uD2B8\uB85C\uC758 \uC5F0\uACB0\uC774 \uB2EB\uD788\uAC70\uB098 \uD0C0\uC784 \uC544\uC6C3\uB418\uBA74 \uBE0C\uB85C\uCEE4\uC740 \uBA54\uC2DC\uC9C0\uAC00 \uCC98\uB9AC\uB418\uC9C0 \uC54A\uC558\uB2E4\uACE0 \uAC00\uC815\uD558\uACE0 \uB2E4\uB978 \uC18C\uBE44\uC790\uC5D0\uAC8C \uB2E4\uC2DC \uC804\uC1A1\uD55C\uB2E4. \uBD80\uD558 \uADE0\uD615 \uBD84\uC0B0\uACFC \uACB0\uD569\uD560 \uB54C \uC774\uB7F0 \uC7AC\uC804\uC1A1 \uD589\uC704\uB294 \uBA54\uC2DC\uC9C0 \uC21C\uC11C\uC5D0 \uC601\uD5A5\uC744 \uBBF8\uCE5C\uB2E4. \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC740 \uBA54\uC2DC\uC9C0 \uC21C\uC11C\uB97C \uC720\uC9C0\uD558\uB824 \uB178\uB825\uD560\uC9C0\uB77C\uB3C4 \uBD80\uD558 \uADE0\uD615 \uBD84\uC0B0\uACFC \uBA54\uC2DC\uC9C0 \uC7AC\uC804\uC1A1\uC744 \uC870\uD569\uD558\uBA74 \uD544\uC5F0\uC801\uC73C\uB85C \uBA54\uC2DC\uC9C0 \uC21C\uC11C\uAC00 \uBCC0\uACBD\uB41C\uB2E4. \uC18C\uBE44\uC790\uB9C8\uB2E4 \uB3C5\uB9BD\uB41C \uD050\uB97C \uC0AC\uC6A9\uD558\uBA74, \uC989 \uBD80\uD558 \uADE0\uD615 \uBD84\uC0B0 \uAE30\uB2A5\uC744 \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uB294\uB2E4\uBA74 \uC774\uBB38\uC81C\uB97C \uD53C\uD560 \uC218 \uC788\uB2E4. \uBA54\uC2DC\uC9C0\uAC00 \uC11C\uB85C \uC644\uC804\uD788 \uB3C5\uB9BD\uC774\uB77C\uBA74 \uBA54\uC2DC\uC9C0 \uC21C\uC11C\uAC00 \uBC14\uB00C\uB294 \uAC83\uC740 \uBB38\uC81C\uAC00 \uB418\uC9C0 \uC54A\uC9C0\uB9CC \uBA54\uC2DC\uC9C0 \uAC04 \uC778\uACFC\uC131\uC774 \uC788\uB2E4\uBA74 \uC774\uAC83\uC740 \uB9E4\uC6B0 \uC911\uC694\uD55C \uBB38\uC81C\uC774\uB2E4.</p><h2 id="\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC-\u1103\u116C\u11AB-\u1105\u1169\u1100\u1173" tabindex="-1"><a class="header-anchor" href="#\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC-\u1103\u116C\u11AB-\u1105\u1169\u1100\u1173" aria-hidden="true">#</a> \uD30C\uD2F0\uC154\uB2DD \uB41C \uB85C\uADF8</h2><p>\uBE0C\uB85C\uCEE4\uC740 \uBA54\uC2DC\uC9C0\uB97C \uC77C\uC2DC\uC801\uC73C\uB85C \uBCF4\uAD00\uD558\uB294 \uAC1C\uB150\uC73C\uB85C \uB9CC\uB4E4 \uC84C\uB2E4. AMQP/JMS\uD615\uC2DD\uC758 \uBA54\uC2DC\uC9C0 \uD615\uC2DD\uC740 \uBE0C\uB85C\uCEE4\uC774 \uD655\uC778 \uC751\uB2F5\uC744 \uBC1B\uC73C\uBA74 \uBE0C\uB85C\uCEE4\uC5D0\uC11C \uBA54\uC2DC\uC9C0\uB97C \uC0AD\uC81C\uD558\uAE30 \uB54C\uBB38\uC5D0 \uC774\uC804 \uBA54\uC2DC\uC9C0\uB294 \uD55C\uBC88 \uC9C0\uB098\uAC00\uBA74 \uB2E4\uC2DC \uBCF5\uAD6C\uD560 \uC218 \uC5C6\uB2E4. \uD30C\uC77C \uC2DC\uC2A4\uD15C, \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB294 \uC774\uC640 \uBC18\uB300\uB2E4. \uACFC\uAC70\uC758 \uAE30\uB85D\uD588\uB358 \uB370\uC774\uD130\uB3C4 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uBA85\uC2DC\uC801\uC73C\uB85C \uC9C0\uC6B0\uAC70\uB098 \uB36E\uC5B4\uC4F0\uC9C0 \uC54A\uC73C\uBA74 \uC5BC\uB9C8\uB4E0\uC9C0 \uC77D\uC744 \uC218 \uC788\uB2E4. \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uC9C0\uC18D\uC131 \uC788\uB294 \uC800\uC7A5 \uBC29\uBC95\uACFC \uBA54\uC2DC\uC9D5 \uC2DC\uC2A4\uD15C\uC758 \uC9C0\uC5F0 \uC2DC\uAC04\uC774 \uC9E7\uC740 \uC54C\uB9BC \uAE30\uB2A5\uC744 \uC870\uD569\uD558\uB294 \uAC83\uC774 <strong>\uB85C\uADF8 \uAE30\uBC18 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4</strong>\uC758 \uAE30\uBCF8 \uC544\uC774\uB514\uC5B4\uB2E4.</p><h3 id="_1-\u1105\u1169\u1100\u1173\u1105\u1173\u11AF-\u1109\u1161\u110B\u116D\u11BC\u1112\u1161\u11AB-\u1106\u1166\u1109\u1175\u110C\u1175-\u110C\u1165\u110C\u1161\u11BC\u1109\u1169" tabindex="-1"><a class="header-anchor" href="#_1-\u1105\u1169\u1100\u1173\u1105\u1173\u11AF-\u1109\u1161\u110B\u116D\u11BC\u1112\u1161\u11AB-\u1106\u1166\u1109\u1175\u110C\u1175-\u110C\u1165\u110C\u1161\u11BC\u1109\u1169" aria-hidden="true">#</a> 1. \uB85C\uADF8\uB97C \uC0AC\uC6A9\uD55C \uBA54\uC2DC\uC9C0 \uC800\uC7A5\uC18C</h3><p>\uB85C\uADF8\uB294 \uB2E8\uC21C\uD788 \uB514\uC2A4\uD06C\uC5D0 \uC800\uC7A5\uB41C \uCD94\uAC00 \uC804\uC6A9 \uB808\uCF54\uB4DC\uC758 \uC5F0\uC18D\uC774\uB2E4. \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC744 \uAD6C\uD604\uD560 \uB54C\uCC98\uB7FC \uC0DD\uC0B0\uC790\uAC00 \uBCF4\uB0B8 \uBA54\uC2DC\uC9C0\uB294 \uB85C\uADF8 \uB05D\uC5D0 \uCD94\uAC00\uD558\uACE0 \uC18C\uBE44\uC790\uB294 \uB85C\uADF8\uB97C \uC21C\uCC28\uC801\uC73C\uB85C \uC77D\uC5B4 \uBA54\uC2DC\uC9C0\uB97C \uBC1B\uB294\uB2E4. \uCC98\uB9AC\uB7C9\uC744 \uB192\uC774\uAE30 \uC704\uD574 \uD655\uC7A5\uD558\uB294 \uBC29\uBC95\uC73C\uB85C \uB85C\uADF8\uB97C \uD30C\uD2F0\uC154\uB2DD \uD558\uB294 \uBC29\uBC95\uC774 \uC788\uB2E4. \uAC01 \uD30C\uD2F0\uC158\uC740 \uB2E4\uB978 \uD30C\uD2F0\uC158\uACFC \uB3C5\uB9BD\uC801\uC73C\uB85C \uC77D\uACE0 \uC4F0\uAE30\uAC00 \uAC00\uB2A5\uD55C \uBD84\uB9AC\uB41C \uB85C\uADF8\uAC00 \uB41C\uB2E4. \uAC01 \uD30C\uD2F0\uC158 \uB0B4\uC5D0\uB294 \uBE0C\uB85C\uCEE4\uC740 \uBAA8\uB4E0 \uBA54\uC2DC\uC9C0\uC5D0 offset\uC758 \uB2E8\uC870 \uC99D\uAC00\uD558\uB294 \uC21C\uBC88\uC744 \uBD80\uC5EC\uD55C\uB2E4. \uD30C\uD2F0\uC158 \uB0B4 \uC804\uCCB4 \uBA54\uC2DC\uC9C0\uB294 \uC804\uCCB4 \uC21C\uC11C\uAC00 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uC21C\uBC88\uC744 \uBD80\uC5EC\uD558\uB294 \uAC83\uC740 \uD0C0\uB2F9\uD558\uB2E4. \uB2E8 \uB2E4\uB978 \uD30C\uD2F0\uC158 \uAC04 \uBA54\uC2DC\uC9C0 \uC21C\uC11C\uB294 \uBCF4\uC7A5\uD558\uC9C0 \uC54A\uB294\uB2E4.</p><p>\uC544\uD30C\uCE58 \uCE74\uD504\uCE74\uAC00 \uC774\uB7F0 \uBC29\uC2DD\uC73C\uB85C \uB3D9\uC791\uD558\uB294 \uB85C\uADF8 \uAE30\uBC18 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC774\uB2E4. \uC774\uB7F0 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC740 \uBAA8\uB4E0 \uBA54\uC2DC\uC9C0\uB97C \uB514\uC2A4\uD06C\uC5D0 \uC800\uC7A5\uD558\uC9C0\uB9CC \uC5EC\uB7EC \uC7A5\uBE44\uC5D0 \uBA54\uC2DC\uC9C0\uB97C \uD30C\uD2F0\uC154\uB2DD\uD574 \uCD08\uB2F9 \uC218\uBC31\uB9CC \uAC1C\uC758 \uBA54\uC2DC\uC9C0\uB97C \uCC98\uB9AC\uD560 \uC218 \uC788\uACE0 \uBA54\uC2DC\uC9C0\uB97C \uBCF5\uC81C\uD568\uC73C\uB85C\uC368 \uC7A5\uC560\uC5D0 \uB300\uBE44\uD560 \uC218 \uC788\uB2E4.</p><h3 id="_2-\u1105\u1169\u1100\u1173-\u1107\u1161\u11BC\u1109\u1175\u11A8\u1100\u116A-\u110C\u1165\u11AB\u1110\u1169\u11BC\u110C\u1165\u11A8\u110B\u1175\u11AB-\u1106\u1166\u1109\u1175\u110C\u1175\u11BC-\u1107\u1161\u11BC\u1109\u1175\u11A8\u110B\u1174-\u1107\u1175\u1100\u116D" tabindex="-1"><a class="header-anchor" href="#_2-\u1105\u1169\u1100\u1173-\u1107\u1161\u11BC\u1109\u1175\u11A8\u1100\u116A-\u110C\u1165\u11AB\u1110\u1169\u11BC\u110C\u1165\u11A8\u110B\u1175\u11AB-\u1106\u1166\u1109\u1175\u110C\u1175\u11BC-\u1107\u1161\u11BC\u1109\u1175\u11A8\u110B\u1174-\u1107\u1175\u1100\u116D" aria-hidden="true">#</a> 2. \uB85C\uADF8 \uBC29\uC2DD\uACFC \uC804\uD1B5\uC801\uC778 \uBA54\uC2DC\uC9D5 \uBC29\uC2DD\uC758 \uBE44\uAD50</h3><p>\uBA54\uC2DC\uC9C0\uB97C \uCC98\uB9AC\uD558\uB294 \uBE44\uC6A9\uC774 \uBE44\uC2F8\uACE0 \uBA54\uC2DC\uC9C0 \uB2E8\uC704\uB85C \uBCD1\uB82C\uD654 \uCC98\uB9AC\uD558\uACE0 \uC2F6\uC9C0\uB9CC \uBA54\uC2DC\uC9C0 \uC21C\uC11C\uB294 \uC911\uC694\uD558\uC9C0 \uC54A\uB2E4\uBA74 AMQP/JMS \uBC29\uC2DD\uC758 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC774 \uC801\uD569\uD558\uB2E4. \uBC18\uBA74 \uCC98\uB9AC\uB7C9\uC774 \uB9CE\uACE0 \uBA54\uC2DC\uC9C0\uB97C \uCC98\uB9AC\uD558\uB294 \uC18D\uB3C4\uAC00 \uBE60\uB974\uC9C0\uB9CC \uBA54\uC2DC\uC9C0 \uC21C\uC11C\uAC00 \uC911\uC694\uD558\uB2E4\uBA74 \uB85C\uADF8 \uAE30\uBC18 \uC811\uADFC\uBC95\uC774 \uD6A8\uACFC\uC801\uC774\uB2E4.</p><h3 id="_3-\u1109\u1169\u1107\u1175\u110C\u1161-\u110B\u1169\u1111\u1173\u1109\u1166\u11BA" tabindex="-1"><a class="header-anchor" href="#_3-\u1109\u1169\u1107\u1175\u110C\u1161-\u110B\u1169\u1111\u1173\u1109\u1166\u11BA" aria-hidden="true">#</a> 3. \uC18C\uBE44\uC790 \uC624\uD504\uC14B</h3><p>\uD30C\uD2F0\uC158 \uD558\uB098\uB97C \uC21C\uC11C\uB300\uB85C \uCC98\uB9AC\uD558\uBA74 \uBA54\uC2DC\uC9C0\uB97C \uC5B4\uB514\uAE4C\uC9C0 \uCC98\uB9AC\uD588\uB294\uC9C0 \uC54C\uAE30 \uC27D\uB2E4. \uC18C\uBE44\uC790\uC758 \uD604\uC7AC \uC624\uD504\uC14B\uBCF4\uB2E4 \uC791\uC740 \uC624\uD504\uC14B\uC744 \uAC00\uC9C4 \uBA54\uC2DC\uC9C0\uB294 \uC774\uBBF8 \uCC98\uB9AC\uD55C \uBA54\uC2DC\uC9C0\uACE0 \uC18C\uBE44\uC790\uC758 \uD604\uC7AC \uC624\uD504\uC14B\uBCF4\uB2E4 \uD070 \uC624\uD504\uC14B\uC744 \uAC00\uC9C4 \uBA54\uC2DC\uC9C0\uB294 \uC544\uC9C1 \uCC98\uB9AC\uD558\uC9C0 \uC54A\uC740 \uBA54\uC2DC\uC9C0\uB2E4. \uB530\uB77C\uC11C \uBE0C\uB85C\uCEE4\uB294 \uBAA8\uB4E0 \uAC1C\uBCC4 \uBA54\uC2DC\uC9C0\uB9C8\uB2E4 \uBCF4\uB0B4\uB294 \uD655\uC778 \uC751\uB2F5\uC744 \uCD94\uC801\uD560 \uD544\uC694\uAC00 \uC5C6\uB2E4. \uC8FC\uAE30\uC801\uC73C\uB85C \uC18C\uBE44\uC790 \uC624\uD504\uC14B\uC744 \uAE30\uB85D\uD558\uBA74 \uB41C\uB2E4. \uCD94\uC801 \uC624\uBC84\uD5E4\uB4DC\uAC00 \uAC10\uC18C\uD558\uACE0 \uC77C\uAD04 \uCC98\uB9AC\uC640 \uD30C\uC774\uD504\uB77C\uC774\uB2DD\uC744 \uC218\uD589\uD560 \uC218 \uC788\uB294 \uAE30\uD68C\uB97C \uC81C\uACF5\uD574 \uB85C\uADF8 \uAE30\uBC18 \uC2DC\uC2A4\uD15C\uC758 \uCC98\uB9AC\uB7C9\uC744 \uB298\uB9AC\uB294 \uB370 \uB3C4\uC6C0\uC744 \uC900\uB2E4. \uC18C\uBE44\uC790 \uB178\uB4DC\uC5D0 \uC7A5\uC560\uAC00 \uBC1C\uC0DD\uD558\uBA74 \uC18C\uBE44\uC790 \uADF8\uB8F9 \uB0B4 \uB2E4\uB978 \uB178\uB4DC\uC5D0 \uC7A5\uC560\uAC00 \uBC1C\uC0DD\uD55C \uC18C\uBE44\uC790\uC758 \uD30C\uD2F0\uC158\uC744 \uD560\uB2F9\uD558\uACE0 \uB9C8\uC9C0\uB9C9 \uAE30\uB85D\uB41C \uC624\uD504\uC14B\uBD80\uD130 \uBA54\uC2DC\uC9C0\uB97C \uCC98\uB9AC\uD558\uAE30 \uC2DC\uC791\uD55C\uB2E4.</p><h3 id="_4-\u1103\u1175\u1109\u1173\u110F\u1173-\u1100\u1169\u11BC\u1100\u1161\u11AB-\u1109\u1161\u110B\u116D\u11BC" tabindex="-1"><a class="header-anchor" href="#_4-\u1103\u1175\u1109\u1173\u110F\u1173-\u1100\u1169\u11BC\u1100\u1161\u11AB-\u1109\u1161\u110B\u116D\u11BC" aria-hidden="true">#</a> 4. \uB514\uC2A4\uD06C \uACF5\uAC04 \uC0AC\uC6A9</h3><p>\uB514\uC2A4\uD06C \uACF5\uAC04\uC744 \uC7AC\uC0AC\uC6A9\uD558\uAE30 \uC704\uD574 \uC2E4\uC81C\uB85C\uB294 \uB85C\uADF8\uB97C \uC5EC\uB7EC \uC870\uAC01\uC73C\uB85C \uB098\uB204\uACE0 \uAC00\uB054 \uC624\uB798\uB41C \uC870\uAC01\uC744 \uC0AD\uC81C\uD558\uAC70\uB098 \uBCF4\uAD00 \uC800\uC7A5\uC18C\uB85C \uC774\uB3D9\uD55C\uB2E4. \uC18C\uBE44\uC790 \uCC98\uB9AC \uC18D\uB3C4\uAC00 \uB290\uB824 \uBA54\uC2DC\uC9C0\uAC00 \uC0DD\uC0B0\uB418\uB294 \uC18D\uB3C4\uB97C \uB530\uB77C\uC7A1\uC9C0 \uBABB\uD558\uBA74 \uC18C\uBE44\uC790 \uC624\uD504\uC14B\uC774 \uC774\uBBF8 \uC0AD\uC81C\uD55C \uC870\uAC01\uC744 \uAC00\uB9AC\uD0AC \uC218\uB3C4 \uC788\uB2E4. \uC989 \uBA54\uC2DC\uC9C0 \uC77C\uBD80\uB97C \uC783\uC5B4\uBC84\uB9B4 \uAC00\uB2A5\uC131\uC774 \uC788\uB2E4. \uB85C\uADF8\uB97C \uD06C\uAE30\uAC00 \uC81C\uD55C\uB41C \uBC84\uD37C\uB85C \uAD6C\uD604\uD558\uACE0 \uBC84\uD37C\uAC00 \uAC00\uB4DD \uCC28\uBA74 \uC624\uB798\uB41C \uBA54\uC2DC\uC9C0 \uC21C\uC11C\uB300\uB85C \uBC84\uB9B0\uB2E4 \uC774\uB7F0 \uBC84\uD37C\uB97C \uC6D0\uD615 \uBC84\uD37C, \uB9C1 \uBC84\uD37C\uB77C\uACE0 \uD55C\uB2E4. \uBC84\uD37C\uAC00 \uB514\uC2A4\uD06C\uC0C1\uC5D0 \uC788\uB2E4\uBA74 \uC0C1\uB2F9\uD788 \uCEE4\uC9C8 \uC218 \uC788\uB2E4. \uC774\uB7F0 \uB85C\uADF8 \uAE30\uBC18\uC758 \uACBD\uC6B0 \uBAA8\uB4E0 \uBA54\uC2DC\uC9C0\uB97C \uB514\uC2A4\uD06C\uC5D0 \uAE30\uB85D\uD558\uAE30 \uB54C\uBB38\uC5D0 \uB85C\uADF8 \uCC98\uB9AC\uB7C9\uC740 \uC77C\uC815\uD558\uB2E4. \uBA54\uC2DC\uC9D5 \uC2DC\uC2A4\uD15C\uC758 \uACBD\uC6B0\uB294 \uAE30\uBCF8\uC801\uC73C\uB85C \uBA54\uBAA8\uB9AC\uC5D0 \uBA54\uC2DC\uC9C0\uB97C \uC720\uC9C0\uD558\uACE0 \uD050\uAC00 \uCEE4\uC9C8 \uB54C\uB9CC \uB514\uC2A4\uD06C\uC5D0 \uAE30\uB85D\uD558\uAE30 \uB54C\uBB38\uC5D0 \uD050\uAC00 \uC791\uC744 \uB54C\uB294 \uBE60\uB974\uC9C0\uB9CC \uB514\uC2A4\uD06C\uC5D0 \uAE30\uB85D\uD558\uAE30 \uC2DC\uC791\uD558\uBA74 \uB9E4\uC6B0 \uB290\uB824\uC9C4\uB2E4. \uC2DC\uC2A4\uD15C \uCC98\uB9AC\uB7C9\uC774 \uBCF4\uC720\uD55C \uBA54\uC2DC\uC9C0\uC5D0 \uB530\uB77C \uB2E4\uB974\uB2E4.</p><h3 id="_5-\u1109\u1169\u1107\u1175\u110C\u1161\u1100\u1161-\u1109\u1162\u11BC\u1109\u1161\u11AB\u110C\u1161\u1105\u1173\u11AF-\u1104\u1161\u1105\u1161\u1100\u1161\u11AF-\u1109\u116E-\u110B\u1165\u11B9\u110B\u1173\u11AF-\u1104\u1162" tabindex="-1"><a class="header-anchor" href="#_5-\u1109\u1169\u1107\u1175\u110C\u1161\u1100\u1161-\u1109\u1162\u11BC\u1109\u1161\u11AB\u110C\u1161\u1105\u1173\u11AF-\u1104\u1161\u1105\u1161\u1100\u1161\u11AF-\u1109\u116E-\u110B\u1165\u11B9\u110B\u1173\u11AF-\u1104\u1162" aria-hidden="true">#</a> 5. \uC18C\uBE44\uC790\uAC00 \uC0DD\uC0B0\uC790\uB97C \uB530\uB77C\uAC08 \uC218 \uC5C6\uC744 \uB54C</h3><p>\uC18C\uBE44\uC790\uAC00 \uB4A4\uCC98\uC838 \uD544\uC694\uD55C \uBA54\uC2DC\uC9C0\uAC00 \uB514\uC2A4\uD06C\uC5D0 \uBCF4\uC720\uD55C \uBA54\uC2DC\uC9C0\uBCF4\uB2E4 \uC624\uB798\uB418\uBA74 \uD544\uC694\uD55C \uBA54\uC2DC\uC9C0\uB294 \uC77D\uC744 \uC218 \uC5C6\uB2E4. \uADF8\uB798\uC11C \uBE0C\uB85C\uCEE4\uC740 \uBC84\uD37C \uD06C\uAE30\uB97C \uB118\uB294 \uC624\uB798\uB41C \uBA54\uC2DC\uC9C0\uB97C \uC790\uC5F0\uC2A4\uB7FD\uAC8C \uBC84\uB9B0\uB2E4. \uBC84\uD37C\uAC00 \uCEE4\uC9C8\uC218\uB85D, \uC0AC\uB78C\uC774 \uC18C\uBE44\uC790 \uCC98\uB9AC\uAC00 \uB290\uB9B0 \uBB38\uC81C\uB97C \uACE0\uCCD0 \uBA54\uC2DC\uC9C0\uB97C \uC783\uAE30 \uC804\uC5D0 \uB530\uB77C\uC7A1\uC744 \uC2DC\uAC04\uC744 \uCDA9\uBD84\uD788 \uBC8C \uC218 \uC788\uB2E4.</p><h3 id="_6-\u110B\u1169\u1105\u1162\u1103\u116C\u11AB-\u1106\u1166\u1109\u1175\u110C\u1175-\u110C\u1162\u1109\u1162\u11BC" tabindex="-1"><a class="header-anchor" href="#_6-\u110B\u1169\u1105\u1162\u1103\u116C\u11AB-\u1106\u1166\u1109\u1175\u110C\u1175-\u110C\u1162\u1109\u1162\u11BC" aria-hidden="true">#</a> 6. \uC624\uB798\uB41C \uBA54\uC2DC\uC9C0 \uC7AC\uC0DD</h3><p>AMQP \uAC19\uC740 \uC778\uBA54\uBAA8\uB9AC \uC720\uD615\uC758 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC5D0\uC11C\uB294 \uCC98\uB9AC\uD558\uACE0 \uD655\uC778 \uC751\uB2F5\uD558\uB294 \uC791\uC5C5\uC740 \uBA54\uC2DC\uC9C0\uB97C \uC81C\uAC70\uD558\uAE30 \uB54C\uBB38\uC5D0 \uD30C\uAD34\uC801 \uC5F0\uC0B0\uC774\uB2E4. \uBC18\uBA74 \uB85C\uADF8 \uAE30\uBC18 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC740 \uBA54\uC2DC\uC9C0\uB97C \uC18C\uBE44\uD558\uB294 \uAC8C \uC624\uD788\uB824 \uD30C\uC77C\uC744 \uC77D\uB294 \uC791\uC5C5\uACFC \uB354 \uC720\uC0AC\uD558\uB2E4. \uB85C\uADF8\uB97C \uBCC0\uD654\uC2DC\uD0A4\uC9C0 \uC54A\uB294 \uC77D\uAE30 \uC804\uC6A9 \uC5F0\uC0B0\uC774\uAE30 \uB54C\uBB38\uC774\uB2E4. \uC18C\uBE44\uC790 \uC624\uD504\uC14B\uC740 \uC18C\uBE44\uC790 \uAD00\uB9AC \uC544\uB798\uC5D0 \uC788\uB2E4. \uADF8\uB798\uC11C \uD544\uC694\uD558\uB2E4\uBA74 \uC27D\uAC8C \uC870\uC791\uD560 \uC218 \uC788\uB2E4 \uC5B4\uC81C\uC758 \uC624\uD504\uC14B\uC744 \uBCF5\uC0AC\uD588\uB2E4\uAC00 \uC7AC\uCC98\uB9AC\uD560 \uC218 \uC788\uB2E4.</p><div class="custom-container info"><p class="custom-container-title">\uC815\uB9AC</p><ul><li><p>AMQP/JMS \uC2A4\uD0C0\uC77C \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4 \uBE0C\uB85C\uCEE4\uB294 \uAC1C\uBCC4 \uBA54\uC2DC\uC9C0\uB97C \uC18C\uBE44\uC790\uC5D0\uAC8C \uD560\uB2F9\uD558\uACE0 \uC18C\uBE44\uC790\uB294 \uBC1B\uC740 \uBA54\uC2DC\uC9C0\uB97C \uCC98\uB9AC\uD558\uB294 \uB370 \uC131\uACF5\uD558\uBA74 \uD655\uC778 \uC751\uB2F5\uC744 \uBCF4\uB0B8\uB2E4. \uD655\uC778 \uC751\uB2F5\uC744 \uBC1B\uC740 \uBA54\uC2DC\uC9C0\uB294 \uC0AD\uC81C\uB41C\uB2E4. RPC\uC640 \uAC19\uC740 \uBE44\uB3D9\uAE30 \uC591\uC2DD\uC5D0 \uC801\uC808\uD558\uB2E4. \uBA54\uC2DC\uC9C0\uC758 \uCC98\uB9AC\uAC00 \uC815\uD655\uD55C \uC21C\uC11C\uB300\uB85C \uC774\uB904\uC9C0\uB294 \uAC83\uC774 \uC911\uC694\uD558\uC9C0 \uC54A\uACE0 \uBA54\uC2DC\uC9C0\uAC00 \uCC98\uB9AC\uB41C \uD6C4\uC5D0 \uC774\uC804\uC73C\uB85C \uB3CC\uC544\uAC00 \uACFC\uAC70 \uBA54\uC2DC\uC9C0\uB97C \uB2E4\uC2DC \uC77D\uC744 \uD544\uC694\uAC00 \uC5C6\uB2E4.</p></li><li><p>\uB85C\uADF8 \uAE30\uBC18 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4 \uBE0C\uB85C\uCEE4\uB294 \uD55C \uD30C\uD2F0\uC158 \uB0B4\uC758 \uBAA8\uB4E0 \uBA54\uC2DC\uC9C0\uB97C \uB3D9\uC77C\uD55C \uC18C\uBE44\uC790 \uB178\uB4DC\uC5D0\uAC8C \uD560\uB2F9\uD558\uACE0 \uD56D\uC0C1 \uAC19\uC740 \uC21C\uC11C\uB85C \uBA54\uC2DC\uC9C0\uB97C \uC804\uB2EC\uD55C\uB2E4. \uBCD1\uB82C\uD654\uB294 \uD30C\uD2F0\uC158\uC744 \uB098\uB204\uB294 \uBC29\uC2DD\uC73C\uB85C \uC0AC\uC6A9\uD55C\uB2E4. \uC18C\uBE44\uC790\uB294 \uCD5C\uADFC\uC5D0 \uCC98\uB9AC\uD55C \uBA54\uC2DC\uC9C0\uC758 \uC624\uD504\uC14B\uC744 \uCCB4\uD06C\uD3EC\uC778\uD2B8\uB85C \uB0A8\uACA8 \uC9C4\uD589 \uC0C1\uD669\uC744 \uCD94\uC801\uD55C\uB2E4. \uBE0C\uB85C\uCEE4\uB294 \uBA54\uC2DC\uC9C0\uB97C \uB514\uC2A4\uD06C\uC5D0 \uC720\uC9C0\uD558\uAE30 \uB54C\uBB38\uC5D0 \uD544\uC694\uD55C \uACBD\uC6B0 \uB4A4\uB85C \uB3CC\uC544\uAC00 \uC774\uC804 \uBA54\uC2DC\uC9C0\uB97C \uB2E4\uC2DC \uC77D\uC744 \uC218 \uC788\uB2E4.</p></li></ul></div><h2 id="\u1107\u1167\u11AB\u1100\u1167\u11BC-\u1103\u1166\u110B\u1175\u1110\u1165-\u110F\u1162\u11B8\u110E\u1167\u110B\u116A-\u110B\u1175\u1107\u1166\u11AB\u1110\u1173-\u1109\u1169\u1109\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1107\u1167\u11AB\u1100\u1167\u11BC-\u1103\u1166\u110B\u1175\u1110\u1165-\u110F\u1162\u11B8\u110E\u1167\u110B\u116A-\u110B\u1175\u1107\u1166\u11AB\u1110\u1173-\u1109\u1169\u1109\u1175\u11BC" aria-hidden="true">#</a> \uBCC0\uACBD \uB370\uC774\uD130 \uCEA1\uCCD0\uC640 \uC774\uBCA4\uD2B8 \uC18C\uC2F1</h2><h3 id="_1-\u1107\u1167\u11AB\u1100\u1167\u11BC-\u1103\u1166\u110B\u1175\u1110\u1165-\u110F\u1162\u11B8\u110E\u1165" tabindex="-1"><a class="header-anchor" href="#_1-\u1107\u1167\u11AB\u1100\u1167\u11BC-\u1103\u1166\u110B\u1175\u1110\u1165-\u110F\u1162\u11B8\u110E\u1165" aria-hidden="true">#</a> 1. \uBCC0\uACBD \uB370\uC774\uD130 \uCEA1\uCC98</h3><p>\uB300\uBD80\uBD84\uC758 \uC911\uC694 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uC694\uAD6C\uC0AC\uD56D\uC744 \uB9CC\uC871\uD558\uAE30 \uC704\uD574\uC11C\uB294 \uBA87 \uAC00\uC9C0 \uB2E4\uB978 \uAE30\uC220\uC758 \uC870\uD569\uC774 \uD544\uC694\uD558\uB2E4. \uC0AC\uC6A9\uC790 \uC694\uCCAD\uC5D0 \uB300\uC751\uD558\uAE30 \uC704\uD55C OLTP \uB370\uC774\uD130\uBCA0\uC774\uC2A4, \uACF5\uD1B5 \uC694\uCCAD\uC758 \uC751\uB2F5 \uC18D\uB3C4\uB97C \uB192\uC774\uAE30 \uC704\uD55C \uCE90\uC2DC, \uAC80\uC0C9 \uC9C8\uC758\uB97C \uB2E4\uB8E8\uAE30 \uC704\uD55C \uC804\uBB38 \uC0C9\uC778, \uBD84\uC11D\uC6A9 \uB370\uC774\uD130 \uC6E8\uC5B4\uD558\uC6B0\uC2A4\uAC00 \uADF8 \uC608\uB2E4. \uC774 \uC2DC\uC2A4\uD15C \uAC01\uAC01\uC740 \uB370\uC774\uD130\uC758 \uBCF5\uC81C\uBCF8\uC744 \uAC00\uC9C0\uACE0 \uC788\uACE0 \uADF8 \uB370\uC774\uD130\uB294 \uBAA9\uC801\uC5D0 \uB9DE\uAC8C \uCD5C\uC801\uD654\uB41C \uD615\uD0DC\uB85C \uAC01\uAC01 \uC800\uC7A5\uB41C\uB2E4. \uAD00\uB828\uC774 \uC788\uAC70\uB098 \uB3D9\uC77C\uD55C \uB370\uC774\uD130\uAC00 \uC5EC\uB7EC \uB2E4\uB978 \uC7A5\uC18C\uC5D0\uC11C \uB098\uD0C0\uB098\uAE30 \uB54C\uBB38\uC5D0 \uC774 \uC2DC\uC2A4\uD15C\uB4E4\uC740 \uC11C\uB85C \uB3D9\uAE30\uD654\uAC00 \uD544\uC218\uB2E4.</p><p>\uC8FC\uAE30\uC801\uC73C\uB85C \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC804\uCCB4\uB97C \uB364\uD504 \uD558\uB294 \uC791\uC5C5\uC774 \uB108\uBB34 \uB290\uB9AC\uBA74 \uB300\uC548\uC73C\uB85C \uC0AC\uC6A9\uD558\uB294 \uBC29\uBC95\uC73C\uB85C \uC774\uC911 \uAE30\uB85D\uC774 \uC788\uB2E4. \uB370\uC774\uD130\uAC00 \uBCC0\uD560 \uB54C\uB9C8\uB2E4 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uCF54\uB4DC\uC5D0\uC11C \uBA85\uC2DC\uC801\uC73C\uB85C \uAC01 \uC2DC\uC2A4\uD15C\uC5D0 \uAE30\uB85D\uD55C\uB2E4. \uACBD\uC7C1\uC870\uAC74\uC758 \uBB38\uC81C\uC640, \uD55C\uCABD\uC758 \uC4F0\uAE30 \uC2E4\uD328\uC758 \uACBD\uC6B0\uB85C \uBD88\uC77C\uCE58\uAC00 \uBC1C\uC0DD\uD558\uB294 \uD604\uC0C1\uC774 \uBC1C\uC0DD\uD55C\uB2E4.</p><p>\uCD5C\uADFC \uB4E4\uC5B4 \uBCC0\uACBD \uB370\uC774\uD130 \uCEA1\uCC98(CDC)\uC5D0 \uAD00\uC2EC\uC774 \uB192\uC544\uC9C0\uACE0 \uC788\uB2E4. \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uAE30\uB85D\uD558\uB294 \uBAA8\uB4E0 \uB370\uC774\uD130\uC758 \uBCC0\uD654\uB97C \uAD00\uCC30\uD574 \uB2E4\uB978 \uC2DC\uC2A4\uD15C\uC73C\uB85C \uB370\uC774\uD130\uB97C \uBCF5\uC81C\uD560 \uC218 \uC788\uB294 \uD615\uD0DC\uB85C \uCD94\uCD9C\uD558\uB294 \uACFC\uC815\uC774\uB2E4. \uBCC0\uACBD \uB0B4\uC6A9\uC744 \uC2A4\uD2B8\uB9BC\uC73C\uB85C \uC81C\uACF5\uD560 \uC218 \uC788\uC73C\uBA74 \uD2B9\uD788 \uC720\uC6A9\uD558\uB2E4. \uC608\uB97C \uB4E4\uBA74 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC758 \uBCC0\uACBD\uC0AC\uD56D\uC744 \uCEA1\uCC98\uD574 \uAC80\uC0C9 \uC0C9\uC778\uC5D0 \uAFB8\uC900\uD788 \uBC18\uC601\uD560 \uC218 \uC788\uB2E4. \uB2E4\uB978 \uD30C\uC0DD \uB370\uC774\uD130 \uC2DC\uC2A4\uD15C\uB3C4 \uB2E8\uC9C0 \uBCC0\uACBD \uC2A4\uD2B8\uB9BC\uC758 \uC18C\uBE44\uC790\uB85C \uB9CC\uB4E4 \uC218 \uC788\uB2E4. \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0 \uC4F0\uC778 \uC21C\uC11C\uB300\uB85C \uB370\uC774\uD130\uB97C \uAC00\uC838\uC640 \uB2E4\uB978 \uC2DC\uC2A4\uD15C\uC5D0 \uBCC0\uACBD\uC0AC\uD56D\uC744 \uAC19\uC740 \uC21C\uC11C\uB85C \uC801\uC6A9\uD55C\uB2E4. \uADF8\uB7F0 \uC758\uBBF8\uC5D0\uC11C \uB85C\uADF8 \uAE30\uBC18 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uC740 \uC6CC \uB17C \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0 \uC11C \uBCC0\uACBD \uC774\uBCA4\uD2B8\uB97C \uC804\uC1A1\uD558\uAE30\uC5D0 \uC801\uD569\uD558\uB2E4. \uBCC0\uACBD \uB370\uC774\uD130 \uCEA1\uCC98\uB294 \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4\uACFC \uB3D9\uC77C\uD558\uAC8C \uBE44\uB3D9\uAE30 \uBC29\uC2DD\uC73C\uB85C \uB3D9\uC791\uD55C\uB2E4. \uB808\uCF54\uB4DC \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC2DC\uC2A4\uD15C\uC740 \uBCC0\uACBD \uC0AC\uD56D\uC744 \uCEE4\uBC0B\uD558\uAE30 \uC804\uC5D0 \uBCC0\uACBD\uC0AC\uD56D\uC774 \uC18C\uBE44\uC790\uC5D0\uAC8C \uC801\uC6A9\uB420 \uB54C\uAE4C\uC9C0 \uAE30\uB2E4\uB9AC\uC9C0 \uC54A\uB294\uB2E4. <img src="'+i+'" alt="cdc" loading="lazy"></p><h3 id="_2-\u110B\u1175\u1107\u1166\u11AB\u1110\u1173\u1109\u1169\u1109\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_2-\u110B\u1175\u1107\u1166\u11AB\u1110\u1173\u1109\u1169\u1109\u1175\u11BC" aria-hidden="true">#</a> 2. \uC774\uBCA4\uD2B8\uC18C\uC2F1</h3><p>CDC\uC640 \uC774\uBCA4\uD2B8 \uC18C\uC2F1\uC740 \uC720\uC0AC\uD55C \uBA74\uC774 \uC788\uB2E4. \uC774\uBCA4\uD2B8 \uC18C\uC2F1\uC740 \uB3C4\uBA54\uC778 \uC8FC\uB3C4 \uC124\uACC4 \uCEE4\uBBA4\uB2C8\uD2F0\uC5D0\uC11C \uAC1C\uBC1C\uD55C \uAE30\uBC95\uC774\uB2E4, \uC774\uBCA4\uD2B8 \uC18C\uC2F1\uC740 \uBCC0\uACBD \uB370\uC774\uD130 \uCEA1\uCC98\uC640 \uC720\uC0AC\uD558\uAC8C \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uAC15\uD0DC \uBCC0\uD654\uB97C \uBAA8\uB450 \uBCC0\uACBD \uC774\uBCA4\uD2B8 \uB85C\uADF8\uB85C \uC800\uC7A5\uD55C\uB2E4. \uBCC0\uACBD \uB370\uC774\uD130 \uCEA1\uCC98\uC640 \uAC00\uC7A5 \uD070 \uCC28\uC774\uC810\uC740 \uC774 \uC544\uC774\uB514\uC5B4\uB97C \uC801\uC6A9\uD558\uB294 \uCD94\uC0C1\uD654 \uB808\uBCA8\uC774 \uB2E4\uB974\uB2E4\uB294 \uC810\uC774\uB2E4.</p><p>CDC \uBCC0\uACBD \uC774\uBCA4\uD2B8 \uCEA1\uCC98\uC5D0\uC11C \uBCC0\uACBD \uB85C\uADF8\uB294 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0\uC11C \uC800\uC218\uC900\uC73C\uB85C \uCD94\uCD9C\uD55C\uB2E4. \uADF8\uB7EC\uB098 \uC774\uBCA4\uD2B8 \uC18C\uC2F1\uC5D0\uC11C\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uAD00\uC810\uC5D0\uC11C \uC0AC\uC6A9\uC790\uC758 \uD589\uB3D9\uC744 \uBD88\uBCC0 \uC774\uBCA4\uD2B8\uB85C \uAE30\uB85D\uD55C\uB2E4.</p><p>\uC774\uBCA4\uD2B8 \uC18C\uC2F1\uC744 \uC0AC\uC6A9\uD558\uBA74 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uC9C0\uC18D\uC801\uC73C\uB85C \uAC1C\uC120\uD558\uAE30\uC5D0 \uB9E4\uC6B0 \uC720\uB9AC\uD558\uB2E4. \uC5B4\uB5A4 \uC0C1\uD669\uC774 \uBC1C\uC0DD\uD55C \uD6C4\uC5D0 \uC0C1\uD669 \uD30C\uC545\uC774 \uC27D\uAE30 \uB54C\uBB38\uC5D0 \uB514\uBC84\uAE45\uC5D0 \uB3C4\uC6C0\uC774 \uB418\uACE0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBC84\uADF8\uB97C \uBC29\uC9C0\uD55C\uB2E4. \uBB3C\uB860 \uC774\uBCA4\uD2B8 \uB85C\uADF8\uB294 \uADF8 \uC790\uCCB4\uB85C\uB294 \uADF8\uB807\uAC8C \uC720\uC6A9\uD558\uC9C0 \uC54A\uB2E4. \uC774\uBCA4\uD2B8 \uC18C\uC2F1\uC744 \uC0AC\uC6A9\uD558\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC740 \uC2DC\uC2A4\uD15C\uC5D0 \uAE30\uB85D\uD55C \uB370\uC774\uD130\uB97C \uD45C\uD604\uD55C \uC774\uBCA4\uD2B8 \uB85C\uADF8\uB97C \uAC00\uC838\uC640 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uBCF4\uC5EC\uC8FC\uAE30 \uC801\uB2F9\uD55C \uC0C1\uD0DC\uB85C \uB9CC\uB4E4\uC5B4\uC57C \uD55C\uB2E4.</p><h2 id="\u1107\u116E\u11AF\u1107\u1167\u11AB\u1109\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1107\u116E\u11AF\u1107\u1167\u11AB\u1109\u1165\u11BC" aria-hidden="true">#</a> \uBD88\uBCC0\uC131</h2><p>\uBCC0\uACBD \uB85C\uADF8\uB97C \uC9C0\uC18D\uC131 \uC788\uAC8C \uC800\uC7A5\uD55C\uB2E4\uBA74 \uC0C1\uD0DC\uB97C \uAC04\uB2E8\uD788 \uC7AC\uC0DD\uC131\uD560 \uC218 \uC788\uB294 \uD6A8\uACFC\uAC00 \uC788\uB2E4. \uC774\uBCA4\uD2B8 \uB85C\uADF8\uB97C \uB808\uCF54\uB4DC \uC2DC\uC2A4\uD15C\uC73C\uB85C \uC0DD\uAC01\uD558\uACE0 \uBAA8\uB4E0 \uBCC0\uACBD \uAC00\uB2A5 \uC0C1\uD0DC\uB97C \uC774\uBCA4\uD2B8 \uB85C\uADF8\uB85C\uBD80\uD130 \uD30C\uC0DD\uB41C \uAC83\uC73C\uB85C \uC0DD\uAC01\uD558\uBA74 \uC2DC\uC2A4\uD15C\uC744 \uAC70\uCE58\uB294 \uB370\uC774\uD130 \uD750\uB984\uC5D0 \uAD00\uD574 \uCD94\uB860\uD558\uAE30 \uC27D\uB2E4. \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC798\uBABB\uB41C \uB370\uC774\uD130\uB97C \uAE30\uB85D\uD588\uC744 \uB54C \uCF54\uB4DC\uAC00 \uB370\uC774\uD130\uB97C \uB36E\uC5B4\uC37C\uB354\uBA74 \uBCF5\uAD6C\uD558\uAE30\uAC00 \uB9E4\uC6B0 \uC5B4\uB835\uB2E4. \uCD94\uAC00\uB9CC \uD558\uB294 \uBD88\uBCC0 \uC774\uBCA4\uD2B8 \uB85C\uADF8\uB97C \uC37C\uB2E4\uBA74 \uBB38\uC81C \uC0C1\uD669\uC758 \uC9C4\uB2E8\uACFC \uBCF5\uAD6C\uAC00 \uD6E8\uC52C \uC27D\uB2E4. \uB610\uD55C \uBD88\uBCC0 \uC774\uBCA4\uD2B8\uB294 \uD604\uC7AC \uC0C1\uD0DC\uBCF4\uB2E4 \uD6E8\uC52C \uB9CE\uC740 \uC815\uBCF4\uB97C \uD3EC\uD568\uD55C\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uC1FC\uD551 \uC6F9\uC0AC\uC774\uD2B8\uC5D0\uC11C \uACE0\uAC1D\uC774 \uC7A5\uBC14\uAD6C\uB2C8\uC5D0 \uD56D\uBAA9 \uD558\uB098\uB97C \uB123\uC5C8\uB2E4\uAC00 \uC81C\uAC70\uD588\uB2E4\uACE0 \uD558\uC790 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0\uC11C\uB294 \uC783\uC5B4\uBC84\uB9AC\uB294 \uC815\uBCF4\uC9C0\uB9CC \uC774 \uC774\uBCA4\uD2B8 \uB85C\uADF8\uB85C\uB294 \uB0A8\uAC8C \uB41C\uB2E4. \uC774\uAC83\uC740 \uBD84\uC11D\uAC00\uC5D0\uAC8C \uC788\uC5B4\uC11C\uB294 \uC911\uC694\uD55C \uC815\uBCF4\uB2E4.</p><p>\uBD88\uBCC0 \uC774\uBCA4\uD2B8 \uB85C\uADF8\uC5D0\uC11C \uAC00\uBCC0 \uC0C1\uD0DC\uB97C \uBD84\uB9AC\uD558\uBA74 \uB3D9\uC77C\uD55C \uC774\uBCA4\uD2B8 \uB85C\uADF8\uB85C \uB2E4\uB978 \uC5EC\uB7EC \uC77D\uAE30 \uC804\uC6A9 \uBDF0\uB97C \uB9CC\uB4E4 \uC218 \uC788\uB2E4. \uC77C\uBC18\uC801\uC73C\uB85C \uB370\uC774\uD130\uC5D0 \uC5B4\uB5BB\uAC8C \uC9C8\uC758\uD558\uACE0 \uC811\uADFC\uD558\uB294\uC9C0 \uC2E0\uACBD \uC4F0\uC9C0 \uC54A\uB294\uB2E4\uBA74 \uB370\uC774\uD130 \uC800\uC7A5\uC740 \uC0C1\uB2F9\uD788 \uC9C1\uAD00\uC801\uC778 \uC791\uC5C5\uC774\uB2E4. \uC2A4\uD0A4\uB9C8 \uC124\uACC4, \uC0C9\uC778, \uC800\uC7A5\uC18C \uC5D4\uC9C4\uC774 \uAC00\uC9C4 \uBCF5\uC7A1\uC131\uC740 \uD2B9\uC815 \uC9C8\uC758\uC640 \uD2B9\uC815 \uC811\uADFC \uD615\uC2DD\uC744 \uC9C0\uC6D0\uD558\uAE30 \uC704\uD55C \uACB0\uACFC\uB85C \uBC1C\uC0DD\uD55C\uB2E4. \uC774\uB7F0 \uC774\uC720\uB85C \uB370\uC774\uD130\uB97C \uC4F0\uB294 \uD615\uC2DD\uACFC \uC77D\uB294 \uD615\uC2DD\uC744 \uBD84\uB9AC\uD574 \uB2E4\uC591\uD55C \uC77D\uAE30 \uBDF0\uB97C \uD5C8\uC6A9\uD55C\uB2E4\uBA74 \uC0C1\uB2F9\uD55C \uC720\uC5F0\uC131\uC744 \uC5BB\uC744 \uC218 \uC788\uB2E4.</p><p>\uC601\uAD6C\uC801\uC73C\uB85C \uBAA8\uB4E0 \uBCC0\uD654\uC758 \uBD88\uBCC0 \uD788\uC2A4\uD1A0\uB9AC\uB97C \uC720\uC9C0\uD558\uB294 \uAC83\uC774 \uC5B4\uB290 \uC815\uB3C4\uAE4C\uC9C0 \uAC00\uB2A5\uD560\uAE4C? \uB300\uBD80\uBD84\uC740 \uB370\uC774\uD130\uB97C \uCD94\uAC00\uD558\uB294 \uC791\uC5C5\uC774\uACE0 \uAC31\uC2E0\uC774\uB098 \uC0AD\uC81C\uB294 \uB4DC\uBB3C\uAC8C \uBC1C\uC0DD\uD558\uB294 \uC791\uC5C5 \uBD80\uD558\uB294 \uBD88\uBCC0\uC73C\uB85C \uB9CC\uB4E4\uAE30 \uC27D\uB2E4. \uC0C1\uB300\uC801\uC73C\uB85C \uC791\uC740 \uB370\uC774\uD130\uC14B\uC5D0\uC11C \uB9E4\uC6B0 \uBE48\uBC88\uD788 \uAC31\uC2E0\uACFC \uC0AD\uC81C\uB97C \uD558\uB294 \uC791\uC5C5 \uBD80\uD558\uB294 \uBD88\uBCC0 \uD788\uC2A4\uD1A0\uB9AC\uAC00 \uAC10\uB2F9\uD558\uAE30 \uD798\uB4E4 \uC815\uB3C4\uB85C \uCEE4\uC9C0\uAC70\uB098 \uD30C\uD3B8\uD654 \uBB38\uC81C\uAC00 \uBC1C\uC0DD\uD560 \uC218 \uC788\uB2E4.</p><p>\uC131\uB2A5\uC801\uC778 \uC774\uC720 \uC678\uC5D0\uB3C4 \uAC1C\uC778\uC815\uBCF4 \uADDC\uC81C \uB4F1\uC758 \uAD00\uB9AC\uC0C1\uC758 \uC774\uC720\uB85C \uB370\uC774\uD130\uB97C \uC0AD\uC81C\uD560 \uD544\uC694\uAC00 \uC788\uB294 \uC0C1\uD669\uC77C \uC218 \uC788\uB2E4. \uC774\uB7F0 \uC0C1\uD669\uC5D0\uC11C\uB294 \uC774\uC804 \uB370\uC774\uD130\uB97C \uC0AD\uC81C\uD574\uC57C \uD55C\uB2E4\uB294 \uC774\uBCA4\uD2B8\uB97C \uCD94\uAC00\uD574\uC11C \uD574\uACB0\uB418\uB294 \uACBD\uC6B0\uAC00 \uC544\uB2C8\uB2E4. \uC2E4\uC81C\uB85C \uC6D0\uD558\uB294 \uBC14\uB294 \uD788\uC2A4\uD1A0\uB9AC\uB97C \uC0C8\uB85C \uC4F0\uACE0 \uBB38\uC81C\uAC00 \uB418\uB294 \uB370\uC774\uD130\uB97C \uCC98\uC74C\uBD80\uD130 \uAE30\uB85D\uD558\uC9C0 \uC54A\uC558\uB358 \uAC83\uCC98\uB7FC \uD558\uB294 \uC77C\uC774\uB2E4. \uC774 \uAE30\uB2A5\uC744 \uC801\uCD9C(exicision)\uC774\uB77C\uACE0 \uBD80\uB978\uB2E4.</p><p>\uB370\uC774\uD130\uB97C \uC9C4\uC9DC\uB85C \uC0AD\uC81C\uD558\uB294 \uC791\uC5C5\uC740 \uC5B4\uB835\uB2E4. \uB9CE\uC740 \uACF3\uC5D0 \uBCF5\uC81C\uBCF8\uC774 \uB0A8\uC544 \uC788\uAE30 \uB54C\uBB38\uC774\uB2E4.</p><h2 id="\u110C\u1165\u11BC\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#\u110C\u1165\u11BC\u1105\u1175" aria-hidden="true">#</a> \uC815\uB9AC</h2><p>\uC9C0\uAE08\uAE4C\uC9C0 \uC2A4\uD2B8\uB9BC\uC774 \uC5B4\uB514\uC5D0\uC11C \uC624\uB294\uC9C0(\uC0AC\uC6A9\uC790 \uD65C\uB3D9 \uC774\uBCA4\uD2B8, \uC13C\uC11C, \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC4F0\uAE30) \uC2A4\uD2B8\uB9BC\uC774 \uC5B4\uB5BB\uAC8C \uC804\uC1A1\uB418\uB294\uC9C0(\uC9C1\uC811 \uBA54\uC2DC\uC9D5, \uBA54\uC2DC\uC9C0 \uBE0C\uB85C\uCEE4, \uC774\uBCA4\uD2B8 \uB85C\uADF8) \uC0B4\uD3B4\uBD24\uB2E4.</p><p>\uC0AC\uC6A9\uC790 \uD65C\uB3D9 \uC774\uBCA4\uD2B8, \uC8FC\uAE30\uC801\uC778 \uC13C\uC11C \uD310\uB3C5 \uAC12, \uB370\uC774\uD130 \uD53C\uB4DC(ex. \uAE08\uC735\uC2DC\uC7A5 \uB370\uC774\uD130)\uB294 \uC790\uC5F0\uC2A4\uB7FD\uAC8C \uC2A4\uD2B8\uB9BC\uC73C\uB85C \uD45C\uD604\uD560 \uC218 \uC788\uB2E4. \uC554\uBB35\uC801\uC73C\uB85C \uBCC0\uACBD \uB370\uC774\uD130 \uCEA1\uCC98(CDC)\uB97C \uD1B5\uD558\uAC70\uB098 \uBA85\uC2DC\uC801\uC73C\uB85C \uC774\uBCA4\uD2B8 \uC18C\uC2F1\uC744 \uD1B5\uD574 \uBCC0\uACBD \uB85C\uADF8\uB97C \uCEA1\uCC98\uD560 \uC218 \uC788\uB2E4.</p><p>\uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB97C \uC2A4\uD2B8\uB9BC\uCC98\uB7FC \uD45C\uD604\uD558\uBA74 \uC5EC\uB7EC \uC2DC\uC2A4\uD15C\uC744 \uC190\uC27D\uAC8C \uD1B5\uD569\uD558\uB294 \uAE30\uD68C\uAC00 \uC5F4\uB9B0\uB2E4. \uBCC0\uACBD \uB85C\uADF8\uB97C \uC18C\uBE44\uD574 \uADF8 \uB85C\uADF8\uB97C \uD30C\uC0DD \uC2DC\uC2A4\uD15C\uC5D0 \uC801\uC6A9\uD558\uBA74 \uC0C9\uC778, \uCE90\uC2DC, \uBD84\uC11D\uC6A9 \uC2DC\uC2A4\uD15C\uACFC \uAC19\uC740 \uD30C\uC0DD \uB370\uC774\uD130 \uC2DC\uC2A4\uD15C\uC744 \uD56D\uC0C1 \uCD5C\uC2E0 \uC0C1\uD0DC\uB85C \uC720\uC9C0\uD560 \uC218 \uC788\uB2E4. \uCC98\uC74C\uBD80\uD130 \uC2DC\uC791\uD574 \uD604\uC7AC\uAE4C\uC9C0 \uBAA8\uB4E0 \uBCC0\uACBD \uB85C\uADF8\uB97C \uC18C\uBE44\uD558\uBA74 \uAE30\uC874 \uB370\uC774\uD130\uC758 \uC0C8\uB85C\uC6B4 \uBDF0\uB97C \uAD6C\uC131\uD558\uB294 \uAC83\uB3C4 \uAC00\uB2A5\uD558\uB2E4.</p>',56),n=[p];function s(t,c){return e(),r("div",null,n)}var _=a(d,[["render",s],["__file","ch11-stream-processing.html.vue"]]);export{_ as default};
