import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";import{r as a,o,c as i,a as e,b as n,d as l,e as s}from"./app.d6b42b88.js";var c="/blog/assets/2-wireshark-ethernet.7baf0389.png",d="/blog/assets/2-wireshark-arp.f3197958.png";const P={},h=l('<div class="custom-container tip"><p class="custom-container-title">GOAL</p><p>Q. TCP/IP\uBAA8\uB378\uACFC OSI 7\uACC4\uCE35 \uBAA8\uB378\uC774\uB780?<br> Q. \uC704 \uB450\uBAA8\uB378\uC758 \uACF5\uD1B5\uC810\uACFC \uCC28\uC774\uC810\uC740?<br> Q. \uD328\uD0B7\uC774\uB780? \uD328\uD0B7\uC744 \uC774\uC6A9\uD55C \uD1B5\uC2E0\uACFC\uC815\uC740?<br> Q. \uB124\uD2B8\uC6CC\uD06C \uD504\uB85C\uD1A0\uCF5C\uC774\uB780?</p></div><p>\uD574\uB2F9\uD398\uC774\uC9C0\uB294 2022\uB144 3\uC6D4 \uB178\uC158\uC5D0\uC11C \uB9C8\uC774\uADF8\uB808\uC774\uC158 \uB418\uC5C8\uC2B5\uB2C8\uB2E4.</p><h2 id="tcp-ip-\u1106\u1169\u1103\u1166\u11AF" tabindex="-1"><a class="header-anchor" href="#tcp-ip-\u1106\u1169\u1103\u1166\u11AF" aria-hidden="true">#</a> TCP/IP \uBAA8\uB378</h2><p>1960\uB144\uB300 \uB9D0 \uBBF8\uAD6D\uBC29\uC131\uC758 \uC5F0\uAD6C\uC5D0\uC11C \uC2DC\uC791\uB418\uC5B4 1980\uB144\uB300 \uCD08 \uD504\uB85C\uD1A0\uCF5C \uBAA8\uB378\uB85C \uACF5\uAC1C \uD604\uC7AC\uC758 \uC778\uD130\uB137\uC5D0\uC11C \uCEF4\uD4E8\uD130\uB4E4\uC774 \uC11C\uB85C \uC815\uBCF4\uB97C \uC8FC\uACE0\uBC1B\uB294\uB370 \uC4F0\uC774\uB294 \uD1B5\uC2E0 \uADDC\uC57D(\uD504\uB85C\uD1A0\uCF5C)\uC758 \uBAA8\uC74C\uC774\uB2E4.</p><p>4\uACC4\uCE35: \uC751\uC6A9<br> 3\uACC4\uCE35: \uC804\uC1A1<br> 2\uACC4\uCE35: \uB124\uD2B8\uC6CC\uD06C<br> 1\uACC4\uCE35: \uB124\uD2B8\uC6CC\uD06C \uC778\uD130\uD398\uC774\uC2A4</p><h2 id="osi-7\u1100\u1168\u110E\u1173\u11BC" tabindex="-1"><a class="header-anchor" href="#osi-7\u1100\u1168\u110E\u1173\u11BC" aria-hidden="true">#</a> OSI 7\uACC4\uCE35</h2><p>1984\uB144 \uB124\uD2B8\uC6CC\uD06C \uD1B5\uC2E0\uC744 \uCCB4\uACC4\uC801\uC73C\uB85C \uB2E4\uB8E8\uB294 ISO\uC5D0\uC11C \uD45C\uC900\uC73C\uB85C \uC9C0\uC815\uD55C \uBAA8\uB378<br> \uB370\uC774\uD130\uB97C \uC8FC\uACE0\uBC1B\uC744 \uB54C \uB370\uC774\uD130 \uC790\uCCB4\uC758 \uD750\uB984\uC744 \uAC01 \uAD6C\uAC04\uBCC4\uB85C \uB098\uB220 \uB193\uC740 \uAC83</p><p>7\uACC4\uCE35 \uC751\uC6A9 : <strong>HTTP</strong>, SMTP, IMAP, POP, SNMP, FTP, TELNET, SSH<br> 6\uACC4\uCE35 \uD45C\uD604 : SMB, AFP, XDR<br> 5\uACC4\uCE35 \uC138\uC158 : NetBIOS<br> 4\uACC4\uCE35 \uC804\uC1A1 : <strong>TCP</strong>, <strong>UDP</strong>, SPX<br> 3\uACC4\uCE35 \uB124\uD2B8\uC6CC\uD06C : <strong>IP</strong>, <strong>ICMP</strong>, IGMP, X.25, CLNP, <strong>ARP</strong>, RARP, BGP, OSPF, RIP, IPX, DDP<br> 2\uACC4\uCE35 \uB370\uC774\uD130 \uB9C1\uD06C : <strong>\uC774\uB354\uB137</strong>, \uD1A0\uD070\uB9C1, PPP, HDLC, \uD504\uB808\uC784 \uB9B4\uB808\uC774, ISDN, ATM, \uBB34\uC120\uB79C, FDDI<br> 1\uACC4\uCE35 \uBB3C\uB9AC : \uC804\uC120, \uC804\uD30C, \uAD11\uC12C\uC720, \uB3D9\uCD95\uCF00\uC774\uBE14, \uB3C4\uD30C\uAD00, PSTN, \uB9AC\uD53C\uD130, DSU, CSU, \uBAA8\uB380</p><p>*\uB514\uD14C\uC77C\uD558\uAC8C\uB294 \uAD75\uC740 \uAE00\uC528 \uD504\uB85C\uD1A0\uCF5C \uC815\uB3C4\uB9CC \uC54C\uC544\uB3C4 \uB41C\uB2E4.</p><h2 id="\u1103\u116E-\u1106\u1169\u1103\u1166\u11AF\u110B\u1174-\u1100\u1169\u11BC\u1110\u1169\u11BC\u110C\u1165\u11B7\u1100\u116A-\u110E\u1161\u110B\u1175\u110C\u1165\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1103\u116E-\u1106\u1169\u1103\u1166\u11AF\u110B\u1174-\u1100\u1169\u11BC\u1110\u1169\u11BC\u110C\u1165\u11B7\u1100\u116A-\u110E\u1161\u110B\u1175\u110C\u1165\u11B7" aria-hidden="true">#</a> \uB450 \uBAA8\uB378\uC758 \uACF5\uD1B5\uC810\uACFC \uCC28\uC774\uC810</h2><p>\uC2E4\uC81C \uB124\uD2B8\uC6CC\uD06C\uB294 TCP/IP\uBAA8\uB378\uC5D0 \uB354 \uB9DE\uB294\uB2E4.</p><ul><li>\uACF5\uD1B5\uC810 <ul><li>\uB124\uD2B8\uC6CC\uD06C\uB97C \uACC4\uCE35\uC73C\uB85C \uB098\uB220\uC11C \uBD84\uB958\uD588\uB2E4.</li><li>\uACC4\uCE35\uAC04 \uC5ED\uD560 \uC815\uC758\uD558\uACE0 \uC0AC\uC6A9\uD558\uB294 \uBAA8\uB378\uC774\uB2E4.</li></ul></li><li>\uCC28\uC774\uC810 <ul><li>\uACC4\uCE35\uC758 \uC218 \uCC28\uC774</li><li>OSI\uB294 \uD1B5\uC2E0\uC744 \uC5ED\uD560\uC744 \uAE30\uBC18\uC73C\uB85C \uBD84\uB958\uD588\uB2E4.(\uB17C\uB9AC\uC801\uC778 \uB290\uB08C)</li><li>TCP/IP\uB294 \uB124\uD2B8\uC6CC\uD06C\uB97C \uD1B5\uD574\uC11C \uB370\uC774\uD130\uB97C \uC804\uB2EC \uD560 \uB54C,\uD504\uB85C\uD1A0\uCF5C \uAE30\uBC18\uC73C\uB85C \uAE30\uC220 \uC790\uCCB4\uB97C \uBD84\uB958\uD588\uB2E4.(\uC2E4\uBB34\uC801\uC778 \uB290\uB08C) \uC608\uB97C \uB4E4\uC5B4 http\uB77C\uB294 \uD504\uB85C\uD1A0\uCF5C\uC740 OSI\uAC8C\uCE35\uC5D0\uC11C 5,6,7\uACC4\uCE35\uC758 \uC5ED\uD560\uC744 \uB2E4 \uAC00\uC9C0\uACE0 \uC788\uC5B4\uC11C \uBD84\uB958\uD558\uAE30 \uC560\uB9E4\uD558\uB2E4.</li><li>OSI\uB294 \uD1B5\uC2E0 \uC804\uBC18\uC5D0 \uB300\uD55C \uD45C\uC900</li><li>TCP/IP\uB294 \uB370\uC774\uD130 \uC804\uC1A1\uAE30\uC220 \uD2B9\uD654</li></ul></li></ul><h2 id="\u1111\u1162\u110F\u1175\u11BA-\u1102\u1166\u1110\u1173\u110B\u116F\u110F\u1173\u1105\u1173\u11AF-\u1110\u1169\u11BC\u1112\u1162-\u110C\u1165\u11AB\u1103\u1161\u11AF\u1103\u116C\u1102\u1173\u11AB-\u1103\u1166\u110B\u1175\u1110\u1165" tabindex="-1"><a class="header-anchor" href="#\u1111\u1162\u110F\u1175\u11BA-\u1102\u1166\u1110\u1173\u110B\u116F\u110F\u1173\u1105\u1173\u11AF-\u1110\u1169\u11BC\u1112\u1162-\u110C\u1165\u11AB\u1103\u1161\u11AF\u1103\u116C\u1102\u1173\u11AB-\u1103\u1166\u110B\u1175\u1110\u1165" aria-hidden="true">#</a> \uD328\uD0B7, \uB124\uD2B8\uC6CC\uD06C\uB97C \uD1B5\uD574 \uC804\uB2EC\uB418\uB294 \uB370\uC774\uD130</h2><ul><li>\uD328\uD0B7\uC774\uB780?<br> \uD328\uD0B7\uC774\uB780 \uB124\uD2B8\uC6CC\uD06C \uC0C1\uC5D0\uC11C \uC804\uB2EC\uB418\uB294 \uB370\uC774\uD130\uB97C \uD1B5\uCE6D\uD558\uB294 \uB9D0\uB85C \uB124\uD2B8\uC6CC\uD06C\uC5D0\uC11C \uC804\uB2EC\uD558\uB294 \uB370\uC774\uD130\uC758 \uD615\uC2DD\uD654\uB41C \uBE14\uB85D\uC774\uB2E4. \uD328\uD0B7\uC740 \uC81C\uC5B4 \uC815\uBCF4\uC640 \uC0AC\uC6A9\uC790 \uB370\uC774\uD130\uB85C \uC774\uB8E8\uC5B4\uC9C0\uBA70 \uC0AC\uC6A9\uC790 \uB370\uC774\uD130\uB294 \uD398\uC774\uB85C\uB4DC\uB77C\uACE0\uB3C4 \uD55C\uB2E4. \uC5EC\uB7EC \uBC88 \uD3EC\uC7A5\uB41C \uD0DD\uBC30 \uC0C1\uC790\uCC98\uB7FC \uD328\uD0B7\uC740 \uD328\uD0B7\uC744 \uAC10\uC2F8\uACE0 \uC788\uB2E4.(\uC5EC\uB7EC \uD504\uB85C\uD1A0\uCF5C\uB4E4\uB85C \uCEA1\uC290\uD654 \uB418\uC5B4\uC788\uB2E4.)</li></ul><ul><li><p>\uCEA1\uC290\uD654(\uC778\uCEA1\uC290\uB808\uC774\uC158)<br> \uC5EC\uB7EC \uD504\uB85C\uD1A0\uCF5C\uC744 \uC774\uC6A9\uD574\uC11C \uCD5C\uC885\uC801\uC73C\uB85C \uBCF4\uB0BC \uB54C \uD328\uD0B7\uC744 \uB9CC\uB4DC\uB294 \uACFC\uC815 \uB192\uC740 \uACC4\uCE35\uC5D0\uC11C \uB0AE\uC740 \uACC4\uCE35\uC73C\uB85C \uC778\uCEA1\uC290\uB808\uC774\uC158 \uB41C\uB2E4. <code>\uB370\uC774\uD130</code> -&gt; <code>TCP + \uD398\uC774\uB85C\uB4DC(\uB370\uC774\uD130)</code> -&gt; <code>IPv4 + \uD398\uC774\uB85C\uB4DC(TCP + \uB370\uC774\uD130)</code> -&gt; <code>Ethernet + \uD398\uC774\uB85C\uB4DC(IPv4 + TCP + \uB370\uC774\uD130)</code></p></li><li><p>\uB514\uCEA1\uC290\uD654(\uB514\uCEA1\uC290\uB808\uC774\uC158)<br> \uB0AE\uC740\uACC4\uCE35\uC5D0\uC11C \uB192\uC740\uACC4\uCE35\uC73C\uB85C \uB514\uCEA1\uC290\uB808\uC774\uC158 \uD55C\uB2E4. \uD328\uD0B7\uC744 \uBC1B\uC558\uC744 \uB54C \uD504\uB85C\uD1A0\uCF5C\uB4E4\uC744 \uD558\uB098\uC529 \uD655\uC778\uD558\uBA74\uC11C \uB370\uC774\uD130\uB97C \uD655\uC778\uD558\uB294 \uACFC\uC815 <code>Ethernet + \uD398\uC774\uB85C\uB4DC(IPv4 + TCP + \uB370\uC774\uD130)</code> -&gt; <code>IPv4 + \uD398\uC774\uB85C\uB4DC(TCP + \uB370\uC774\uD130)</code>-&gt; <code>TCP + \uD398\uC774\uB85C\uB4DC(\uB370\uC774\uD130)</code>-&gt; <code>\uB370\uC774\uD130</code></p></li><li><p>PDU(Protocol Data Unit)<br> \uACC4\uCE35\uBCC4\uB85C \uD328\uD0B7\uB4E4\uC744 \uBD80\uB974\uB294 \uC774\uB984\uC774 \uB2E4\uB974\uB2E4.<br> 4\uACC4\uCE35\uAE4C\uC9C0 \uD569\uCCE4\uC744 \uB54C PDU = \uC138\uADF8\uBA3C\uD2B8 ex)TCP + \uD398\uC774\uB85C\uB4DC(\uB370\uC774\uD130)<br> 3\uACC4\uCE35\uAE4C\uC9C0 \uD569\uCCE4\uC744 \uB54C PDU = \uD328\uD0B7 ex)IPv4 + \uD398\uC774\uB85C\uB4DC(TCP + \uB370\uC774\uD130)<br> 2\uACC4\uCE35\uAE4C\uC9C0 \uD569\uCCE4\uC744 \uB54C PDU = \uD504\uB808\uC784 ex)Ethernet + \uD398\uC774\uB85C\uB4DC(IPv4 + TCP + \uB370\uC774\uD130)<br> \uADF8\uB7EC\uB098 \uC77C\uBC18\uC801\uC73C\uB85C \uD328\uD0B7\uC774\uB77C \uB9D0\uD558\uBA74 3\uACC4\uCE35PDU\uC758 \uD328\uD0B7\uC774 \uC544\uB2C8\uB77C \uB124\uD2B8\uC6CC\uD06C\uC0C1\uC5D0\uC11C \uC804\uB2EC\uB418\uB294 \uB370\uC774\uD130 \uC790\uCCB4\uB97C \uD328\uD0B7\uC774\uB77C\uACE0 \uBD80\uB978\uB2E4. \uADF8\uB7EC\uB098 \uD504\uB808\uC784\uC774\uB77C\uACE0 \uC598\uAE30\uD558\uBA74 2\uACC4\uCE35\uAE4C\uC9C0 \uC778\uCEA1\uC290\uB808\uC774\uC158\uB41C \uC0C1\uD0DC\uC774\uB2E4.</p></li></ul><h2 id="\u1109\u1175\u11AF\u1109\u1173\u11B8" tabindex="-1"><a class="header-anchor" href="#\u1109\u1175\u11AF\u1109\u1173\u11B8" aria-hidden="true">#</a> \uC2E4\uC2B5</h2><ol><li>\uD504\uB85C\uD1A0\uCF5C\uC758 \uCEA1\uC290\uD654 \uB41C \uBAA8\uC2B5\uACFC \uACC4\uCE35\uBCC4 \uD504\uB85C\uD1A0\uCF5C\uB4E4\uC744 \uD655\uC778\uD574\uBCF4\uAE30 : wireshark\uB97C \uC774\uC6A9\uD558\uC5EC \uD328\uD0B7\uC744 \uCEA1\uCCD0\uD574\uBCF4\uACE0 \uD574\uB2F9 \uD328\uD0B7\uC774 \uC5B4\uB5BB\uAC8C \uCEA1\uC290\uD654 \uB418\uC5C8\uB294\uC9C0 \uC790\uC138\uD788 \uC0B4\uD3B4\uBCF8\uB2E4.</li></ol><p><img src="'+c+'" alt="ethernet" loading="lazy"><img src="'+d+'" alt="arp" loading="lazy"></p><p><strong>\uACB0\uACFC</strong> : \uC704\uC0AC\uC9C4\uC744 \uBCF4\uBA74 \uC774\uB354\uB137(Ethernet)\uC785\uC7A5\uC5D0\uC11C\uB294 \uC774\uB354\uB137 \uD504\uB85C\uD1A0\uCF5C\uC774 \uD5E4\uB354\uACE0 ARP\uD504\uB85C\uD1A0\uCF5C\uC774 \uD398\uC774\uB85C\uB4DC\uC774\uB2E4. ARP\uD504\uB85C\uD1A0\uCF5C\uC740 3\uACC4\uCE35\uC774\uACE0 \uD5E4\uB354\uB85C\uC368 2\uACC4\uCE35\uC774 \uBD99\uC740 \uAC83\uC774\uB2E4. \uC778\uCEA1\uC290\uB808\uC774\uC158 \uB418\uC5C8\uB2E4. ARP\uC785\uC7A5\uC5D0\uC11C \uBCF4\uBA74 ARP\uD504\uB85C\uD1A0\uCF5C\uC774 \uD5E4\uB354\uACE0 \uD398\uC774\uB85C\uB4DC\uB294 \uC5C6\uB2E4.</p><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',20),p={href:"https://www.youtube.com/playlist?list=PL0d8NnikouEWcF1jJueLdjRIC4HsUlULi",target:"_blank",rel:"noopener noreferrer"},b=s("\uB530\uB77C\uD558\uBA70 \uBC30\uC6B0\uB294 IT - \uB124\uD2B8\uC6CC\uD06C \uAE30\uCD08(YouTube)");function _(g,u){const r=a("ExternalLinkIcon");return o(),i("div",null,[h,e("p",null,[e("a",p,[b,n(r)])])])}var f=t(P,[["render",_],["__file","ch2-\uB124\uD2B8\uC6CC\uD06C-\uBAA8\uB378.html.vue"]]);export{f as default};
