import{_ as r,a as t}from"./3-\uAC00\uBCC0\uBD84\uD560-\uBC29\uC2DD.20abec87.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as n,c as s,d as l}from"./app.d6b42b88.js";const o={},e=l('<div class="custom-container tip"><p class="custom-container-title">GOAL</p><p>Q. \uC6B4\uC601\uCCB4\uC81C\uC758 \uC5ED\uD560\uC740 \uBB34\uC5C7\uC778\uAC00\uC694?<br> Q. \uC6B4\uC601\uCCB4\uC81C\uC758 \uC790\uC6D0\uAD00\uB9AC \uAE30\uB2A5\uC744 \uC124\uBA85\uD558\uC138\uC694.<br> Q.\uC778\uD130\uB7FD\uD2B8\uAC00 \uBB34\uC5C7\uC778\uAC00\uC694? \uBC1C\uC0DD\uC5D0 \uB530\uB978 \uCEF4\uD4E8\uD130 \uC218\uD589 \uD750\uB984\uC740?</p></div><p>\uD574\uB2F9\uD398\uC774\uC9C0\uB294 2022\uB144 3\uC6D4 \uB178\uC158\uC5D0\uC11C \uB9C8\uC774\uADF8\uB808\uC774\uC158 \uB418\uC5C8\uC2B5\uB2C8\uB2E4.<br> \uBC11\uC5D0 \uC788\uB294 \uADF8\uB9BC\uB4E4\uC740 \uAC1C\uC778\uC801\uC778 \uC774\uD574\uB97C \uB3D5\uAE30 \uC704\uD574 \uADF8\uB9B0 \uADF8\uB9BC\uC73C\uB85C \uC2E4\uC81C \uC6D0\uB9AC\uC640 \uB9DE\uC9C0 \uC54A\uC744 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166\u110B\u1174-\u110B\u1167\u11A8\u1112\u1161\u11AF" tabindex="-1"><a class="header-anchor" href="#\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166\u110B\u1174-\u110B\u1167\u11A8\u1112\u1161\u11AF" aria-hidden="true">#</a> \uC6B4\uC601\uCCB4\uC81C\uC758 \uC5ED\uD560</h2><ul><li>\uC790\uC6D0 \uD560\uB2F9\uC758 \uC8FC\uCCB4 <ul><li>\uBAA8\uB4E0 \uC790\uC6D0\uB97C \uAD00\uB9AC</li><li>\uC751\uC6A9 \uD504\uB85C\uADF8\uB7A8\uAC04\uC758 \uD558\uB4DC\uC6E8\uC5B4 \uC0AC\uC6A9\uC744 \uC81C\uC5B4, \uC870\uC815\uD558\uB294 \uC5ED\uD560</li><li>\uC790\uC6D0\uC5D0 \uB300\uD55C \uCDA9\uB3CC\uB418\uB294 \uC694\uCCAD \uC0AC\uC774\uC5D0\uC11C \uD6A8\uC728\uC801\uC774\uACE0 \uACF5\uC815\uD558\uAC8C \uC0AC\uC6A9\uD558\uB3C4\uB85D \uC790\uC6D0 \uBD84\uBC30</li></ul></li><li>\uC81C\uC5B4\uB97C \uC804\uB2F4 <ul><li>\uD504\uB85C\uADF8\uB7A8 \uC2E4\uD589\uC744 \uC81C\uC5B4, \uC624\uB958 \uBC0F \uBD80\uC801\uC808\uD55C \uC0AC\uC6A9 \uBC29\uC9C0</li></ul></li></ul><p>\uC5EC\uAE30\uC11C \uC790\uC6D0\uC774\uB780 CPU(Central processing Unit), \uBA54\uBAA8\uB9AC, \uD558\uB4DC\uB514\uC2A4\uD06C \uB4F1 \uD558\uB4DC\uC6E8\uC5B4 \uC790\uC6D0\uBFD0 \uC544\uB2C8\uB77C \uC18C\uD504\uD2B8\uC6E8\uC5B4 \uC790\uC6D0\uAE4C\uC9C0\uB97C \uD1B5\uCE6D\uD574\uC11C \uBD80\uB974\uB294 \uB9D0</p><h2 id="\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166\u110B\u1174-\u110C\u1161\u110B\u116F\u11AB\u1100\u116A\u11AB\u1105\u1175-\u1100\u1175\u1102\u1173\u11BC" tabindex="-1"><a class="header-anchor" href="#\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166\u110B\u1174-\u110C\u1161\u110B\u116F\u11AB\u1100\u116A\u11AB\u1105\u1175-\u1100\u1175\u1102\u1173\u11BC" aria-hidden="true">#</a> \uC6B4\uC601\uCCB4\uC81C\uC758 \uC790\uC6D0\uAD00\uB9AC \uAE30\uB2A5</h2><ul><li>\uC5B4\uB5A4 \uD504\uB85C\uADF8\uB7A8\uC5D0 CPU\uB97C \uC904 \uAC83\uC778\uAC00? <strong>CPU\uC2A4\uCF00\uC904\uB9C1-\uD504\uB85C\uC138\uC2A4 \uAD00\uB9AC</strong></li><li>\uD55C\uC815\uB41C \uBA54\uBAA8\uB9AC\uB97C \uC5B4\uB5BB\uAC8C \uB098\uB204\uC5B4 \uC0AC\uC6A9\uD560 \uAC83\uC778\uAC00? <strong>\uBA54\uBAA8\uB9AC \uAD00\uB9AC</strong></li><li>\uB514\uC2A4\uD06C\uC5D0 \uD30C\uC77C\uC744 \uC5B4\uB5BB\uAC8C \uBCF4\uAD00\uD560 \uAC83\uC778\uAC00? <strong>\uD30C\uC77C \uAD00\uB9AC</strong></li><li>\uAC01\uAE30 \uB2E4\uB978 \uC785\uCD9C\uB825 \uC7A5\uCE58\uC640 \uCEF4\uD4E8\uD130 \uAC04\uC5D0 \uC5B4\uB5BB\uAC8C \uC815\uBCF4\uB97C \uC8FC\uACE0\uBC1B\uC744 \uAC83\uC778\uAC00? <strong>\uC785\uCD9C\uB825\uC7A5\uCE58 \uAD00\uB9AC</strong></li></ul><h3 id="_1-cpu-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_1-cpu-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC" aria-hidden="true">#</a> 1. CPU \uC2A4\uCF00\uC904\uB9C1</h3><p>\uB9E4 \uC2DC\uC810 \uC5B4\uB5A4 \uD504\uB85C\uC138\uC2A4\uC5D0 CPU\uB97C \uD560\uB2F9\uD574 \uC791\uC5C5\uC744 \uCC98\uB9AC\uD560 \uAC83\uC778\uC9C0 \uACB0\uC815\uD558\uB294 \uC77C\uC774 \uD544\uC694\uD558\uB2E4. \uC774\uB7EC\uD55C \uC77C\uC744 CPU\uC2A4\uCF00\uC904\uB9C1 \uC774\uB77C\uACE0 \uD55C\uB2E4 CPU\uC2A4\uCF00\uC904\uB9C1 \uBAA9\uD45C\uB294 CPU\uB97C \uD6A8\uC728\uC801\uC73C\uB85C \uC0AC\uC6A9\uD558\uBA74\uC11C\uB3C4 \uD2B9\uC815 \uD504\uB85C\uC138\uC2A4\uAC00 \uBD88\uC774\uC775\uC744 \uB2F9\uD558\uC9C0 \uC54A\uB3C4\uB85D \uD558\uB294\uAC83\uC774\uB2E4. \uB300\uD45C\uC801\uC73C\uB85C \uC120\uC785\uC120\uCD9C(FIFS), \uB77C\uC6B4\uB4DC \uB85C\uBE48(Round Robin), \uC6B0\uC120\uC21C\uC704(priority) \uAE30\uBC95 \uB4F1\uC774 \uC788\uB2E4.</p><p><strong>\uC120\uC785\uC120\uCD9C</strong><br> \uBA3C\uC800 \uB3C4\uCC29\uD55C \uD504\uB85C\uC138\uC2A4\uB97C \uBA3C\uC800 \uCC98\uB9AC\uD558\uB294 \uC2A4\uCF00\uC904\uB9C1 \uAE30\uBC95\uC774\uB2E4. \uC2DC\uC2A4\uD15C \uC785\uC7A5\uC5D0\uC11C \uBE44\uD6A8\uC728\uC801\uC774\uB2E4. \uB9CC\uC57D \uAE34 \uC791\uC5C5\uC2DC\uAC04\uC744 \uC694\uAD6C\uD558\uB294 \uD504\uB85C\uC138\uC2A4\uAC00 \uC55E\uC5D0 \uC788\uB2E4\uBA74 \uD6C4\uC5D0 \uB3C4\uCC29\uD558\uB294 \uD504\uB85C\uC138\uC2A4\uB4E4\uC740 \uADF8 \uC791\uC5C5\uC774 \uC644\uB8CC\uB420 \uB54C \uAE4C\uC9C0 \uC624\uB79C\uC2DC\uAC04 \uAE30\uB2E4\uB824\uC57C \uD55C\uB2E4.</p><p><strong>\uB77C\uC6B4\uB4DC\uB85C\uBE48</strong><br> CPU\uB97C \uD560\uB2F9\uBC1B\uC544 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uC2DC\uAC04\uC774 \uC77C\uC815\uD558\uB3C4\uB85D \uACE0\uC815\uB41C \uC2DC\uAC04\uC73C\uB85C \uC81C\uD55C\uD558\uB294 \uC2A4\uCF00\uC904\uB9C1 \uAE30\uBC95\uC774\uB2E4. \uAE34 \uC791\uC5C5\uC2DC\uAC04\uC744 \uC694\uAD6C\uD558\uB294 \uD504\uB85C\uC138\uC2A4\uAC00 CPU\uB97C \uD560\uB2F9\uBC1B\uACE0, \uC9C4\uD589\uD558\uB358 \uC791\uC5C5\uC774 \uB05D\uB098\uC9C0 \uC54A\uC558\uB354\uB77C\uB3C4 \uC815\uD574\uC9C4 \uC2DC\uAC04\uC774 \uC9C0\uB098\uBA74 CPU\uB97C \uB0B4\uC5B4\uB193\uACE0 \uB300\uAE30\uC5F4\uC758 \uC81C\uC77C \uB4A4\uB85C \uAC00\uC57C\uD55C\uB2E4.</p><p><strong>\uC6B0\uC120\uC21C\uC704</strong><br> \uB300\uAE30\uC911\uC778 \uD504\uB85C\uC138\uC2A4\uB4E4\uC5D0\uAC8C \uC6B0\uC120\uC21C\uC704\uB97C \uBD80\uC5EC\uD558\uACE0 \uC6B0\uC120\uC21C\uC704\uAC00 \uB192\uC740 \uD504\uB85C\uC138\uC2A4\uC5D0\uAC8C CPU\uB97C \uBA3C\uC800 \uD560\uB2F9\uD558\uB294 \uC2A4\uCF00\uC904\uB9C1 \uAE30\uBC95\uC774\uB2E4. \uB9CC\uC57D \uD2B9\uC815 \uC791\uC5C5\uC774 \uC9C0\uB098\uCE58\uAC8C \uC624\uB798 \uAE30\uB2E4\uB9AC\uAC8C \uB418\uBA74 \uC6B0\uC120\uC21C\uC704\uB97C \uB192\uC5EC\uC8FC\uB294 \uB4F1\uC758 \uBC29\uC548\uC744 \uACE0\uB824\uD574\uC57C \uD55C\uB2E4.(\uAE30\uC544\uC0C1\uD0DC\uB97C \uBC29\uC9C0\uD558\uAE30 \uC704\uD55C \uC5D0\uC774\uC9D5)</p><h3 id="_2-\u1106\u1166\u1106\u1169\u1105\u1175-\u1100\u116A\u11AB\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#_2-\u1106\u1166\u1106\u1169\u1105\u1175-\u1100\u116A\u11AB\u1105\u1175" aria-hidden="true">#</a> 2. \uBA54\uBAA8\uB9AC \uAD00\uB9AC</h3><p>\uBA54\uBAA8\uB9AC\uB780 CPU\uAC00 \uC9C1\uC811 \uC811\uADFC\uD560 \uC218 \uC788\uB294 \uCEF4\uD4E8\uD130 \uB0B4\uBD80\uC758 \uAE30\uC5B5\uC7A5\uCE58\uC774\uB2E4. \uD504\uB85C\uADF8\uB7A8\uC774 CPU\uC5D0\uC11C \uC2E4\uD589\uB418\uB824\uBA74 \uD574\uB2F9 \uBD80\uBD84\uC774 \uBA54\uBAA8\uB9AC\uC5D0 \uC62C\uB77C\uAC00 \uC788\uC5B4\uC57C\uD55C\uB294\uB370 \uD55C\uC815\uB41C \uBA54\uBAA8\uB9AC \uACF5\uAC04\uC5D0 \uC5EC\uB7EC \uD504\uB85C\uADF8\uB7A8\uC744 \uC218\uC6A9\uD558\uAE30 \uC704\uD574\uC11C \uD6A8\uC728\uC801\uC778 \uAD00\uB9AC \uBA54\uCEE4\uB2C8\uC998\uC774 \uD544\uC694\uD558\uB2E4.<br> \uB530\uB77C\uC11C \uBA54\uBAA8\uB9AC \uAD00\uB9AC\uB97C \uC704\uD574 \uC6B4\uC601\uCCB4\uC81C\uB294 \uBA54\uBAA8\uB9AC\uC758 \uC5B4\uB5A4 \uBD80\uBD84\uC774 \uC5B4\uB5A4 \uD504\uB85C\uADF8\uB7A8\uC5D0 \uC758\uD574 \uC0AC\uC6A9\uB418\uACE0 \uC788\uB294\uC9C0\uB97C \uD30C\uC545\uD558\uC5EC \uC774\uB97C \uC720\uC9C0 \uD558\uAC8C \uB418\uB294\uB370 \uC774\uB7EC\uD55C \uC815\uBCF4\uB294 <strong>\uC8FC\uC18C(address)</strong> \uB97C \uD1B5\uD574 \uAD00\uB9AC \uB41C\uB2E4. \uBB3C\uB9AC\uC801 \uBA54\uBAA8\uB9AC\uB97C \uAD00\uB9AC\uD558\uB294 \uBC29\uC2DD\uC5D0\uB294 <strong>\uACE0\uC815\uBD84\uD560(fixed partition) \uBC29\uC2DD</strong>, <strong>\uAC00\uBCC0\uBD84\uD560(variable partition) \uBC29\uC2DD</strong>, <strong>\uAC00\uC0C1\uBA54\uBAA8\uB9AC(virtual memory) \uBC29\uC2DD</strong> \uB4F1\uC774 \uC788\uB2E4.</p><p><strong>1. \uACE0\uC815\uBD84\uD560 \uBC29\uC2DD</strong><br> \uBB3C\uB9AC\uC801 \uBA54\uBAA8\uB9AC\uB97C \uBA87 \uAC1C\uC758 \uBD84\uD560\uB85C \uBBF8\uB9AC \uB098\uB204\uC5B4 \uAD00\uB9AC\uD55C\uB2E4. \uBA54\uBAA8\uB9AC\uC5D0 \uB3D9\uC2DC \uC801\uC7AC\uB418\uB294 \uCD5C\uB300 \uD504\uB85C\uADF8\uB7A8\uC218\uAC00 \uBD84\uD560 \uAC1C\uC218\uB85C \uD55C\uC815\uB418\uBA70, \uBD84\uD560\uC758 \uD06C\uAE30\uBCF4\uB2E4 \uD070 \uD504\uB85C\uADF8\uB7A8\uC740 \uC801\uC7AC \uBD88\uAC00\uB2A5\uD558\uB2E4. \uB610\uD55C \uBD84\uD560\uC774 \uACE0\uC815\uC801\uC774\uAE30 \uB54C\uBB38\uC5D0 \uBD84\uD560\uC758 \uD06C\uAE30\uBCF4\uB2E4 \uC791\uC740 \uD504\uB85C\uADF8\uB7A8\uC774 \uC801\uC7AC\uB418\uB294 \uACBD\uC6B0 \uD574\uB2F9 \uBD84\uD560 \uB0B4\uC5D0 \uB0A8\uB294 \uC601\uC5ED\uC774 \uBC1C\uC0DD\uD55C\uB2E4. \uC774\uAC83\uC744 <strong>\uB0B4\uBD80\uC870\uAC01(internal fragmentation)</strong> \uC774\uB77C\uACE0 \uD558\uB294\uB370 \uC774 \uB0B4\uBD80\uC870\uAC01\uC740 \uC0AC\uC6A9\uB420 \uC218 \uC5C6\uC73C\uBBC0\uB85C \uB0AD\uBE44\uB418\uB294 \uACF5\uAC04\uC774\uB2E4. \uC774 \uBC29\uC2DD\uC740 \uBA54\uBAA8\uB9AC\uC758 \uD6A8\uC728\uC801\uC778 \uC0AC\uC6A9\uCE21\uBA74\uC5D0\uC11C \uBC14\uB78C\uC9C1\uD558\uC9C0 \uBABB\uD558\uB2E4.</p><p><img src="'+r+'" alt="\uACE0\uC815\uBD84\uD560" loading="lazy"></p><p><strong>2.\uAC00\uBC88\uBD84\uD560 \uBC29\uC2DD</strong><br> \uAC00\uBCC0\uBD84\uD560 \uBC29\uC2DD\uC740 \uB9E4 \uC2DC\uC810 \uD504\uB85C\uADF8\uB7A8\uC758 \uD06C\uAE30\uC5D0 \uB9DE\uAC8C \uBA54\uBAA8\uB9AC\uB97C \uBD84\uD560\uD574\uC11C \uC0AC\uC6A9\uD558\uB294 \uBC29\uC2DD\uC744 \uB9D0\uD55C\uB2E4. \uB530\uB77C\uC11C \uBD84\uD560\uC758 \uD06C\uAE30 \uB54C\uBB38\uC5D0 \uD070 \uD504\uB85C\uADF8\uB7A8\uC758 \uC2E4\uD589\uC774 \uC81C\uD55C\uB418\uB294 \uBB38\uC81C\uB294 \uBC1C\uC0DD\uD558\uC9C0 \uC54A\uB294\uB2E4 \uADF8\uB7EC\uB098 \uBB3C\uB9AC\uC801 \uBA54\uBAA8\uB9AC \uD06C\uAE30\uBCF4\uB2E4 \uD070 \uD504\uB85C\uADF8\uB7A8 \uC2E4\uD589\uC740 \uC5EC\uC804\uD788 \uBD88\uAC00\uB2A5 \uD558\uB2E4. \uAC00\uBCC0\uBD84\uD560 \uBC29\uC2DD\uC5D0\uC11C\uB294 \uBD84\uD560\uC758 \uD06C\uAE30\uC640 \uAC1C\uC218\uAC00 \uB3D9\uC801\uC73C\uB85C \uBCC0\uD558\uBBC0\uB85C \uAE30\uC220\uC801 \uAD00\uB9AC \uAE30\uBC95\uC774 \uD544\uC694\uD558\uB2E4. \uAC00\uBCC0\uBD84\uD560\uC5D0\uC11C\uB294 \uB0B4\uBD80\uC870\uAC01\uC774 \uBC1C\uC0DD\uD558\uC9C4 \uC54A\uC9C0\uB9CC \uC678\uBD80\uC870\uAC01(external fragmentation)\uC774 \uBC1C\uC0DD \uD560 \uC218\uC788\uB2E4 \uC678\uBD80\uC870\uAC01\uC774\uB780 \uD504\uB85C\uADF8\uB7A8\uC5D0 \uD560\uB2F9 \uB418\uC9C0\uB294 \uC54A\uC558\uC9C0\uB9CC \uADF8 \uD06C\uAE30\uAC00 \uC791\uC544 \uD504\uB85C\uADF8\uB7A8\uC744 \uC62C\uB9AC\uC9C0 \uBABB\uD558\uB294 \uBA54\uBAA8\uB9AC \uC601\uC5ED\uC744 \uB9D0\uD55C\uB2E4. \uD06C\uAE30\uAC00 100\uC778 \uD504\uB85C\uADF8\uB7A8 A\uAC00 \uC2E4\uD589\uB418\uC5B4 \uBA54\uBAA8\uB9AC\uC5D0 100\uB9CC\uD07C\uC758 \uC601\uC5ED\uC744 \uD560\uB2F9 \uBC1B\uC558\uB2E4. \uADF8\uD6C4 \uD06C\uAE30\uAC00 50\uC778\uD504\uB85C\uADF8\uB7A8\uC774 \uC2E4\uD589\uB418\uC5B4 A\uC601\uC5ED \uBC14\uB85C \uB2E4\uC74C\uBD80\uD130 \uBA54\uBAA8\uB9AC \uACF5\uAC04 50\uC744 \uD560\uB2F9\uBC1B\uC558\uB2E4. A \uD504\uB85C\uADF8\uB7A8\uC774 \uB05D\uB09C \uD6C4 \uD06C\uAE30\uAC00 90\uC778 \uD504\uB85C\uADF8\uB7A8 C\uAC00 A\uAC00 \uCC28\uC9C0 \uD588\uB358 \uC601\uC5ED\uC5D0 \uC801\uC7AC\uB418\uBA74 10\uB9CC\uD07C\uC758 \uBE48\uACF5\uAC04\uC774 \uC0DD\uAE30\uAC8C\uB41C\uB2E4. \uC774\uD6C4 \uD06C\uAE30\uAC00 40\uC778\uD504\uB85C\uADF8\uB7A8 D\uAC00 \uB4E4\uC5B4\uC628\uB2E4\uBA74 10\uC758 \uACF5\uAC04\uC740 D\uAC00 \uC0AC\uC6A9\uD558\uAE30\uC5D0 \uCDA9\uBD84\uD558\uC9C0 \uBABB\uD558\uAE30 \uB54C\uBB38\uC5D0 \uBE48\uACF5\uAC04 10\uC744 \uC0AC\uC6A9\uD558\uC9C0 \uBABB\uD55C\uB2E4 \uC774\uC640\uAC19\uC740 \uD06C\uAE30 10\uC758 \uB0AD\uBE44\uB418\uB294 \uACF5\uAC04\uC744 \uC678\uBD80\uC870\uAC01\uC774\uB77C\uD55C\uB2E4. \uC678\uBD80\uC870\uAC01 \uB610\uD55C \uB0AD\uBE44\uB41C \uBA54\uBAA8\uB9AC \uC790\uC6D0\uC774\uB2E4.</p><p><img src="'+t+'" alt="\uAC00\uBC88\uBD84\uD560" loading="lazy"></p><p><strong>3. \uAC00\uC0C1\uBA54\uBAA8\uB9AC \uBC29\uC2DD</strong><br> \uAC00\uC0C1 \uBA54\uBAA8\uB9AC \uAE30\uBC95\uC740 \uD604\uB300\uC5D0\uC11C \uAC00\uC7A5 \uB110\uB9AC \uC0AC\uC6A9\uB418\uB294 \uBA54\uBAA8\uB9AC \uAD00\uB9AC \uAE30\uBC95\uC774\uB2E4. <strong>\uC774 \uAE30\uBC95\uC740 \uBB3C\uB9AC\uC801 \uBA54\uBAA8\uB9AC \uBCF4\uB2E4 \uB354 \uD070 \uD504\uB85C\uADF8\uB7A8\uC774 \uC2E4\uD589 \uB418\uB294 \uAC83\uC744 \uC9C0\uC6D0 \uD55C\uB2E4.</strong> \uC774\uB54C \uC2E4\uD589 \uB420 \uC218 \uC788\uB294 \uD504\uB85C\uADF8\uB7A8\uC758 \uD06C\uAE30\uB294 \uAC00\uC0C1\uBA54\uBAA8\uB9AC\uC758 \uD06C\uAE30\uC5D0 \uC758\uD574 \uACB0\uC815\uB41C\uB2E4. \uBAA8\uB4E0 \uD504\uB85C\uADF8\uB7A8\uC740 \uBB3C\uB9AC\uC801 \uBA54\uBAA8\uB9AC\uC640\uB294 \uB3C5\uB9BD\uC801\uB85C 0\uBC88\uC9C0 \uBD80\uD130 \uC2DC\uC791\uD558\uB294 \uC790\uC2E0\uB9CC\uC758 \uAC00\uC0C1\uBA54\uBAA8\uB9AC \uC8FC\uC18C\uB97C \uAC16\uB294\uB2E4. \uC6B4\uC601\uCCB4\uC81C\uB294 \uC774 \uAC00\uC0C1\uBA54\uBAA8\uB9AC \uC8FC\uC18C\uB97C \uBB3C\uB9AC\uC801 \uBA54\uBAA8\uB9AC \uC8FC\uC18C\uB85C \uB9E4\uD551\uD558\uB294 \uAE30\uC220\uC744 \uC774\uC6A9\uD574 \uC8FC\uC18C\uB97C \uBCC0\uD658\uC2DC\uD0A8 \uD6C4 \uD504\uB85C\uADF8\uB7A8\uC744 \uBB3C\uB9AC\uC801 \uBA54\uBAA8\uB9AC\uC5D0 \uC62C\uB9AC\uAC8C \uB41C\uB2E4. \uAC00\uC0C1\uBA54\uBAA8\uB9AC \uAE30\uBC95\uC5D0\uC11C\uB294 \uBB3C\uB9AC\uC801\uBA54\uBAA8\uB9AC\uC758 \uD06C\uAE30\uC640 \uC0C1\uAD00\uC5C6\uC774 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uBA54\uBAA8\uB9AC \uD06C\uAE30\uAC00 \uCDA9\uBD84\uD788 \uD06C\uB2E4\uACE0 \uAC00\uC815\uD558\uACE0 \uD504\uB85C\uADF8\uB7A8\uC744 \uAC1C\uBC1C\uD560 \uC218 \uC788\uB2E4. <strong>\uC774\uAC83\uC744 \uAC00\uB2A5\uD558\uAC8C \uD558\uB294 \uC6D0\uB9AC\uB294 \uBA54\uBAA8\uB9AC\uAC00 1Gbyte\uACE0 \uD504\uB85C\uADF8\uB7A8\uC774 2Gbyte\uB77C \uD558\uB354\uB77C\uB3C4 \uC804\uCCB4\uAC00 \uD56D\uC0C1 \uB3D9\uC2DC\uC5D0 \uC0AC\uC6A9\uB418\uB294 \uAC83\uC740 \uC544\uB2C8\uAE30 \uB54C\uBB38\uC774\uB2E4.</strong> \uADF8\uB7EC\uBBC0\uB85C \uD604\uC7AC \uC0AC\uC6A9\uB418\uACE0 \uC788\uB294 \uBD80\uBD84\uB9CC \uBA54\uBAA8\uB9AC\uC5D0 \uC62C\uB9AC\uACE0 \uB098\uBA38\uC9C0\uB294 \uD558\uB4DC\uB514\uC2A4\uD06C\uC640 \uAC19\uC740 \uBCF4\uC870\uAE30\uC5B5\uC7A5\uCE58\uC5D0 \uC800\uC7A5\uD574\uB450\uC5C8\uB2E4\uAC00 \uD544\uC694\uD560 \uB54C \uC801\uC7AC\uD558\uB294 \uBC29\uC2DD\uC744 \uCDE8\uD55C\uB2E4. \uC774\uB54C \uC0AC\uC6A9\uB418\uB294 \uBCF4\uC870\uAE30\uC5B5\uC7A5\uCE58 \uC601\uC5ED\uC744 \uC2A4\uC651\uC601\uC5ED(swap area)\uC774\uB77C\uACE0 \uBD80\uB978\uB2E4, \uD504\uB85C\uADF8\uB7A8\uC744 \uAD6C\uC131\uD558\uB294 \uAC00\uC0C1\uBA54\uBAA8\uB9AC \uC8FC\uC18C\uACF5\uAC04\uC740 \uD398\uC774\uC9C0(page)\uB77C\uB294 \uB3D9\uC77C\uD55C \uD06C\uAE30\uC758 \uC791\uC740 \uB2E8\uC704\uB85C \uB098\uB258\uC5B4 \uBB3C\uB9AC\uC801 \uBA54\uBAA8\uB9AC\uC640 \uC2A4\uC651\uC601\uC5ED\uC5D0 \uC77C\uBD80\uBD84\uC529 \uC800\uC7A5\uB41C\uB2E4. \uC774\uB807\uAC8C \uB3D9\uC77C\uD55C \uB2E8\uC704\uB85C \uBA54\uBAA8\uB9AC\uB97C \uB098\uB204\uB294 \uAE30\uBC95\uC744 \uD398\uC774\uC9D5(pagging)\uAE30\uBC95\uC774\uB77C\uACE0 \uD55C\uB2E4.</p><h3 id="_3-\u110C\u116E\u1107\u1167\u11AB\u110C\u1161\u11BC\u110E\u1175-\u1106\u1175\u11BE-\u110B\u1175\u11B8\u110E\u116E\u11AF\u1105\u1167\u11A8-\u110C\u1161\u11BC\u110E\u1175" tabindex="-1"><a class="header-anchor" href="#_3-\u110C\u116E\u1107\u1167\u11AB\u110C\u1161\u11BC\u110E\u1175-\u1106\u1175\u11BE-\u110B\u1175\u11B8\u110E\u116E\u11AF\u1105\u1167\u11A8-\u110C\u1161\u11BC\u110E\u1175" aria-hidden="true">#</a> 3. \uC8FC\uBCC0\uC7A5\uCE58 \uBC0F \uC785\uCD9C\uB825 \uC7A5\uCE58</h3><p>CPU\uB294 \uC778\uD130\uB7FD\uD2B8(interrupt)\uB77C\uB294 \uBA54\uCEE4\uB2C8\uC998\uC744 \uD1B5\uD574 \uAD00\uB9AC\uAC00 \uC774\uB8E8\uC5B4\uC9C4\uB2E4. \uC8FC\uBCC0\uC7A5\uCE58\uB4E4\uC740 CPU\uC758 \uC11C\uBE44\uC2A4\uAC00 \uD544\uC694\uD55C \uACBD\uC6B0 \uC2E0\uD638\uB97C \uBC1C\uC0DD\uC2DC\uCF1C \uC11C\uBE44\uC2A4\uB97C \uC694\uCCAD \uD558\uB294\uB370 \uC774\uB54C \uBC1C\uC0DD\uD558\uB294 \uC2E0\uD638\uB97C \uC778\uD130\uB7FD\uD2B8 \uB77C\uACE0 \uD55C\uB2E4. CPU\uB294 \uD3C9\uC18C\uC5D0 CPU \uC2A4\uCF00\uC904\uB9C1\uC5D0 \uB530\uB77C \uC790\uAE30\uC5D0\uAC8C \uC8FC\uC5B4\uC9C4 \uC791\uC5C5\uC744 \uC218\uD589\uD558\uB2E4\uAC00 \uC778\uD130\uB7FD\uD2B8\uAC00 \uBC1C\uC0DD\uD558\uBA74 \uD558\uB358 \uC77C\uC744 \uC7A0\uC2DC \uBA48\uCD94\uACE0 \uC778\uD130\uB7FD\uD2B8\uC5D0 \uC758\uD55C \uC694\uCCAD \uC791\uC5C5\uC744 \uC218\uD589\uD55C\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uD0A4\uBCF4\uB4DC\uC5D0 \uC758\uD574 \uC778\uD130\uB7FD\uD2B8\uAC00 \uBC1C\uC0DD\uD558\uBA74 CPU\uB294 \uD558\uB358 \uC77C\uC744 \uBA48\uCD94\uACE0 \uD574\uB2F9 \uC785\uB825 \uB370\uC774\uD130\uB97C \uD574\uB2F9 \uD504\uB85C\uADF8\uB7A8\uC5D0 \uC804\uB2EC\uD558\uB294 \uC808\uCC28\uB97C \uC218\uD589\uD55C\uB2E4. \uC778\uD130\uB7FD\uD2B8\uC5D0 \uC758\uD55C \uC791\uC5C5\uC744 \uCC98\uB9AC\uD55C \uD6C4\uC5D0 \uD558\uB358 \uC77C\uC744 \uACC4\uC18D \uC218\uD589\uD55C\uB2E4. \uC774\uB54C \uC218\uD589\uD558\uB358 \uC791\uC5C5\uC73C\uB85C \uB3CC\uC544\uC624\uAE30 \uC704\uD574 \uC9C1\uC804\uC758 \uC791\uC5C5\uC0C1\uD0DC\uB97C \uC800\uC7A5\uD574\uB454\uB2E4. \uC778\uD130\uB7FD\uD2B8\uB294 \uC694\uCCAD\uD558\uB294 \uC7A5\uCE58\uC640 \uBC1C\uC0DD\uC0C1\uD669\uC5D0 \uB530\uB77C \uB2E4\uC591\uD55C \uC885\uB958\uAC00 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uC778\uD130\uB7FD\uD2B8 \uC885\uB958\uB9C8\uB2E4 \uC11C\uB85C \uB2E4\uB978 \uC778\uD130\uB7FD\uD2B8 \uCC98\uB9AC\uB8E8\uD2F4\uC744 \uAC00\uC9C0\uACE0 \uC788\uB2E4. \uC778\uD130\uB7FD\uD2B8 \uCC98\uB9AC\uB8E8\uD2F4\uC774\uB780 \uC778\uD130\uB7FD\uD2B8\uAC00 \uBC1C\uC0DD\uD588\uC744 \uB54C \uD574\uC8FC\uC5B4\uC57C \uD560 \uC791\uC5C5\uC744 \uC815\uC758\uD55C \uD504\uB85C\uADF8\uB7A8 \uCF54\uB4DC\uB97C \uB9D0\uD55C\uB2E4.</p><p>\uD55C\uD3B8 \uC8FC\uBCC0 \uC7A5\uCE58\uB4E4\uC740 \uAC01 \uC7A5\uCE58 \uB9C8\uB2E4 \uADF8 \uC7A5\uCE58\uC5D0\uC11C \uC77C\uC5B4\uB098\uB294 \uC5C5\uBB34\uB97C \uAD00\uB9AC\uD558\uAE30 \uC704\uD55C \uC77C\uC885\uC758 \uC791\uC740 CPU\uB97C \uAC00\uC9C0\uACE0 \uC788\uB2E4. \uC774\uB294 \uC6B0\uB9AC\uB294 \uCEE8\uD2B8\uB864\uB7EC(controller, \uC81C\uC5B4\uAE30)\uB77C\uACE0 \uBD80\uB978\uB2E4 \uCEE8\uD2B8\uB864\uB7EC\uB294 \uD574\uB2F9\uC7A5\uCE58\uC5D0 \uB300\uD55C \uC5C5\uBB34\uB97C \uCC98\uB9AC\uD558\uACE0 \uC774\uB97C \uBA54\uC778 cpu\uC5D0 \uC778\uD130\uB7FD\uD2B8\uB97C \uBC1C\uC0DD\uC2DC\uCF1C \uBCF4\uACE0\uD558\uB294 \uC5ED\uD560\uC744 \uD55C\uB2E4.</p><div class="custom-container info"><p class="custom-container-title">\uC778\uD130\uB7FD\uD2B8</p><p>CPU\uAC00 \uD504\uB85C\uADF8\uB7A8\uC744 \uAD6C\uB3D9\uD558\uACE0 \uC788\uC744 \uB54C \uC785\uCD9C\uB825 \uD639\uC740 \uC608\uC678 \uC0C1\uD669\uC774 \uBC1C\uC0DD\uD574\uC11C \uCC98\uB9AC\uAC00 \uD544\uC694\uD55C \uACBD\uC6B0\uC5D0 CPU\uC5D0 \uC2E0\uD638\uB97C \uBCF4\uB0B4\uB294 \uAC83</p><p>[\uC778\uD130\uB7FD\uD2B8 \uC218\uD589\uD750\uB984]</p><ol><li>\uAE30\uC874\uC5D0 \uAE30\uB3D9\uB418\uACE0 \uC788\uB358 \uC720\uC800 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC778\uD130\uB7FD\uD2B8\uAC00 \uBC1C\uC0DD(\uC778\uD130\uB7FD\uD2B8 \uC2E0\uD638 \uBC1C\uC0DD)</li><li>IRQ(Interrupt ReQuest)\uC778\uD130\uB7FD\uD2B8 \uB9AC\uD018\uC2A4\uD2B8\uAC00 \uB4E4\uC5B4\uC624\uBA74 CPU\uC5D0\uAC8C signal\uC744 \uBCF4\uB0B8\uB2E4.</li><li>signal\uC774 transfer\uC774 \uC644\uB8CC\uAC00 \uB418\uBA74 interrupt\uAC00 \uAC78\uB9B0\uB2E4.</li><li>CPU\uB294 \uC790\uC2E0\uC758 \uC77C(process)\uC744 \uC7A0\uC2DC \uBA48\uCD94\uACE0 PCB(Process Control Block)\uC5D0 \uC800\uC7A5\uD55C\uB2E4. (PCB- \uC778\uD130\uB7FD\uD2B8\uAC00 \uBC1C\uC0DD\uD588\uC744 \uB54C \uADF8 \uD504\uB85C\uADF8\uB7A8\uC758 \uC5B4\uB290 \uBD80\uBD84\uAE4C\uC9C0 \uC218\uD589\uD588\uB294\uC9C0\uB97C \uC800\uC7A5\uD558\uAE30 \uC704\uD55C \uC601\uC5ED)</li><li>CPU\uB294 interrupt service routine(interrupt handler)/\uC778\uD130\uB7FD\uD2B8 \uCC98\uB9AC\uB8E8\uD2F4\uC73C\uB85C \uAC00\uC11C interrupt processing\uCC98\uB9AC \uD55C\uB2E4.</li><li>CPU\uB294 interrupt service routine\uC5D0\uC11C \uC77C\uC744 \uCC98\uB9AC\uAC00 \uC885\uB8CC\uB418\uBA74, \uC6D0\uB798 process \uC704\uCE58(\uC8FC\uC18C)\uB85C \uB3CC\uC544\uAC00 \uC6D0\uB798 \uD558\uB358 \uC77C\uC744 \uD55C\uB2E4.</li></ol><p>[\uC608\uC2DC] \uB300\uB7B5\uC801\uC778 \uD0A4\uBCF4\uB4DC \uC778\uD130\uB7FD\uD2B8 \uCC98\uB9AC\uC21C\uC11C \uD0A4\uBCF4\uB4DC \uC785\uB825 -&gt; \uD0A4\uBCF4\uB4DC \uCEE8\uD2B8\uB864\uB7EC\uAC00 \uC778\uD130\uB7FD\uD2B8 \uBC1C\uC0DD -&gt; CPU\uD604\uC7AC \uC218\uD589\uC911\uC774\uB358 \uC791\uC5C5 \uC800\uC7A5 -&gt; \uC778\uD130\uB7FD\uD2B8 \uC694\uCCAD\uCC98\uB9AC\uC704\uD574 \uC6B4\uC601\uCCB4\uC81C\uB0B4\uC5D0 \uD0A4\uBCF4\uB4DC \uC778\uD130\uB7FD\uD2B8\uCC98\uB9AC\uB8E8\uD2F4 \uCC3E\uC544\uAC10 -&gt; \uC778\uD130\uB7FD\uD2B8\uCC98\uB9AC\uB8E8\uD2F4\uC774 \uD0A4\uBCF4\uB4DC\uB85C\uBD80\uD130 \uC785\uB825\uBC1B\uC740 \uB0B4\uC6A9\uC744 \uBA54\uBAA8\uB9AC \uD2B9\uC815\uBD80\uBD84\uC5D0 \uC800\uC7A5\uD5E4 \uD574\uB2F9\uD504\uB85C\uADF8\uB7A8\uC5D0\uAC8C \uD0A4\uBCF4\uB4DC \uC785\uB825\uC774 \uB4E4\uC5B4\uC654\uC74C\uC744 \uC54C\uB9BC-&gt; \uC778\uD130\uB7FD\uD2B8 \uCC98\uB9AC\uC644\uB8CC -&gt; \uC778\uD130\uB7FD\uD2B8 \uBC1C\uC0DD\uC9C1\uC804\uC0C1\uD0DC \uBCF5\uAD6C</p><p>\uC774\uC640 \uAC19\uC740 \uBC29\uC2DD\uC73C\uB85C \uC8FC\uBCC0\uC7A5\uCE58\uB4E4\uC740 CPU \uC11C\uBE44\uC2A4 \uBC1B\uC744\uC218\uC788\uC74C</p></div><div class="custom-container info"><p class="custom-container-title">\uCEF4\uD4E8\uD130 \uC2DC\uC2A4\uD15C \uC6B4\uC601, \uB3D9\uC791</p><ul><li>\uBA54\uBAA8\uB9AC \uAD00\uB9AC\uB8E8\uD2F4, CPU\uC2A4\uCF00\uC904\uB9C1, \uC778\uD130\uB7FD\uD2B8 \uCC98\uB9AC\uB8E8\uD2F4 \uB4F1\uC758 \uCF54\uB4DC\uB294 \uC6B4\uC601\uCCB4\uC81C \uCEE4\uB110\uB0B4\uC5D0 \uC874\uC7AC\uD558\uB294 \uCF54\uB4DC\uC774\uB2E4.</li><li>\uBA54\uBAA8\uB9AC\uB97C \uB450\uACE0 \uACBD\uC7C1\uD558\uB294 \uC785\uCD9C\uB825 \uC7A5\uCE58, CPU\uB294 \uB3D9\uC2DC \uC2E4\uD589 \uAC00\uB2A5</li><li>\uD558\uB098 \uC774\uC0C1\uC758 CPU, \uC7A5\uCE58 \uCEE8\uD2B8\uB864\uB7EC\uB294 \uACF5\uC720\uB41C \uBA54\uBAA8\uB9AC\uC5D0 \uB300\uD55C \uC811\uADFC\uC744 \uC81C\uACF5\uD558\uB294 \uACF5\uC6A9 \uBC84\uC2A4\uB97C \uD1B5\uD574 \uC5F0\uACB0\uB428</li><li>\uAC01 \uC7A5\uCE58 \uCEE8\uD2B8\uB864\uB7EC\uB294 \uD2B9\uC815 \uB514\uBC14\uC774\uC2A4 \uC720\uD615\uC744 \uB2F4\uB2F9. \uB85C\uCEEC \uBC84\uD37C \uC874\uC7AC</li><li>CPU\uB294 \uBA54\uC778 \uBA54\uBAA8\uB9AC - \uB85C\uCEEC \uBC84\uD37C\uAC04 \uB370\uC774\uD130 \uC774\uB3D9</li><li>\uC785\uCD9C\uB825\uC740 \uC7A5\uCE58\uC5D0\uC11C \uCEE8\uD2B8\uB864\uB7EC\uC758 \uB85C\uCEEC \uBC84\uD37C\uB85C \uC804\uB2EC</li><li>\uC7A5\uCE58 \uCEE8\uD2B8\uB864\uB7EC\uB294 \uC778\uD130\uB7FD\uD2B8\uB97C \uBC1C\uC0DD\uC2DC\uCF1C CPU\uC5D0 \uC791\uC5C5 \uC644\uB8CC\uB97C \uC54C\uB9BC</li></ul></div>',24),a=[e];function p(c,g){return n(),s("div",null,a)}var h=i(o,[["render",p],["__file","ch3-\uC6B4\uC601\uCCB4\uC81C-\uC5ED\uD560\uACFC-\uAE30\uB2A5.html.vue"]]);export{h as default};
