import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as e,d as l}from"./app.d6b42b88.js";var r="/blog/assets/05-replication-partition.76bca98f.png";const p={},d=l('<h2 id="\u1107\u1169\u11A8\u110C\u1166\u110B\u116A-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1107\u1169\u11A8\u110C\u1166\u110B\u116A-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" aria-hidden="true">#</a> \uBCF5\uC81C\uC640 \uD30C\uD2F0\uC154\uB2DD</h2><p>\uC5EC\uB7EC \uB178\uB4DC\uC5D0 \uB370\uC774\uD130\uB97C \uBD84\uC0B0\uD558\uB294 \uBC29\uBC95\uC740 \uC77C\uBC18\uC801\uC73C\uB85C \uB450 \uAC00\uC9C0\uAC00 \uC788\uB2E4.</p><ul><li>\uBCF5\uC81C\uB780? \uBD84\uC0B0\uB41C \uB370\uC774\uD130 \uBCA0\uC774\uC2A4 \uAD00\uC810\uC5D0\uC11C \uBCF5\uC81C\uB780 \uB124\uD2B8\uC6CC\uD06C\uB85C \uC5F0\uACB0\uB41C \uC5EC\uB7EC \uB178\uB4DC\uC5D0 \uB3D9\uC77C\uD55C \uB370\uC774\uD130\uC758 \uBCF5\uC0AC\uBCF8\uC744 \uC720\uC9C0\uD55C\uB2E4\uB294 \uC758\uBBF8\uC774\uB2E4.</li><li>\uD30C\uD2F0\uC154\uB2DD(\uC0E4\uB529)\uC774\uB780? \uB370\uC774\uD130 \uC14B\uC774 \uB2E8\uC77C \uC7A5\uBE44\uC5D0 \uBCF4\uAD00\uD558\uAE30\uC5D0 \uD074 \uACBD\uC6B0 \uB370\uC774\uD130\uB97C \uC791\uC740 \uC11C\uBE0C\uC14B\uC73C\uB85C \uB098\uB204\uACE0 \uAC01 \uD30C\uD2F0\uC158\uC740 \uAC01\uAE30 \uB2E4\uB978 \uB178\uB4DC\uC5D0 \uD560\uB2F9\uD55C\uB2E4\uB294 \uC758\uBBF8 \uC778\uB2E4.</li></ul><p>\uBCF5\uC81C\uC640 \uD30C\uD2F0\uC154\uB2DD\uC740 \uBCC4\uB3C4\uC758 \uBA54\uCEE4\uB2C8\uC998\uC774\uC9C0\uB9CC \uBCF4\uD1B5 \uC544\uB798\uCC98\uB7FC \uAD00\uB828\uC774 \uC788\uB2E4.<br><img src="'+r+'" alt="replication-partition" loading="lazy"></p><p>\uC774\uBC88\uC7A5\uC5D0\uC11C\uB294 \uBCF5\uC81C \uBA54\uCEE4\uB2C8\uC998\uC5D0 \uB300\uD574 \uC0B4\uD3B4\uBCF4\uACA0\uB2E4. \uC544\uB798 \uB0B4\uC6A9\uB4E4\uC740 \uB370\uC774\uD130 \uC14B\uC774 \uC791\uC544\uC11C \uAC01 \uC7A5\uBE44\uC5D0 \uC804\uCCB4 \uB370\uC774\uD130 \uC14B\uC758 \uBCF5\uC0AC\uBCF8\uC744 \uBCF4\uC720\uD560 \uC218 \uC788\uB2E4\uB294 \uB0B4\uC6A9\uC744 \uAC00\uC815\uD558\uACE0 \uC791\uC131\uB418\uC5C8\uB2E4.</p><h2 id="\u1107\u1169\u11A8\u110C\u1166" tabindex="-1"><a class="header-anchor" href="#\u1107\u1169\u11A8\u110C\u1166" aria-hidden="true">#</a> \uBCF5\uC81C</h2><p>\uBCF5\uC81C\uB85C \uC778\uD574\uC11C \uC5BB\uC744 \uC218 \uC788\uB294 \uC774\uC810\uC740 \uB2E4\uC74C\uACFC \uAC19\uB2E4.</p><ul><li>reduce latency: \uC9C0\uB9AC\uC801\uC73C\uB85C \uC0AC\uC6A9\uC790\uC640 \uAC00\uAE5D\uAC8C \uB370\uC774\uD130\uB97C \uC720\uC9C0\uD574 \uC9C0\uC5F0\uC2DC\uAC04\uC744 \uC904\uC778\uB2E4.</li><li>increase availability: \uC2DC\uC2A4\uD15C \uC77C\uBD80\uC5D0 \uC7A5\uC560 \uBC1C\uC0DD\uD558\uB354\uB77C\uB3C4 \uC9C0\uC18D\uC801\uC73C\uB85C \uB3D9\uC791\uD560 \uC218 \uC788\uC73C\uBBC0\uB85C \uAC00\uC6A9\uC131\uC744 \uB192\uC778\uB2E4.</li><li>increase read throughput: \uC77D\uAE30 \uC9C8\uC758\uB97C \uC81C\uACF5\uD558\uB294 \uC7A5\uBE44\uC758 \uC218\uB97C \uD655\uC7A5\uD574 \uC77D\uAE30 \uCC98\uB9AC\uB7C9\uC744 \uB298\uB9B0\uB2E4.</li></ul><p>\uC774\uBC88\uC7A5\uC5D0\uC11C <strong>\uAF2D \uC774\uD574\uD558\uACE0 \uB118\uC5B4\uAC08 \uBD80\uBD84\uC740 1) \uB9AC\uB354\uC640 \uD314\uB85C\uC6CC \uBC29\uC2DD\uC758 \uB300\uD55C \uC774\uD574 2) \uCD5C\uC885\uC801 \uC77C\uAD00\uC131\uC5D0 \uB300\uD55C \uC774\uD574 3) \uBCF5\uC81C\uC758 3\uAC00\uC9C0 \uC811\uADFC\uBC29\uC2DD\uC5D0 \uB300\uD55C \uC774\uD574\uB77C\uACE0 \uC0DD\uAC01\uD55C\uB2E4.</strong></p><h2 id="\u1105\u1175\u1103\u1165\u110B\u116A-\u1111\u1161\u11AF\u1105\u1169\u110B\u116F" tabindex="-1"><a class="header-anchor" href="#\u1105\u1175\u1103\u1165\u110B\u116A-\u1111\u1161\u11AF\u1105\u1169\u110B\u116F" aria-hidden="true">#</a> \uB9AC\uB354\uC640 \uD314\uB85C\uC6CC</h2><p>\uB2E4\uC911 \uBCF5\uC81C \uC11C\uBC84\uB97C \uC0AC\uC6A9\uD558\uBA74 \uBAA8\uB4E0 \uBCF5\uC81C \uC11C\uBC84\uC5D0 \uBAA8\uB4E0 \uB370\uC774\uD130\uAC00 \uC788\uB2E4\uB294 \uC0AC\uC2E4\uC744 \uC5B4\uB5BB\uAC8C \uBCF4\uC7A5\uD560 \uC218 \uC788\uC744\uAE4C? \uBAA8\uB4E0 \uC4F0\uAE30\uB294 \uBAA8\uB4E0 \uBCF5\uC81C \uC11C\uBC84\uC5D0\uC11C \uCC98\uB9AC\uB418\uC5B4\uC57C \uD55C\uB2E4. \uADF8\uB807\uC9C0 \uC54A\uC73C\uBA74 \uBCF5\uC81C \uC11C\uBC84\uB294 \uB354 \uC774\uC0C1 \uB3D9\uC77C\uD55C \uB370\uC774\uD130\uB97C \uC720\uC9C0\uD558\uB294 \uAC8C \uC544\uB2C8\uB2E4. \uC774 \uBB38\uC81C\uB97C \uC704\uD55C \uAC00\uC7A5 \uC77C\uBC18\uC801\uC778 \uD574\uACB0\uCC45\uC774 \uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C\uC774\uB2E4(reader-based replication)</p><p>\uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C\uB780 \uD074\uB77C\uC774\uC5B8\uD2B8\uC758 \uC77D\uAE30 \uC694\uCCAD\uC740 \uBAA8\uB4E0 \uB370\uC774\uD130 \uB370\uC774\uC2A4\uAC00 \uBC1B\uC744 \uC218 \uC788\uC73C\uB098 \uC4F0\uAE30 \uC694\uCCAD\uC740 \uB9AC\uB354\uC5D0\uC11C\uB9CC \uBC1B\uC544\uC11C \uCC98\uB9AC\uD558\uB294 \uBC29\uBC95\uC774\uB2E4. \uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C\uC758 \uC4F0\uAE30 \uB3D9\uC791\uC740 \uB2E4\uC74C \uC21C\uC11C\uB85C \uC9C4\uD589\uB41C\uB2E4.</p><ol><li>\uBCF5\uC81C \uC11C\uBC84 \uC911 \uD558\uB098\uB97C \uB9AC\uB354\uB85C \uC9C0\uC815\uD55C\uB2E4. \uB2E4\uB978 \uBCF5\uC81C \uC11C\uBC84\uB294 \uD314\uB85C\uC6CC\uB77C\uACE0 \uD55C\uB2E4.</li><li>\uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uAC8C \uC4F0\uAE30 \uC694\uCCAD\uC774 \uC624\uBA74 \uB9AC\uB354\uC5D0\uAC8C \uBCF4\uB0B4\uACE0, \uB9AC\uB354\uAC00 \uBA3C\uC800 \uB85C\uCEEC \uC800\uC7A5\uC18C\uC5D0 \uC0C8\uB85C\uC6B4 \uB370\uC774\uD130\uB97C \uC800\uC7A5\uD55C\uB2E4.</li><li>\uB9AC\uB354\uAC00 \uC0C8\uB85C\uC6B4 \uB370\uC774\uD130\uB97C \uAE30\uB85D\uD560 \uB54C\uB9C8\uB2E4 \uB370\uC774\uD130 \uBCC0\uACBD\uC744 \uBCF5\uC81C \uB85C\uADF8\uB098 \uBCC0\uACBD \uC2A4\uD2B8\uB9BC\uC758 \uC77C\uBD80\uB85C \uD314\uB85C\uC6CC\uC5D0\uAC8C \uC804\uC1A1\uD55C\uB2E4.</li><li>\uD314\uB85C\uC6CC\uAC00 \uB9AC\uB354\uB85C\uBD80\uD130 \uB85C\uADF8\uB97C \uBC1B\uC544 \uB9AC\uB354\uC758 \uCC98\uB9AC \uC21C\uC11C\uC640 \uB3D9\uC77C\uD558\uAC8C \uC4F0\uAE30 \uC791\uC5C5\uC744 \uC801\uC6A9\uD55C\uB2E4.</li></ol><p>\uD074\uB77C\uC774\uC5B8\uD2B8\uC758 <strong>\uC77D\uAE30 \uC694\uCCAD\uC740 \uB9AC\uB354 \uB610\uB294 \uC784\uC758 \uD314\uB85C\uC6CC\uC5D0\uAC8C \uC9C8\uC758\uD560 \uC218 \uC788\uB2E4. \uADF8\uB7EC\uB098 \uC4F0\uAE30\uB294 \uB9AC\uB354\uC5D0\uAC8C\uB9CC \uC801\uC6A9\uB41C\uB2E4.</strong> \uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uAC8C \uD314\uB85C\uC6CC \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB294 \uC77D\uAE30 \uC804\uC6A9\uC774\uB2E4.</p><div class="custom-container info"><p class="custom-container-title">\uB2E8\uC5B4 \uC124\uBA85</p><ul><li>\uB9AC\uB354 \uD314\uB85C\uC6CC \uBCF5\uC81C\uB294 \uB9C8\uC2A4\uD130-\uC2AC\uB808\uC774\uBE0C\uB77C\uB294 \uC6A9\uC5B4\uB97C \uC0AC\uC6A9\uD558\uAE30\uB3C4 \uD558\uB294\uB370. \uBCF4\uD1B5 &#39;\uC6D0\uBCF8&#39; &#39;\uC0AC\uBCF8&#39;\uC758 \uAC1C\uB150\uC73C\uB85C \uC0AC\uC6A9\uB418\uB294 \uB9D0\uC774\uC5C8\uC73C\uB098 \uB178\uC608\uC81C\uB3C4\uB97C \uC5F0\uC0C1\uB41C\uB2E4\uB294 \uC9C0\uC801\uC774 \uB9CE\uC73C\uBBC0\uB85C \uC0AC\uC6A9\uC744 \uC790\uC81C\uD558\uB294 \uCD94\uC138\uC774\uB2E4. \uAE43 \uD5D9 \uBE0C\uB79C\uCE58 \uC774\uB984\uC774 master -&gt; main\uC73C\uB85C \uBC14\uB010 \uC774\uC720\uC640 \uAC19\uC740 \uC774\uC720\uC774\uB2E4.</li><li>\uB9AC\uB354(\uB9C8\uC2A4\uD130/\uD504\uB77C\uC774\uBA38\uB9AC \uB4F1 \uC0AC\uC6A9) , \uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C(\uB2A5\uB3D9/\uC218\uB3D9, \uB9C8\uC2A4\uD130/\uC2AC\uB808\uC774\uBE0C \uB4F1 \uC0AC\uC6A9)</li><li>\uBCF5\uC81C \uC11C\uBC84(replica) : \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uBCF5\uC0AC\uBCF8\uC744 \uC800\uC7A5\uD558\uB294 \uAC01 \uB178\uB4DC</li></ul></div><p>\uB9AC\uB354\uC640 \uD314\uB85C\uC6CC\uC758 \uC138\uBD80 \uB3D9\uC791 \uBC29\uC2DD\uC5D0 \uB300\uD574 \uC0B4\uD3B4\uBCF4\uC790.</p><h3 id="_1-\u1103\u1169\u11BC\u1100\u1175\u1109\u1175\u11A8-\u1103\u1162-\u1107\u1175\u1103\u1169\u11BC\u1100\u1175\u1109\u1175\u11A8-\u1106\u116E\u11AB\u110C\u1166" tabindex="-1"><a class="header-anchor" href="#_1-\u1103\u1169\u11BC\u1100\u1175\u1109\u1175\u11A8-\u1103\u1162-\u1107\u1175\u1103\u1169\u11BC\u1100\u1175\u1109\u1175\u11A8-\u1106\u116E\u11AB\u110C\u1166" aria-hidden="true">#</a> 1. \uB3D9\uAE30\uC2DD \uB300 \uBE44\uB3D9\uAE30\uC2DD \uBB38\uC81C</h3><p>\uBCF5\uC81C \uC2DC\uC2A4\uD15C\uC758 \uC911\uC694\uD55C \uC138\uBD80 \uC0AC\uD56D\uC740 \uBCF5\uC81C\uAC00 \uB3D9\uAE30\uC2DD\uC73C\uB85C \uBC1C\uC0DD\uD558\uB294\uC9C0 \uBE44\uB3D9\uAE30\uC2DD\uC73C\uB85C \uBC1C\uC0DD\uD558\uB294\uC9C0 \uC5EC\uBD80\uC774\uB2E4.</p><p>\uC5EC\uAE30\uC11C \uB3D9\uAE30 \uBE44\uB3D9\uAE30\uB294 \uC758\uBBF8\uB294 \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uB9AC\uB354\uC5D0\uAC8C \uC4F0\uAE30 \uC694\uCCAD\uC744 \uBCF4\uB0B8 \uD6C4 \uB9AC\uB354\uAC00 \uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uAC8C \uC791\uC5C5\uC758 \uACB0\uACFC\uB97C \uC751\uB2F5\uD558\uB294 \uC2DC\uC810\uACFC \uAD00\uB828\uC774 \uC788\uB2E4. \uB9AC\uB354\uB294 \uC4F0\uAE30 \uC694\uCCAD\uC744 \uBC1B\uC73C\uBA74 \uD314\uB85C\uC6CC\uC5D0\uAC8C \uBCC0\uACBD\uC744 \uC54C\uB9B0\uB2E4. \uB3D9\uAE30\uC2DD \uBCF5\uC81C\uB294 \uB9AC\uB354\uAC00 \uD314\uB85C\uC6CC\uAC00 \uC4F0\uAE30\uB97C \uC218\uC2E0\uD588\uB294\uC9C0 \uD655\uC778\uD55C \uD6C4 \uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uAC8C \uC751\uB2F5\uD558\uB294 \uBC29\uC2DD\uC774\uACE0 \uBE44\uB3D9\uAE30\uC2DD \uBCF5\uC81C\uB294 \uD314\uB85C\uC6D0\uC758 OK\uBA54\uC2DC\uC9C0\uB97C \uAE30\uB2E4\uB9AC\uC9C0 \uC54A\uACE0 \uBC14\uB85C \uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uAC8C \uC751\uB2F5\uD558\uB294 \uBC29\uC2DD\uC774\uB2E4.</p><p>\uB3D9\uAE30\uC2DD \uBCF5\uC81C\uC758 \uC7A5\uC810\uC740 \uD314\uB85C\uC6CC\uC640 \uB9AC\uB354\uAC00 \uC77C\uAD00\uC131 \uC788\uAC8C \uCD5C\uC2E0 \uB370\uC774\uD130 \uBCF5\uC0AC\uBCF8\uC744 \uAC00\uC9C0\uB294 \uAC83\uC744 \uBCF4\uC7A5\uD558\uB294 \uAC83\uC774\uB2E4. \uADF8\uB7EC\uB098 \uB2E8\uC810\uC740 \uB3D9\uAE30 \uD314\uB85C\uC6CC\uAC00 \uC751\uB2F5\uD558\uC9C0 \uC54A\uB294\uB2E4\uBA74 \uC4F0\uAE30 \uC694\uCCAD\uC774 \uCC98\uB9AC\uB418\uC9C0 \uC54A\uB294\uB2E4. \uB3D9\uAE30 \uBCF5\uC81C \uC11C\uBC84\uAC00 \uB2E4\uC2DC \uC815\uC0C1\uD654\uB420 \uB54C\uAE4C\uC9C0 \uAE30\uB2E4\uB824\uC57C \uD55C\uB2E4. \uADF8\uB807\uAE30\uC5D0 \uBAA8\uB4E0 \uD314\uB85C\uC6CC\uAC00 \uB3D9\uAE30\uC2DD\uC778 \uC0C1\uD669\uC740 \uBE44\uD604\uC2E4\uC801\uC774\uBA70 \uBCF4\uD1B5 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C \uB3D9\uAE30\uC2DD \uBCF5\uC81C\uB97C \uC0AC\uC6A9\uD560 \uB54C\uB294 \uD314\uB85C\uC6CC \uD558\uB098\uB294 \uB3D9\uAE30\uC2DD\uC73C\uB85C \uD558\uACE0 \uADF8\uBC16\uC5D0\uB294 \uBE44\uB3D9\uAE30\uC2DD\uC73C\uB85C \uD558\uB294 \uAC83\uC744 \uC758\uBBF8\uD55C\uB2E4. \uC774\uAC83\uC740 \uC801\uC5B4\uB3C4 \uB450 \uB178\uB4DC\uC5D0 \uB370\uC774\uD130 \uCD5C\uC2E0 \uBCF5\uC0AC\uBCF8\uC774 \uC788\uB2E4\uB294 \uAC83\uC744 \uBCF4\uC7A5\uD558\uBA70 \uC774\uB7F0 \uC124\uC815\uC744 \uBC18\uB3D9\uAE30\uC2DD\uC774\uB77C\uACE0 \uD55C\uB2E4.</p><p>\uB3D9\uAE30\uC2DD\uC758 \uC7A5\uC810\uC744 \uC124\uBA85\uD588\uC9C0\uB9CC \uBCF4\uD1B5 \uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C\uB294 \uC644\uC804\uD788 \uBE44\uB3D9\uAE30\uC2DD\uC73C\uB85C \uAD6C\uC131\uD55C\uB2E4. \uC774 \uACBD\uC6B0 \uB9AC\uB354\uAC00 \uC798\uBABB\uB418\uACE0 \uBCF5\uAD6C\uD560 \uC218 \uC5C6\uC744 \uB54C \uC544\uC9C1 \uD314\uB85C\uC6CC\uC5D0 \uBCF5\uC81C\uB418\uC9C0 \uC54A\uC740 \uBAA8\uB4E0 \uC4F0\uAE30\uB294 \uC720\uC2E4\uB41C\uB2E4. \uADF8\uB7EC\uB098 \uC644\uC804 \uBE44\uB3D9\uAE30\uC2DD \uC124\uC815\uC740 \uBAA8\uB4E0 \uD314\uB85C\uC6CC\uAC00 \uC798\uBABB\uB418\uB354\uB77C\uB3C4 \uB9AC\uB354\uAC00 \uC4F0\uAE30 \uCC98\uB9AC\uB97C \uACC4\uC18D\uD560 \uC218 \uC788\uB2E4\uB294 \uC7A5\uC810\uC774 \uC788\uB2E4.</p><p>\uC644\uC804 \uBE44\uB3D9\uAE30\uC2DD \uBCF5\uC81C\uB294 \uB0B4\uAD6C\uC131\uC744 \uC57D\uD654\uC2DC\uD0A4\uAE30 \uB54C\uBB38\uC5D0 \uB098\uC05C \uD2B8\uB808\uC774\uB4DC \uC624\uD504\uB77C \uC0DD\uAC01\uD560 \uC218 \uC788\uC9C0\uB9CC \uB9CE\uC740 \uD314\uB85C\uC6CC\uAC00 \uC788\uAC70\uB098 \uC9C0\uB9AC\uC801\uC73C\uB85C \uBD84\uC0B0\uB418\uC5B4 \uC788\uB2E4\uBA74 \uBE44\uB3D9\uAE30\uC2DD \uBCF5\uC81C\uB97C \uB110\uB9AC \uC0AC\uC6A9\uD558\uACE0 \uC788\uB2E4. \uC774\uAC83\uC740 \uBCF5\uC81C \uC9C0\uC5F0 \uBB38\uC81C\uC640 \uAD00\uB828\uB418\uC5B4 \uC788\uB294\uB370 \uB4B7\uBD80\uBD84\uC5D0\uC11C \uC790\uC138\uD788 \uC124\uBA85\uD55C\uB2E4.</p><h3 id="_2-\u1109\u1162\u1105\u1169\u110B\u116E\u11AB-\u1111\u1161\u11AF\u1105\u1169\u110B\u116F-\u1109\u1165\u11AF\u110C\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#_2-\u1109\u1162\u1105\u1169\u110B\u116E\u11AB-\u1111\u1161\u11AF\u1105\u1169\u110B\u116F-\u1109\u1165\u11AF\u110C\u1165\u11BC" aria-hidden="true">#</a> 2. \uC0C8\uB85C\uC6B4 \uD314\uB85C\uC6CC \uC124\uC815</h3><p>\uBCF5\uC81C \uC11C\uBC84 \uC218\uB97C \uB298\uB9AC\uAC70\uB098 \uC7A5\uC560 \uB178\uB4DC\uC758 \uB300\uCCB4\uB97C \uC704\uD574 \uC0C8\uB85C\uC6B4 \uD314\uB85C\uC6CC\uB97C \uC124\uC815\uD574\uC57C \uD560 \uB54C\uAC00 \uC788\uB2E4. \uC0C8\uB85C\uC6B4 \uD314\uB85C\uC6CC\uAC00 \uB9AC\uB354\uC758 \uB370\uC774\uD130 \uBCF5\uC81C\uBCF8\uC744 \uC815\uD655\uD788 \uAC00\uC9C0\uACE0 \uC788\uB294\uC9C0\uB294 \uC5B4\uB5BB\uAC8C \uBCF4\uC7A5\uD560 \uC218 \uC788\uC744\uAE4C?</p><p>\uB370\uC774\uD130\uB294 \uD56D\uC0C1 \uC720\uB3D9\uC801\uC774\uAE30 \uB54C\uBB38\uC5D0 \uB2E8\uC21C\uD55C \uB178\uB4DC\uC5D0\uC11C \uB178\uB4DC\uB85C\uC758 \uD30C\uC77C \uBCF5\uC0AC\uB294 \uACB0\uACFC\uAC00 \uC720\uD6A8\uD558\uC9C0 \uC54A\uC744 \uC218 \uC788\uB2E4. \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB97C \uC7A0\uADF8\uACE0(lock\uC744 \uAC78\uACE0) \uB178\uB4DC\uB97C \uBCF5\uC0AC\uD558\uBA74 \uC27D\uAC8C \uD574\uACB0\uD560 \uC218 \uC788\uC744\uC9C0\uB3C4 \uBAA8\uB974\uB098. \uACE0\uAC00\uC6A9\uC131\uC774\uB77C\uB294 \uBAA9\uD45C\uC5D0\uB294 \uBD80\uD569\uD558\uC9C0 \uC54A\uB294\uB2E4.</p><p>\uC911\uB2E8\uC2DC\uAC04 \uC5C6\uC774 \uC0C8\uB85C\uC6B4 \uD314\uB85C\uC6CC \uC124\uC815\uD558\uB294 \uAC1C\uB150\uC801\uC778 \uBC29\uBC95\uC740 \uB2E4\uC74C\uACFC \uAC19\uB2E4.</p><ol><li>\uB9AC\uB354\uC758 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uC2A4\uB0C5\uC20F\uC744 \uC77C\uC815 \uC2DC\uC810\uC5D0 \uAC00\uC838\uC628\uB2E4.</li><li>\uC2A4\uB0C5\uC20F\uC744 \uC0C8\uB85C\uC6B4 \uD314\uB85C\uC6CC \uB178\uB4DC\uC5D0 \uBCF5\uC0AC\uD55C\uB2E4.</li><li>\uD314\uB85C\uC6CC\uB294 \uB9AC\uB354\uC5D0 \uC5F0\uACB0\uD574 \uC2A4\uB0C5\uC20F \uC774\uD6C4 \uBC1C\uC0DD\uD55C \uBAA8\uB4E0 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4 \uBCC0\uACBD\uC744 \uC694\uCCAD\uD55C\uB2E4.</li><li>\uD314\uB85C\uC6CC\uAC00 \uC2A4\uB0C5\uC20F \uC774\uD6C4\uC758 \uB370\uC774\uD130 \uBBF8\uCC98\uB9AC\uBD84(backlog)\uB97C \uBAA8\uB450 \uCC98\uB9AC\uD588\uC744 \uB54C \uB530\uB77C\uC7A1\uC558\uB2E4\uACE0 \uB9D0\uD558\uACE0, \uC774\uD6C4\uBD80\uD130 \uB9AC\uB354\uC5D0\uAC8C \uBC1C\uC0DD\uD558\uB294 \uB370\uC774\uD130 \uBCC0\uD654\uB97C \uC774\uC5B4\uC11C \uCC98\uB9AC\uD560 \uC218 \uC788\uB2E4.</li></ol><p>\uBB3C\uB860 \uC2E4\uC81C \uACFC\uC815\uC740 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB9C8\uB2E4 \uD06C\uAC8C \uB2E4\uB974\uB2E4.</p><h3 id="_3-\u1102\u1169\u1103\u1173-\u110C\u116E\u11BC\u1103\u1161\u11AB-\u110E\u1165\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#_3-\u1102\u1169\u1103\u1173-\u110C\u116E\u11BC\u1103\u1161\u11AB-\u110E\u1165\u1105\u1175" aria-hidden="true">#</a> 3. \uB178\uB4DC \uC911\uB2E8 \uCC98\uB9AC</h3><p>\uC2DC\uC2A4\uD15C\uC758 \uBAA8\uB4E0 \uB178\uB4DC\uB294 \uC7A5\uC560\uB85C \uD639\uC740 \uACC4\uD68D\uB41C \uC720\uC9C0\uBCF4\uC218\uB85C \uC778\uD574 \uC911\uB2E8\uB420 \uC218 \uC788\uB2E4. \uAC1C\uBCC4 \uB178\uB4DC\uC758 \uC7A5\uC560\uC5D0\uB3C4 \uC804\uCCB4 \uC2DC\uC2A4\uD15C\uC774 \uC815\uC0C1 \uC791\uB3D9\uD558\uACE0 \uB178\uB4DC \uC911\uB2E8\uC758 \uC601\uD5A5\uC744 \uCD5C\uC18C\uD654\uD560 \uC218 \uC788\uC5B4\uC57C \uD55C\uB2E4. \uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C\uC5D0\uC11C\uB294 \uACE0\uAC00\uC6A9\uC131\uC740 \uC5B4\uB5BB\uAC8C \uB2EC\uC131\uD560 \uC218 \uC788\uC744\uAE4C?</p><p>\uC7A5\uC560\uB294 \uB450 \uAC00\uC9C0\uB85C \uB098\uB204\uC5B4\uC9C8 \uC218 \uC788\uB2E4.</p><ul><li><p>\uD314\uB85C\uC6CC \uC7A5\uC560 : \uB530\uB77C\uC7A1\uAE30 \uBCF5\uAD6C<br> \uAC01 \uD314\uB85C\uC6CC\uB294 \uB9AC\uB354\uB85C\uBD80\uD130 \uC218\uC2E0\uD55C \uB370\uC774\uD130 \uBCC0\uACBD \uB85C\uADF8\uB97C \uB85C\uCEEC \uB514\uC2A4\uD06C\uC5D0 \uBCF4\uAD00\uD55C\uB2E4. \uD314\uB85C\uC6CC \uC7A5\uC560 \uBCF5\uAD6C\uB294 \uAC04\uB2E8\uD558\uB2E4. \uC790\uC2E0\uC5D0\uAC8C \uACB0\uD568\uC774 \uBC1C\uC0DD\uD558\uAE30 \uC804\uC5D0 \uCC98\uB9AC\uD55C \uB9C8\uC9C0\uB9C9 \uD2B8\uB79C\uC7AD\uC158\uC744 \uC54C\uC544\uB0B8 \uD6C4 \uD314\uB85C\uC6CC\uB294 \uB9AC\uB354\uC5D0\uAC8C \uC5F0\uACB0\uC774 \uB04A\uC5B4\uC84C\uB358 \uB3D9\uC548 \uBC1C\uC0DD\uD55C \uB370\uC774\uD130 \uBCC0\uACBD\uC744 \uBAA8\uB450 \uC694\uCCAD\uD55C\uB2E4. \uD574\uB2F9 \uBCC0\uACBD\uC0AC\uD56D\uC774 \uB2E4 \uC801\uC6A9\uB3FC\uC11C \uB9AC\uB354\uB97C \uB530\uB77C\uC7A1\uC740 \uD6C4\uC5D0 \uC774\uC804\uACFC \uAC19\uC774 \uB370\uC774\uD130 \uBCC0\uACBD \uC2A4\uD2B8\uB9BC\uC744 \uACC4\uC18D \uBC1B\uB294\uB2E4.</p></li><li><p>\uB9AC\uB354 \uC7A5\uC560 : \uC7A5\uC560 \uBCF5\uAD6C<br> \uB9AC\uB354\uC758 \uC7A5\uC560\uB97C \uCC98\uB9AC\uD558\uB294 \uC77C\uC740 \uAE4C\uB2E4\uB86D\uB2E4. \uBCF4\uD1B5 \uC544\uB798\uC758 \uC808\uCC28\uB97C \uB530\uB978\uB2E4.</p><ol><li>\uD314\uB85C\uC6CC \uC911 \uD558\uB098\uB97C \uC0C8\uB85C\uC6B4 \uB9AC\uB354\uB85C \uC2B9\uACA9\uD574\uC57C \uD558\uACE0</li><li>\uD074\uB77C\uC774\uC5B8\uD2B8\uB294 \uC0C8\uB85C\uC6B4 \uB9AC\uB354\uC5D0\uAC8C \uC4F0\uAE30 \uC694\uCCAD\uC744 \uC804\uC1A1\uD558\uAE30 \uC704\uD574 \uC7AC\uC124\uC815\uC774 \uD544\uC694\uD558\uBA74</li><li>\uB2E4\uB978 \uD314\uB85C\uC6CC\uB294 \uC0C8\uB85C\uC6B4 \uB9AC\uB354\uB85C\uBD80\uD130 \uB370\uC774\uD130 \uBCC0\uACBD\uC744 \uC18C\uBE44\uD574\uC57C \uD55C\uB2E4.</li></ol></li></ul><p>\uC704 \uACFC\uC815\uC744 \uC7A5\uC560 \uBCF5\uAD6C(failover)\uB77C \uD55C\uB2E4.</p><p>\uC7A5\uC560 \uBCF5\uAD6C\uB294 \uC218\uB3D9 \uD639\uC740 \uC790\uB3D9\uC73C\uB85C \uC9C4\uD589\uD558\uB294\uB370 \uC790\uB3D9 \uC7A5\uC560 \uBCF5\uAD6C\uB294 \uB2E4\uC74C\uACFC \uAC19\uC740 \uB2E8\uACC4\uB97C \uAC70\uCE5C\uB2E4.</p><ol><li>\uB9AC\uB354\uAC00 \uC7A5\uC560\uC778\uC9C0 \uD310\uB2E8\uD55C\uB2E4 : \uC7A5\uC560\uC758 \uC6D0\uC778\uC744 \uD30C\uC545\uD560 \uBC29\uBC95 \uC5C6\uAE30 \uB54C\uBB38\uC5D0 \uB300\uBD80\uBD84\uC740 \uD0C0\uC784\uC544\uC6C3\uC744 \uC0AC\uC6A9\uD55C\uB2E4. (\uD310\uB2E8\uC740 \uD558\uD2B8\uBE44\uD2B8? \uC0AC\uC6A9 \uB178\uB4DC\uB4E4\uC774 \uC11C\uB85C \uBA54\uC2DC\uC9C0 \uC8FC\uACE0\uBC1B\uC73C\uBA70 \uC77C\uC815 \uC2DC\uAC04 \uB3D9\uC548 \uB178\uB4DC \uC751\uB2F5 \uC5C6\uC73C\uBA74 \uC8FD\uC740 \uAC83\uC73C\uB85C \uAC04\uC8FC)</li><li>\uC0C8\uB85C\uC6B4 \uB9AC\uB354\uB97C \uC120\uD0DD\uD55C\uB2E4: \uAC00\uC7A5 \uC801\uD569\uD55C \uD6C4\uBCF4\uB294 \uBCF4\uD1B5 \uC774\uC804 \uB9AC\uB354\uC758 \uCD5C\uC2E0 \uB370\uC774\uD130 \uBCC0\uACBD\uC0AC\uD56D\uC744 \uAC00\uC9C4 \uBCF5\uC81C \uC11C\uBC84 \uBAA8\uB4E0 \uB178\uB4DC\uAC00 \uC0C8 \uB178\uB4DC\uC758 \uB3D9\uC758\uB97C \uAD6C\uD558\uB294 \uD569\uC758 \uBB38\uC81C\uB294 \uC774\uD6C4 \uC77C\uAD00\uC131 \uD569\uC758\uC5D0\uC11C \uC790\uC138\uD788 \uB2E4\uB8EC\uB2E4.</li><li>\uC0C8\uB85C\uC6B4 \uB9AC\uB354 \uC0AC\uC6A9\uC744 \uC704\uD574 \uC2DC\uC2A4\uD15C\uC744 \uC7AC\uC124\uC815\uD55C\uB2E4 : \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uC4F0\uAE30 \uC694\uCCAD \uC0C8\uB85C\uC6B4 \uB9AC\uB354\uC5D0\uAC8C \uBCF4\uB0B4\uC57C \uD558\uBA70, \uC2DC\uC2A4\uD15C\uC774 \uC774\uC804 \uB9AC\uB354\uAC00 \uD314\uB85C\uC6CC \uB418\uACE0 \uC0C8\uB85C\uC6B4 \uB9AC\uB354 \uC778\uC2DD\uD558\uAC8C\uB054 \uC124\uC815</li></ol><p>\uADF8\uB7EC\uB098 \uC790\uB3D9 \uC7A5\uC560 \uBCF5\uAD6C \uACFC\uC815\uC740 \uBB38\uC81C\uC810\uC774 \uB9CE\uB2E4. \uBE44\uB3D9\uAE30 \uBCF5\uC81C \uC0AC\uC6A9 \uC2DC \uC774\uC804 \uB9AC\uB354\uAC00 \uAC00\uC9C0\uACE0 \uC788\uB358 \uC4F0\uAE30\uC758 \uC77C\uBD80\uAC00 \uC218\uC2E0\uB418\uC9C0 \uBABB\uD55C \uACBD\uC6B0\uB294 \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uD560 \uAC83\uC778\uAC00? \uB9AC\uB354\uAC00 \uC8FD\uC5C8\uB2E4\uACE0 \uD310\uB2E8\uD558\uB294 \uC801\uC808\uD55C \uD0C0\uC784\uC544\uC6C3\uC740 \uC5B4\uB290 \uC815\uB3C4 \uC778\uAC00? \uB610\uD55C \uD2B9\uC815 \uACB0\uD568 \uC2DC\uB098\uB9AC\uC624\uC5D0\uC11C\uB294 \uB450 \uB178\uB4DC\uAC00 \uC790\uC2E0\uC774 \uB9AC\uB354\uB77C\uACE0 \uBBFF\uB294 \uC0C1\uD669\uC774 \uC62C \uC218 \uB3C4 \uC788\uB2E4.</p><p>\uC774\uB7F0 \uC774\uC720\uB85C \uC77C\uBD80 \uC6B4\uC601\uD300\uC740 \uC790\uB3D9 \uC7A5\uC560 \uBCF5\uAD6C\uB97C \uC9C0\uC6D0\uD574\uB3C4 \uC218\uB3D9\uC73C\uB85C \uC7A5\uC560 \uBCF5\uAD6C\uB97C \uC218\uD589\uD558\uB294 \uBC29\uC2DD\uC744 \uC120\uD638\uD558\uAE30\uB3C4 \uD55C\uB2E4.</p><h2 id="\u1107\u1169\u11A8\u110C\u1166-\u110C\u1175\u110B\u1167\u11AB\u1100\u116A-\u110E\u116C\u110C\u1169\u11BC\u110C\u1165\u11A8-\u110B\u1175\u11AF\u1100\u116A\u11AB\u1109\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1107\u1169\u11A8\u110C\u1166-\u110C\u1175\u110B\u1167\u11AB\u1100\u116A-\u110E\u116C\u110C\u1169\u11BC\u110C\u1165\u11A8-\u110B\u1175\u11AF\u1100\u116A\u11AB\u1109\u1165\u11BC" aria-hidden="true">#</a> \uBCF5\uC81C \uC9C0\uC5F0\uACFC \uCD5C\uC885\uC801 \uC77C\uAD00\uC131</h2><p>\uBCF5\uC81C\uB294 \uB0B4\uACB0\uD568\uC131\uC5D0\uB9CC \uD544\uC694\uD55C \uAC83\uC774 \uC544\uB2C8\uB77C \uD655\uC7A5\uC131(\uB2E8\uC77C \uC11C\uBC84\uC5D0\uC11C \uAC10\uB2F9 \uBABB\uD558\uB294 \uC694\uCCAD)\uACFC \uC9C0\uC5F0\uC2DC\uAC04(\uC9C0\uB9AC\uC801 \uAC00\uAE4C\uC6B4 \uC704\uCE58) \uAD00\uC810\uC5D0\uC11C\uB3C4 \uC911\uC694\uD558\uB2E4. \uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C\uB294 \uC4F0\uAE30\uB294 \uB9AC\uB354\uB97C \uAC70\uCCD0\uC57C \uD558\uC9C0\uB9CC \uC77D\uAE30\uB294 \uC5B4\uB5A4 \uBCF5\uC81C \uC11C\uBC84\uC5D0\uC11C\uB3C4 \uAC00\uB2A5\uD558\uB2E4. \uB300\uBD80\uBD84\uC774 \uC77D\uAE30 \uC694\uCCAD\uC774\uACE0 \uC791\uC740 \uBE44\uC728\uC758 \uC4F0\uAE30 \uC694\uCCAD\uC774 \uC788\uB294 \uC791\uC5C5 \uBD80\uD558(\uC6F9\uC0C1\uC758 \uACF5\uD1B5 \uD328\uD134\uC774\uB2E4)\uB77C\uBA74 \uC77D\uAE30 \uC694\uCCAD\uC744 \uBD84\uC0B0\uD560 \uC218 \uC788\uAE30\uC5D0 \uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C\uB294 \uB9E4\uB825\uC801\uC774\uB2E4. \uC774\uB7F0 \uC77D\uAE30 \uD655\uC7A5 \uC544\uD0A4\uD14D\uCC98\uB294 \uC2E4\uC81C\uB85C\uB294 \uBE44\uB3D9\uAE30\uC5D0\uC11C\uB9CC \uB3D9\uC791\uD55C\uB2E4. \uB3D9\uAE30\uC2DD\uC73C\uB85C \uD314\uB85C\uC6CC\uC5D0 \uBCF5\uC81C\uB97C \uC2DC\uB3C4\uD558\uBA74 \uB178\uB4DC \uC7A5\uC560\uB098 \uB124\uD2B8\uC6CC\uD06C \uC911\uB2E8\uC73C\uB85C \uC804\uCCB4 \uC2DC\uC2A4\uD15C\uC758 \uC4F0\uAE30\uAC00 \uBD88\uC548\uC815\uD574\uC9C4\uB2E4. \uD2B9\uD788 \uB178\uB4DC\uAC00 \uB9CE\uC544\uC9C0\uBA74 \uB2E4\uC6B4\uB420 \uAC00\uB2A5\uC131\uB3C4 \uCEE4\uC9C4\uB2E4.</p><p>\uADF8\uB7EC\uB098 \uBE44\uB3D9\uAE30 \uD314\uB85C\uC6CC\uC5D0\uC11C \uB370\uC774\uD130\uB97C \uC77D\uC744 \uB54C\uB3C4 \uD314\uB85C\uC6CC\uAC00 \uB4A4\uCCD0\uC9C4\uB2E4\uBA74 \uC9C0\uB09C \uC815\uBCF4\uB97C \uBCFC \uC218 \uB3C4 \uC788\uB2E4. \uC774 \uC0C1\uD669\uC740 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC640 \uBA85\uBC31\uD55C \uBD88\uC77C\uCE58\uC774\uB2E4. \uB610\uD55C \uB3D9\uC2DC\uC5D0 \uB9AC\uB354\uC640 \uD314\uB85C\uC6CC\uC5D0 \uB3D9\uC77C\uD55C \uC9C8\uC758\uB97C \uBCF4\uB0B4\uB354\uB77C\uB3C4 \uBAA8\uB4E0 \uC4F0\uAE30\uAC00 \uD314\uB85C\uC6CC\uC5D0 \uBC18\uC601\uB418\uC9C0 \uC54A\uC558\uAE30 \uB54C\uBB38\uC5D0 \uC11C\uB85C \uB2E4\uB978 \uACB0\uACFC\uB97C \uC5BB\uC744 \uC218 \uC788\uB2E4. \uD558\uC9C0\uB9CC <strong>\uC774\uB7F0 \uBD88\uC77C\uCE58\uB294 \uC77C\uC2DC\uC801\uC778 \uC0C1\uD0DC\uC5D0 \uBD88\uACFC\uD558\uBA70 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0 \uC4F0\uAE30\uB97C \uBA48\uCD94\uACE0 \uC7A0\uC2DC \uB3D9\uC548 \uAE30\uB2E4\uB9AC\uBA74 \uD314\uB85C\uC6CC\uB294 \uACB0\uAD6D \uB530\uB77C\uC7A1\uACE0\uB9AC\uB354\uC640 \uC77C\uCE58\uD558\uAC8C \uB41C\uB2E4. \uC774\uB7F0 \uD6A8\uACFC\uB97C \uCD5C\uC885\uC801 \uC77C\uAD00\uC131\uC774\uB77C\uACE0 \uD55C\uB2E4.</strong></p><p>\uC77C\uBC18\uC801\uC73C\uB85C \uB9AC\uB354\uC5D0 \uC77C\uC5B4\uB09C \uC4F0\uAE30\uC640 \uD314\uB85C\uC6CC\uC5D0\uC11C \uBC18\uC601 \uC0AC\uC774\uC758 \uC9C0\uC5F0\uC740 \uB208\uC5D0 \uB744\uC9C0 \uC54A\uB294 \uC544\uC8FC \uC9E7\uC740 \uC21C\uAC04\uC774\uC9C0\uB9CC \uC2DC\uC2A4\uD15C\uC774 \uAC00\uC6A9\uB7C9 \uADFC\uCC98\uC5D0\uC11C \uB3D9\uC791\uD558\uAC70\uB098 \uB124\uD2B8\uC6CC\uD06C \uBB38\uC81C\uAC00 \uC788\uB2E4\uBA74 \uC9C0\uC5F0\uC740 \uC218\uCD08 \uC218\uBD84\uC774 \uB420 \uC218 \uC788\uB2E4,</p><p>\uC774\uB807\uAC8C \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C \uC9C0\uC5F0\uC2DC\uAC04\uC774 \uB9E4\uC6B0 \uCEE4\uC9C0\uBA74 \uBD88\uC77C\uCE58\uB294 \uC2E4\uC81C \uBB38\uC81C\uAC00 \uB41C\uB2E4. \uC544\uB798\uC5D0\uC11C \uBCF5\uC81C \uC9C0\uC5F0\uC774 \uC788\uC744 \uB54C \uBC1C\uC0DD\uD560 \uC218 \uC788\uB294 3\uAC00\uC9C0 \uC0AC\uB840\uC640 \uD574\uACB0\uBC95\uC744 \uC0B4\uD3B4\uBCF4\uC790.</p><h3 id="_1-\u110E\u1165\u11BA-\u1107\u1165\u11AB\u110D\u1162-\u1109\u1161\u1105\u1168-\u110C\u1161\u1109\u1175\u11AB\u110B\u1175-\u110A\u1173\u11AB-\u1102\u1162\u110B\u116D\u11BC-\u110B\u1175\u11B0\u1100\u1175" tabindex="-1"><a class="header-anchor" href="#_1-\u110E\u1165\u11BA-\u1107\u1165\u11AB\u110D\u1162-\u1109\u1161\u1105\u1168-\u110C\u1161\u1109\u1175\u11AB\u110B\u1175-\u110A\u1173\u11AB-\u1102\u1162\u110B\u116D\u11BC-\u110B\u1175\u11B0\u1100\u1175" aria-hidden="true">#</a> 1. \uCCAB \uBC88\uC9F8 \uC0AC\uB840 : \uC790\uC2E0\uC774 \uC4F4 \uB0B4\uC6A9 \uC77D\uAE30</h3><p>\uC0AC\uC6A9\uC790\uAC00 \uC4F0\uAE30\uB97C \uC218\uD589\uD55C \uC9C1\uD6C4 \uB370\uC774\uD130\uB97C \uBCF8\uB2E4\uBA74 \uC0C8\uB85C\uC6B4 \uB370\uC774\uD130\uB294 \uC544\uC9C1 \uBCF5\uC81C \uC11C\uBC84\uC5D0 \uBC18\uC601\uB418\uC9C0 \uC54A\uC558\uC744 \uC218 \uC788\uB2E4. \uC774\uAC83\uC740 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uC81C\uCD9C\uD588\uC74C\uC5D0\uB3C4 \uC720\uC2E4\uB41C \uAC83\uCC98\uB7FC \uBCF4\uC5EC \uBD88\uB9CC\uC871\uC2A4\uB7EC\uC6B4 \uB3D9\uC791\uC774\uB2E4. \uC774 \uACBD\uC6B0\uC5D0\uB294 \uC4F0\uAE30 \uD6C4 \uC77D\uAE30 \uC77C\uAD00\uC131(\uC790\uC2E0\uC758 \uC4F0\uAE30 \uC77D\uAE30 \uC77C\uAD00\uC131)\uC774 \uD544\uC694\uD558\uB2E4. <strong>\uC4F0\uAE30 \uD6C4 \uC77D\uAE30 \uC77C\uAD00\uC131\uC740 \uC0AC\uC6A9\uC790\uAC00 \uD398\uC774\uC9C0\uB97C \uC7AC \uB85C\uB529\uD588\uC744 \uB54C \uD56D\uC0C1 \uC790\uC2E0\uC774 \uC81C\uCD9C\uD55C \uBAA8\uB4E0 \uAC31\uC2E0\uC744 \uBCFC \uC218 \uC788\uC744 \uC744 \uBCF4\uC7A5\uD55C\uB2E4.</strong></p><p>\uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C \uC2DC\uC2A4\uD15C\uC5D0\uC11C\uB294 \uC4F0\uAE30 \uD6C4 \uC77C\uAE30 \uC77C\uAD00\uC131\uC744 \uB2E4\uC591\uD55C \uAE30\uBC95\uC73C\uB85C \uAD6C\uD604\uD558\uB294\uB370 \uBA87 \uAC00\uC9C0 \uC5B8\uAE09\uD558\uC790\uBA74</p><ul><li>\uC0AC\uC6A9\uC790\uAC00 \uC218\uC815\uD55C \uB0B4\uC6A9\uC744 \uC77D\uC744 \uB54C\uB294 \uB9AC\uB354\uC5D0\uC11C \uC77D\uB294\uB2E4: \uC790\uC2E0\uC758 \uD504\uB85C\uD544\uC740 \uB9AC\uB354\uC5D0\uC11C \uD0C0\uC778\uC758 \uD504\uB85C\uD544\uC740 \uD314\uB85C\uC6CC\uC5D0\uC11C \uC77D\uC740 \uAE30\uBC95\uC774\uB2E4. \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uB0B4 \uB300\uBD80\uBD84\uC758 \uB0B4\uC6A9\uC744 \uC0AC\uC6A9\uC790\uAC00 \uD3B8\uC9D1\uD560 \uAC00\uB2A5\uC131\uC774 \uC788\uB2E4\uBA74 \uC774 \uC811\uADFC \uBC29\uC2DD\uC740 \uB300\uBD80\uBD84\uC758 \uB9AC\uB354\uC5D0\uC11C \uC77D\uAE30 \uB54C\uBB38\uC5D0 \uD6A8\uC728\uC801\uC774\uC9C0 \uC54A\uB2E4.</li><li>\uB9C8\uC9C0\uB9C9 \uAC31\uC2E0 \uC2DC\uAC01\uC744 \uCC3E\uC544\uC11C \uB9C8\uC9C0\uB9C9 \uAC31\uC2E0 \uD6C4 1\uBD84 \uB3D9\uC548\uC740 \uB9AC\uB354\uC5D0\uC11C \uBAA8\uB4E0 \uC77D\uAE30 \uC218\uD589\uD55C\uB2E4. \uD639\uC740 \uB9AC\uB354\uBCF4\uB2E4 \uBAA8\uB2C8\uD130\uB9C1 \uD1B5\uD574 1\uBD84 \uC774\uC0C1 \uB2A6\uB294 \uBAA8\uB4E0 \uD314\uB85C\uC6CC\uC5D0 \uB300\uD55C \uC9C8\uC758\uB97C \uAE08\uC9C0\uD55C\uB2E4.</li><li>\uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uAC00\uC7A5 \uCD5C\uADFC \uC4F0\uAE30 \uC2DC\uAC04 \uAE30\uC5B5\uD560 \uC218 \uC788\uB2E4\uBA74 \uBCF5\uC81C \uC11C\uBC84\uAC00 \uCD5C\uC18C\uD55C \uD574\uB2F9 \uC2DC\uAC04\uAE4C\uC9C0 \uAC31\uC2E0\uD558\uB3C4\uB85D \uB300\uAE30\uD558\uAC70\uB098, \uB2E4\uB978 \uBCF5\uC81C \uC11C\uBC84\uAC00 \uCC98\uB9AC\uD558\uB3C4\uB85D \uD55C\uB2E4.</li></ul><h3 id="_2-\u1103\u116E-\u1107\u1165\u11AB\u110D\u1162-\u1109\u1161\u1105\u1168-\u1109\u1161\u110B\u116D\u11BC\u110C\u1161\u110B\u1174-\u1109\u1175\u1100\u1161\u11AB\u110B\u1175-\u1100\u1165\u1101\u116E\u1105\u1169-\u1112\u1173\u1105\u1173\u11AB\u1103\u1161" tabindex="-1"><a class="header-anchor" href="#_2-\u1103\u116E-\u1107\u1165\u11AB\u110D\u1162-\u1109\u1161\u1105\u1168-\u1109\u1161\u110B\u116D\u11BC\u110C\u1161\u110B\u1174-\u1109\u1175\u1100\u1161\u11AB\u110B\u1175-\u1100\u1165\u1101\u116E\u1105\u1169-\u1112\u1173\u1105\u1173\u11AB\u1103\u1161" aria-hidden="true">#</a> 2. \uB450 \uBC88\uC9F8 \uC0AC\uB840: \uC0AC\uC6A9\uC790\uC758 \uC2DC\uAC04\uC774 \uAC70\uAFB8\uB85C \uD750\uB978\uB2E4</h3><p>\uC774 \uC0AC\uB840\uB294 \uC0AC\uC6A9\uC790\uAC00 \uAC01\uAE30 \uB2E4\uB978 \uBCF5\uC81C \uC11C\uBC84\uC5D0\uC11C \uC5EC\uB7EC \uC77D\uAE30\uB97C \uC218\uD589\uD560 \uB54C \uBC1C\uC0DD\uD560 \uC218 \uC788\uB2E4. \uCCAB \uBC88\uC9F8 \uC9C8\uC758\uB97C \uD55C \uD314\uB85C\uC6CC\uBCF4\uB2E4 \uB450 \uBC88\uC9F8 \uC9C8\uC758\uB97C \uD55C \uD314\uB85C\uC6CC\uAC00 \uB354 \uC774\uB978 \uC2DC\uC810\uC5D0 \uC2DC\uC2A4\uD15C\uC744 \uBCF4\uACE0 \uC788\uC744 \uB54C(\uBCF5\uC81C\uAC00 \uC9C0\uC5F0\uB410\uC744 \uB54C) \uC0AC\uC6A9\uC790\uB294 \uB370\uC774\uD130\uB97C \uBCF4\uACE0 \uB2E4\uC2DC \uC0AC\uB77C\uC9C0\uAC8C \uB3FC\uC11C \uD63C\uB780\uC2A4\uB7EC\uC6B8 \uAC83\uC774\uB2E4. \uB2E8\uC870 \uC77D\uAE30(monotonic read)\uB294 \uC774\uB7F0 \uC885\uB958\uC758 \uC774\uC0C1\uD604\uC0C1\uC774 \uBC1C\uC0DD\uD558\uC9C0 \uC54A\uC74C \uC744 \uBCF4\uC7A5\uD55C\uB2E4. <strong>\uB2E8\uC870 \uC77D\uAE30\uB294 \uAC15\uD55C \uC77C\uAD00\uC131\uBCF4\uB2E4\uB294 \uB35C\uD558\uC9C0\uB9CC \uCD5C\uC885\uC801 \uC77C\uAD00\uC131\uBCF4\uB2E4\uB294 \uB354 \uAC15\uD55C \uBCF4\uC7A5\uC774\uB2E4. \uD55C \uC0AC\uC6A9\uC790\uAC00 \uC5EC\uB7EC \uBC88\uC5D0 \uAC78\uCCD0 \uB370\uC774\uD130\uB97C \uC77D\uC5B4\uB3C4 \uC2DC\uAC04\uC774 \uB418\uB3CC\uC544\uAC00\uB294 \uD604\uC0C1\uC744 \uBCF4\uC9C0 \uC54A\uB294\uB2E4.</strong> \uB2E8\uC870 \uC77D\uAE30\uB97C \uB2EC\uC131\uD558\uB294 \uD55C \uBC29\uBC95\uC740 \uAC01 \uC0AC\uC6A9\uC790\uC758 \uC77D\uAE30\uAC00 \uD56D\uC0C1 \uB3D9\uC77C\uD55C \uBCF5\uC81C \uC11C\uBC84\uC5D0\uC11C \uC218\uD589\uB418\uAC8C\uB054 \uD558\uB294 \uAC83\uC774\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uC784\uC758 \uC120\uD0DD\uBCF4\uB2E4\uB294 \uC0AC\uC6A9\uC790 id\uC758 \uD574\uC2DC\uB97C \uAE30\uBC18\uC73C\uB85C \uBCF5\uC81C \uC11C\uBC84\uB97C \uC120\uD0DD\uD55C\uB2E4. \uB9CC\uC57D \uBCF5\uC81C \uC11C\uBC84\uAC00 \uACE0\uC7A5 \uB09C \uB2E4\uBA74 \uC0AC\uC6A9\uC790 \uC9C8\uC758\uB97C \uB2E4\uB978 \uBCF5\uC81C \uC11C\uBC84\uB85C \uB77C\uC6B0\uD305 \uD560 \uD544\uC694\uAC00 \uC788\uB2E4.</p><h3 id="_3-\u1109\u1166-\u1107\u1165\u11AB\u110D\u1162-\u1109\u1161\u1105\u1168-\u110B\u1175\u11AB\u1100\u116A\u1109\u1165\u11BC-\u110B\u1171\u1107\u1161\u11AB" tabindex="-1"><a class="header-anchor" href="#_3-\u1109\u1166-\u1107\u1165\u11AB\u110D\u1162-\u1109\u1161\u1105\u1168-\u110B\u1175\u11AB\u1100\u116A\u1109\u1165\u11BC-\u110B\u1171\u1107\u1161\u11AB" aria-hidden="true">#</a> 3. \uC138 \uBC88\uC9F8 \uC0AC\uB840 : \uC778\uACFC\uC131 \uC704\uBC18</h3><p>A: \uC774 \uC0AC\uACFC \uB204\uAC00 \uBA39\uC5C8\uB2C8?<br> B: \uC81C\uAC00 \uBA39\uC5C8\uC5B4\uC694.</p><p>\uC774 \uB450 \uBB38\uC7A5 \uC0AC\uC774\uC5D0\uB294 \uC778\uACFC\uC131\uC774 \uC788\uB2E4. \uC774\uB7F0 \uB300\uD654\uAC00 \uC788\uC744 \uB54C \uD314\uB85C\uC6CC\uB97C \uD1B5\uD574 \uC774 \uB300\uD654\uB97C \uB4E3\uACE0 \uC788\uB294 \uC81C3 \uAD00\uCC30\uC790\uAC00 \uC788\uB2E4\uACE0 \uD574\uBCF4\uC790. B\uB294 \uC9C0\uC5F0\uC2DC\uAC04\uC774 \uAC70\uC758 \uC5C6\uC774 \uD314\uB85C\uC6CC\uC5D0 \uC804\uB2EC\uB410\uC9C0\uB9CC A\uB294 \uC544\uC8FC \uAE34 \uBCF5\uC81C \uC9C0\uC5F0\uC774 \uC788\uC5C8\uB2E4.</p><p>\uADF8\uB7EC\uBA74 \uAD00\uCC30\uC790\uB294 \uB300\uD654\uB97C \uC544\uB798\uC640 \uAC19\uC774 \uB4E3\uAC8C \uB41C\uB2E4.</p><p>B: \uC81C\uAC00 \uBA39\uC5C8\uC5B4\uC694.<br> A: \uC774 \uC0AC\uACFC \uB204\uAC00 \uBA39\uC5C8\uB2C8?</p><p>\uC774\uB7F0 \uC885\uB958\uC758 \uC774\uC0C1\uD604\uC0C1\uC744 \uBC29\uC9C0\uD558\uB824\uBA74 \uC77C\uAD00\uB41C \uC21C\uC11C\uB85C \uC77D\uAE30(Consistent Prefix Read) \uAC19\uC740 \uC720\uD615\uC758 \uBCF4\uC7A5\uC774 \uD544\uC694\uD55C\uB2E4.</p><p><strong>\uC77C\uAD00\uB41C \uC21C\uC11C\uB85C \uC77D\uAE30\uB294 \uC77C\uB828\uC758 \uC4F0\uAE30\uAC00 \uD2B9\uC815 \uC21C\uC11C\uB85C \uBC1C\uC0DD\uD55C\uB2E4\uBA74 \uC774 \uC4F0\uAE30\uB97C \uC77D\uB294 \uBAA8\uB4E0 \uC0AC\uC6A9\uC790\uB294 \uAC19\uC740 \uC21C\uC11C\uB85C \uC4F0\uC778 \uB0B4\uC6A9\uC744 \uBCF4\uAC8C \uB428\uC744 \uBCF4\uC7A5\uD55C\uB2E4.</strong></p><p>\uC774\uB294 \uD30C\uD2F0\uC154\uB2DD \uB41C \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0\uC11C \uBC1C\uC0DD\uD558\uB294 \uD2B9\uC9D5\uC801\uC778 \uBB38\uC81C\uC774\uB2E4. \uB9CE\uC740 \uBD84\uC0B0 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0 \uC11C\uB85C \uB2E4\uB978 \uD30C\uD2F0\uC158\uC740 \uB3C5\uB9BD\uC801\uC73C\uB85C \uB3D9\uC791\uD558\uBBC0\uB85C \uC4F0\uAE30\uC758 \uC804\uC5ED \uC21C\uC11C\uB294 \uC5C6\uB2E4. \uD55C \uAC00\uC9C0 \uD574\uACB0\uCC45\uC740 \uC11C\uB85C \uC778\uACFC\uC131\uC774 \uC788\uB294 \uC4F0\uAE30\uAC00 \uB3D9\uC77C\uD55C \uD30C\uD2F0\uC158\uC5D0 \uAE30\uB85D\uB418\uAC8C\uB054 \uD558\uB294 \uBC29\uBC95\uC774\uB2E4. \uD558\uC9C0\uB9CC \uC77C\uBD80 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C\uB294 \uD6A8\uC728\uC801\uC774\uC9C0 \uC54A\uB2E4.</p><p>\u2B50 3\uAC00\uC9C0 \uC77C\uAD00\uC131\uC5D0 \uB300\uD55C \uC774\uC288\uB97C \uC0B4\uD3B4\uBCF4\uC558\uB2E4. \uACB0\uAD6D \uBCF5\uC81C \uC9C0\uC5F0\uC758 \uD574\uACB0\uCC45\uC740 \uBCF5\uC81C\uAC00 \uBE44\uB3D9\uAE30\uC2DD\uC73C\uB85C \uB3D9\uC791\uD558\uC9C0\uB9CC \uB3D9\uAE30\uC2DD\uC73C\uB85C \uB3D9\uC791\uD558\uB294 \uCC99\uD558\uB294 \uAC83\uC774\uB2E4.</p><h2 id="\u1103\u1161\u11AB\u110B\u1175\u11AF\u1105\u1175\u1103\u1165-\u1107\u1169\u11A8\u110C\u1166-single-leader" tabindex="-1"><a class="header-anchor" href="#\u1103\u1161\u11AB\u110B\u1175\u11AF\u1105\u1175\u1103\u1165-\u1107\u1169\u11A8\u110C\u1166-single-leader" aria-hidden="true">#</a> \uB2E8\uC77C\uB9AC\uB354 \uBCF5\uC81C(single-leader)</h2><p>\uC704\uC5D0 \uC124\uBA85\uB4E4\uC758 \uBCA0\uC774\uC2A4\uAC00 \uB41C \uAC83\uCC98\uB7FC \uB9AC\uB354\uAC00 \uD558\uB098\uB9CC \uC874\uC7AC\uD558\uB294 \uAC83\uC744 \uB2E8\uC77C \uB9AC\uB354 \uBCF5\uC81C\uB77C\uACE0 \uD55C\uB2E4. \uC774\uB7EC\uD55C \uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C\uC5D0\uB294 \uC8FC\uC694\uD55C \uB2E8\uC810 \uD558\uB098\uAC00 \uC788\uB294\uB370 \uC5B4\uB5A4 \uC774\uC720\uB85C \uB9AC\uB354\uC5D0 \uC5F0\uACB0\uD560 \uC218 \uC5C6\uB2E4\uBA74 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0 \uC4F0\uAE30\uB97C \uD560 \uC218 \uC5C6\uB2E4.</p><h2 id="\u1103\u1161\u110C\u116E\u11BC\u1105\u1175\u1103\u1165-\u1107\u1169\u11A8\u110C\u1166-multi-leader" tabindex="-1"><a class="header-anchor" href="#\u1103\u1161\u110C\u116E\u11BC\u1105\u1175\u1103\u1165-\u1107\u1169\u11A8\u110C\u1166-multi-leader" aria-hidden="true">#</a> \uB2E4\uC911\uB9AC\uB354 \uBCF5\uC81C(multi-leader)</h2><p>\uB9AC\uB354 \uAE30\uBC18 \uBCF5\uC81C \uBAA8\uB378\uC740 \uC4F0\uAE30\uB97C \uD5C8\uC6A9\uD558\uB294 \uB178\uB4DC\uB97C \uD558\uB098 \uC774\uC0C1 \uB450\uB294 \uAC83\uC73C\uB85C \uC790\uC5F0\uC2A4\uB808 \uD655\uC7A5\uB41C\uB2E4. \uC774 \uBC29\uC2DD\uC744 \uB2E4\uC911 \uB9AC\uB354 \uC124\uC815\uC774\uB77C \uBD80\uB978\uB2E4(\uB9C8\uC2A4\uD130/\uB9C8\uC2A4\uD130 \uC561\uD2F0\uBE0C/\uC561\uD2F0\uBE0C \uBCF5\uC81C\uB77C\uACE0\uB3C4 \uD568) \uC774 \uBC29\uC2DD\uC5D0\uC11C \uAC01 \uB9AC\uB354\uB294 \uB3D9\uC2DC\uC5D0 \uB2E4\uB978 \uB9AC\uB354\uC758 \uD314\uB85C\uC6CC \uC5ED\uD560 \uB3C4 \uD55C\uB2E4.</p><ul><li><p>\uB2E4\uC911 \uB9AC\uB354 \uC0AC\uC6A9 \uC0AC\uB840</p><ul><li>\uB2E4\uC911 \uB370\uC774\uD130 \uC13C\uD130 \uC6B4\uC601</li><li>\uC624\uD504\uB77C\uC778 \uC791\uC5C5\uC744 \uD558\uB294 \uD074\uB77C\uC774\uC5B8\uD2B8</li><li>\uD611\uC5C5 \uD3B8\uC9D1</li></ul></li><li><p>\uC4F0\uAE30 \uCDA9\uB3CC \uB2E4\uB8E8\uAE30<br> \uB2E4\uC911 \uB9AC\uB354 \uBCF5\uC81C\uC5D0\uC11C \uC81C\uC77C \uD070 \uBB38\uC81C\uB294 \uC4F0\uAE30 \uCDA9\uB3CC\uC774 \uBC1C\uC0DD\uD55C\uB2E4\uB294 \uC810\uC774\uB2E4. \uC0AC\uC6A9\uC790\uAC00 \uB3D9\uC2DC\uC5D0 \uB3D9\uC77C\uD55C \uBD80\uBD84\uC744 \uBCC0\uACBD\uD55C\uB2E4\uBA74 \uAC01 \uBCC0\uACBD\uC740 \uB85C\uCEEC \uB9AC\uB354\uC5D0 \uC131\uACF5\uC801\uC73C\uB85C \uC801\uC6A9\uB418\uC9C0\uB9CC \uBCC0\uACBD\uC744 \uBE44\uB3D9\uAE30\uB85C \uBCF5\uC81C\uD560 \uB54C \uCDA9\uB3CC\uC744 \uAC10\uC9C0\uD55C\uB2E4. \uCDA9\uB3CC\uC744 \uCC98\uB9AC\uD558\uB294 \uC81C\uC77C \uAC04\uB2E8\uD55C \uC804\uB7B5\uC740 \uCDA9\uB3CC\uC744 \uD53C\uD558\uB294 \uAC83\uC774\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uAC19\uC740 \uB370\uC774\uD130\uB97C \uD3B8\uC9D1\uD560 \uC218 \uC788\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC0AC\uC6A9\uC790\uC758 \uC694\uCCAD\uC744 \uB3D9\uC77C\uD55C \uB370\uC774\uD130 \uC13C\uD130\uB85C \uB77C\uC6B0\uD305 \uD558\uB294 \uB4F1\uC758 \uBC29\uBC95\uC744 \uC0AC\uC6A9\uD55C\uB2E4. \uB354 \uAE4A\uC740 \uB0B4\uC6A9\uC740 \uCC45\uC744 \uCC38\uC870\uD558\uAE30 \uBC14\uB780\uB2E4.</p></li></ul><h2 id="\u1105\u1175\u1103\u1165\u110B\u1165\u11B9\u1102\u1173\u11AB-\u1107\u1169\u11A8\u110C\u1166-leaderless" tabindex="-1"><a class="header-anchor" href="#\u1105\u1175\u1103\u1165\u110B\u1165\u11B9\u1102\u1173\u11AB-\u1107\u1169\u11A8\u110C\u1166-leaderless" aria-hidden="true">#</a> \uB9AC\uB354\uC5C6\uB294 \uBCF5\uC81C(leaderless)</h2><p>\uB9AC\uB354 \uC5C6\uB294 \uBCF5\uC81C\uB780 \uC9C0\uAE08\uAE4C\uC9C0\uC758 \uB9AC\uB354-\uD314\uB85C\uC6CC \uAC1C\uB150\uC744 \uBC84\uB9AC\uACE0 \uBAA8\uB4E0 \uBCF5\uC81C \uC11C\uBC84\uAC00 \uD074\uB77C\uC774\uC5B8\uD2B8\uB85C\uBD80\uD130 \uC4F0\uAE30\uB97C \uC9C1\uC811 \uBC1B\uC744 \uC218 \uC788\uAC8C \uD5C8\uC6A9\uD55C\uB2E4. \uC791\uB3D9\uBC29\uC2DD\uC744 \uAC04\uB7B5\uD558\uAC8C \uC124\uBA85\uD558\uC790\uBA74 \uD074\uB77C\uC774\uC5B8\uD2B8\uB294 <strong>\uAC01 \uC4F0\uAE30\uB97C \uC5EC\uB7EC \uB178\uB4DC\uB85C \uC804\uC1A1\uD55C\uB2E4. \uB9CC\uC57D 3\uAC1C\uC758 \uB178\uB4DC\uB85C \uC4F0\uAE30 \uC694\uCCAD\uC744 \uBCF4\uB0C8\uB294\uB370 2\uAC1C\uC758 \uBCF5\uC81C \uC11C\uBC84\uAC00 \uC4F0\uAE30\uB97C \uD655\uC778\uD558\uBA74 \uCDA9\uBD84\uD558\uB2E4\uACE0 \uAC00\uC815\uD55C\uB2E4.</strong> \uD074\uB77C\uC774\uC5B8\uD2B8\uB294 \uB098\uBA38\uC9C0 \uBCF5\uC81C \uC11C\uBC84 \uD558\uB098\uAC00 \uC4F0\uAE30\uB97C \uB193\uCE5C \uC0AC\uC2E4\uC740 \uBB34\uC2DC\uD558\uACE0 \uB450 \uAC1C\uC758 ok\uC751\uB2F5\uC744 \uBC1B\uC558\uB2E4\uBA74 \uC4F0\uAE30\uAC00 \uC131\uACF5\uD55C \uAC83\uC73C\uB85C \uAC04\uC8FC\uD55C\uB2E4. \uADF8 \uD6C4 \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uD574\uB2F9 \uB370\uC774\uD130\uB97C \uC77D\uC744 \uB54C \uD574\uB2F9 \uB178\uB4DC\uC5D0\uC11C \uAC12\uC744 \uC5BB\uB294\uB2E4\uBA74 \uC624\uB798\uB41C(outdated) \uAC12\uC744 \uC5BB\uC744 \uC218 \uC788\uB2E4. \uC774 \uBB38\uC81C\uB97C \uD574\uACB0\uD558\uAE30 \uC704\uD574 \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uB370\uC774\uD130\uB97C \uC77D\uC744 \uB54C \uD558\uB098\uC758 \uBCF5\uC81C \uC11C\uBC84\uB85C \uC694\uCCAD\uC744 \uBCF4\uB0B4\uC9C0 \uC54A\uACE0 <strong>\uC77D\uAE30 \uC694\uCCAD\uC744 \uBCD1\uB82C\uB85C \uC5EC\uB7EC \uB178\uB4DC\uC5D0 \uC804\uC1A1\uD55C\uB2E4.</strong> \uADF8\uB7EC\uBA74 \uD074\uB77C\uC774\uC5B8\uD2B8\uB294 \uC5EC\uB7EC \uB178\uB4DC\uC5D0\uC11C \uB2E4\uB978 \uC751\uB2F5\uC744 \uBC1B\uC744 \uC218 \uC788\uB2E4, \uC989 \uD55C \uB178\uB4DC\uC5D0\uC11C\uB294 \uCD5C\uC2E0 \uAC12\uC744 \uB2E4\uB978 \uB178\uB4DC\uC5D0\uC11C\uB294 \uC624\uB798\uB41C \uAC12\uC744 \uBC1B\uB294\uB2E4. \uC774\uB54C \uBC84\uC804 \uC22B\uC790\uB97C \uC0AC\uC6A9\uD574 \uC5B4\uB5A4 \uAC12\uC774 \uCD5C\uC2E0 \uB0B4\uC6A9\uC778\uC9C0 \uACB0\uC815\uD558\uB294 \uBC29\uC2DD\uC774\uB2E4.</p>',64),t=[d];function n(h,s){return a(),e("div",null,t)}var u=i(p,[["render",n],["__file","ch5-replication.html.vue"]]);export{u as default};
