import{_ as s}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as t,d as i}from"./app.d6b42b88.js";const r={},a=i('<div class="custom-container tip"><p class="custom-container-title">GOAL</p><p>Q. \uB2E4\uC911 \uC791\uC5C5, \uC2DC\uBD84\uD560, \uB2E4\uC911 \uD504\uB85C\uADF8\uB798\uBC0D, \uB300\uD654\uD615 \uC2DC\uC2A4\uD15C \uAC01 \uC815\uC758\uC5D0 \uB300\uD574 \uC124\uBA85\uD558\uC138\uC694.<br> Q. \uC791\uC5C5\uCC98\uB9AC \uBC29\uC2DD\uC5D0 \uB530\uB77C. \uC77C\uAD04\uCC98\uB9AC(batch processing) vs \uC2DC\uBD84\uD560 \uBC29\uC2DD vs \uC2E4\uC2DC\uAC04 \uC6B4\uC601\uCCB4\uC81C\uB97C \uC124\uBA85\uD558\uC138\uC694.</p></div><p>\uD574\uB2F9\uD398\uC774\uC9C0\uB294 2022\uB144 3\uC6D4 \uB178\uC158\uC5D0\uC11C \uB9C8\uC774\uADF8\uB808\uC774\uC158 \uB418\uC5C8\uC2B5\uB2C8\uB2E4.</p><h2 id="\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166-\u110B\u1172\u1112\u1167\u11BC" tabindex="-1"><a class="header-anchor" href="#\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166-\u110B\u1172\u1112\u1167\u11BC" aria-hidden="true">#</a> \uC6B4\uC601\uCCB4\uC81C \uC720\uD615</h2><p>\uCEF4\uD4E8\uD130\uC2DC\uC2A4\uD15C\uC758 \uBC1C\uC804\uACFC\uC815\uACFC \uC6A9\uB3C4 \uB610\uB294 \uC751\uB2F5\uC2DC\uAC04\uC774\uB098 \uB370\uC774\uD130 \uC785\uB825 \uBC29\uBC95\uC5D0 \uB530\uB77C \uC77C\uAD04\uCC98\uB9AC \uC2DC\uC2A4\uD15C, \uB2E4\uC911 \uD504\uB85C\uADF8\uB798\uBC0D \uC2DC\uC2A4\uD15C, \uB2E4\uC911 \uCC98\uB9AC \uC2DC\uC2A4\uD15C, \uC2DC\uBD84\uD560 \uC2DC\uC2A4\uD15C, \uC2E4\uC2DC\uAC04 \uCC98\uB9AC \uC2DC\uC2A4\uD15C, \uBD84\uC0B0 \uCC98\uB9AC \uC2DC\uC2A4\uD15C\uC73C\uB85C \uAD6C\uBD84\uD560 \uC218\uC788\uB2E4.</p><p>\uC6B4\uC601\uCCB4\uC81C\uB294 \uB3D9\uC2DC\uC791\uC5C5\uC744 \uC9C0\uC6D0\uC5EC\uBD80\uC5D0 \uB530\uB77C \uB2E8\uC77C\uC791\uC5C5(single tasking)\uC6A9 \uC6B4\uC601\uCCB4\uC81C\uC640 \uB2E4\uC911\uC791\uC5C5(multi tasking)\uC6A9 \uC6B4\uC601\uCCB4\uC81C\uB85C \uB098\uB20C \uC218\uC788\uB2E4. \uB2E8\uC77C \uC791\uC5C5\uC6A9 \uC6B4\uC601\uCCB4\uC81C\uB294 \uD55C\uBC88\uC5D0 \uD558\uB098\uC758 \uD504\uB85C\uADF8\uB7A8\uB9CC \uC2E4\uD589 \uC2DC\uD0AC \uC218 \uC788\uB294 \uC6B4\uC601\uCCB4\uC81C\uB85C \uCD08\uCC3D\uAE30 \uC6B4\uC601\uCCB4\uC81C\uB294 \uB300\uAC8C \uB2E8\uC77C \uC791\uC5C5\uC6A9 \uC6B4\uC601\uCCB4\uC81C\uC758 \uD574\uB2F9 \uB418\uC5C8\uB2E4(ex.DOS). \uADF8\uB7EC\uB098 \uCD5C\uADFC\uC5D0\uB294 \uB3D9\uC2DC\uC5D0 2\uAC1C \uC774\uC0C1\uC758 \uD504\uB85C\uADF8\uB7A8\uC744 \uCC98\uB9AC \uD560 \uC218 \uC788\uB294 \uB2E4\uC911\uC791\uC5C5\uC744 \uC9C0\uC6D0\uD55C\uB2E4.(ex. MS\uC708\uB3C4\uC6B0, \uC720\uB2C9\uC2A4) pc\uD658\uACBD\uC5D0\uC11C \uB2E4\uC591\uD55C \uD504\uB85C\uADF8\uB7A8\uC744 \uC11C\uB85C \uB2E4\uB978\uD658\uACBD\uC5D0 \uB744\uC5B4 \uB193\uC744 \uC218 \uC788\uB2E4\uB294 \uAC83\uC774 \uC708\uB3C4\uC6B0\uAC00 \uB2E4\uC911 \uC791\uC5C5\uC6A9 \uC6B4\uC601\uCCB4\uC81C\uC784\uC744 \uB9D0\uD574\uC8FC\uB294 \uAC83\uC774\uB2E4</p><p>\uC6B4\uC601\uCCB4\uC81C\uAC00 \uB2E4\uC911\uC791\uC5C5\uC744 \uCC98\uB9AC\uD560\uB54C \uC5EC\uB7EC\uD504\uB85C\uADF8\uB7A8\uC774 CPU\uC640 \uBA54\uBAA8\uB9AC\uB97C \uACF5\uC720 \uD558\uAC8C \uB41C\uB2E4. CPU\uAC00 \uD558\uB098\uBC16\uC5D0 \uC5C6\uC5B4 \uD558\uB098\uC758 \uD504\uB85C\uADF8\uB7A8\uB9CC CPU\uC5D0\uC11C \uC2E4\uD589\uB420 \uC218 \uC788\uB354\uB77C\uB3C4 CPU\uC758 \uCC98\uB9AC\uC18D\uB3C4\uAC00 \uC6CC\uB099 \uBE68\uB77C \uC218 \uBC00\uB9AC \uCD08(ms) \uC774\uB0B4\uC758 \uC9E7\uC740 \uAC04\uACA9\uC73C\uB85C \uC5EC\uB7EC \uD504\uB85C\uADF8\uB7A8\uB4E4\uC774 cpu\uC5D0\uC11C \uBC88\uAC08\uC544 \uC2E4\uD589 \uB418\uC5B4 \uC0AC\uC6A9\uC790 \uC785\uC7A5\uC5D0\uC11C \uC5EC\uB7EC \uD504\uB85C\uADF8\uB7A8\uC774 \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uB514\uB294 \uAC83 \uCC98\uB7FC \uBCF4\uC774\uAC8C \uB41C\uB2E4.<strong>\uC774\uC640 \uAC19\uC774 CPU\uC758 \uC791\uC5C5\uC2DC\uAC04\uC744 \uC5EC\uB7EC \uD504\uB85C\uADF8\uB7A8\uB4E4\uC774 \uC870\uAE08\uC529 \uB098\uB204\uC5B4 \uC4F0\uB294 \uC2DC\uC2A4\uD15C\uC744 \uC2DC\uBD84\uD560 \uC2DC\uC2A4\uD15C(system sharing sysrem)\uC774\uB77C\uACE0 \uBD80\uB978\uB2E4.</strong></p><p>CPU\uC640 \uB2EC\uB9AC \uBA54\uBAA8\uB9AC\uC758 \uACBD\uC6B0 \uC5EC\uB7EC \uD504\uB85C\uADF8\uB7A8\uB4E4\uC774 \uC870\uAE08\uC529 \uBA54\uBAA8\uB9AC \uACF5\uAC04\uC744 \uBCF4\uC720\uD558\uC5EC \uB3D9\uC2DC\uC5D0 \uBA54\uBAA8\uB9AC\uC5D0 \uC62C\uB77C\uAC00 \uC788\uC744 \uC218 \uC788\uB2E4. <strong>\uBA54\uBAA8\uB9AC \uACF5\uAC04\uC744 \uBD84\uD560 \uD574 \uC5EC\uB7EC \uD504\uB85C\uADF8\uB7A8\uB4E4\uC744 \uB3D9\uC2DC\uC5D0 \uBA54\uBAA8\uB9AC\uC5D0 \uC62C\uB824\uB193\uACE0 \uCC98\uB9AC\uD558\uB294 \uC2DC\uC2A4\uD15C\uC744 \uB2E4\uC911 \uD504\uB85C\uADF8\uB798\uBC0D \uC2DC\uC2A4\uD15C(multi-programming system)\uC774\uB77C\uACE0 \uBD80\uB978\uB2E4.</strong></p><p>\uB300\uD654\uD615 \uC2DC\uC2A4\uD15C\uC774\uB780 \uC0AC\uC6A9\uC790 \uAC1C\uC778\uC758 \uAD00\uC810\uC5D0\uC11C \uAC01 \uD504\uB85C\uADF8\uB798\uC5D0\uB300\uD55C \uD0A4\uBCF4\uB4DC \uC785\uB825\uC758 \uACB0\uACFC\uB97C \uACE7\uBC14\uB85C \uD654\uBA74\uC5D0 \uBCF4\uC5EC\uC8FC\uB294 \uAC83\uACFC \uAC19\uC740 \uC2DC\uC2A4\uD15C\uC774\uB2E4.</p><p>\uB2E4\uC911 \uC791\uC5C5(2\uAC1C \uC774\uC0C1 \uD504\uB85C\uADF8\uB7A8), \uC2DC\uBD84\uD560(CPU \uC791\uC5C5\uC2DC\uAC04 \uB098\uB204\uC5B4 \uC0AC\uC6A9), \uB2E4\uC911 \uD504\uB85C\uADF8\uB798\uBC0D(\uC5EC\uB7EC \uD504\uB85C\uADF8\uB7A8 \uBA54\uBAA8\uB9AC\uC5D0 \uC62C\uB9BC) , \uB300\uD654\uD615 \uC2DC\uC2A4\uD15C(\uC0AC\uC6A9\uC790 \uC785\uC7A5\uC5D0\uC11C \uC989\uAC01 \uBC18\uC751) \uB4F1\uC740 \uBAA8\uB450 \uC5EC\uB7EC \uD504\uB85C\uADF8\uB7A8\uC774 \uD558\uB098\uC758 \uCEF4\uD4E8\uD130\uC5D0\uC11C \uB3D9\uC2DC\uC5D0 \uC2E4\uD589 \uB418\uBA70 \uC720\uC0AC\uD55C \uC758\uBBF8\uB85C \uC0AC\uC6A9\uB41C\uB2E4.</p><p>(\uCC38\uACE0)\uB2E4\uC911\uCC98\uB9AC\uAE30 \uC2DC\uC2A4\uD15C: \uD558\uB098\uC758 \uCEF4\uD4E8\uD130 \uC548\uC5D0 CPU\uAC00 \uC5EC\uB7EC\uAC1C \uC124\uCE58\uB41C \uACBD\uC6B0\uB97C \uB9D0\uD55C\uB2E4. \uC55E\uC758 \uB2E8\uC5B4\uB4E4\uACFC \uC758\uBBF8\uAC00 \uB2E4\uB974\uB2E4. (\uCC38\uACE0)\uB2E4\uC911\uC0AC\uC6A9\uC790 \uC9C0\uC6D0 \uC5EC\uBD80 \uB530\uB77C \uB2E8\uC77C \uC0AC\uC6A9\uC790\uC6A9 \uC6B4\uC601\uCCB4\uC81C vs \uB2E4\uC911 \uC0AC\uC6A9\uC790\uC6A9 \uC6B4\uC601\uCCB4\uC81C\uB85C \uAD6C\uBD84\uD558\uAE30\uB3C4 \uD55C\uB2E4.<br> ex. \uC708\uB3C4\uC6B0 dos ex. \uC774\uBA54\uC77C\uC11C\uBC84 \uC6F9\uC11C\uBC84\uB4E4 \uB4F1 \uC11C\uBC84\uB77C\uACE0 \uBD80\uB974\uB294 \uCEF4\uD4E8\uD130</p><h2 id="\u110C\u1161\u11A8\u110B\u1165\u11B8\u110E\u1165\u1105\u1175-\u1107\u1161\u11BC\u1109\u1175\u11A8\u110B\u1166-\u1104\u1161\u1105\u1161-\u110B\u1175\u11AF\u1100\u116A\u11AF\u110E\u1165\u1105\u1175-batch-processing-vs-\u1109\u1175\u1107\u116E\u11AB\u1112\u1161\u11AF-\u1107\u1161\u11BC\u1109\u1175\u11A8-vs-\u1109\u1175\u11AF\u1109\u1175\u1100\u1161\u11AB-\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166" tabindex="-1"><a class="header-anchor" href="#\u110C\u1161\u11A8\u110B\u1165\u11B8\u110E\u1165\u1105\u1175-\u1107\u1161\u11BC\u1109\u1175\u11A8\u110B\u1166-\u1104\u1161\u1105\u1161-\u110B\u1175\u11AF\u1100\u116A\u11AF\u110E\u1165\u1105\u1175-batch-processing-vs-\u1109\u1175\u1107\u116E\u11AB\u1112\u1161\u11AF-\u1107\u1161\u11BC\u1109\u1175\u11A8-vs-\u1109\u1175\u11AF\u1109\u1175\u1100\u1161\u11AB-\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166" aria-hidden="true">#</a> \uC791\uC5C5\uCC98\uB9AC \uBC29\uC2DD\uC5D0 \uB530\uB77C: \uC77C\uAD04\uCC98\uB9AC(batch processing) vs \uC2DC\uBD84\uD560 \uBC29\uC2DD vs \uC2E4\uC2DC\uAC04 \uC6B4\uC601\uCCB4\uC81C</h2><ul><li><p>\uC77C\uAD04\uCC98\uB9AC : \uC694\uCCAD\uB41C \uC791\uC5C5\uC744 \uC77C\uC815\uB7C9\uC529 \uBAA8\uC544\uC11C \uD55C\uAEBC\uBC88\uC5D0 \uCC98\uB9AC\uD558\uB294 \uBC29\uC2DD\uC774\uB2E4. \uC751\uB2F5\uC2DC\uAC04\uC774 \uAE38\uB2E4</p></li><li><p>\uC2DC\uBD84\uD560 \uBC29\uC2DD: \uC5EC\uB7EC\uC791\uC5C5\uC744 \uC218\uD589 \uD560 \uB54C \uCEF4\uD4E8\uD130\uC758 \uCC98\uB9AC\uB2A5\uB825\uC744 \uC77C\uC815\uD55C \uC2DC\uAC04 \uB2E8\uC704\uB85C \uBD84\uD560 \uD574 \uC0AC\uC6A9\uD558\uB294 \uBC29\uC2DD\uC774\uB2E4.</p><ul><li>\uD604\uB300 \uB300\uBD80\uBD84 \uC0AC\uC6A9. \uC751\uB2F5\uC2DC\uAC04\uC774 \uC9E7\uC544 \uC0AC\uC6A9\uC790\uAC00 \uCEF4\uD4E8\uD130\uB97C \uD63C\uC790 \uB3C5\uC810\uC801\uC73C\uB85C \uC0AC\uC6A9\uD558\uB294 \uAC83\uCC98\uB7FC \uC0DD\uAC01\uD560 \uC218 \uC788\uB2E4.(\uB300\uD654\uD615\uC2DC\uC2A4\uD15C)</li></ul></li><li><p>\uC2E4\uC2DC\uAC04 \uC6B4\uC601\uCCB4\uC81C : \uC815\uD574\uC9C4 \uC2DC\uAC04\uC548\uC5D0 \uC5B4\uB5A0\uD55C \uC77C\uC774 \uBC18\uB4DC\uC2DC \uCC98\uB9AC\uB428\uC744 \uBCF4\uC7A5\uD574\uC57C \uD558\uB294 \uC2DC\uC2A4\uD15C</p><ul><li><p>\uC5F0\uC131 \uC2E4\uC2DC\uAC04 \uC2DC\uC2A4\uD15C(soft realtime system) : \uBA40\uD2F0\uBBF8\uB514\uC5B4 \uC2A4\uD2B8\uB9AC\uBC0D \uC2DC\uC2A4\uD15C\uACFC \uAC19\uC774 \uB370\uC774\uD130\uAC00 \uC815\uD574\uC9C4 \uC2DC\uAC04 \uB2E8\uC704\uB85C \uC804\uB2EC\uB418\uC5B4\uC57C \uC62C\uBC14\uB978 \uAE30\uB2A5\uC744 \uC218\uD589\uD560 \uC218\uC788\uB294 \uC2DC\uC2A4\uD15C</p></li><li><p>\uACBD\uC131 \uC2E4\uC2DC\uAC04 \uC2DC\uC2A4\uD15C (hard realtime system) : \uC8FC\uC5B4\uC9C4 \uC2DC\uAC04\uC744 \uC9C0\uD0A4\uC9C0 \uBAA8\uD560 \uACBD\uC6B0 \uB9E4\uC6B0 \uC704\uD5D8\uD55C \uACB0\uACFC\uB97C \uCD08\uB798 \uD560 \uAC00\uB2A5\uC131\uC774 \uC788\uB294 \uC2DC\uC2A4\uD15C\uC744 \uB9D0\uD55C\uB2E4. ex. \uB85C\uCF13 \uC6D0\uC790\uB85C \uC81C\uC5B4 \uC2DC\uC2A4\uD15C</p></li></ul></li></ul>',12),p=[a];function c(l,n){return e(),t("div",null,p)}var _=s(r,[["render",c],["__file","ch5-\uC6B4\uC601\uCCB4\uC81C-\uC720\uD615.html.vue"]]);export{_ as default};
