import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as i,d as r}from"./app.d6b42b88.js";var h="/blog/assets/6-replication-partiton-leader.aab5b038.png";const d={},p=r('<h2 id="\u1100\u1162\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#\u1100\u1162\u110B\u116D" aria-hidden="true">#</a> \uAC1C\uC694</h2><p>\uC55E\uC11C 5\uC7A5\uC5D0\uC11C \uB2E4\uB8EC \uBCF5\uC81C\uB294 \uB3D9\uC77C\uD55C \uB370\uC774\uD130\uC758 \uBCF5\uC0AC\uBCF8 \uC5EC\uB7EC \uAC1C\uB97C \uB2E4\uB978 \uB178\uB4DC\uC5D0 \uC800\uC7A5\uD558\uB294 \uAC1C\uB150\uC774\uC5C8\uB2E4 \uADF8\uB7EC\uB098 \uB370\uC774\uD130 \uC14B\uC774 \uB9E4\uC6B0 \uD06C\uAC70\uB098 \uC9C8\uC758 \uCC98\uB9AC\uB7C9\uC774 \uB9E4\uC6B0 \uB192\uB2E4\uBA74 \uBCF5\uC81C \uB9CC\uC73C\uB85C \uBD80\uC871\uD558\uACE0 \uB370\uC774\uD130\uC14B\uC744 \uD30C\uD2F0\uC158\uC73C\uB85C \uCABC\uAC24 \uD544\uC694\uAC00 \uC788\uB2E4 \uC774 \uC791\uC5C5\uC744 \uD30C\uD2F0\uC154\uB2DD \uB610\uB294 \uC0E4\uB529\uC774\uB77C\uACE0 \uD55C\uB2E4.</p><div class="custom-container info"><p class="custom-container-title">\uC6A9\uC5B4 \uD63C\uB3D9 \uC8FC\uC758</p><p>\uD30C\uD2F0\uC154\uB2DD == \uC0E4\uB529 == \uB300\uC6A9\uB7C9 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB97C \uC758\uB3C4\uC801\uC73C\uB85C \uC791\uC740 \uB2E8\uC704\uB85C \uCABC\uAC1C\uB294 \uBC29\uBC95<br> \uD30C\uD2F0\uC158 == \uC0E4\uB4DC == \uCABC\uAC1C\uC9C4 \uB2E8\uC704</p><p>MongoDB\uC5D0\uC11C\uB294 \uC0E4\uB529\uC774\uB780 \uB2E8\uC5B4\uB85C \uC0AC\uC6A9\uD55C\uB2E4. \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB9C8\uB2E4 \uBD80\uB974\uB294 \uBA85\uCE6D\uC740 \uB2E4\uB974\uB2E4.</p></div><p>\uBCF4\uD1B5 \uD30C\uD2F0\uC154\uB2DD\uC744 \uBCF5\uC81C\uC640 \uD568\uAED8 \uC801\uC6A9\uD574 \uAC01 \uD30C\uD2F0\uC158\uC758 \uBCF5\uC0AC\uBCF8\uC744 \uC5EC\uB7EC \uB178\uB4DC\uC5D0 \uC800\uC7A5\uD55C\uB2E4. \uC55E\uC11C 5\uC7A5\uC5D0\uC11C\uB3C4 \uADF8\uB9BC\uC73C\uB85C \uC124\uBA85\uD55C \uC801\uC774 \uC788\uB294\uB370 \uC774\uC81C \uB9AC\uB354 \uD314\uB85C\uC6CC \uBCF5\uC81C \uBAA8\uB378\uC744 \uACE0\uB824\uD574\uC11C \uC0AC\uC6A9\uD55C\uB2E4\uBA74 \uD30C\uD2F0\uC154\uB2DD\uACFC \uBCF5\uC81C\uC758 \uC870\uD569\uC740 \uC544\uB798 \uADF8\uB9BC\uACFC \uAC19\uC740 \uD615\uD0DC\uAC00 \uB41C\uB2E4.<br><img src="'+h+'" alt="replication-partiton-leader-follower" loading="lazy"> \uAC01 \uD30C\uD2F0\uC158 \uB9AC\uB354\uB294 \uD558\uB098\uC758 \uB178\uB4DC\uC5D0 \uD560\uB2F9\uB418\uACE0 \uD314\uB85C\uC6CC\uB4E4\uC740 \uB2E4\uB978 \uB178\uB4DC\uC5D0 \uD560\uB2F9\uB41C\uB2E4. \uAC01 \uB178\uB4DC\uB294 \uC5B4\uB5A4 \uD30C\uD2F0\uC158\uC5D0\uAC8C\uB294 \uB9AC\uB354\uC774\uBA74\uC11C \uB2E4\uB978 \uD30C\uD2F0\uC158\uC5D0\uAC8C\uB294 \uD314\uB85C\uC6CC\uAC00 \uB420 \uC218 \uC788\uB2E4.</p><p>\uC774\uBC88 \uC7A5\uC5D0\uC11C\uB294 1) \uB300\uC6A9\uB7C9 \uB370\uC774\uD130\uC14B\uC744 \uD30C\uD2F0\uC154\uB2DD \uD558\uB294 \uBA87 \uAC00\uC9C0 \uBC29\uBC95 2) \uB370\uC774\uD130 \uC0C9\uC778\uACFC \uD30C\uD2F0\uC154\uB2DD\uC758 \uC0C1\uD638\uC791\uC6A9 3) \uD074\uB7EC\uC2A4\uD130\uC5D0 \uB178\uB4DC \uCD94\uAC00 \uBC0F \uC81C\uAC70 \uC2DC \uD544\uC694\uD55C \uC7AC\uADE0\uD615\uD654 4) \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uAC00 \uC5B4\uB5BB\uAC8C \uC694\uCCAD\uC744 \uC62C\uBC14\uB978 \uD30C\uD2F0\uC158\uC5D0 \uC804\uB2EC\uD558\uACE0 \uC9C8\uC758\uB97C \uC2E4\uD589\uD558\uB294\uC9C0 \uC774 4\uAC00\uC9C0\uB97C \uAC1C\uB7B5\uC801\uC73C\uB85C \uACF5\uBD80\uD55C\uB2E4.</p><h2 id="\u110F\u1175-\u1100\u1161\u11B9-\u1103\u1166\u110B\u1175\u1110\u1165-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u110F\u1175-\u1100\u1161\u11B9-\u1103\u1166\u110B\u1175\u1110\u1165-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" aria-hidden="true">#</a> \uD0A4-\uAC12 \uB370\uC774\uD130 \uD30C\uD2F0\uC154\uB2DD</h2><p>\uB300\uC6A9\uB7C9\uC758 \uB370\uC774\uD130\uB97C \uD30C\uD2F0\uC154\uB2DD \uD560 \uB54C \uC5B4\uB5A4 \uB808\uCF54\uB4DC\uB97C \uC5B4\uB290 \uB178\uB4DC\uC5D0 \uC800\uC7A5\uD560\uC9C0 \uC5B4\uB5BB\uAC8C \uACB0\uC815\uD574\uC57C \uD560\uAE4C? <strong>\uD30C\uD2F0\uC154\uB2DD\uC758 \uBAA9\uC801\uC740 \uB370\uC774\uD130\uC640 \uC9C8\uC758 \uBD80\uD558\uB97C \uB178\uB4DC \uC0AC\uC774\uC5D0 \uACE0\uB974\uAC8C \uBD84\uC0B0\uC2DC\uD0A4\uB294 \uAC83\uC774\uB2E4.</strong> \uADFC\uB370 \uD30C\uD2F0\uC154\uB2DD\uC774 \uACE0\uB974\uAC8C \uC774\uB8E8\uC5B4\uC9C0\uC9C0 \uC54A\uC544 \uB2E4\uB978 \uD30C\uD2F0\uC158\uBCF4\uB2E4 \uB370\uC774\uD130\uAC00 \uB9CE\uAC70\uB098 \uC9C8\uC758\uB97C \uB9CE\uC774 \uBC1B\uB294 \uD30C\uD2F0\uC158\uC774 \uC788\uB2E4\uBA74 \uC3E0\uB838\uB2E4(skewed)\uB77C\uACE0 \uB9D0\uD55C\uB2E4. \uC774\uB7F0 \uBD88\uADE0\uD615\uD558\uAC8C \uBD80\uD558\uAC00 \uB192\uC740 \uD30C\uD2F0\uC158\uC744 \uD56B\uC2A4\uD31F\uC774\uB77C\uACE0 \uD55C\uB2E4.</p><p>\uD56B\uC2A4\uD31F\uC744 \uD68C\uD53C\uD558\uB294 \uAC00\uC7A5 \uB2E8\uC21C\uD55C \uBC29\uBC95\uC740 \uB808\uCF54\uB4DC\uB97C \uD560\uB2F9\uD560 \uB178\uB4DC\uB97C \uBB34\uC791\uC704\uB85C \uC120\uD0DD\uD558\uB294 \uAC83\uC774\uB2E4. \uADF8\uB7EC\uBA74 \uB370\uC774\uD130\uAC00 \uB178\uB4DC\uB4E4 \uC0AC\uC774\uC5D0 \uB9E4\uC6B0 \uACE0\uB974\uAC8C \uBD84\uC0B0\uB418\uC9C0\uB9CC \uC77D\uAE30 \uC791\uC5C5 \uC2DC \uD574\uB2F9 \uB808\uCF54\uB4DC\uAC00 \uC5B4\uB290 \uB178\uB4DC\uC5D0 \uC800\uC7A5\uB410\uB294\uC9C0 \uC54C \uC218 \uC5C6\uC73C\uBBC0\uB85C \uBAA8\uB4E0 \uB178\uB4DC\uC5D0 \uBCD1\uB82C \uC9C8\uC758\uB97C \uC2E4\uD589\uD574\uC57C \uD55C\uB2E4\uB294 \uB2E8\uC810\uC774 \uC788\uB2E4.</p><p>\uB354 \uC88B\uC740 \uBC29\uBC95\uC740 \uD0A4-\uAC12 \uB370\uC774\uD130 \uBAA8\uB378\uC744 \uC0AC\uC6A9\uD558\uB294 \uAC83\uC774\uB2E4. \uC774 \uBAA8\uB378\uC5D0\uC11C\uB294 \uD56D\uC0C1 \uAE30\uBCF8\uD0A4\uB97C \uD1B5\uD574 \uB808\uCF54\uB4DC\uC5D0 \uC811\uADFC\uD55C\uB2E4. \uD0A4\uB294 \uC5B8\uC81C\uB098 \uC815\uB82C\uB418\uC5B4 \uC788\uAE30\uC5D0 \uC6D0\uD558\uB294 \uAC12\uC744 \uBE68\uB9AC \uCC3E\uC744 \uC218 \uC788\uB2E4.</p><h3 id="_1-\u110F\u1175-\u1107\u1165\u11B7\u110B\u1171-\u1100\u1175\u110C\u116E\u11AB-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_1-\u110F\u1175-\u1107\u1165\u11B7\u110B\u1171-\u1100\u1175\u110C\u116E\u11AB-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" aria-hidden="true">#</a> 1. \uD0A4 \uBC94\uC704 \uAE30\uC900 \uD30C\uD2F0\uC154\uB2DD</h3><p>\uD30C\uD2F0\uC154\uB2DD \uD558\uB294 \uBC29\uBC95 \uC911 \uD558\uB098\uB294 \uBC31\uACFC\uC0AC\uC804\uCC98\uB7FC \uAC01 \uD30C\uD2F0\uC158\uC5D0 \uC5F0\uC18D\uB41C \uBC94\uC704\uC758 \uD0A4\uB97C \uD560\uB2F9\uD558\uB294 \uAC83\uC774\uB2E4. \uAC01 \uBC94\uC704\uB4E4 \uC0AC\uC774\uC758 \uACBD\uACC4\uB97C \uC54C\uBA74 \uC5B4\uB5A4 \uD0A4\uAC00 \uC5B4\uB290 \uD30C\uD2F0\uC158\uC5D0 \uC18D\uD558\uB294\uC9C0 \uC27D\uAC8C \uCC3E\uC744 \uC218 \uC788\uB2E4. \uB610 \uC5B4\uB5A4 \uD30C\uD2F0\uC158\uC774 \uC5B4\uB290 \uB178\uB4DC\uC5D0 \uD560\uB2F9\uB410\uB294\uC9C0 \uC54C\uBA74 \uC801\uC808\uD55C \uB178\uB4DC\uB85C \uC694\uCCAD\uC744 \uC9C1\uC811 \uBCF4\uB0BC \uC218 \uC788\uB2E4.</p><p>\uB370\uC774\uD130\uAC00 \uACE0\uB974\uAC8C \uBD84\uD3EC\uD558\uC9C0 \uC54A\uC744 \uC218\uB3C4 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uD0A4 \uBC94\uC704\uAC00 \uBC18\uB4DC\uC2DC \uB3D9\uC77C\uD560 \uD544\uC694\uB294 \uC5C6\uB2E4. \uB370\uC774\uD130\uB97C \uACE0\uB974\uAC8C \uBD84\uC0B0\uC2DC\uD0A4\uB824\uBA74 \uD30C\uD2F0\uC158 \uACBD\uACC4\uB97C \uB370\uC774\uD130\uC5D0 \uB9DE\uCDB0 \uC870\uC815\uD574\uC57C \uD55C\uB2E4. \uD30C\uD2F0\uC158 \uACBD\uACC4\uB294 \uAD00\uB9AC\uC790\uAC00 \uC218\uB3D9\uC73C\uB85C \uC120\uD0DD\uD558\uAC70\uB098 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0\uC11C \uC790\uB3D9\uC73C\uB85C \uC120\uD0DD\uB418\uAC8C \uD560 \uC218 \uC788\uB2E4.</p><p>\uAC01 \uD30C\uD2F0\uC158 \uB0B4\uC5D0\uC11C\uB294 \uD0A4\uB97C \uC815\uB82C\uB41C \uC21C\uC11C\uB85C \uC800\uC7A5\uD560 \uC218 \uC788\uB2E4.(SS Table, LSM Tree) <strong>\uD0A4 \uBC94\uC704 \uAE30\uC900 \uD30C\uD2F0\uC154\uB2DD\uC740 \uD2B9\uC815\uD55C \uC811\uADFC \uD328\uD134\uC774 \uD56B\uC2A4\uD31F\uC744 \uC720\uBC1C\uD560 \uC218 \uC788\uB2E4\uB294 \uB2E8\uC810\uC774 \uC788\uB2E4.</strong> \uB9CC\uC57D \uD0C0\uC784 \uC2A4\uD0DC\uD504\uAC00 \uD0A4\uB77C\uBA74 \uD30C\uD2F0\uC158\uC740 \uC2DC\uAC04 \uBC94\uC704\uC5D0 \uB300\uC751\uB3FC\uC11C \uC13C\uC11C \uB370\uC774\uD130 \uBCA0\uC774\uC2A4 \uAC19\uC740 \uACBD\uC6B0\uB294 \uB370\uC774\uD130 \uC3E0\uB9BC\uC758 \uD604\uC0C1\uC774 \uB098\uD0C0\uB0A0 \uC218 \uC788\uB2E4. \uD574\uB2F9 \uBB38\uC81C\uB97C \uD68C\uD53C\uD558\uB824\uBA74 \uD0A4\uC758 \uCCAB \uBC88\uC9F8 \uC694\uC18C\uB85C \uD0C0\uC784 \uC2A4\uD0DC\uD504\uAC00 \uC544\uB2CC \uB2E4\uB978 \uAC83\uC744 \uC0AC\uC6A9\uD574\uC57C \uD55C\uB2E4. \uC774\uB97C \uD14C\uBA70 \uD0C0\uC784\uC2A4\uD0EC\uD504 \uC55E\uC5D0 \uC13C\uC11C \uC774\uB984\uC744 \uBD99\uC5EC\uC11C \uD30C\uD2F0\uC154\uB2DD \uC2DC \uC13C\uC11C \uC774\uB984\uC744 \uBA3C\uC800 \uC0AC\uC6A9\uD55C \uD6C4 \uC2DC\uAC04\uC744 \uC0AC\uC6A9\uD558\uAC8C \uD560 \uC218 \uC788\uB2E4. \uADF8\uB7EC\uB098 \uC774 \uACBD\uC6B0\uB294 \uB9CC\uC57D \uB3D9\uC77C\uD55C \uC2DC\uAC04\uB300\uC758 \uC5EC\uB7EC \uC13C\uC11C \uAC12\uC744 \uC5BB\uC73C\uB824 \uD560 \uB54C \uAC01 \uC13C\uC11C \uC774\uB984\uB9C8\uB2E4 \uBCC4\uAC1C\uC758 \uBC94\uC704 \uC9C8\uC758\uAC00 \uD544\uC694\uD574\uC9C4\uB2E4.</p><h3 id="_2-\u110F\u1175\u110B\u1174-\u1112\u1162\u1109\u1175\u1100\u1161\u11B9-\u1100\u1175\u110C\u116E\u11AB-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_2-\u110F\u1175\u110B\u1174-\u1112\u1162\u1109\u1175\u1100\u1161\u11B9-\u1100\u1175\u110C\u116E\u11AB-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" aria-hidden="true">#</a> 2. \uD0A4\uC758 \uD574\uC2DC\uAC12 \uAE30\uC900 \uD30C\uD2F0\uC154\uB2DD</h3><p>\uC88B\uC740 \uD574\uC2DC \uD568\uC218\uB294 \uC3E0\uB9B0 \uB370\uC774\uD130\uB97C \uC785\uB825\uC73C\uB85C \uBC1B\uC544 \uADE0\uC77C\uD558\uAC8C \uBD84\uC0B0\uB418\uAC8C \uD55C\uB2E4. \uD0A4\uC5D0 \uC801\uD569\uD55C \uD574\uC2DC \uD568\uC218\uB97C \uAD6C\uD588\uB2E4\uBA74 \uAC01 \uD30C\uD2F0\uC158\uC5D0 (\uD0A4 \uBC94\uC704 \uB300\uC2E0) \uD574\uC2DC\uAC12 \uBC94\uC704\uB97C \uD560\uB2F9\uD558\uACE0 \uD574\uC2DC\uAC12\uC774 \uD30C\uD2F0\uC158\uC758 \uBC94\uC704\uC5D0 \uC18D\uD558\uB294 \uBAA8\uB4E0 \uD0A4\uB97C \uADF8 \uD30C\uD2F0\uC158\uC5D0 \uD560\uB2F9\uD558\uBA74 \uB41C\uB2E4. \uC774 \uAE30\uBC95\uC740 \uD0A4\uB97C \uD30C\uD2F0\uC158 \uC0AC\uC774\uC5D0 \uADE0\uC77C\uD558\uAC8C \uBD84\uC0B0\uC2DC\uD0A4\uB294 \uB370 \uC88B\uB2E4. \uADF8\uB7EC\uB098 <strong>\uC774 \uAE30\uBC95\uC744 \uC0AC\uC6A9\uD558\uBA74 \uBC94\uC704 \uC9C8\uC758\uB97C \uD6A8\uC728\uC801\uC73C\uB85C \uC2E4\uD589\uD560 \uC218 \uC788\uB294 \uB2A5\uB825\uC744 \uC783\uC5B4\uBC84\uB9B0\uB2E4. \uC778\uC811\uD588\uB358 \uD0A4\uB4E4\uC774 \uBAA8\uB4E0 \uD30C\uD2F0\uC158\uC5D0 \uD769\uC5B4\uC838\uC11C \uC815\uB82C\uC21C\uC11C\uAC00 \uC720\uC9C0\uB418\uC9C0 \uC54A\uB294\uB2E4</strong></p><h3 id="_3-\u110A\u1169\u11AF\u1105\u1175\u11B7-\u110C\u1161\u11A8\u110B\u1165\u11B8-\u1107\u116E\u1112\u1161\u110B\u116A-\u1112\u1161\u11BA\u1109\u1173\u1111\u1161\u11BA-\u110B\u116A\u11AB\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#_3-\u110A\u1169\u11AF\u1105\u1175\u11B7-\u110C\u1161\u11A8\u110B\u1165\u11B8-\u1107\u116E\u1112\u1161\u110B\u116A-\u1112\u1161\u11BA\u1109\u1173\u1111\u1161\u11BA-\u110B\u116A\u11AB\u1112\u116A" aria-hidden="true">#</a> 3. \uC3E0\uB9BC \uC791\uC5C5 \uBD80\uD558\uC640 \uD56B\uC2A4\uD31F \uC644\uD654</h3><p>\uC55E\uC11C \uB9D0\uD55C \uB300\uB85C \uD0A4\uB97C \uD574\uC2F1\uD574\uC11C \uD30C\uD2F0\uC158\uC744 \uC815\uD558\uBA74 \uD56B\uC2A4\uD31F\uC744 \uC904\uC774 \uB294\uB370 \uB3C4\uC6C0\uC774 \uB41C\uB2E4 \uD558\uC9C0\uB9CC \uD56B\uC2A4\uD31F\uC744 \uC644\uBCBD\uD788 \uC81C\uAC70\uD560 \uC218\uB294 \uC5C6\uB2E4. \uD56D\uC0C1 \uB3D9\uC77C\uD55C \uD0A4\uB97C \uC77D\uACE0 \uC4F0\uB294 \uADF9\uB2E8\uC801\uC778 \uC0C1\uD669\uC5D0\uC11C\uB294 \uBAA8\uB4E0 \uC694\uCCAD\uC774 \uB3D9\uC77C\uD55C \uD30C\uD2F0\uC158\uC73C\uB85C \uC3E0\uB9AC\uAC8C \uB41C\uB2E4.</p><p>\uC720\uBA85\uC778 \uC608\uC2DC\uAC00 \uADF8\uB7F0 \uACBD\uC6B0\uC774\uB2E4. \uC18C\uC15C\uBBF8\uB514\uC5B4 \uC0AC\uC774\uD2B8\uC5D0\uC11C \uC218\uBC31\uB9CC\uBA85\uC758 \uD314\uB85C\uC6CC\uB97C \uAC70\uB290\uB9B0 \uC720\uBA85\uC778\uC774 \uBB54\uAC00\uB97C \uD558\uAC8C \uB418\uBA74 \uD6C4\uD3ED\uD48D\uC774 \uBC1C\uC0DD\uD560 \uC218 \uC788\uB2E4. \uB3D9\uC77C\uD55C \uD0A4\uC5D0 \uB300\uD55C \uC5C4\uCCAD\uB09C \uC561\uC138\uC2A4\uB294 \uD604\uB300 \uB370\uC774\uD130 \uC2DC\uC2A4\uD15C\uC774 \uD574\uACB0\uD560 \uBC29\uBC95\uC774 \uC5C6\uB2E4. \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C \uC3E0\uB9BC\uC744 \uC644\uD654\uD574\uC57C \uD55C\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uD0A4\uC758 \uC2DC\uC791\uC774\uB098 \uB05D\uC5D0 \uC784\uC758\uC758 \uC22B\uC790 10\uC9C4\uC218 \uB450 \uAC1C\uB97C \uBD99\uC5EC\uC11C 100\uAC1C\uC758 \uB2E4\uB978 \uD0A4\uB85C \uBD84\uC0B0\uC2DC\uD0A4\uB294 \uB4F1\uC758 \uBC29\uBC95\uC774 \uC788\uB2E4. \uADF8\uB7EC\uB098 \uC774 \uBC29\uBC95\uB3C4 \uC77D\uAE30\uB97C \uC218\uD589\uD560 \uB54C \uCD94\uAC00 \uC791\uC5C5\uC774 \uD544\uC694\uD558\uBBC0\uB85C \uD2B8\uB808\uC774\uB4DC\uC624\uD504\uAC00 \uC788\uB294 \uC791\uC5C5\uC774\uB2E4.</p><h2 id="\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC\u1100\u116A-\u1107\u1169\u110C\u1169-\u1109\u1162\u11A8\u110B\u1175\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC\u1100\u116A-\u1107\u1169\u110C\u1169-\u1109\u1162\u11A8\u110B\u1175\u11AB" aria-hidden="true">#</a> \uD30C\uD2F0\uC154\uB2DD\uACFC \uBCF4\uC870 \uC0C9\uC778</h2><p>\uD30C\uD2F0\uC154\uB2DD\uC740 \uD0A4-\uAC12 \uB370\uC774\uD130 \uBAA8\uB378\uC5D0 \uC758\uC874\uD55C\uB2E4. \uADF8\uB7EC\uB098 \uBCF4\uC870 \uC0C9\uC778\uC774 \uC5F0\uAD00\uB418\uBA74 \uC0C1\uD669\uC740 \uBCF5\uC7A1\uD574\uC9C4\uB2E4. \uBCF4\uC870 \uC0C9\uC778\uC740 \uBCF4\uD1B5 \uB808\uCF54\uB4DC\uB97C \uC720\uC77C\uD558\uAC8C \uC2DD\uBCC4\uD558\uB294 \uC6A9\uB3C4\uAC00 \uC544\uB2C8\uB77C \uD2B9\uC815\uD55C \uAC12\uC774 \uBC1C\uC0DD\uD55C \uD56D\uBAA9\uC744 \uAC80\uC0C9\uD558\uB294 \uC218\uB2E8\uC774\uB2E4. \uBCF4\uC870 \uC0C9\uC778 \uC740 \uD30C\uD2F0\uC158\uC5D0 \uAE54\uB054\uD558\uAC8C \uB300\uC751\uB418\uC9C0 \uC54A\uB294 \uBB38\uC81C\uAC00 \uC788\uB2E4. \uBCF4\uC870 \uC0C9\uC778\uC774 \uC788\uB294 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB97C \uD30C\uD2F0\uC154\uB2DD \uD558\uB294 \uB370 \uB110\uB9AC \uC4F0\uC774\uB294 \uB450 \uAC00\uC9C0 \uBC29\uBC95\uC774 \uC788\uB2E4. \uBB38\uC11C \uAE30\uBC18 \uD30C\uD2F0\uC154\uB2DD\uACFC \uC6A9\uC5B4 \uAE30\uBC18 \uD30C\uD2F0\uC154\uB2DD\uC774\uB2E4.</p><h3 id="_1-\u1106\u116E\u11AB\u1109\u1165-\u1100\u1175\u110C\u116E\u11AB-\u1107\u1169\u110C\u1169\u1109\u1162\u11A8\u110B\u1175\u11AB-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_1-\u1106\u116E\u11AB\u1109\u1165-\u1100\u1175\u110C\u116E\u11AB-\u1107\u1169\u110C\u1169\u1109\u1162\u11A8\u110B\u1175\u11AB-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" aria-hidden="true">#</a> 1. \uBB38\uC11C \uAE30\uC900 \uBCF4\uC870\uC0C9\uC778 \uD30C\uD2F0\uC154\uB2DD</h3><p>\uAC01 \uD30C\uD2F0\uC158\uC740 \uC790\uC2E0\uC758 \uBCF4\uC870 \uC0C9\uC778\uC744 \uC720\uC9C0\uD558\uBA70 \uADF8 \uD30C\uD2F0\uC158\uC5D0 \uC18D\uD558\uB294 \uBB38\uC11C\uB9CC \uB2F4\uB2F9\uD55C\uB2E4. \uB2E4\uB978 \uD30C\uD2F0\uC158\uC5D0 \uC5B4\uB5A4 \uB370\uC774\uD130\uAC00 \uC800\uC7A5\uB418\uB294\uC9C0\uB294 \uC2E0\uACBD \uC4F0\uC9C0 \uC54A\uB294\uB2E4. \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC4F0\uAE30 \uC791\uC5C5\uC744 \uC2E4\uD589\uD560 \uB54C\uB294 \uC4F0\uB824\uACE0 \uD558\uB294 \uBB38\uC11C id\uB97C \uD3EC\uD568\uD558\uB294 \uD30C\uD2F0\uC158\uB9CC \uB2E4\uB8E8\uBA74 \uB41C\uB2E4. \uC9C0\uC5ED \uC0C9\uC778(local index)\uB77C\uACE0 \uD55C\uB2E4.</p><p>\uADF8\uB7EC\uB098 \uBB38\uC11C \uAE30\uC900\uC73C\uB85C \uD30C\uD2F0\uC158\uC774 \uB41C \uC0C9\uC778\uC744 \uC368\uC11C \uC77D\uC744 \uB54C\uB294 \uC8FC\uC758\uB97C \uAE30\uC6B8\uC5EC\uC57C \uD55C\uB2E4. \uD2B9\uBCC4\uD55C \uC791\uC5C5\uC744 \uD558\uC9C0 \uC54A\uC73C\uBA74 \uD2B9\uC815 \uC870\uAC74\uC758 \uB370\uC774\uD130\uAC00 \uB3D9\uC77C\uD55C \uD30C\uD2F0\uC158\uC5D0 \uC788\uB2E4\uACE0 \uBCF4\uC7A5\uD560 \uC218 \uC5C6\uC73C\uB2C8 \uBAA8\uB4E0 \uD30C\uD2F0\uC158\uC73C\uB85C \uC9C8\uC758\uB97C \uBCF4\uB0B4\uC11C \uC5BB\uC740 \uACB0\uACFC\uB97C \uBAA8\uB450 \uBAA8\uC544\uC57C \uD55C\uB2E4. \uC774\uB7F0 \uC2DD\uC758 \uC9C8\uC758\uB97C \uBCF4\uB0B4\uB294 \uBC29\uBC95\uC744 \uC2A4\uCE90\uD130/\uAC1C\uB354(scatter/gather)\uB77C\uACE0 \uD558\uB294\uB370 \uBCF4\uC870 \uC0C9\uC778\uC744 \uC368\uC11C \uC77D\uB294 \uC9C8\uC758\uB294 \uD070 \uBE44\uC6A9\uC774 \uB4E4 \uC218 \uC788\uB2E4.</p><h3 id="_2-\u110B\u116D\u11BC\u110B\u1165-\u1100\u1175\u110C\u116E\u11AB-\u1107\u1169\u110C\u1169\u1109\u1162\u11A8\u110B\u1175\u11AB-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_2-\u110B\u116D\u11BC\u110B\u1165-\u1100\u1175\u110C\u116E\u11AB-\u1107\u1169\u110C\u1169\u1109\u1162\u11A8\u110B\u1175\u11AB-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" aria-hidden="true">#</a> 2. \uC6A9\uC5B4 \uAE30\uC900 \uBCF4\uC870\uC0C9\uC778 \uD30C\uD2F0\uC154\uB2DD</h3><p>\uBAA8\uB4E0 \uD30C\uD2F0\uC158\uC758 \uB370\uC774\uD130\uB97C \uB2F4\uB2F9\uD558\uB294 \uC804\uC5ED \uC0C9\uC778(global index)\uC744 \uB9CC\uB4E4 \uC218 \uB3C4 \uC788\uB2E4. \uD55C \uB178\uB4DC\uC5D0\uB9CC \uC0C9\uC778\uC744 \uC800\uC7A5\uD560 \uC218\uB294 \uC5C6\uAE30 \uB54C\uBB38\uC5D0 \uC804\uC5ED \uC0C9\uC778\uB3C4 \uD30C\uD2F0\uC154\uB2DD\uC774 \uD544\uC694\uD558\uB2E4.</p><p>\uCC3E\uACE0\uC790 \uD558\uB294 \uC6A9\uC5B4\uC5D0 \uB530\uB77C \uD30C\uD2F0\uC158\uC774 \uACB0\uC815\uB418\uBBC0\uB85C \uC774\uB7F0 \uC2DD\uC758 \uC0C9\uC778\uC744 \uC6A9\uC5B4 \uAE30\uC900\uC73C\uB85C \uD30C\uD2F0\uC158\uC774 \uB418\uC5C8\uB2E4\uACE0 \uD558\uBA70 \uC0C9\uC778\uC744 \uD30C\uD2F0\uC158 \uD560 \uB54C\uB294 \uC6A9\uC5B4 \uC790\uCCB4\uB97C \uC4F0\uAC70\uB098 \uC6A9\uC5B4\uC758 \uD574\uC2DC\uAC12\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB2E4.</p><p>\uBB38\uC11C \uD30C\uD2F0\uC154\uB2DD\uC5D0 \uBE44\uD574 \uAC00\uC9C0\uB294 \uC774\uC810\uC740 \uC77D\uAE30\uAC00 \uD6A8\uC728\uC801\uC774\uB780 \uAC83\uC774\uB2E4. \uBAA8\uB4E0 \uD30C\uD2F0\uC158\uC5D0 \uC2A4\uCE90\uD130/\uAC1C\uB354\uB97C \uC2E4\uD589\uD560 \uD544\uC694 \uC5C6\uC774 \uC6D0\uD558\uB294 \uC6A9\uC5B4\uB97C \uD3EC\uD568\uD558\uB294 \uD30C\uD2F0\uC158\uC73C\uB85C\uB9CC \uC694\uCCAD\uC744 \uBCF4\uB0B4\uBA74 \uB41C\uB2E4. \uADF8\uB807\uC9C0\uB9CC \uC804\uC5ED \uC0C9\uC778\uC740 \uC4F0\uAE30\uAC00 \uB290\uB9AC\uACE0 \uBCF5\uC7A1\uD558\uB2E4\uB294 \uB2E8\uC810\uC774 \uC788\uB2E4. \uC774\uC0C1\uC801\uC73C\uB85C\uB294 \uC0C9\uC778\uC740 \uD56D\uC0C1 \uCD5C\uC2E0 \uC0C1\uD0DC\uC774\uACE0 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uAE30\uB85D\uB41C \uBB38\uC11C\uB294 \uBC14\uB85C \uC0C9\uC778\uC5D0 \uBC18\uC601\uB418\uC5B4\uC57C \uD558\uC9C0\uB9CC \uADF8\uB7EC\uAE30 \uC704\uD574\uC11C\uB294 \uC4F0\uAE30\uC5D0 \uC601\uD5A5\uBC1B\uB294 \uBAA8\uB4E0 \uD30C\uD2F0\uC158\uC5D0 \uAC78\uCE5C \uBD84\uC0B0 \uD2B8\uB79C\uC7AD\uC158\uC744 \uC0AC\uC6A9\uD574\uC57C \uD558\uBBC0\uB85C \uD604\uC2E4\uC5D0\uC11C\uB294 \uC804\uC5ED \uBCF4\uC870 \uC0C9\uC778\uC740 \uB300\uAC1C \uBE44\uB3D9\uAE30\uB85C \uC0DD\uC2E0 \uB41C\uB2E4.</p><h2 id="\u1111\u1161\u1110\u1175\u1109\u1167\u11AB-\u110C\u1162\u1100\u1172\u11AB\u1112\u1167\u11BC\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#\u1111\u1161\u1110\u1175\u1109\u1167\u11AB-\u110C\u1162\u1100\u1172\u11AB\u1112\u1167\u11BC\u1112\u116A" aria-hidden="true">#</a> \uD30C\uD2F0\uC158 \uC7AC\uADE0\uD615\uD654</h2><p>\uC9C8\uC758 \uCC98\uB9AC\uB7C9\uC774 \uC99D\uAC00\uD558\uAC70\uB098(cpu \uCD94\uAC00), \uB370\uC774\uD130 \uC14B \uD06C\uAE30\uAC00 \uC99D\uAC00\uD558\uAC70\uB098(\uB514\uC2A4\uD06C, \uB7A8 \uCD94\uAC00), \uC7A5\uBE44\uC5D0 \uC7A5\uC560\uAC00 \uBC1C\uC0DD\uD588\uC744 \uB54C \uB4F1\uC758 \uBCC0\uD654\uAC00 \uC0DD\uAE30\uBA74 \uB370\uC774\uD130 \uC694\uCCAD\uC744 \uB2E4\uB978 \uB178\uB4DC\uB85C \uC62E\uACA8\uC57C \uD55C\uB2E4. \uD074\uB7EC\uC2A4\uD130\uC5D0\uC11C \uD55C \uB178\uB4DC\uAC00 \uB2F4\uB2F9\uD558\uB358 \uBD80\uD558\uB97C \uB2E4\uB978 \uB178\uB4DC\uB85C \uC62E\uAE30\uB294 \uACFC\uC815\uC744 \uC7AC\uADE0\uD615\uD654(rebalancing)\uC774\uB77C\uACE0 \uD55C\uB2E4.</p><p>\uC7AC\uADE0\uD615\uD654\uAC00 \uC2E4\uD589\uB420 \uB54C \uBCF4\uD1B5 \uB9CC\uC871\uC2DC\uD0AC \uAC83\uC774\uB77C\uACE0 \uC0DD\uAC01\uD558\uB294 \uCD5C\uC18C \uC694\uAD6C\uC0AC\uD56D\uC740 \uC544\uB798\uC640 \uAC19\uB2E4.</p><ul><li>\uC7AC\uADE0\uD615\uD654 \uD6C4 \uBD80\uD558\uAC00 \uD074\uB7EC\uC2A4\uD130 \uB0B4\uC5D0 \uC788\uB294 \uB178\uB4DC\uB4E4 \uC0AC\uC774\uC5D0 \uADE0\uB4F1\uD558\uAC8C \uBD84\uBC30\uB3FC\uC57C \uD55C\uB2E4.</li><li>\uC7AC\uADE0\uD615\uD654 \uB3C4\uC911\uC5D0\uB3C4 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB294 \uC77D\uAE30 \uC4F0\uAE30 \uC694\uCCAD\uC744 \uBC1B\uC544\uB4E4\uC5EC\uC57C \uD55C\uB2E4.</li><li>\uC7AC\uADE0\uD615\uD654\uAC00 \uBE68\uB9AC \uC2E4\uD589\uB418\uACE0 \uB124\uD2B8\uC6CC\uD06C\uC640 \uB514\uC2A4\uD06C I/O \uBD80\uD558 \uCD5C\uC18C\uD654\uD560 \uC218 \uC788\uB3C4\uB85D \uB178\uB4DC\uB4E4 \uC0AC\uC774\uC5D0 \uB370\uC774\uD130\uAC00 \uD544\uC694 \uC774\uC0C1\uC73C\uB85C \uC62E\uACA8\uC838\uC11C\uB294 \uC548 \uB41C\uB2E4.</li></ul><h3 id="\u110C\u1162\u1100\u1172\u11AB\u1112\u1167\u11BC\u1112\u116A-\u110C\u1165\u11AB\u1105\u1163\u11A8" tabindex="-1"><a class="header-anchor" href="#\u110C\u1162\u1100\u1172\u11AB\u1112\u1167\u11BC\u1112\u116A-\u110C\u1165\u11AB\u1105\u1163\u11A8" aria-hidden="true">#</a> [\uC7AC\uADE0\uD615\uD654 \uC804\uB7B5]</h3><h3 id="_1-\u110A\u1173\u1106\u1167\u11AB-\u110B\u1161\u11AB\u1103\u116C\u1102\u1173\u11AB-\u1107\u1161\u11BC\u1107\u1165\u11B8-\u1112\u1162\u1109\u1175-\u1100\u1161\u11B9\u110B\u1166-mod-n-\u110B\u1167\u11AB\u1109\u1161\u11AB\u110B\u1173\u11AF-\u1109\u1175\u11AF\u1112\u1162\u11BC" tabindex="-1"><a class="header-anchor" href="#_1-\u110A\u1173\u1106\u1167\u11AB-\u110B\u1161\u11AB\u1103\u116C\u1102\u1173\u11AB-\u1107\u1161\u11BC\u1107\u1165\u11B8-\u1112\u1162\u1109\u1175-\u1100\u1161\u11B9\u110B\u1166-mod-n-\u110B\u1167\u11AB\u1109\u1161\u11AB\u110B\u1173\u11AF-\u1109\u1175\u11AF\u1112\u1162\u11BC" aria-hidden="true">#</a> 1. \uC4F0\uBA74 \uC548\uB418\uB294 \uBC29\uBC95! : \uD574\uC2DC \uAC12\uC5D0 mod N \uC5F0\uC0B0\uC744 \uC2E4\uD589</h3><p>\uC55E\uC11C \uD0A4\uC758 \uD574\uC2DC\uAC12\uC744 \uAE30\uC900\uC73C\uB85C \uD30C\uD2F0\uC154\uB2DD \uD560 \uB54C\uB294 \uC0AC\uC6A9 \uAC00\uB2A5\uD55C \uD574\uC2DC \uAC12\uC758 \uBC94\uC704\uB97C \uB098\uB204\uACE0 \uBC94\uC704\uB97C \uD30C\uD2F0\uC158\uC5D0 \uD560\uB2F9\uD558\uB294 \uAC8C \uCD5C\uC120\uC774\uB77C\uACE0 \uD588\uB2E4. \uC65C \uADF8\uB0E5 mod(%) N \uC5F0\uC0B0\uC744 \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uC744\uAE4C? \uADF8 \uC774\uC720\uB294 \uB178\uB4DC \uAC1C\uC218 N\uC774 \uBC14\uB00C\uBA74 \uB300\uBD80\uBD84\uC758 \uD0A4\uAC00 \uB178\uB4DC \uC0AC\uC774\uC5D0\uC11C \uC62E\uACA8\uC838\uC57C \uD55C\uB2E4\uB294 \uC810\uC774\uB2E4. \uC774\uB807\uAC8C \uD0A4\uAC00 \uC790\uC8FC \uC774\uB3D9\uD558\uBA74 \uC7AC \uADE0\uD615\uD654 \uBE44\uC6A9\uC774 \uC9C0\uB098\uCE58\uAC8C \uCEE4\uC9C4\uB2E4.</p><h3 id="_2-\u1111\u1161\u1110\u1175\u1109\u1167\u11AB-\u1100\u1162\u1109\u116E-\u1100\u1169\u110C\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#_2-\u1111\u1161\u1110\u1175\u1109\u1167\u11AB-\u1100\u1162\u1109\u116E-\u1100\u1169\u110C\u1165\u11BC" aria-hidden="true">#</a> 2. \uD30C\uD2F0\uC158 \uAC1C\uC218 \uACE0\uC815</h3><p>\uC774 \uBC29\uBC95\uC740 \uD30C\uD2F0\uC158\uC744 \uB178\uB4DC \uB300\uC218\uBCF4\uB2E4 \uB9CE\uC774 \uB9CC\uB4E4\uACE0 \uAC01 \uB178\uB4DC\uC5D0 \uC5EC\uB7EC \uD30C\uD2F0\uC158\uC744 \uD560\uB2F9\uD558\uB294 \uAC83\uC774\uB2E4. \uD074\uB7EC\uC2A4\uD130\uC5D0 \uB178\uB4DC\uAC00 \uCD94\uAC00\uB418\uBA74 \uC0C8 \uB178\uB4DC\uB294 \uAE30\uC874 \uB178\uB4DC\uC5D0\uC11C \uD30C\uD2F0\uC158 \uBA87 \uAC1C\uB97C \uBE8F\uC5B4\uC62C \uC218 \uC788\uB2E4. \uD074\uB7EC\uC2A4\uD130\uC5D0\uC11C \uB178\uB4DC\uAC00 \uC81C\uAC70\uB418\uBA74 \uC774 \uACFC\uC815\uC774 \uBC18\uB300\uB85C \uC2E4\uD589\uB41C\uB2E4. \uD30C\uD2F0\uC158\uC740 \uB178\uB4DC \uC0AC\uC774\uC5D0\uC11C \uD1B5\uC9F8\uB85C \uC774\uB3D9\uD558\uAE30\uB9CC \uD55C\uB2E4. \uC720\uC77C\uD55C \uBCC0\uD654\uB294 \uB178\uB4DC\uC5D0 \uC5B4\uB5A4 \uD30C\uD2F0\uC158\uC774 \uD560\uB2F9\uB418\uB294\uAC00 \uBFD0\uC774\uB2E4. \uC774 \uBC29\uC2DD\uC744 \uC0AC\uC6A9\uD560 \uB54C\uB294 \uBCF4\uD1B5 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uAC00 \uCC98\uC74C \uAD6C\uCD95\uB420 \uB54C \uD30C\uD2F0\uC158 \uAC1C\uC218\uAC00 \uACE0\uC815\uB418\uACE0 \uC774\uD6C4\uC5D0 \uBCC0\uD558\uC9C0 \uC54A\uB294\uB2E4. \uC804\uCCB4 \uB370\uC774\uD130 \uC14B\uC758 \uD06C\uAE30 \uBCC0\uB3D9\uC774 \uC2EC\uD558\uB2E4\uBA74 \uC801\uC808\uD55C \uD30C\uD2F0\uC158 \uAC1C\uC218\uB97C \uC815\uD558\uB294 \uAC8C \uC5B4\uB824\uC6B8 \uC218 \uC788\uB2E4.</p><p>\uC0AC\uC6A9\uD558\uB294 DB : \uB9AC\uC545, \uC5D8\uB77C\uC2A4\uD2F1 \uC11C\uCE58 \uB4F1</p><h3 id="_3-\u1103\u1169\u11BC\u110C\u1165\u11A8-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_3-\u1103\u1169\u11BC\u110C\u1165\u11A8-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" aria-hidden="true">#</a> 3. \uB3D9\uC801 \uD30C\uD2F0\uC154\uB2DD</h3><p>\uD0A4 \uBC94\uC704 \uD30C\uD2F0\uC154\uB2DD\uC744 \uC0AC\uC6A9\uD558\uB294 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0\uC11C\uB294 \uD30C\uD2F0\uC158\uC758 \uACBD\uACC4\uC640 \uAC1C\uC218\uAC00 \uACE0\uC815\uB418\uC5B4 \uC788\uB294 \uAC8C \uBD88\uD3B8\uD558\uB2E4 \uACBD\uACC4\uB97C \uC798\uBABB \uC9C0\uC815\uD558\uBA74 \uBAA8\uB4E0 \uB370\uC774\uD130\uAC00 \uD55C \uD30C\uD2F0\uC158\uC5D0 \uBAB0\uB9B4 \uC218 \uB3C4 \uC788\uB2E4. \uD30C\uD2F0\uC158 \uACBD\uACC4\uB97C \uC218\uB3D9\uC73C\uB85C \uC7AC\uC124\uC815\uD558\uB294 \uAC83\uC740 \uB9E4\uC6B0 \uC131\uAC00\uC2E0 \uC77C\uC774\uAE30\uC5D0 \uD0A4 \uBC94\uC704 \uD30C\uD2F0\uC154\uB2DD\uC744 \uC0AC\uC6A9\uD558\uB294 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0\uC11C\uB294 \uD30C\uD2F0\uC158\uC744 \uB3D9\uC801\uC73C\uB85C \uB9CC\uB4E0\uB2E4. \uD30C\uD2F0\uC158 \uD06C\uAE30\uAC00 \uC124\uC815\uB41C \uAC12\uC744 \uB118\uC5B4\uC11C\uBA74 \uD30C\uD2F0\uC158\uC744 \uB450 \uAC1C\uB85C \uCABC\uAC1C \uAC01\uAC01\uC5D0 \uC6D0\uB798 \uD30C\uD2F0\uC158\uC758 \uC808\uBC18 \uC815\uB3C4\uC758 \uB370\uC774\uD130\uAC00 \uD3EC\uD568\uB418\uAC8C \uD55C\uB2E4. \uBC18\uB300\uB85C \uD30C\uD2F0\uC158 \uD06C\uAE30\uAC00 \uC784\uACC4\uAC12 \uC544\uB798\uB85C \uB5A8\uC5B4\uC9C0\uBA74 \uC778\uC811\uD55C \uD30C\uD2F0\uC158\uACFC \uD569\uCE60 \uC218 \uB3C4 \uC788\uB2E4. \uD070 \uD30C\uD2F0\uC158\uC774 \uCABC\uAC1C\uC9C4 \uD6C4 \uBD80\uD558\uC758 \uADE0\uD615\uC744 \uC704\uD574 \uBD84\uD560\uB41C \uD30C\uD2F0\uC158 \uC911 \uD558\uB098\uAC00 \uB2E4\uB978 \uB178\uB4DC\uB85C \uC774\uB3D9\uB420 \uC218 \uC788\uB2E4. \uB3D9\uC801 \uD30C\uD2F0\uC154\uB2DD\uC740 \uD30C\uD2F0\uC158 \uAC1C\uC218\uAC00 \uC804\uCCB4 \uB370\uC774\uD130 \uC6A9\uB7C9\uC5D0 \uB9DE\uCDB0 \uC870\uC815\uB41C\uB2E4\uB294 \uC774\uC810\uC774 \uC788\uB2E4 \uD0A4 \uBC94\uC704 \uD30C\uD2F0\uC154\uB2DD\uBFD0\uB9CC \uC544\uB2CC \uD574\uC2DC \uD30C\uD2F0\uC154\uB2DD\uC5D0\uB3C4 \uB611\uAC19\uC774 \uC0AC\uC6A9\uB420 \uC218 \uC788\uB2E4. \uBABD\uACE0 DB\uC758 \uACBD\uC6B0 2.4 \uBC84\uC804\uBD80\uD130 \uD0A4 \uBC94\uC704 \uD30C\uD2F0\uC154\uB2DD\uACFC \uD574\uC2DC \uD30C\uD2F0\uC154\uB2DD \uBAA8\uB450 \uC9C0\uC6D0\uD558\uACE0 \uB450 \uACBD\uC6B0 \uBAA8\uB450 \uD30C\uD2F0\uC158\uC744 \uB3D9\uC801 \uBD84\uD560\uD55C\uB2E4</p><p>\uC0AC\uC6A9\uD558\uB294 DB :Hbase, \uB9AC\uC2F1\uD06C DB, \uBABD\uACE0 DB</p><h3 id="_4-\u1102\u1169\u1103\u1173-\u1107\u1175\u1105\u1168-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#_4-\u1102\u1169\u1103\u1173-\u1107\u1175\u1105\u1168-\u1111\u1161\u1110\u1175\u1109\u1167\u1102\u1175\u11BC" aria-hidden="true">#</a> 4. \uB178\uB4DC \uBE44\uB840 \uD30C\uD2F0\uC154\uB2DD</h3><p>\uB3D9\uC801 \uD30C\uD2F0\uC154\uB2DD\uC740 \uAC1C\uBCC4 \uD30C\uD2F0\uC158 \uD06C\uAE30\uB2E4 \uC5B4\uB5A4 \uACE0\uC815\uB41C \uCD5C\uB313\uAC12\uACFC \uCD5C\uC19F\uAC12 \uC0AC\uC774\uC5D0 \uC720\uC9C0\uB418\uAC8C \uD558\uBBC0\uB85C \uD30C\uD2F0\uC158 \uAC1C\uC218\uAC00 \uB370\uC774\uD130\uC14B \uD06C\uAE30\uC5D0 \uBE44\uB840\uD55C\uB2E4. \uBC18\uBA74 \uD30C\uD2F0\uC158 \uAC1C\uC218\uB97C \uACE0\uC815\uD558\uBA74 \uAC1C\uBCC4 \uD30C\uD2F0\uC158\uC758 \uD06C\uAE30\uAC00 \uB370\uC774\uD130\uC14B \uD06C\uAE30\uC5D0 \uBE44\uB840\uD55C\uB2E4. \uB450 \uACBD\uC6B0 \uBAA8\uB450 \uD30C\uD2F0\uC158 \uAC1C\uC218\uB294 \uB178\uB4DC \uB300\uC218\uC640 \uB3C5\uB9BD\uC801\uC774\uB2E4. \uB178\uB4DC \uBE44\uB840 \uD30C\uD2F0\uC154\uB2DD\uC740 \uB178\uB4DC\uB2F9 \uD560\uB2F9\uB418\uB294 \uD30C\uD2F0\uC158 \uAC1C\uC218\uB97C \uACE0\uC815\uD55C\uB2E4. \uC774 \uACBD\uC6B0 \uB178\uB4DC \uB300\uC218\uAC00 \uBCC0\uD568\uC5C6\uB294 \uB3D9\uC548\uC740 \uAC1C\uBCC4 \uD30C\uD2F0\uC158 \uD06C\uAE30\uAC00 \uB370\uC774\uD130 \uC14B \uD06C\uAE30\uC5D0 \uBE44\uB840\uD558\uC5EC \uC99D\uAC00\uD558\uC9C0\uB9CC \uB178\uB4DC \uB300\uC218\uB97C \uB298\uB9AC\uBA74 \uD30C\uD2F0\uC158 \uD06C\uAE30\uB294 \uB2E4\uC2DC \uC791\uC544\uC9C4\uB2E4. \uC77C\uBC18\uC801\uC73C\uB85C \uB370\uC774\uD130 \uC6A9\uB7C9\uC774 \uD074\uC218\uB85D \uB370\uC774\uD130\uB97C \uC800\uC7A5\uD560 \uB178\uB4DC\uB3C4 \uB9CE\uC774 \uD544\uC694\uD558\uBBC0\uB85C \uC774 \uBC29\uBC95\uC744 \uC4F0\uBA74 \uAC1C\uBCC4 \uD30C\uD2F0\uC158\uC758 \uD06C\uAE30\uB3C4 \uC0C1\uB2F9\uD788 \uC548\uC815\uC801\uC73C\uB85C \uC720\uC9C0\uB41C\uB2E4. \uC0C8 \uB178\uB4DC\uAC00 \uD074\uB7EC\uC2A4\uD130\uC5D0 \uCD94\uAC00\uB418\uBA74 \uACE0\uC815\uB41C \uAC1C\uC218\uC758 \uD30C\uD2F0\uC158\uC744 \uBB34\uC791\uC704\uB85C \uC120\uD0DD\uD574 \uBD84\uD560\uD558\uACE0 \uAC01 \uBD84\uD560\uB41C \uD30C\uD2F0\uC158\uC758 \uC808\uBC18\uC740 \uADF8\uB300\uB85C \uB450\uACE0 \uB2E4\uB978 \uC808\uBC18\uC740 \uC0C8 \uB178\uB4DC\uC5D0 \uD560\uB2F9\uD55C\uB2E4 \uD30C\uD2F0\uC158\uC744 \uBB34\uC791\uC704\uB85C \uC120\uD0DD\uD574 \uADE0\uB4F1\uD558\uC9C0 \uC54A\uC740 \uBD84\uD560\uC774 \uC0DD\uAE38 \uC218\uB3C4 \uC788\uC9C0\uB9CC \uD3C9\uADE0\uC801\uC73C\uB85C \uBCF4\uBA70 \uC0C8 \uB178\uB4DC\uB294 \uAE30\uC874 \uB178\uB4DC\uB4E4\uC774 \uB2F4\uB2F9\uD558\uB358 \uBD80\uD558\uC5D0\uC11C \uADE0\uB4F1\uD55C \uBAAB\uC744 \uD560\uB2F9\uBC1B\uAC8C \uB41C\uB2E4. \uCE74\uC0B0\uB4DC\uB77C 3.0\uC5D0\uC11C\uB294 \uBD88\uADE0\uB4F1\uD55C \uBD84\uD560\uC744 \uD68C\uD53C\uD560 \uC218 \uC788\uB294 \uB300\uC548\uC801\uC778 \uC7AC\uADE0\uD615\uD654 \uC54C\uACE0\uB9AC\uC998\uC774 \uCD94\uAC00\uB418\uC5C8\uB2E4. \uD30C\uD2F0\uC158 \uACBD\uACC4\uB97C \uBB34\uC791\uC704\uB85C \uC120\uD0DD\uD558\uB824\uBA74 \uD574\uC2DC \uAE30\uBC18 \uD30C\uD2F0\uC154\uB2DD\uC744 \uC0AC\uC6A9\uD574\uC57C \uD55C\uB2E4.</p><p>\uC0AC\uC6A9\uD558\uB294 DB : \uCE74\uC0B0\uB4DC\uB77C</p><h2 id="\u110B\u116D\u110E\u1165\u11BC-\u1105\u1161\u110B\u116E\u1110\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u110B\u116D\u110E\u1165\u11BC-\u1105\u1161\u110B\u116E\u1110\u1175\u11BC" aria-hidden="true">#</a> \uC694\uCCAD \uB77C\uC6B0\uD305</h2><p>\uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uC11C \uC694\uCCAD\uC744 \uBCF4\uB0B4\uB824\uACE0 \uD560 \uB54C \uC5B4\uB290 \uB178\uB4DC\uB85C \uC811\uC18D\uD574\uC57C \uD558\uB294\uC9C0 \uC5B4\uB5BB\uAC8C \uC54C \uC218 \uC788\uC744\uAE4C? \uD30C\uD2F0\uC158\uC774 \uC7AC\uADE0\uD615\uD654\uB418\uBA74\uC11C \uB178\uB4DC\uC5D0 \uD560\uB2F9\uB418\uB294 \uD30C\uD2F0\uC158\uC774 \uBC14 \uB6F4\uB2E4. \uD575\uC2EC\uBB38\uC81C\uB294 \uB77C\uC6B0\uD305 \uACB0\uC815\uC744 \uB0B4\uB9AC\uB294 \uAD6C\uC131\uC694\uC18C\uAC00 \uB178\uB4DC\uC5D0 \uD560\uB2F9\uB41C \uD30C\uD2F0\uC158\uC758 \uBCC0\uACBD\uC0AC\uD56D\uC744 \uC5B4\uB5BB\uAC8C \uC544\uB290\uB0D0\uB2E4. \uC774 \uBB38\uC81C\uB294 \uCC38\uC5EC\uD558\uB294 \uBAA8\uB4E0 \uACF3\uC5D0\uC11C \uC815\uBCF4\uAC00 \uC77C\uCE58\uD574\uC57C \uD558\uBBC0\uB85C \uB2E4\uB8E8\uAE30 \uC5B4\uB835\uB2E4.</p><p>\uB9CE\uC740 \uBD84\uC0B0 \uB370\uC774\uD130 \uC2DC\uC2A4\uD15C\uC740 \uD074\uB7EC\uC2A4\uD130 \uBA54\uD0C0\uB370\uC774\uD130\uB97C \uCD94\uC801\uD558\uAE30 \uC704\uD574 \uC8FC\uD0A4\uD37C(zookeeper) \uAC19\uC740 \uBCC4\uB3C4\uC758 \uCF54\uB514\uB124\uC774\uC158 \uC11C\uBE44\uC2A4\uB97C \uC0AC\uC6A9\uD55C\uB2E4. \uAC01 \uB178\uB4DC\uB294 \uC8FC\uD0A4\uD37C\uC5D0 \uC790\uC2E0\uC744 \uB4F1\uB85D\uD558\uACE0 \uC8FC\uD0A4\uD37C\uB294 \uD30C\uD2F0\uC158\uACFC \uB178\uB4DC \uC0AC\uC774\uC758 \uC2E0\uB8B0\uC131 \uC788\uB294 \uD560\uB2F9 \uC815\uBCF4\uB97C \uAD00\uB9AC\uD55C\uB2E4. \uB77C\uC6B0\uD305 \uACC4\uCE35\uC774\uB098 \uD30C\uD2F0\uC158 \uC778\uC9C0 \uD074\uB77C\uC774\uC5B8\uD2B8 \uAC19\uC740 \uB2E4\uB978 \uAD6C\uC131\uC694\uC18C\uB4E4\uC740 \uC8FC\uD0A4\uD37C\uC5D0 \uC788\uB294 \uC815\uBCF4\uB97C \uAD6C\uB3C5\uD560 \uC218 \uC788\uB2E4. \uD30C\uD2F0\uC158\uC774\uB098 \uB178\uB4DC\uC5D0 \uC774\uBCA4\uD2B8\uAC00 \uC77C\uC5B4\uB098\uBA74 \uC8FC\uD0A4\uD37C\uB294 \uB77C\uC6B0\uD305 \uACC4\uCE35\uC5D0 \uC774\uB97C \uC54C\uB824 \uB77C\uC6B0\uD305 \uC815\uBCF4\uB97C \uCD5C\uC2E0\uC73C\uB85C \uC720\uC9C0\uD560 \uC218 \uC788\uAC8C \uD55C\uB2E4. \uADF8\uB7EC\uB098 \uC774 \uBC29\uBC95\uC740 \uC678\uBD80 \uC11C\uBE44\uC2A4\uC5D0 \uC758\uC874\uD574\uC57C \uD558\uB294 \uB2E8\uC810\uC774 \uC788\uB2E4. \uCE74\uC0B0\uB4DC\uB77C\uC640 \uB9AC\uC545\uAC19\uC740 \uACBD\uC6B0\uC5D0\uB294 \uAC00\uC2ED \uD504\uB85C\uD1A0\uCF5C(gossip protocol)\uC744 \uC0AC\uC6A9\uD574\uC11C \uD074\uB7EC\uC2A4\uD130 \uC0C1\uD0DC \uBCC0\uD654\uB97C \uB178\uB4DC \uC0AC\uC774\uC5D0 \uD37C\uB728\uB9B0\uB2E4. \uC544\uBB34 \uB178\uB4DC\uB098 \uC694\uCCAD\uC744 \uBC1B\uC744 \uC218 \uC788\uACE0 \uC694\uCCAD\uC744 \uBC1B\uC740 \uB178\uB4DC\uB290 \uC694\uCCAD\uC744 \uCC98\uB9AC\uD560 \uD30C\uD2F0\uC158\uC744 \uAC16\uACE0 \uC788\u3132\uB294 \uC62C\uBC14\uB978 \uB178\uB4DC\uB85C \uC694\uCCAD\uC744 \uC804\uB2EC\uD574\uC900\uB2E4. \uC774 \uBAA8\uB378\uC740 \uB178\uB4DC\uC5D0 \uBCF5\uC7A1\uC131\uC744 \uB354\uD558\uC9C0\uB9CC \uC678\uBD80 \uC11C\uBE44\uC2A4\uC5D0 \uC758\uC874\uD558\uC9C0 \uC54A\uB294\uB2E4.</p><h2 id="\u110C\u1165\u11BC\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#\u110C\u1165\u11BC\u1105\u1175" aria-hidden="true">#</a> \uC815\uB9AC</h2><ul><li><p>\uD30C\uD2F0\uC154\uB2DD\uC758 \uBAA9\uC801 : \uD56B\uC2A4\uD31F\uC774 \uC0DD\uAE30\uC9C0 \uC54A\uAC8C \uD558\uBA74\uC11C \uB370\uC774\uD130\uC640 \uC9C8\uC758 \uBD80\uD558\uB97C \uC5EC\uB7EC \uC7A5\uBE44\uC5D0 \uADE0\uC77C\uD558\uAC8C \uBD84\uBC30\uD558\uB294 \uAC83</p></li><li><p>\uBAA9\uC801 \uB2EC\uC131 \uBC29\uBC95 : 1. \uC801\uD569\uD55C \uD30C\uD2F0\uC154\uB2DD \uBC29\uC2DD \uC120\uD0DD 2. \uD074\uB7EC\uC2A4\uD130\uC5D0 \uB178\uB4DC\uAC00 \uCD94\uAC00\uB418\uAC70\uB098 \uC0AD\uC81C\uB420 \uB54C \uD30C\uD2F0\uC158 \uC7AC\uADE0\uD615\uD654\uB97C \uD574\uC57C \uD568</p></li><li><p>\uD30C\uD2F0\uC154\uB2DD \uBC29\uC2DD</p><ul><li><p>\uD0A4 \uBC94\uC704 \uD30C\uD2F0\uC154\uB2DD : \uD0A4\uAC00 \uC815\uB82C\uB418\uC5B4 \uC788\uACE0 \uAC1C\uBCC4 \uD30C\uD2F0\uC158\uC740 \uC5B4\uB5A4 \uCD5C\uC19F\uAC12\uACFC \uCD5C\uB313\uAC12 \uC0AC\uC774\uC5D0 \uC18D\uD558\uB294 \uBAA8\uB4E0 \uD0A4\uB97C \uB2F4\uB2F9\uD55C\uB2E4. \uD0A4\uAC00 \uC815\uB82C\uB418\uC5B4\uC788\uAE30 \uB54C\uBB38\uC5D0 \uBC94\uC704 \uC9C8\uC758\uAC00 \uD6A8\uC728\uC801\uC774\uB77C\uB294 \uC7A5\uC810\uC774 \uC788\uC9C0\uB9CC \uC815\uB82C\uC21C\uC11C\uAC00 \uC11C\uB85C \uAC00\uAE4C\uC6B4 \uD0A4\uC5D0 \uC790\uC8FC \uC811\uADFC\uD558\uBA74 \uD56B\uC2A4\uD31F\uC774 \uC0DD\uAE38 \uC704\uD5D8\uC774 \uC788\uB2E4, \uC774 \uBC29\uBC95\uC5D0\uC11C\uB294 \uBCF4\uD1B5 \uD55C \uD30C\uD2F0\uC158\uC774 \uB108\uBB34 \uCEE4\uC9C0\uBA74 \uD0A4\uBC94 \uC704\uB97C \uB450 \uAC1C\uB85C \uCABC\uAC1C \uB3D9\uC801\uC73C\uB85C \uC7AC\uADE0\uD615\uD654\uB97C \uC2E4\uD589\uD55C\uB2E4</p></li><li><p>\uD574\uC2DC \uD30C\uD2F0\uC154\uB2DD : \uAC01 \uD0A4\uC5D0 \uD574\uC2DC \uD568\uC218\uB97C \uC801\uC6A9\uD558\uACE0 \uAC1C\uBCC4 \uD30C\uD2F0\uC158\uC740 \uD2B9\uC815 \uBC94\uC704\uC758 \uD574\uC2DC\uAC12\uC744 \uB2F4\uB2F9\uD55C\uB2E4. \uC774 \uBC29\uBC95\uC744 \uC4F0\uBA74 \uD0A4\uC21C\uC11C\uAC00 \uBCF4\uC7A5\uB418\uC9C0 \uC54A\uC544 \uBC94\uC704 \uC9C8\uC758\uAC00 \uBE44\uD6A8\uC728\uC801\uC774\uC9C0\uB9CC \uBD80\uD558\uB97C \uB354\uC6B1 \uADE0\uC77C\uD558\uAC8C \uBD84\uC0B0\uD560 \uC218 \uC788\uB2E4. \uD574\uC2DC \uD30C\uD2F0\uC154\uB2DD\uC744 \uC0AC\uC6A9\uD560 \uB54C\uB294 \uBCF4\uD1B5 \uACE0\uC815\uB41C \uAC1C\uC218\uC758 \uD30C\uD2F0\uC158\uC744 \uBBF8\uB9AC \uB9CC\uB4E4\uC5B4 \uAC01 \uB178\uB4DC\uC5D0 \uBA87 \uAC1C\uC529 \uD30C\uD2F0\uC158\uC744 \uD560\uB2F9\uD558\uBA74 \uB178\uB4DC\uAC00 \uCD94\uAC00\uB418\uAC70\uB098 \uC81C\uAC70\uB418\uBA74 \uD30C\uD2F0\uC158\uC744 \uD1B5\uC9F8\uB85C \uC0AC\uC774\uC5D0\uC11C \uC774\uB3D9\uD55C\uB2E4. \uB3D9\uC801 \uD30C\uD2F0\uC154\uB2DD\uC744 \uC4F8 \uC218\uB3C4 \uC788\uB2E4.</p></li><li><p>\uB450 \uAC00\uC9C0 \uBC29\uBC95\uC744 \uC11E\uC5B4 \uC4F8 \uC218\uB3C4 \uC788\uB2E4. \uD0A4\uC758 \uC77C\uBD80\uBD84\uC740 \uD30C\uD2F0\uC158 \uAD6C\uBCC4\uC6A9\uC73C\uB85C \uB098\uBA38\uC9C0 \uBD80\uBD84\uC740 \uC815\uB82C\uC21C\uC11C\uC6A9\uC73C\uB85C \uB9CC\uB4E0 \uBCF5\uD569 \uD0A4\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC788\uB2E4.</p></li></ul></li></ul><ul><li><p>\uD30C\uD2F0\uC154\uB2DD\uACFC \uBCF4\uC870\uC0C9\uC778 \uC0AC\uC774\uC758 \uC0C1\uD638\uC791\uC6A9: \uBCF4\uC870\uC0C9\uC778\uC758 \uD30C\uD2F0\uC154\uB2DD \uBC29\uBC95</p><ul><li>\uBB38\uC11C \uD30C\uD2F0\uC154\uB2DD \uC0C9\uC778(\uC9C0\uC5ED \uC0C9\uC778) : \uBCF4\uC870\uC0C9\uC778\uC744 \uAE30\uBCF8\uD0A4\uC640 \uAC12\uC774 \uC800\uC7A5\uB41C \uD30C\uD2F0\uC158\uC5D0 \uC800\uC7A5\uD55C\uB2E4. \uC4F0\uAE30 \uC791\uC5C5 \uB54C\uB294 \uD30C\uD2F0\uC158 \uD558\uB098\uB9CC \uAC31\uC2E0\uD558\uBA74 \uB418\uC9C0\uB9CC \uBCF4\uC870\uC0C9\uC778\uC744 \uC77D\uC73C\uB824\uBA74 \uBAA8\uB4E0 \uD30C\uD2F0\uC158\uC5D0 \uAC78\uCCD0\uC11C \uC2A4\uCE90\uD130/\uAC1C\uB354\uB97C \uC2E4\uD589\uD574\uC57C \uD55C\uB2E4.</li><li>\uC6A9\uC5B4 \uD30C\uD2F0\uC154\uB2DD \uC0C9\uC778(\uC804\uC5ED \uC0C9\uC778): \uC0C9\uC778\uB41C \uAC12\uC744 \uC0AC\uC6A9\uD574\uC11C \uBCF4\uC870\uC0C9\uC778\uC744 \uBCC4\uB3C4\uB85C \uD30C\uD2F0\uC154\uB2DD \uD55C\uB2E4. \uBCF4\uC870 \uC0C9\uC778 \uD56D\uBAA9\uC740 \uAE30\uBCF8\uD0A4\uC758 \uBAA8\uB4E0 \uD30C\uD2F0\uC158\uC5D0 \uC788\uB294 \uB808\uCF54\uB4DC\uB97C \uD3EC\uD568\uD560 \uC218\uB3C4 \uC788\uB2E4. \uBB38\uC11C\uB97C \uC4F8 \uB54C\uB294 \uBCF4\uC870\uC0C9\uC778 \uC5EC\uB7EC \uAC1C\uB97C \uAC31\uC2E0\uD574\uC57C \uD558\uC9C0\uB9CC \uC77D\uAE30\uB294 \uB2E8\uC77C \uD30C\uD2F0\uC158\uC5D0\uC11C \uC2E4\uD589\uB420 \uC218 \uC788\uB2E4.</li></ul></li></ul>',49),n=[p];function t(l,o){return e(),i("div",null,n)}var _=a(d,[["render",t],["__file","ch6-partitioning.html.vue"]]);export{_ as default};
