import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";import{o as i,c as e,d as r}from"./app.d6b42b88.js";var a="/blog/assets/6-\uD504\uB85C\uC138\uC2A4-\uBA54\uBAA8\uB9AC.3df70081.png",t="/blog/assets/6-\uD504\uB85C\uC138\uC2A4-\uC0C1\uD0DC\uBCC0\uD654.7e79fc21.png",o="/blog/assets/6-PCB.e60b7bfd.png";const s={},n=r('<div class="custom-container tip"><p class="custom-container-title">GOAL</p><p>Q. \uD504\uB85C\uC138\uC2A4\uB780 \uBB34\uC5C7\uC778\uAC00\uC694?<br> Q. \uD504\uB85C\uC138\uC2A4\uC758 \uBA54\uBAA8\uB9AC \uAD6C\uC870\uB97C \uC124\uBA85\uD558\uC138\uC694.<br> Q. \uCEE8\uD14D\uC2A4\uD2B8 \uC2A4\uC704\uCE58\uC640 PCB\uB294 \uBB34\uC5C7\uC778\uAC00\uC694?</p></div><p>\uD574\uB2F9\uD398\uC774\uC9C0\uB294 2022\uB144 3\uC6D4 \uB178\uC158\uC5D0\uC11C \uB9C8\uC774\uADF8\uB808\uC774\uC158 \uB418\uC5C8\uC2B5\uB2C8\uB2E4.</p><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u1100\u1162\u1102\u1167\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u1100\u1162\u1102\u1167\u11B7" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4\uC758 \uAC1C\uB150</h2><ul><li>\uD504\uB85C\uC138\uC2A4 : <ul><li>\uC2E4\uD589\uD558\uACE0 \uC788\uB294 \uD504\uB85C\uADF8\uB7A8</li><li>\uB514\uC2A4\uD06C\uC5D0 \uC788\uB358 \uD504\uB85C\uADF8\uB7A8\uC744 <strong>\uBA54\uBAA8\uB9AC\uC5D0 \uC801\uC7AC</strong>\uD558\uC5EC <strong>\uC6B4\uC601\uCCB4\uC81C\uC758 \uC81C\uC5B4\uB97C \uBC1B\uB294 \uC0C1\uD0DC</strong>\uAC00 \uB418\uC5C8\uB2E4.</li><li>\uC790\uC2E0\uB9CC\uC758 \uBA54\uBAA8\uB9AC \uC8FC\uC18C\uAC00 \uC788\uB2E4.</li></ul></li><li>\uD504\uB85C\uADF8\uB7A8\uACFC \uD504\uB85C\uC138\uC2A4 \uBE44\uAD50 <ul><li>\uD504\uB85C\uADF8\uB7A8\uC740 \uCEF4\uD30C\uC77C\uD55C \uCF54\uB4DC\uC640 \uCD08\uAE30\uD654 \uC804\uC5ED\uBCC0\uC218, \uBB38\uC790\uC5F4\uACFC \uBB38\uC790\uC5F4 \uC0C1\uC218 \uB4F1 \uC815\uC801 \uB370\uC774\uD130\uB97C \uD3EC\uD568\uD558\uB294 <strong>\uC815\uC801\uC778 \uAC1C\uCCB4</strong>\uC774\uB2E4.</li><li>\uD504\uB85C\uC138\uC2A4\uB294 \uBA54\uBAA8\uB9AC \uAD6C\uC870\uB97C \uC774\uB8E8\uACE0 \uD604\uC7AC \uC5B4\uB5A4 \uC790\uC6D0\uC744 \uC0AC\uC6A9\uD558\uB294\uC9C0 \uAD00\uB828 \uC815\uBCF4\uAC00 \uB4E4\uC5B4 \uC788\uB294 <strong>\uB3D9\uC801\uC778 \uAC1C\uCCB4</strong> \uC774\uB2E4.</li></ul></li></ul><h2 id="\u1106\u1166\u1106\u1169\u1105\u1175-\u1109\u1161\u11BC\u110B\u1166\u1109\u1165\u110B\u1174-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u1106\u1166\u1106\u1169\u1105\u1175-\u1100\u116E\u110C\u1169" tabindex="-1"><a class="header-anchor" href="#\u1106\u1166\u1106\u1169\u1105\u1175-\u1109\u1161\u11BC\u110B\u1166\u1109\u1165\u110B\u1174-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u1106\u1166\u1106\u1169\u1105\u1175-\u1100\u116E\u110C\u1169" aria-hidden="true">#</a> \uBA54\uBAA8\uB9AC \uC0C1\uC5D0\uC11C\uC758 \uD504\uB85C\uC138\uC2A4(\uD504\uB85C\uC138\uC2A4\uC758 \uBA54\uBAA8\uB9AC \uAD6C\uC870)</h2><p><img src="'+a+'" alt="\uD504\uB85C\uC138\uC2A4 \uBA54\uBAA8\uB9AC \uAD6C\uC870" loading="lazy"></p><ul><li><p>\uC2A4\uD0DD : \uB370\uC774\uD130\uB97C \uC77C\uC2DC\uC801\uC73C\uB85C \uC800\uC7A5\uD558\uB294 \uC601\uC5ED\uC774\uB2E4. \uC9C0\uC5ED\uBCC0\uC218, \uD638\uCD9C\uD55C \uD568\uC218\uC758 \uBC18\uD658 \uC8FC\uC18C, \uBC18\uD658 \uAC12, \uB9E4\uAC1C\uBCC0\uC218 \uB4F1\uC5D0 \uC0AC\uC6A9 \uD55C\uB2E4. \uD568\uC218\uB97C \uD638\uCD9C\uD560\uC218\uB85D \uCEE4\uC9C0\uACE0 \uBC18\uD658\uD558\uBA74 \uC904\uC5B4\uB4E0\uB2E4.</p></li><li><p>\uD799 : \uD799\uC740 \uCF54\uB4DC \uC601\uC5ED\uACFC\uB294 \uBCC4\uB3C4\uB85C \uC720\uC9C0\uB418\uB294 \uC790\uC720 \uC601\uC5ED\uC774\uB2E4. \uD504\uB85C\uADF8\uB798\uBA38\uAC00 \uD544\uC694\uD560 \uB54C \uC0AC\uC6A9\uD558\uB294 \uBA54\uBAA8\uB9AC \uC601\uC5ED\uC73C\uB85C \uD504\uB85C\uADF8\uB7A8 \uC2E4\uD589 \uC911 \uC2DC\uC2A4\uD15C \uD638\uCD9C\uC744 \uC0AC\uC6A9\uD558\uC5EC \uB3D9\uC801\uC73C\uB85C \uBA54\uBAA8\uB9AC\uB97C \uD560\uB2F9\uD588\uB2E4\uAC00 \uD574\uC81C\uD558\uB294 \uBC29\uBC95\uC73C\uB85C \uD65C\uC6A9\uD55C\uB2E4. \uB7F0\uD0C0\uC784\uC5D0 \uACB0\uC815</p></li><li><p>\uB370\uC774\uD130 : \uC804\uC5ED\uBCC0\uC218\uC640 \uC815\uC801(static)\uBCC0\uC218\uAC00 \uC800\uC7A5\uB418\uB294 \uACF5\uAC04\uC774\uB2E4. \uC774 \uBCC0\uC218\uB4E4\uC740 \uD504\uB85C\uADF8\uB7A8\uC774 \uC2DC\uC791\uB420 \uB54C \uD560\uB2F9\uB418\uC5B4 \uD504\uB85C\uADF8\uB7A8 \uC885\uB8CC \uC2DC \uC18C\uBA78\uB41C\uB2E4. \uB354 \uC790\uC138\uD788 \uC0B4\uD3B4\uBCF4\uBA74 BSS(Block Stated Symbol) \uC601\uC5ED\uC774 \uC788\uB294\uB370 \uD574\uB2F9 \uC601\uC5ED\uC5D0\uB294 \uCD08\uAE30\uD654 \uB418\uC9C0 \uC54A\uC740 \uC804\uC5ED\uBCC0\uC218\uAC00 \uC800\uC7A5\uB41C\uB2E4. \uCD08\uAE30\uD654 \uB41C \uC804\uC5ED\uBCC0\uC218\uB294 Data \uC601\uC5ED\uC5D0 \uC800\uC7A5\uB418\uC5B4 \uBE44\uD718\uBC1C\uC131 \uBA54\uBAA8\uB9AC\uC778 ROM\uC5D0 \uC800\uC7A5\uB418\uB294\uB370 \uC774 \uACBD\uC6B0 \uBE44\uC6A9\uC774 \uB9CE\uC774 \uB4E4\uC5B4 RAM\uC5D0 \uC800\uC7A5\uB420 \uAC83\uACFC ROM\uC5D0 \uC800\uC7A5\uB420 \uAC83\uC744 \uAD6C\uBD84\uD558\uAE30 \uC704\uD574 \uC601\uC5ED\uC744 \uAD6C\uBD84\uD574 \uC0AC\uC6A9\uD55C\uB2E4.</p></li><li><p>\uD14D\uC2A4\uD2B8(\uCF54\uB4DC) : CPU\uAC00 \uB514\uC2A4\uD06C\uC5D0\uC11C \uC77D\uC5B4 \uC2E4\uD589\uD558\uB294 \uCEF4\uD30C\uC77C\uD55C \uD504\uB85C\uADF8\uB7A8\uC744 \uC800\uC7A5\uD55C\uB2E4.(\uC2E4\uD589\uD560 \uD504\uB85C\uADF8\uB7A8\uC758 \uCF54\uB4DC\uAC00 \uC800\uC7A5\uB41C\uB2E4) CPU\uB85C \uBCC0\uACBD \uD560 \uC218 \uC5C6\uB2E4. \uC989 \uC774 \uC601\uC5ED\uC740 \uCEF4\uD30C\uC77C \uD0C0\uC784\uC5D0 \uACB0\uC815\uB418\uBA70 read-olny\uC774\uB2E4</p></li></ul><p>\uC2A4\uD0DD\uACFC \uD799\uC740 \uBA54\uBAA8\uB9AC \uD06C\uAE30\uAC00 \uBCC0\uD55C\uB2E4. \uBCF4\uD1B5 \uC2A4\uD0DD\uD3EC\uC778\uD130\uC640 \uD799 \uD3EC\uC778\uD130\uAC00 \uB9CC\uB098\uBA74 \uBA54\uBAA8\uB9AC\uAC00 \uC18C\uC9C4\uB418\uC5C8\uB2E4\uB294 \uC758\uBBF8\uC774\uB2E4.</p><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u110C\u1169\u11BC\u1105\u1172" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u110C\u1169\u11BC\u1105\u1172" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4 \uC885\uB958</h2><ul><li><p>\uCEE4\uB110 \uD504\uB85C\uC138\uC2A4 vs \uC0AC\uC6A9\uC790 \uD504\uB85C\uC138\uC2A4(\uD504\uB85C\uC138\uC2A4\uC758 \uC5ED\uD560\uC5D0 \uB530\uB77C)</p><ul><li><p>\uCEE4\uB110 \uD504\uB85C\uC138\uC2A4 : \uBAA8\uB4E0 \uC2DC\uC2A4\uD15C \uBA54\uBAA8\uB9AC\uC640 \uD504\uB85C\uC138\uC11C\uC758 \uBA85\uB839\uC5D0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uD504\uB85C\uC138\uC2A4\uC774\uB2E4. \uD504\uB85C\uC138\uC2A4 \uC2E4\uD589 \uC21C\uC11C\uB97C \uC81C\uC5B4\uD558\uAC70\uB098 \uB2E4\uB978 \uC0AC\uC6A9\uC790 \uBC0F \uCEE4\uB110\uC601\uC5ED\uC744 \uCE68\uBC94\uD558\uC9C0 \uC54A\uB3C4\uB85D \uAC10\uC2DC\uD558\uACE0 \uC0AC\uC6A9\uC790 \uD504\uB85C\uC138\uC2A4\uB97C \uC0DD\uC131\uD558\uB294 \uAE30\uB2A5\uC744 \uD55C\uB2E4.</p></li><li><p>\uC0AC\uC6A9\uC790 \uD504\uB85C\uC138\uC2A4 : \uC0AC\uC6A9\uC790 \uCF54\uB4DC\uB97C \uC218\uD589\uD558\uB294 \uD504\uB85C\uC138\uC2A4\uC774\uB2E4.</p></li></ul></li><li><p>\uB3C5\uB9BD \uD504\uB85C\uC138\uC2A4 vs \uD611\uB825\uD504\uB85C\uC138\uC2A4(\uBCD1\uD589 \uC218\uD589 \uBC29\uBC95\uC5D0 \uB530\uB77C)</p><ul><li><p>\uB3C5\uB9BD \uD504\uB85C\uC138\uC2A4 : \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC601\uD5A5\uC744 \uC8FC\uC9C0 \uC54A\uAC70\uB098 \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC758 \uC601\uD5A5\uC744 \uBC1B\uC9C0 \uC54A\uC73C\uBA74\uC11C \uC218\uD5A5\uD558\uB294 \uBCD1\uD589 \uD504\uB85C\uC138\uC2A4\uC774\uB2E4.</p></li><li><p>\uD611\uB825 \uD504\uB85C\uC138\uC2A4 : \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC601\uD5A5\uC744 \uC8FC\uAC70\uB098 \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC5D0\u3147\uC11C \uC601\uD5A5\uC744 \uBC1B\uB294 \uBCD1\uD589 \uD504\uB85C\uC138\uC2A4\uC774\uB2E4.</p></li></ul></li></ul><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1161\u11BC\u1110\u1162-\u1107\u1167\u11AB\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1161\u11BC\u1110\u1162-\u1107\u1167\u11AB\u1112\u116A" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4 \uC0C1\uD0DC \uBCC0\uD654</h2><p>\uD504\uB85C\uC138\uC2A4 \uC0C1\uD0DC : New / Waiting / Running / Ready / Terminated<br> \uC6B4\uC601\uCCB4\uC81C\uB294 \uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4\uC5D0 \uD544\uC694\uD55C \uC0C1\uD0DC\uB97C \uC810\uAC80\uD558\uACE0 \uD504\uB85C\uC138\uC2A4\uB97C \uC81C\uC5B4\uD55C\uB2E4.<br><img src="'+t+'" alt="\uD504\uB85C\uC138\uC2A4-\uC0C1\uD0DC\uBCC0\uD654" loading="lazy"></p><ul><li><strong>\uC900\uBE44 -&gt; \uC2E4\uD589(dispatch)</strong><br> \uC900\uBE44 \uD050 \uB9E8 \uC55E\uC5D0 \uC788\uB358 \uD504\uB85C\uC138\uC2A4\uAC00 \uD504\uB85C\uC138\uC11C\uB97C \uC810\uC720\uD558\uB294 \uAC83\uC744 \uB514\uC2A4\uD328\uCE58\uB77C\uACE0 \uD55C\uB2E4.</li><li><strong>\uC2E4\uD589 -&gt; \uC900\uBE44(timeout)</strong><br> \uC6B4\uC601\uCCB4\uC81C\uB294 \uD55C \uD504\uB85C\uC138\uC2A4\uAC00 \uB3C5\uC810\uD558\uB294 \uAC83\uC744 \uB9C9\uAE30 \uC704\uD574 interrupt clock(timer)\uB97C \uB450\uC5B4 \uD2B9\uC815 \uD504\uB85C\uC138\uC2A4\uAC00 \uD560\uB2F9\uB41C \uC2DC\uAC04 \uB9CC\uD07C\uB9CC \uD504\uB85C\uC138\uC11C\uB97C \uC810\uC720\uD558\uAC8C \uD55C\uB2E4. \uD574\uB2F9 \uC2DC\uAC04\uC774 \uB05D\uB098\uBA74 \uD074\uB85D\uC774 \uC778\uD130\uB7FD\uD2B8\uB97C \uBC1C\uC0DD\uC2DC\uCF1C \uC6B4\uC601\uCCB4\uC81C\uC5D0\uAC8C CPU \uC81C\uC5B4\uAD8C\uC744 \uBD80\uC5EC\uD55C\uB2E4. \uADF8\uB7EC\uBA74 \uC2DC\uAC04\uC744 \uC804\uBD80 \uC18C\uC9C4\uD55C \uD504\uB85C\uC138\uC2A4\uB294 \uC900\uBE44\uC0C1\uD0DC\uB85C \uAC00\uACE0 \uC900\uBE44\uD050 \uB9E8 \uC55E\uC758 \uD504\uB85C\uC138\uC2A4\uAC00 \uC2E4\uD589\uC0C1\uD0DC\uAC00 \uB41C\uB2E4.</li><li><strong>\uC2E4\uD589 -&gt; \uB300\uAE30(block)</strong><br> \uD504\uB85C\uC138\uC2A4\uB97C \uC2E4\uD589\uD558\uB2E4\uAC00 \uC785\uCD9C\uB825 \uBA85\uB839\uC774\uB098 \uC2DC\uAC04\uC774 \uB9CE\uC774 \uD544\uC694\uD55C \uC774\uBCA4\uD2B8\uAC00 \uBC1C\uC0DD\uD558\uBA74 \uD504\uB85C\uC138\uC11C\uB97C \uC2A4\uC2A4\uB85C \uBC18\uB0A9\uD558\uACE0 \uB300\uAE30 \uC0C1\uD0DC\uAC00 \uB41C\uB2E4.</li><li><strong>\uB300\uAE30 -&gt; \uC900\uBE44(wakeup)</strong><br> \uB300\uAE30 \uC0C1\uD0DC\uC5D0\uC11C \uC785\uCD9C\uB825 \uC791\uC5C5\uC774 \uB05D\uB098\uBA74 \uB300\uAE30\uC5D0\uC11C \uC900\uBE44 \uC0C1\uD0DC\uAC00 \uB41C\uB2E4</li></ul><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4 \uC2A4\uCF00\uC904\uB9C1</h2><ul><li>\uD504\uB85C\uC138\uC2A4 \uC2A4\uCF00\uC904\uB9C1 \uBAA9\uC801 : CPU \uC774\uC6A9\uC744 \uCD5C\uB300\uD654</li><li>\uC2A4\uCF00\uC904\uB9C1 \uD050 <ul><li>Job queue : \uD504\uB85C\uC138\uC2A4\uAC00 \uC2DC\uC2A4\uD15C\uC5D0 \uB4E4\uC5B4\uC624\uBA74 \uC904\uC744 \uC11C\uB294 \uD050</li><li>Ready queue : \uC900\uBE44\uC644\uB8CC\uC0C1\uD0DC\uC5D0\uC11C \uC2E4\uD589\uC744 \uB300\uAE30 \uD558\uB294 \uD504\uB85C\uC138\uC2A4\uB4E4\uC774 \uB4E4\uC5B4\uC624\uBA74 \uC904\uC744 \uC11C\uB294 \uD050</li><li>Device queue : \uBA54\uC778\uD050\uB294 \uC544\uB2C8\uC9C0\uB9CC \uD2B9\uC815 \uC785\uCD9C\uB825 \uC7A5\uCE58\uB97C \uB300\uAE30\uD558\uBA74\uC11C \uD504\uB85C\uC138\uC2A4\uAC00 \uC904\uC744 \uC11C\uB294 \uD050</li></ul></li><li>\uB2E8\uAE30\uC2A4\uCF00\uC974\uB7EC, \uC911\uAE30 \uC2A4\uCF00\uC974\uB7EC, \uC7A5\uAE30 \uC2A4\uCF00\uC974\uB7EC</li></ul><h2 id="pcb-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u110C\u1166\u110B\u1165\u1107\u1173\u11AF\u1105\u1169\u11A8-process-control-block" tabindex="-1"><a class="header-anchor" href="#pcb-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u110C\u1166\u110B\u1165\u1107\u1173\u11AF\u1105\u1169\u11A8-process-control-block" aria-hidden="true">#</a> PCB(\uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4\uBE14\uB85D, Process Control Block)</h2><p>\uC6B4\uC601\uCCB4\uC81C\uAC00 \uD504\uB85C\uC138\uC2A4\uB97C \uC81C\uC5B4\uD560 \uB54C \uD544\uC694\uD55C \uD504\uB85C\uC138\uC2A4 \uC0C1\uD0DC \uC815\uBCF4\uB294 \uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4 \uBE14\uB85D\uC5D0 \uC800\uC7A5\uB41C\uB2E4. \uD504\uB85C\uC138\uC2A4\uAC00 \uC0DD\uC131\uB418\uBA74 \uBA54\uBAA8\uB9AC\uC5D0 \uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4\uBE14\uB85D\uC744 \uC0DD\uC131\uD558\uACE0 \uD504\uB85C\uC138\uC2A4\uAC00 \uC2E4\uD589\uC744 \uC885\uB8CC\uD558\uBA74 \uD574\uB2F9 \uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4 \uBE14\uB85D\uB3C4 \uC0AD\uC81C\uD55C\uB2E4. \uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4 \uBE14\uB85D\uC5D0\uB294 \uC544\uB798\uC640 \uAC19\uC740 \uC815\uBCF4\uAC00 \uB4E4\uC5B4 \uC788\uB294\uB370 \uC6B4\uC601\uCCB4\uC81C\uC758 \uBAA8\uB4E0 \uBAA8\uB4C8\uC774 \uC774 \uC815\uBCF4\uB97C \uC77D\uACE0 \uC218\uC815\uD560 \uC218 \uC788\uB2E4. <img src="'+o+'" alt="\uD504\uB85C\uC138\uC2A4-\uC81C\uC5B4\uBE14\uB85D" loading="lazy"></p><h2 id="\u110F\u1165\u11AB\u1110\u1166\u11A8\u1109\u1173\u1110\u1173-\u1109\u1173\u110B\u1171\u110E\u1175" tabindex="-1"><a class="header-anchor" href="#\u110F\u1165\u11AB\u1110\u1166\u11A8\u1109\u1173\u1110\u1173-\u1109\u1173\u110B\u1171\u110E\u1175" aria-hidden="true">#</a> \uCEE8\uD14D\uC2A4\uD2B8 \uC2A4\uC704\uCE58</h2><ul><li>\uC2E4\uD589\uC911\uC774\uB358 \uC791\uC5C5 \uC911\uAC04\uC5D0 \uB2E4\uB978 \uC791\uC5C5\uC758 \uC218\uD589\uC774 \uD544\uC694\uD560 \uB54C, \uC774\uC804 \uD504\uB85C\uC138\uC2A4 \uC0C1\uD0DC\uB97C \uBCF4\uAD00, \uD504\uB85C\uC138\uC2A4 \uC7AC\uAC1C\uC2DC \uCC38\uC870 \uD560 \uC218 \uC788\uB3C4\uB85D \uD568</li><li>\uCEE8\uD14D\uC2A4\uD2B8 \uBCF5\uAD6C\uC791\uC5C5, \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uB85C \uAD50\uD658\uD558\uB294 \uC791\uC5C5</li><li>PCB\uC5D0 \uC2E4\uD589\uC911\uC774\uB358 \uD504\uB85C\uC138\uC2A4 \uBB38\uB9E5\uC800\uC7A5</li><li>\uCEE8\uD14D\uC2A4\uD2B8 \uC2A4\uC704\uCE58 \uC218\uD589\uC2DC\uAC04\uC740 \uC21C\uC218\uD55C \uC624\uBC84\uD5E4\uB4DC\uC774\uB2E4. OS \uAD6C\uC131\uC2DC \uACE0\uB824 \uD544\uC694</li></ul><h2 id="\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166\u110B\u1166\u1109\u1165\u110B\u1174-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1162\u11BC\u1109\u1165\u11BC-\u110C\u1169\u11BC\u1105\u116D" tabindex="-1"><a class="header-anchor" href="#\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166\u110B\u1166\u1109\u1165\u110B\u1174-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1162\u11BC\u1109\u1165\u11BC-\u110C\u1169\u11BC\u1105\u116D" aria-hidden="true">#</a> \uC6B4\uC601\uCCB4\uC81C\uC5D0\uC11C\uC758 \uD504\uB85C\uC138\uC2A4 \uC0DD\uC131/\uC885\uB8CC</h2><ul><li>\uD504\uB85C\uC138\uC2A4\uB294 \uC2E4\uD589 \uC911\uC5D0 \uD504\uB85C\uC138\uC2A4 \uC0DD\uC131 \uC2DC\uC2A4\uD15C\uC744 \uD638\uCD9C\uC744 \uC774\uC6A9\uD558\uC5EC \uC0C8\uB85C\uC6B4 \uD504\uB85C\uC138\uC2A4\uB97C \uC0DD\uC131\uD560 \uC218 \uC788\uB2E4. \uC774\uB54C \uBD80\uBAA8-\uC790\uC2DD \uAD00\uACC4\uB97C \uC720\uC9C0\uD558\uC5EC \uACC4\uCE35\uC801\uC73C\uB85C \uC0DD\uC131\uD55C\uB2E4.</li><li>\uD504\uB85C\uC138\uC2A4 \uC0DD\uC131 : <ul><li>\uD504\uB85C\uC138\uC2A4\uAC00 \uC0C8\uB85C\uC6B4 \uD504\uB85C\uC138\uC2A4\uB97C \uC0DD\uC131\uD560 \uB54C \uB2E4\uC74C \uB450 \uAC00\uC9C0 \uC2E4\uD589\uC774 \uBC1C\uC0DD\uD560 \uC218 \uC788\uB2E4.</li><li><ol><li>\uBD80\uBAA8\uD504\uB85C\uC138\uC2A4\uC640 \uC790\uC2DD \uD504\uB85C\uC138\uC2A4\uB97C \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uD55C\uB2E4</li></ol></li><li><ol start="2"><li>\uBD80\uBAA8 \uD504\uB85C\uC138\uC2A4\uB294 \uC790\uC2DD \uD504\uB85C\uC138\uC2A4\uB97C \uBAA8\uB450 \uC885\uB8CC\uD560 \uB54C \uAE4C\uC9C0 \uAE30\uB2E4\uB9B0\uB2E4.</li></ol></li></ul></li><li>fork() (\uC0C8\uB85C\uC6B4 \uD504\uB85C\uC138\uC2A4 \uC704\uD55C \uBA54\uBAA8\uB9AC \uD560\uB2F9) , exec() (\uD638\uCD9C\uD55C \uD504\uB85C\uC138\uC2A4\uC758 PID \uC0C8\uB85C\uC6B4 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC801\uC6A9, \uB36E\uC5B4\uC4F0\uAE30) \uC2DC\uC2A4\uD15C \uCF5C \uD1B5\uD574\uC11C \uD504\uB85C\uC138\uC2A4 \uC0DD\uC131</li><li>exit() (\uC815\uC0C1\uC885\uB8CC) , abort()(\uBE44\uC815\uC0C1 \uC885\uB8CC - \uC880\uBE44 \uD504\uB85C\uC138\uC2A4, \uACE0\uC544\uD504\uB85C\uC138\uC2A4)</li></ul><h2 id="ipc-\u1100\u1169\u11BC\u110B\u1172-\u1106\u1166\u1106\u1169\u1105\u1175\u110B\u116A-\u1106\u1166\u1109\u1166\u110C\u1175-\u110C\u1165\u11AB\u1103\u1161\u11AF\u110B\u1173\u11AF-\u1110\u1169\u11BC\u1112\u1161\u11AB-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u1100\u1161\u11AB-\u1110\u1169\u11BC\u1109\u1175\u11AB" tabindex="-1"><a class="header-anchor" href="#ipc-\u1100\u1169\u11BC\u110B\u1172-\u1106\u1166\u1106\u1169\u1105\u1175\u110B\u116A-\u1106\u1166\u1109\u1166\u110C\u1175-\u110C\u1165\u11AB\u1103\u1161\u11AF\u110B\u1173\u11AF-\u1110\u1169\u11BC\u1112\u1161\u11AB-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u1100\u1161\u11AB-\u1110\u1169\u11BC\u1109\u1175\u11AB" aria-hidden="true">#</a> [IPC]\uACF5\uC720 \uBA54\uBAA8\uB9AC\uC640 \uBA54\uC138\uC9C0 \uC804\uB2EC\uC744 \uD1B5\uD55C \uD504\uB85C\uC138\uC2A4\uAC04 \uD1B5\uC2E0</h2><ul><li><p>Interprocess Communication : \uC5EC\uB7EC \uD504\uB85C\uC138\uC2A4\uAC04\uC758 \uB370\uC774\uD130\uB97C \uC8FC\uACE0 \uBC1B\uC744 \uC218 \uC788\uB3C4\uB85D \uD558\uB294 \uBA54\uCEE4\uB2C8\uC998.</p></li><li><p>\uD504\uB85C\uC138\uC2A4\uAC00 \uD611\uB825\uC744 \uD5C8\uC6A9\uD558\uB294 \uD658\uACBD\uC744 \uC81C\uACF5\uD558\uB294 \uC774\uC720</p><ul><li>\uC815\uBCF4\uACF5\uC720,\uACC4\uC0B0\uAC00\uC18D\uD654, \uBAA8\uB4C8\uC131, \uD3B8\uC758\uC131</li></ul></li><li><p>\uACF5\uC720\uBA54\uBAA8\uB9AC:</p><ul><li>\uB2E4\uC218\uC758 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC758\uD574\uC11C \uACF5\uC720\uB418\uB294 \uBA54\uBAA8\uB9AC \uC601\uC5ED</li><li>\uD1B5\uC2E0\uD558\uB294 \uD504\uB85C\uC138\uC2A4\uB4E4 \uACF5\uC720 \uBA54\uBAA8\uB9AC \uC601\uC5ED \uAD6C\uCD95\uD574\uC11C \uC0AC\uC6A9\uD55C\uB2E4.</li></ul></li><li><p>\uBA54\uC2DC\uC9C0 \uC804\uB2EC \uBC29\uC2DD:</p><ul><li>\uB3D9\uC77C\uD55C \uC8FC\uC18C\uACF5\uAC04\uC744 \uACF5\uC720\uD558\uC9C0 \uC54A\uACE0\uB3C4 \uD504\uB85C\uC138\uC2A4\uB4E4\uC774 \uD1B5\uC2E0\uC774 \uAC00\uB2A5\uD558\uACE0 \uADF8 \uB3D9\uC791\uC744 \uB3D9\uAE30\uD654 \uD560 \uC218 \uC788\uB3C4\uB85D \uD5C8\uC6A9\uD558\uB294 \uAE30\uBC95</li><li>\uD1B5\uC2E0\uD558\uB294 \uD504\uB85C\uC138\uC2A4\uB4E4\uC774 \uB2E4\uB978 \uB124\uD2B8\uC6CC\uD06C\uC5D0 \uC788\uB294 \uBD84\uC0B0\uD658\uACBD\uC77C \uB54C \uC720\uC6A9\uD558\uB2E4.</li><li>send / receive \uC774 \uB450 \uBA85\uB839\uC5B4\uB85C \uAD6C\uC131\uB41C\uB2E4.</li><li>\uC9C1\uC811 - \uAC04\uC811\uD1B5\uC2E0 / \uB3D9\uAE30\uC2DD-\uBE44\uB3D9\uAE30\uC2DD \uD1B5\uC2E0/ \uC790\uB3D9-\uBA85\uC2DC\uC801 \uBC84\uD37C\uB9C1</li></ul></li><li><p>\uBE44\uAD50<br> \uACF5\uC720\uBA54\uBAA8\uB9AC \u2192 \uC18D\uB3C4 / \uBA54\uC9C0\uC9C0 \uC804\uB2EC \u2192 \uAD6C\uD604\uC6A9\uC774\uC131, \uBD84\uC0B0\uD658\uACBD</p></li></ul><h2 id="\u1109\u1169\u110F\u1166\u11BA\u1110\u1169\u11BC\u1109\u1175\u11AB\u1100\u116A-\u110B\u116F\u11AB\u1100\u1167\u11A8-\u1111\u1173\u1105\u1169\u1109\u1175\u110C\u1167-\u110F\u1169\u11AF\u110B\u1173\u11AF-\u1110\u1169\u11BC\u1112\u1161\u11AB-client-server-\u1110\u1169\u11BC\u1109\u1175\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1109\u1169\u110F\u1166\u11BA\u1110\u1169\u11BC\u1109\u1175\u11AB\u1100\u116A-\u110B\u116F\u11AB\u1100\u1167\u11A8-\u1111\u1173\u1105\u1169\u1109\u1175\u110C\u1167-\u110F\u1169\u11AF\u110B\u1173\u11AF-\u1110\u1169\u11BC\u1112\u1161\u11AB-client-server-\u1110\u1169\u11BC\u1109\u1175\u11AB" aria-hidden="true">#</a> \uC18C\uCF13\uD1B5\uC2E0\uACFC \uC6D0\uACA9 \uD504\uB85C\uC2DC\uC838 \uCF5C\uC744 \uD1B5\uD55C client-server \uD1B5\uC2E0</h2><ul><li>\uC6D0\uACA9 \uD504\uB85C\uC2DC\uC838 \uCF5C(Remote Procedure call): <ul><li>IPC \uBC29\uBC95\uC758 \uD55C \uC885\uB958\uB85C \uC6D0\uACA9\uC81C\uC5B4\uB97C \uC704\uD55C \uCF54\uB529 \uC5C6\uC774 \uC6D0\uACA9\uC9C0\uC758 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC811\uADFC\uD558\uC5EC \uD504\uB85C\uC2DC\uC800 \uB610\uB294 \uD568\uC218\uB97C \uD638\uCD9C\uD558\uC5EC \uC0AC\uC6A9\uD558\uB294 \uBC29\uBC95</li><li>\uD604\uC7AC\uC5D0\uB294 MSA(Micro Software Archtecture)\uC5D0\uC11C \uB9CE\uC774 \uC0AC\uC6A9\uC11C\uB85C \uB2E4\uB978 \uD658\uACBD\uC774\uC9C0\uB9CC \uC11C\uBE44\uC2A4\uAC04\uC758 \uD504\uB85C\uC2DC\uC800 \uD638\uCD9C\uC744 \uAC00\uB2A5\uD558\uAC8C \uD574\uC90C \uC5B8\uC5B4\uC5D0 \uAD6C\uC560\uBC1B\uC9C0 \uC54A\uACE0 \uD658\uACBD\uC5D0 \uB300\uD55C \uD655\uC7A5\uC774 \uAC00\uB2A5\uD558\uAC8C \uD574\uC900\uB2E4.</li></ul></li><li>\uC18C\uCF13(sorcket) : IP. Port \uBC88\uD638\uB97C \uC870\uD569\uD558\uC5EC \uC2DD\uBCC4\uD558\uB294 \uBC29\uC2DD</li><li>client-server \uC544\uCF00\uD14D\uCCD0 <ul><li>\uC11C\uBC84\uAC00 \uD2B9\uC815 \uD3EC\uD2B8 \uB9AC\uC2A8\uD558\uBA74\uC11C \uD074\uB77C\uC774\uC5B8\uD2B8 \uC694\uCCAD \uB300\uAE30, \uC694\uCCAD \uBC1B\uC73C\uBA74 \uC11C\uBC84\uB294 \uD074\uB77C\uC774\uC5B8\uD2B8 \uC18C\uCF13\uC73C\uB85C\uBD80\uD130 \uC5F0\uACB0\uC694\uCCAD \uC218\uB77D \uD558\uB294 \uBC29\uC2DD</li><li>\uB124\uD2B8\uC6CC\uD06C\uB85C \uC5F0\uACB0\uB41C \uC11C\uBC84 \uC0C1\uC758 \uD504\uB85C\uC2DC\uC838\uB97C \uC6D0\uACA9\uC73C\uB85C \uD638\uCD9C \uD560 \uC218 \uC788\uB294 \uAE30\uB2A5\uC774\uB2E4.</li></ul></li></ul>',25),c=[n];function d(u,h){return i(),e("div",null,c)}var b=l(s,[["render",d],["__file","ch6-\uD504\uB85C\uC138\uC2A4.html.vue"]]);export{b as default};
