import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as t,d as e}from"./app.d6b42b88.js";const o={},n=e('<h2 id="\u1100\u1162\u110B\u116D" tabindex="-1"><a class="header-anchor" href="#\u1100\u1162\u110B\u116D" aria-hidden="true">#</a> \uAC1C\uC694</h2><p><strong>\uD2B8\uB79C\uC7AD\uC158\uC740 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C \uBA87 \uAC1C\uC758 \uC77D\uAE30\uC640 \uC4F0\uAE30\uB97C \uD558\uB098\uC758 \uB17C\uB9AC\uC801 \uB2E8\uC704\uB85C \uBB36\uB294 \uBC29\uBC95\uC774\uB2E4.</strong> \uAC1C\uB150\uC801\uC73C\uB85C \uD55C \uD2B8\uB79C\uC7AD\uC158 \uB0B4\uC758 \uBAA8\uB4E0 \uC77D\uAE30\uC640 \uC4F0\uAE30\uB294 \uD55C \uC5F0\uC0B0\uC73C\uB85C \uC2E4\uD589\uB41C\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC740 \uC804\uCCB4\uAC00 \uC131\uACF5(commit)\uD558\uAC70\uB098 \uC2E4\uD328(\uC5B4\uBCF4\uD2B8, \uB864\uBC31)\uD55C\uB2E4.</p><p>\uD2B8\uB79C\uC7AD\uC158\uC740 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0 \uC811\uC18D\uD558\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C \uD504\uB85C\uADF8\uB798\uBC0D \uBAA8\uB378\uC744 \uB2E8\uC21C\uD654\uD558\uB824\uB294 \uBAA9\uC801\uC73C\uB85C \uB9CC\uB4E0 \uAC83\uC774\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC744 \uC4F0\uBA74 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C \uC624\uB958 \uCC98\uB9AC\uB97C \uD558\uAE30\uAC00 \uD6E8\uC52C \uB2E8\uC21C\uD574\uC9C4\uB2E4. \uC5B4\uB5A4 \uC5F0\uC0B0\uC740 \uC131\uACF5\uD558\uACE0 \uC5B4\uB5A4 \uC5F0\uC0B0\uC740 \uC2E4\uD328\uD558\uB294 \uACBD\uC6B0\uCC98\uB7FC \uBD80\uBD84\uC801\uC778 \uC2E4\uD328\uB97C \uAC71\uC815\uD560 \uD544\uC694\uAC00 \uC5C6\uAE30 \uB54C\uBB38\uC774\uB2E4. \uB610\uD55C \uD2B8\uB79C\uC7AD\uC158\uC744 \uC0AC\uC6A9\uD568\uC73C\uB85C\uC368 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C \uC5B4\uB290 \uC815\uB3C4 \uC7A0\uC7AC\uC801\uC778 \uC624\uB958\uC640 \uB3D9\uC2DC\uC131 \uBB38\uC81C\uB97C \uBB34\uC2DC\uD560 \uC218 \uC788\uB2E4. \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0\uC11C \uC774\uB7F0 \uC77C\uC744 \uB3C4\uB9E1\uC544 \uC8FC\uAE30 \uB54C\uBB38\uC774\uB2E4(\uC548\uC804\uC131 \uBCF4\uC7A5, safety guarantee)</p><p>\uADF8\uB7EC\uB098 \uBAA8\uB4E0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C \uD2B8\uB79C\uC7AD\uC158\uC774 \uD544\uC694\uD558\uC9C0\uB294 \uC54A\uC73C\uBA70 \uB54C\uB85C\uB294 \uD2B8\uB79C\uC7AD\uC158\uC801\uC778 \uBCF4\uC7A5\uC744 \uC644\uD654\uD558\uAC70\uB098 \uC544\uC608 \uC4F0\uC9C0 \uC54A\uB294 \uAC8C \uC774\uB4DD\uC77C \uC218 \uC788\uB2E4.(\uC131\uB2A5 \uD5A5\uC0C1, \uAC00\uC6A9\uC131\uC744 \uB192\uC744 \uC218 \uC788\uB2E4.) \uD2B8\uB79C\uC7AD\uC158\uC774 \uD544\uC694\uD55C\uC9C0 \uC54C\uAE30 \uC704\uD574\uC11C\uB294 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC81C\uACF5\uD558\uB294 \uC548\uC804\uC131 \uBCF4\uC7A5\uC5D0\uB294 \uC5B4\uB5A4 \uAC83\uC774 \uC788\uC73C\uBA70 \uC774\uC640 \uAD00\uB828\uB41C \uBE44\uC6A9\uC740 \uBB34\uC5C7\uC778\uC9C0 \uC815\uD655\uD558\uAC8C \uC774\uD574\uD574\uC57C \uD55C\uB2E4.</p><p>\uAC70\uC758 \uBAA8\uB4E0 \uAD00\uACC4\uD615 \uB370\uC774\uD130\uC5D0\uC11C\uB294 \uD2B8\uB79C\uC7AD\uC158\uC744 \uC9C0\uC6D0\uD55C\uB2E4. \uADF8\uB7EC\uB098 2000\uB144\uB300 \uD6C4\uBC18\uC5D0 \uBE44 \uAD00\uACC4\uD615(NoSQL) \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uAC00 \uC778\uAE30\uB97C \uC5BB\uAE30 \uC2DC\uC791\uD588\uB2E4. \uC774\uB4E4\uC774 \uC0C8\uB85C\uC6B4 \uB370\uC774\uD130 \uBAA8\uB378\uC744 \uC81C\uC2DC\uD558\uBA70 \uAE30\uBCF8\uC801\uC73C\uB85C \uBCF5\uC81C\uC640 \uD30C\uD2F0\uC154\uB2DD \uAE30\uB2A5\uC744 \uC81C\uACF5\uD558\uC600\uB2E4. \uC0C8\uB85C\uC6B4 \uC138\uB300\uC758 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC911 \uB2E4\uC218\uB294 \uD2B8\uB79C\uC7AD\uC158\uC744 \uC644\uC804\uD788 \uD3EC\uAE30\uD558\uAC70\uB098 \uACFC\uAC70\uC5D0 \uC778\uC2DD\uB418\uB358 \uAC83\uBCF4\uB2E4 \uC57D\uD55C \uBCF4\uC7A5\uC744 \uC758\uBBF8\uD558\uB294 \uB2E8\uC5B4\uB85C \uD2B8\uB79C\uC7AD\uC158\uC758 \uC758\uBBF8\uB97C \uC7AC\uC815\uC758 \uD588\uB2E4. \uC774\uB807\uAC8C \uC0C8\uB85C \uD0C4\uC0DD\uD55C \uBD84\uC0B0 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uAC00 \uD64D\uBCF4\uB418\uBA74\uC11C \uD2B8\uB79C\uC7AD\uC158\uC740 \uD655\uC7A5\uC131\uC758 \uC548\uD2F0\uD14C\uC81C\uC774\uBA70 \uC5B4\uB5A4 \uB300\uADDC\uBAA8 \uC2DC\uC2A4\uD15C\uC774\uB77C\uB3C4 \uB192\uC740 \uC131\uB2A5\uACFC \uACE0\uAC00\uC6A9\uC131\uC744 \uC720\uC9C0\uD558\uB824\uBA74 \uD2B8\uB79C\uC7AD\uC158\uC744 \uD3EC\uAE30\uD574\uC57C \uD55C\uB2E4\uB294 \uBBFF\uC74C\uC774 \uB110\uB9AC \uD37C\uC84C\uB2E4. \uBC18\uBA74 \uD2B8\uB79C\uC7AD\uC158\uC801\uC778 \uBCF4\uC7A5\uC740 &quot;\uAC12\uC9C4 \uB370\uC774\uD130&quot;\uAC00 \uC788\uB294 &quot;\uC911\uB300\uD55C \uC560\uD50C\uB9AC\uCF00\uC774\uC158&quot;\uC5D0 \uD544\uC218\uC801\uC778 \uC694\uAD6C\uC0AC\uD56D\uC774\uB77C\uB294 \uC8FC\uC7A5\uC774 \uC788\uB2E4. \uB450 \uAC00\uC9C0\uB2E4 \uC644\uC804\uD55C \uACFC\uC7A5\uC774\uB2E4.</p><p>\uB2E4\uB978 \uBAA8\uB4E0 \uAE30\uC220\uC801 \uC124\uACC4 \uC120\uD0DD\uACFC \uB9C8\uCC2C\uAC00\uC9C0\uB85C \uD2B8\uB79C\uC7AD\uC158\uC740 \uC774\uC810\uACFC \uD55C\uACC4\uAC00 \uC788\uB2E4. \uC774 \uD2B8\uB808\uC774\uB4DC\uC624\uD504\uB97C \uC774\uD574\uD558\uAE30 \uC704\uD574\uC11C\uB294 \uC815\uC0C1\uC801\uC778 \uC6B4\uC601\uC0C1\uD669\uAD00 \uB2E4\uC591\uD55C \uADF9\uB2E8\uC801\uC774\uACE0 \uD604\uC2E4\uC801\uC778 \uD658\uACBD\uC5D0\uC11C \uD2B8\uB79C\uC7AD\uC158\uC774 \uC81C\uACF5\uD558\uB294 \uBCF4\uC7A5\uC758 \uC138\uBD80 \uC0AC\uD56D\uC744 \uC0B4\uD3B4\uBCF4\uC790.</p><h2 id="acid" tabindex="-1"><a class="header-anchor" href="#acid" aria-hidden="true">#</a> ACID</h2><p>\uD2B8\uB79C\uC7AD\uC158\uC774 \uC81C\uACF5\uD558\uB294 \uC548\uC804\uC131 \uBCF4\uC7A5\uC740 \uD754\uD788 \uC6D0\uC790\uC131(Atomicity), \uC77C\uAD00\uC131(Consistency), \uACA9\uB9AC\uC131(Isolation), \uC9C0\uC18D\uC131(Durability)\uC744 \uC704\uBBF8 \uD558\uB294 \uC57D\uC5B4\uC778 ACID\uB85C \uC798 \uC54C\uB824\uC838 \uC788\uB2E4. \uADF8\uB7EC\uB098 \uD604\uC2E4\uC5D0\uC11C\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB9C8\uB2E4 ACID\uAD6C\uD604\uC774 \uC81C\uAC01\uAC01\uC774\uB2E4. \uC624\uB298\uB0A0 \uC2DC\uC2A4\uD15C\uC774 &#39;ACID&#39;\uB97C \uC900\uC218&#39;\uD55C\uB2E4\uACE0 \uD560 \uB54C \uADF8 \uC2DC\uC2A4\uD15C\uC5D0\uC11C \uC2E4\uC81C\uB85C \uC5B4\uB5A0\uD55C \uAC83\uC744 \uAE30\uB300\uD560 \uC218 \uC788\uB294\uC9C0\uB294 \uBD84\uBA85\uD558\uC9C0 \uC54A\uB2E4.</p><div class="custom-container info"><p class="custom-container-title">Info</p><p>ACID \uD45C\uC900\uC744 \uB530\uB974\uC9C0 \uC54A\uB294 \uC2DC\uC2A4\uD15C\uC740 \uB54C\uB85C BASE\uB77C\uACE0 \uBD88\uB9B0\uB2E4</p><ul><li>Basically Available : \uAE30\uBCF8\uC801\uC73C\uB85C \uAC00\uC6A9\uC131\uC744 \uC81C\uACF5\uD558\uACE0</li><li>Soft state \uC720\uC5F0\uD55C \uC0C1\uD0DC\uB97C \uAC00\uC9C0\uBA70</li><li>Eventual consistency \uCD5C\uC885\uC801 \uC77C\uAD00\uC131\uC744 \uAC00\uC9C4\uB2E4</li></ul><p>\uC704\uC640 \uAC19\uC740 \uC758\uBBF8\uB97C \uAC00\uC9C4\uB2E4. ACID\uBCF4\uB2E4 \uB354 \uBAA8\uD638\uD558\uB2E4</p></div><h3 id="_1-\u110B\u116F\u11AB\u110C\u1161\u1109\u1165\u11BC-atomicity" tabindex="-1"><a class="header-anchor" href="#_1-\u110B\u116F\u11AB\u110C\u1161\u1109\u1165\u11BC-atomicity" aria-hidden="true">#</a> 1. \uC6D0\uC790\uC131(Atomicity)</h3><p>\uC5EC\uB7EC \uC4F0\uAE30 \uC791\uC5C5\uC774 \uD558\uB098\uC758 \uC6D0\uC790\uC801\uC778 \uD2B8\uB79C\uC7AD\uC158\uC73C\uB85C \uBB36\uC774\uACE0 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC644\uB8CC(\uCEE4\uBC0B)\uB418\uAC70\uB098 \uACB0\uD568\uC774 \uBC1C\uC0DD\uD558\uC5EC \uD574\uB2F9 \uD2B8\uB79C\uC7AD\uC158\uC758 \uC791\uC5C5\uC774 \uC911\uB2E8(\uC5B4\uBCF4\uD2B8)\uB418\uAC70\uB098 \uB458 \uC911 \uD558\uB098\uC5EC\uC57C \uD55C\uB2E4.</p><p>\uC624\uB958\uAC00 \uC0DD\uACBC\uC744 \uB54C \uD2B8\uB79C\uC7AD\uC158\uC744 \uC911\uB2E8(\uC5B4\uBCF4\uD2B8)\uD558\uACE0 \uD574\uB2F9 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uAE30\uB85D\uD55C \uBAA8\uB4E0 \uB0B4\uC6A9\uC744 \uCDE8\uC18C\uD558\uB294 \uB2A5\uB825\uC774 ACID\uC758 \uC6D0\uC790\uC131\uC758 \uACB0\uC815\uC801\uC778 \uD2B9\uC131\uC774\uB2E4. \uADF8 \uC758\uBBF8\uC5D0\uC11C <strong>Abortability</strong>\uAC00 \uB354 \uB098\uC740 \uB2E8\uC5B4\uC774\uB2E4.</p><h3 id="_2-\u110B\u1175\u11AF\u1100\u116A\u11AB\u1109\u1165\u11BC-consistency" tabindex="-1"><a class="header-anchor" href="#_2-\u110B\u1175\u11AF\u1100\u116A\u11AB\u1109\u1165\u11BC-consistency" aria-hidden="true">#</a> 2. \uC77C\uAD00\uC131(Consistency)</h3><p>ACID\uC5D0\uC11C\uC758 \uC77C\uAD00\uC131\uC740 \uB370\uC774\uD130\uAC00 \uD56D\uC0C1 \uC5B4\uB5A0\uD55C \uBD88\uBCC0\uC2DD\uC744 \uB9CC\uC871\uD55C\uB2E4\uB294 \uBCF4\uC7A5\uC774\uB2E4. \uC608\uB97C \uB4E4\uBA74 \uD68C\uACC4 \uD504\uB85C\uADF8\uB7A8\uC5D0\uC11C \uCC28\uBCC0\uACFC \uB300\uBCC0\uC774 \uD56D\uC0C1 \uAC19\uC544\uC57C \uD55C\uB2E4 \uAC19\uC740 \uAC83\uC774 \uC788\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC774 \uD56D\uC0C1 \uBD88\uBCC0\uC2DD\uC774 \uC720\uD6A8\uD55C \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uC5D0\uC11C \uC2DC\uC791\uD558\uACE0 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uC2E4\uD589\uB41C \uBAA8\uB4E0 \uC4F0\uAE30\uAC00 \uC720\uD6A8\uC131\uC744 \uBCF4\uC874\uD55C\uB2E4\uBA74 \uBD88\uBCC0\uC2DD\uC774 \uD56D\uC0C1 \uB9CC\uC871\uB41C\uB2E4\uACE0 \uD655\uC2E0\uD560 \uC218 \uC788\uB2E4. \uADF8\uB7EC\uB098 \uC77C\uAD00\uC131\uC740 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC758 \uBD88\uBCC0\uC2DD \uAC1C\uB150\uC5D0 \uC758\uC874\uD558\uACE0 \uC77C\uAD00\uC131\uC744 \uC720\uC9C0\uD558\uB3C4\uB85D \uD2B8\uB79C\uC7AD\uC158\uC744 \uC62C\uBC14\uB974\uAC8C \uC815\uC758\uD558\uB294 \uAC83\uC740 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC758 \uCC45\uC784\uC774\uB2E4. \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB294 \uBD88\uBCC0\uC2DD\uC744 \uC704\uBC18\uD558\uB294 \uC798\uBABB\uB41C \uB370\uC774\uD130\uB97C \uC4F0\uC9C0 \uBABB\uD558\uAC8C \uB9C9\uC744 \uC218 \uC5C6\uB2E4.(\uB2E8 \uC678\uB798\uD0A4, \uC720\uC77C\uC131 \uB4F1\uC740 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C \uBCF4\uC7A5\uD558\uB294 \uD2B9\uC218\uD55C \uACBD\uC6B0 \uC788\uAE30\uB294 \uD558\uB2E4) <strong>ACID\uC5D0\uC11C \uC77C\uAD00\uC131\uB9CC \uC720\uC77C\uD558\uAC8C \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC758 \uC18D\uC131\uC774\uB2E4.</strong></p><h3 id="_3-\u1100\u1167\u11A8\u1105\u1175\u1109\u1165\u11BC-isolation" tabindex="-1"><a class="header-anchor" href="#_3-\u1100\u1167\u11A8\u1105\u1175\u1109\u1165\u11BC-isolation" aria-hidden="true">#</a> 3. \uACA9\uB9AC\uC131(Isolation)</h3><p>\uC5EC\uB7EC \uD2B8\uB79C\uC7AD\uC158\uC774 \uB3D9\uC2DC\uC5D0 \uAC19\uC740 \uB808\uCF54\uB4DC\uC5D0 \uC811\uADFC\uD558\uBA74 \uB3D9\uC2DC\uC131 \uBB38\uC81C(\uACBD\uC7C1 \uC870\uAC74)\uC5D0 \uB9DE\uB2E5\uB728\uB9B0\uB2E4. ACID\uC5D0\uC11C \uACA9\uB9AC\uC131\uC740 \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uB418\uB294 \uD2B8\uB79C\uC7AD\uC158\uC740 \uC11C\uB85C \uACA9\uB9AC\uB41C\uB2E4\uB294 \uAC83\uC744 \uC758\uBBF8\uD55C\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC740 \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC744 \uBC29\uD574\uD560 \uC218 \uC5C6\uB2E4.</p><h3 id="_4-\u110C\u1175\u1109\u1169\u11A8\u1109\u1165\u11BC-durability" tabindex="-1"><a class="header-anchor" href="#_4-\u110C\u1175\u1109\u1169\u11A8\u1109\u1165\u11BC-durability" aria-hidden="true">#</a> 4. \uC9C0\uC18D\uC131(Durability)</h3><p>\uD2B8\uB79C\uC7AD\uC158\uC774 \uC131\uACF5\uC801\uC73C\uB85C \uCEE4\uBC0B\uB410\uB2E4\uBA74 \uD558\uB4DC\uC6E8\uC5B4 \uACB0\uD568\uC774 \uBC1C\uC0DD\uD558\uAC70\uB098 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uAC00 \uC8FD\uB354\uB77C\uB3C4 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uAE30\uB85D\uD55C \uBAA8\uB4E0 \uB370\uC774\uD130\uB294 \uC190\uC2E4\uB418\uC9C0 \uC54A\uB294\uB2E4\uB294 \uBCF4\uC7A5\uC774\uB2E4. \uB2E8\uC77C \uB178\uB4DC\uC5D0\uC11C\uB294 \uC4F0\uAE30\uC804\uB85C\uADF8(Write-ahead log), \uBCF5\uC81C \uAE30\uB2A5\uC774 \uC788\uB294 DB\uC5D0\uC11C\uB294 \uB370\uC774\uD130\uAC00 \uC131\uACF5\uC801\uC73C\uB85C \uB2E4\uB978 \uB178\uB4DC \uBA87 \uAC1C\uC5D0 \uBCF5\uC0AC\uB418\uC5C8\uB2E4\uB294 \uAC83\uC744 \uC758\uBBF8\uD55C\uB2E4. \uC9C0\uC18D\uC131\uC744 \uBCF4\uC7A5\uD558\uAE30 \uC704\uD574 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB294 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC131\uACF5\uC801\uC73C\uB85C \uCEE4\uBC0B\uB418\uC5C8\uB2E4\uACE0 \uBCF4\uACE0\uD558\uAE30 \uC804\uC5D0 \uC4F0\uAE30\uB098 \uBCF5\uC81C\uAC00 \uC644\uB8CC\uB420 \uB54C\uAE4C\uC9C0 \uAE30\uB2E4\uB824\uC57C \uD55C\uB2E4. \uADF8\uB7EC\uB098 \uC644\uBCBD\uD55C \uC9C0\uC18D\uC131\uC740 \uC874\uC7AC\uD558\uC9C0 \uC54A\uB294\uB2E4. \uBAA8\uB4E0 \uD558\uB4DC\uB514\uC2A4\uD06C\uC640 \uBC31\uC5C5\uC774 \uB3D9\uC2DC\uC5D0 \uD30C\uAD34\uB418\uBA74 \uB2F9\uC5F0\uD788 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uAC00 \uD574\uC904 \uC218 \uC788\uB294 \uAC74 \uC544\uBB34\uAC83\uB3C4 \uC5C6\uB2E4.</p><h2 id="\u1103\u1161\u110C\u116E\u11BC-\u1100\u1162\u11A8\u110E\u1166-\u1110\u1173\u1105\u1162\u11AB\u110C\u1162\u11A8\u1109\u1165\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1103\u1161\u110C\u116E\u11BC-\u1100\u1162\u11A8\u110E\u1166-\u1110\u1173\u1105\u1162\u11AB\u110C\u1162\u11A8\u1109\u1165\u11AB" aria-hidden="true">#</a> \uB2E4\uC911 \uAC1D\uCCB4 \uD2B8\uB79C\uC7AD\uC120</h2><p>\uC6B0\uB9AC\uAC00 \uC8FC\uBAA9\uD574\uC57C \uD558\uB294 \uAC83\uC740 \uC6D0\uC790\uC131\uACFC \uACA9\uB9AC\uC131\uC774\uB2E4. \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uD55C \uD2B8\uB79C\uC7AD\uC158 \uB0B4\uC5D0\uC11C \uC4F0\uAE30\uB97C \uC5EC\uB7EC \uBC88 \uD558\uBA74 \uC6D0\uC790\uC131\uACFC \uACA9\uB9AC\uC131\uC740 \uB2E4\uC74C\uACFC \uAC19\uC740 \uAC83\uB4E4\uC744 \uBCF4\uC7A5\uD574\uC8FC\uC5B4\uC57C \uD55C\uB2E4.</p><ul><li><p>\uC6D0\uC790\uC131 : \uC4F0\uAE30\uB97C \uC774\uC5B4\uC11C \uC2E4\uD589\uD558\uB294 \uB3C4\uC911 \uC624\uB958\uAC00 \uBC1C\uC0DD\uD558\uBA74 \uD2B8\uB79C\uC7AD\uC158\uC740 \uC5B4\uBCF4\uD2B8\uB418\uC5B4\uC544\uD558\uACE0 \uADF8\uB54C\uAE4C\uC9C0 \uC4F0\uC778 \uB0B4\uC6A9\uC740 \uD3D0\uAE30\uB418\uC5B4\uC57C \uD55C\uB2E4. \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB294 \uC804\uCCB4 \uBC18\uC601\uB418\uAC70\uB098 \uC804\uCCB4 \uBC18\uC601\uC774 \uB418\uC9C0 \uC54A\uC544\uC57C \uD55C\uB2E4. \uBD80\uBD84 \uC2E4\uD328\uB97C \uAC71\uC815\uD560 \uD544\uC694\uAC00 \uC5C6\uC5B4\uC57C \uD55C\uB2E4.</p></li><li><p>\uACA9\uB9AC\uC131: \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uB418\uB294 \uD2B8\uB79C\uC7AD\uC158\uB4E4\uC740 \uC11C\uB85C\uB97C \uBC29\uD574\uD558\uC9C0 \uB9D0\uC544\uC57C \uD55C\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uD55C \uD2B8\uB79C\uC7AD\uC158\uC774 \uC5EC\uB7EC \uBC88 \uC4F0\uAE30 \uC791\uC5C5\uC744 \uD55C\uB2E4\uBA74 \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC740 \uADF8 \uB0B4\uC6A9\uC744 \uC804\uBD80 \uBCFC \uC218 \uC788\uB4E0\uC9C0 \uC544\uBB34\uAC83\uB3C4 \uBCFC \uC218 \uC5C6\uC5B4\uC57C \uD55C\uB2E4. \uC77C\uBD80\uBD84\uB9CC \uBCFC \uC218 \uC788\uC73C\uBA74 \uC548 \uB41C\uB2E4. \uB2E4\uC911 \uAC1D\uCCB4 \uD2B8\uB79C\uC7AD\uC158\uC740 \uD754\uD788 \uB370\uC774\uD130\uC758 \uC5EC\uB7EC \uC870\uAC01\uC774 \uB3D9\uAE30\uD654\uB41C \uC0C1\uD0DC\uB85C \uC720\uC9C0\uB3FC\uC57C \uD560 \uB54C \uD544\uC694\uD558\uB2E4. \uC774\uBA54\uC77C \uD14C\uC774\uBE14\uC744 \uBE44 \uC815\uADDC\uD654\uD574\uC11C \uC774\uBA54\uC77C\uACFC \uC77D\uC9C0 \uC54A\uC740 \uC774\uBA54\uC77C \uCE74\uC6B4\uD2B8\uAC00 \uB2E4\uB978 \uD14C\uC774\uBE14\uC5D0 \uC788\uACE0, \uAC01\uAC01 \uC5C5\uB370\uC774\uD2B8\uD574\uC57C \uB418\uB294 \uACBD\uC6B0\uB97C \uC0DD\uAC01\uD574\uBCF4\uC790.</p></li></ul><p>\uB9CC\uC57D \uC0AC\uC6A9\uC790\uAC00 \uC774\uBA54\uC77C\uC740 \uC0BD\uC785\uC774 \uB418\uC5C8\uB294\uB370 \uC544\uC9C1 \uCE74\uC6B4\uD2B8\uAC00 \uAC31\uC2E0\uB418\uC9C0 \uC54A\uC544\uC11C \uC0AC\uC6A9\uC790\uAC00 \uC0BD\uC785\uB41C \uC774\uBA54\uC77C\uACFC \uAC31\uC2E0\uB41C \uAC1C\uC218\uB97C \uB2E4\uB974\uAC8C \uBCF4\uBA74 \uD63C\uB780\uC774 \uC0DD\uAE38 \uAC83\uC774\uB2E4. \uACA9\uB9AC\uC131\uC740 \uC774\uB7EC\uD55C \uACBD\uC6B0\uAC00 \uC0DD\uAE30\uC9C0 \uC54A\uB3C4\uB85D \uBCF4\uC7A5\uD55C\uB2E4. \uC0AC\uC6A9\uC790\uAC00 \uB450 \uAC00\uC9C0 \uAC12\uC774 \uB2E4 \uACBD\uC2E0\uB41C \uD6C4\uB97C \uBCF4\uAC70\uB098 \uBAA8\uB450 \uBCF4\uC9C0 \uBABB\uD558\uAC8C \uD558\uACE0 \uC77C\uAD00\uC131\uC774 \uAE68\uC9C4 \uC911\uAC04\uC740 \uBCF4\uC9C0 \uC54A\uAC8C \uD574 \uC900\uB2E4.</p><p>\uC6D0\uC790\uC131\uC740 \uB9CC\uC57D \uC774\uBA54\uC77C\uC774 \uC0BD\uC785\uB41C \uD6C4 \uCE74\uC6B4\uD2B8 \uAC31\uC2E0\uC744 \uC2E4\uD328\uD560 \uACBD\uC6B0\uC5D0\uB294 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC5B4\uBCF4\uD2B8 \uB418\uACE0 \uC0BD\uC785\uB41C \uC774\uBA54\uC77C\uC740 \uB864\uBC31\uB418\uB294 \uAC83\uC744 \uBCF4\uC7A5\uD574\uC900\uB2E4.</p><p>\uB2E4\uC911 \uAC1D\uCCB4 \uC5F0\uC0B0\uC744 \uC704\uD574\uC11C\uB294 \uC77D\uAE30 \uBC0F \uC4F0\uAE30 \uC5F0\uC0B0\uB4E4\uC774 \uB3D9\uC77C\uD55C \uD2B8\uB79C\uC7AD\uC158\uC5D0 \uC18D\uD558\uB294\uC9C0 \uC54C\uC544\uB0BC \uC218\uB2E8\uC774 \uC788\uC5B4\uC57C \uD55C\uB2E4. \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uACBD\uC6B0\uB294 \uC804\uD615\uC801\uC73C\uB85C \uC5B4\uB5A4 \uD2B9\uC815 \uC5F0\uACB0 \uB0B4\uC5D0\uC11C BEGIN TRANSACTION\uBB38, COMMIT\uBB38 \uC0AC\uC774\uC758 \uBAA8\uB4E0 \uAC83\uC740 \uAC19\uC740 \uD2B8\uB79C\uC7AD\uC158\uC5D0 \uC18D\uD558\uB294 \uAC83\uC73C\uB85C \uC5EC\uACA8\uC9C4\uB2E4. \uBC18\uBA74 \uBE44 \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB294 \uC774\uB7F0 \uC2DD\uC73C\uB85C \uC5F0\uC0B0\uC744 \uBB36\uB294 \uBC29\uBC95\uC774 \uC5C6\uB294 \uACBD\uC6B0\uAC00 \uB9CE\uB2E4.</p><p>\uB9CE\uC740 \uBD84\uC0B0 \uB370\uC774\uD130 \uC2A4\uD1A0\uC5B4\uB294 \uB2E4\uC911 \uAC1D\uCCB4 \uD2B8\uB79C\uC7AD\uC158\uC744 \uD3EC\uAE30\uD588\uB2E4. \uC5EC\uB7EC \uD30C\uD2F0\uC158\uC5D0 \uAC78\uCCD0\uC11C \uAD6C\uD604\uD558\uAE30\uAC00 \uC5B4\uB835\uACE0, \uAC00\uC6A9\uC131\uACFC \uC131\uB2A5\uC774 \uD544\uC694\uD55C \uACF3\uC5D0\uC11C \uBC29\uD574\uAC00 \uB418\uAE30\uB3C4 \uD558\uAE30 \uB54C\uBB38\uC774\uB2E4. \uB2E4\uC911 \uAC1D\uCCB4 \uD2B8\uB79C\uC7AD\uC158\uC774 \uD544\uC694\uD560\uAE4C? \uD0A4-\uAC12 \uB370\uC774\uD130 \uBAA8\uB378\uACFC \uB2E8\uC77C \uAC1D\uCCB4 \uC5F0\uC0B0\uB9CC\uC73C\uB85C\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uAD6C\uD604\uD560 \uC218 \uC5C6\uC744\uAE4C \uBB3C\uB860 \uB2E8\uC77C \uAC1D\uCCB4\uC758 \uC0BD\uC785 \uAC31\uC2E0 \uC0AD\uC81C \uC5F0\uC0B0\uB9CC\uC73C\uB85C \uCDA9\uBD84\uD55C \uC0AC\uC6A9 \uC0AC\uB840\uAC00 \uC788\uC9C0\uB9CC \uB9CE\uC740 \uB2E4\uB978 \uACBD\uC6B0\uC5D0\uB294 \uB2E4\uC911 \uAC1D\uCCB4 \uC4F0\uAE30 \uC791\uC5C5\uC740 \uD2B8\uB79C\uC7AD\uC158\uC774 \uD544\uC694\uD558\uB2E4.</p><ul><li>\uC11C\uB85C \uCC38\uC870\uD558\uB294 \uC5EC\uB7EC \uB808\uCF54\uB4DC\uB97C \uC0BD\uC785\uD560 \uB54C \uCC38\uC870 \uD0A4\uB294 \uD56D\uC0C1 \uC62C\uBC14\uB974\uACE0 \uCD5C\uC2E0 \uC815\uBCF4\uB97C \uBC18\uC601\uD574\uC57C \uD55C\uB2E4. \uB2E4 \uC911 \uAC1D\uCCB4 \uD2B8\uB79C\uC7AD\uC158\uC740 \uCC38\uC870\uAC00 \uC720\uD6A8\uD55C \uC0C1\uD0DC\uB85C \uC720\uC9C0\uB418\uB3C4\uB85D \uBCF4\uC7A5\uD574\uC900\uB2E4.</li><li>\uBE44 \uC815\uADDC\uD654\uB41C \uC5EC\uB7EC \uD14C\uC774\uBE14\uC758 \uB370\uC774\uD130\uB4E4\uC744 \uD55C \uBC88\uC5D0 \uAC31\uC2E0\uD574\uC57C \uD560 \uB54C(\uC9C8\uC758 \uC18D\uB3C4 \uBB38\uC81C\uB85C \uC778\uD558\uC5EC \uC77D\uC9C0 \uC54A\uC740 \uC774\uBA54\uC77C \uAC1C\uC218\uB97C \uBCC4\uB3C4\uC5D0 \uD544\uB4DC\uC5D0 \uC800\uC7A5\uD588\uC744 \uB54C)</li><li>\uBCF4\uC870 \uC0C9\uC778\uC774 \uC788\uB294 \uACBD\uC6B0 \uAC12\uC774 \uBCC0\uACBD\uB420 \uB54C \uC0C9\uC778\uB3C4 \uD568\uAED8 \uAC31\uC2E0\uB418\uC5B4\uC57C \uD55C\uB2E4.</li></ul><p>\uD2B8\uB79C\uC7AD\uC158\uC774 \uC5C6\uB354\uB77C\uB3C4 \uC774\uB7F0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uB4E4\uC744 \uAD6C\uD604\uD560 \uC218 \uC788\uC9C0\uB9CC \uC6D0\uC790\uC131\uC774 \uC5C6\uC73C\uBA74 \uC624\uB958\uCC98\uB9AC\uAC00 \uD6E8\uC52C \uB354 \uBCF5\uC7A1\uD574\uC9C0\uACE0, \uACA9\uB9AC\uC131\uC774 \uC5C6\uC73C\uBA74 \uB3D9\uC2DC\uC131 \uBB38\uC81C\uAC00 \uC0DD\uAE38 \uC218 \uC788\uB2E4.</p><div class="custom-container info"><p class="custom-container-title">\uB2E8\uC77C \uAC1D\uCCB4 \uC4F0\uAE30</p><p>\uC6D0\uC790\uC131\uACFC \uACA9\uB9AC\uC131\uC740 \uB2E8\uC77C \uAC1D\uCCB4\uB97C \uBCC0\uACBD\uD558\uB294 \uACBD\uC6B0\uC5D0\uB3C4 \uC801\uC6A9\uB41C\uB2E4. \uC5EC\uB7EC \uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uC11C \uB3D9\uC2DC\uC5D0 \uAC19\uC740 \uAC1D\uCCB4\uC5D0 \uC4F0\uB824\uACE0 \uD560 \uB54C \uAC31\uC2E0 \uC190\uC2E4\uC744 \uBC29\uC9C0\uD558\uBBC0\uB85C \uC720\uC6A9\uD558\uB2E4. \uADF8\uB7EC\uB098 \uC77C\uBC18\uC801\uC73C\uB85C \uC4F0\uC774\uB294 \uC758\uBBF8\uC758 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC544\uB2C8\uB2E4. <strong>\uD2B8\uB79C\uC7AD\uC158\uC740 \uBCF4\uD1B5 \uB2E4\uC911 \uAC1D\uCCB4\uC5D0 \uB300\uD55C \uB2E4\uC911 \uC5F0\uC0B0\uC744 \uD558\uB098\uC758 \uC2E4\uD589 \uB2E8\uC704\uB85C \uBB36\uB294 \uBA54\uCEE4\uB2C8\uC998</strong>\uC73C\uB85C \uC774\uD574\uB41C\uB2E4.</p></div><h2 id="\u110B\u1169\u1105\u1172\u110B\u116A-\u110B\u1165\u1107\u1169\u1110\u1173-\u110E\u1165\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#\u110B\u1169\u1105\u1172\u110B\u116A-\u110B\u1165\u1107\u1169\u1110\u1173-\u110E\u1165\u1105\u1175" aria-hidden="true">#</a> \uC624\uB958\uC640 \uC5B4\uBCF4\uD2B8 \uCC98\uB9AC</h2><p>\uD2B8\uB79C\uC7AD\uC158\uC758 \uD575\uC2EC \uAE30\uB2A5\uC740 \uC624\uB958\uAC00 \uC0DD\uAE30\uBA74 \uC5B4\uBCF4\uD2B8\uB418\uACE0 \uC548\uC804\uD558\uAC8C \uC7AC\uC2DC\uB3C4\uD560 \uC218 \uC788\uB2E4\uB294 \uAC83\uC774\uB2E4. ACID \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB294 \uC774 \uCCA0\uD559\uC744 \uBC14\uD0D5\uC73C\uB85C \uD55C\uB2E4. \uC5B4\uBCF4\uD2B8\uB41C \uD2B8\uB79C\uC7AD\uC158\uC744 \uC7AC\uC2DC\uB3C4\uD558\uB294 \uAC83\uC740 \uAC04\uB2E8\uD558\uACE0 \uD6A8\uACFC\uC801\uC778 \uC624\uB958 \uCC98\uB9AC \uBA54\uCEE4\uB2C8\uC998\uC774\uB2C8\uB9CC \uC644\uBCBD\uD558\uC9C0 \uC54A\uB2E4.</p><ul><li>\uD2B8\uB79C\uC7AD\uC158\uC774 \uC2E4\uC81C\uB860 \uCEE4\uBC0B\uB418\uC5C8\uC9C0\uB9CC \uB124\uD2B8\uC6CC\uD06C \uBB38\uC81C\uB85C \uD074\uB77C\uC774\uC5B8\uD2B8\uB294 \uC2E4\uD328\uD588\uB2E4\uACE0 \uC0DD\uAC01\uD558\uC5EC \uC911\uBCF5 \uCC98\uB9AC\uAC00 \uB420 \uC218 \uC788\uB2E4. \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0 \uCD94\uAC00\uC801\uC778 \uC911\uBCF5 \uC81C\uAC70 \uBA54\uCEE4\uB2C8\uC998\uC774 \uC5C6\uB2E4\uBA74!</li><li>\uC624\uB958\uAC00 \uACFC\uBD80\uD558 \uB54C\uBB38\uC774\uB77C\uBA74 \uD2B8\uB79C\uC7AD\uC158 \uC7AC\uC2DC\uB3C4\uB294 \uBB38\uC81C\uB97C \uC545\uD654\uC2DC\uD0AC \uC218 \uC788\uB2E4. \uD574\uACB0\uD558\uB824\uBA74 \uC7AC\uC2DC\uB3C4 \uD69F\uC218 \uC81C\uD55C, \uC9C0\uC218\uC801 \uBC31\uC624\uD504(1\uCC28\uC2DC\uB3C4 1\uCD08, 2\uCC28\uC2DC\uB3C4 2\uCD08, 3\uCC28\uC2DC\uB3C4 4\uCD08..), \uACFC\uBD80\uD558 \uAD00\uB828 \uC624\uB958 \uB2E4\uB978 \uC624\uB958\uC640 \uBCC4\uB3C4 \uCC98\uB9AC \uB4F1 \uBC29\uBC95\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB2E4.</li><li>\uC77C\uC2DC\uC801\uC778 \uC624\uB958(\uB370\uB4DC\uB77D, \uB124\uD2B8\uC6CC\uD06C \uC624\uB958, \uC2DC\uC2A4\uD15C \uC7A5\uC560)\uB9CC \uAC00\uCE58 \uC788\uACE0 \uC601\uAD6C\uC801\uC778 \uC624\uB958(\uC81C\uC57D \uC870\uAC74 \uC704\uBC18)\uB294 \uC7AC\uC2DC\uB3C4\uD574\uB3C4 \uC18C\uC6A9\uC774 \uC5C6\uB2E4.</li><li>\uD2B8\uB79C\uC7AD\uC158\uC774 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC678\uBD80\uC5D0\uB3C4 \uBD80\uC218 \uD6A8\uACFC\uAC00 \uC788\uB2E4\uBA74 \uD2B8\uB79C\uC7AD\uC158 \uC5B4\uBCF4\uD2B8 \uB420 \uB54C\uB3C4 \uBD80\uC218 \uD6A8\uACFC\uAC00 \uC2E4\uD589\uB420 \uC218 \uC788\uB2E4(ex. \uC774\uBA54\uC77C \uBCF4\uB0B4\uAE30)</li></ul><div class="custom-container info"><p class="custom-container-title">\uCD5C\uC120\uC744 \uB2E4\uD558\uB294 \uC6D0\uCE59</p><p>\uBAA8\uB4E0 \uC2DC\uC2A4\uD15C\uC774 \uC774 \uCCA0\uD559\uC744 \uB530\uB974\uC9C4 \uC54A\uB294\uB2E4. \uD2B9\uD788 \uB9AC\uB354 \uC5C6\uB294 \uBCF5\uC81C\uB97C \uC0AC\uC6A9\uD558\uB294 \uB370\uC774\uD130 \uC2A4\uD1A0\uC5B4\uB294 &quot;\uCD5C\uC120\uC744 \uB2E4\uD558\uB294(best effort)&quot; \uC6D0\uCE59\uC744 \uAE30\uBC18\uC73C\uB85C \uD6E8\uC52C \uB354 \uB9CE\uC740 \uC77C\uC740 \uD55C\uB2E4. \uC694\uC57D\uD558\uBA74 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB294 \uAC00\uB2A5\uD55C \uBAA8\uB4E0 \uAC83\uC744 \uD560 \uAC83\uC774\uBA70 \uADF8 \uB54C\uBB38\uC5D0 \uC624\uB958\uAC00 \uBC1C\uC0DD\uD558\uBA74 \uC774\uBBF8 \uD55C\uC77C\uC740 \uCDE8\uC18C\uD558\uC9C0 \uC54A\uB294\uB2E4&quot; \uB530\uB77C\uC11C \uC624\uB958 \uBCF5\uAD6C\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uAC8C \uCC45\uC784\uC774 \uC788\uB2E4.</p></div>',32),r=[n];function s(l,c){return a(),t("div",null,r)}var h=i(o,[["render",s],["__file","ch7-transactions-1.html.vue"]]);export{h as default};
