import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o as r,c as a,d as i}from"./app.d6b42b88.js";const l={},d=i('<h2 id="\u1103\u1169\u11BC\u1109\u1175\u1109\u1165\u11BC\u1106\u116E\u11AB\u110C\u1166-\u1100\u1167\u11BC\u110C\u1162\u11BC\u110C\u1169\u1100\u1165\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1103\u1169\u11BC\u1109\u1175\u1109\u1165\u11BC\u1106\u116E\u11AB\u110C\u1166-\u1100\u1167\u11BC\u110C\u1162\u11BC\u110C\u1169\u1100\u1165\u11AB" aria-hidden="true">#</a> \uB3D9\uC2DC\uC131\uBB38\uC81C(\uACBD\uC7C1\uC870\uAC74)</h2><p>\uB3D9\uC2DC\uC131 \uBB38\uC81C\uB294 \uD2B8\uB79C\uC7AD\uC158\uC774 \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uB3D9\uC2DC\uC5D0 \uBCC0\uACBD\uD55C \uB370\uC774\uD130\uB97C \uC77D\uAC70\uB098 \uB450 \uD2B8\uB79C\uC7AD\uC158\uC774 \uB3D9\uC2DC\uC5D0 \uAC19\uC740 \uB370\uC774\uD130\uB97C \uBCC0\uACBD\uD558\uB824\uACE0 \uD560 \uB54C \uB098\uD0C0\uB09C\uB2E4.</p><p>\uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB294 \uC624\uB7AB\uB3D9\uC548 \uD2B8\uB79C\uC7AD\uC158 \uACA9\uB9AC\uB97C \uC81C\uACF5\uD568\uC73C\uB85C\uC368 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uAC1C\uBC1C\uC790\uB4E4\uC5D0\uAC8C \uB3D9\uC2DC\uC131 \uBB38\uC81C\uB97C \uAC10\uCD94\uB824\uACE0 \uD588\uB2E4. \uC774\uB860\uC0C1\uC73C\uB85C\uB294 \uACA9\uB9AC\uC131\uC740 \uB3D9\uC2DC\uC131 \uBB38\uC81C\uAC00 \uC5C6\uB294 \uAC83\uCC98\uB7FC \uD589\uB3D9\uD560 \uC218 \uC788\uAC8C\uD574\uC900\uB2E4. \uADF8\uB7EC\uB098 \uD604\uC2E4\uC5D0\uC11C\uC758 \uACA9\uB9AC\uB294 \uADF8\uB807\uAC8C \uAC04\uB2E8\uD55C \uBB38\uC81C\uAC00 \uC544\uB2C8\uB2E4. \uC9C1\uB82C\uC131 \uACA9\uB9AC\uB294 \uC131\uB2A5 \uBE44\uC6A9\uC774 \uC788\uACE0 \uB9CE\uC740 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uB294 \uADF8 \uBE44\uC6A9\uC744 \uC9C0\uBD88\uD558\uB824 \uD558\uC9C0 \uC54A\uB294\uB2E4. \uB530\uB77C\uC11C \uC5B4\uB5A4 \uB3D9\uC2DC\uC131 \uC774\uC288\uB85C\uBD80\uD130\uB294 \uBCF4\uD638\uD574\uC8FC\uC9C0\uB9CC \uBAA8\uB4E0 \uC774\uC288\uB85C\uBD80\uD130\uB294 \uBCF4\uD638\uD574\uC8FC\uC9C0\uB294 \uC54A\uB294 \uC644\uD654\uB41C \uACA9\uB9AC \uC218\uC900\uC744 \uC0AC\uC6A9\uD558\uB294 \uC2DC\uC2A4\uD15C\uB4E4\uC774 \uD754\uD558\uB2E4.</p><p>\uB3D9\uC2DC\uC131 \uBB38\uC81C\uC758 \uC885\uB958\uB97C \uC798 \uC774\uD574\uD558\uACE0 \uBC29\uC9C0\uD558\uB294 \uBC29\uBC95\uC744 \uBC30\uC6B8 \uD544\uC694\uAC00 \uC788\uB2E4. \uC774\uD574\uB97C \uBC14\uD0D5\uC73C\uB85C \uC801\uD569\uD55C \uB3C4\uAD6C\uB97C \uC368\uC11C \uC2E0\uB8B0\uC131 \uC788\uACE0 \uC62C\uBC14\uB974\uAC8C \uB3D9\uC791\uD558\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uB9CC\uB4E4 \uC218 \uC788\uB2E4. \uD604\uC7A5\uC5D0\uC11C \uC0AC\uC6A9\uB418\uB294 \uC644\uD654\uB41C \uACA9\uB9AC \uC218\uC900\uC744 \uBA87 \uAC00\uC9C0 \uC0B4\uD3B4\uBCF4\uACE0 \uBC1C\uC0DD\uD560 \uC218 \uC788\uB294 \uACBD\uC7C1\uC870\uAC74\uACFC \uBC1C\uC0DD\uD560 \uC218 \uC5C6\uB294 \uACBD\uC7C1\uC870\uAC74\uC744 \uC0B4\uD3B4\uBCF4\uC790 \uB610\uD55C \uC9C1\uB82C \uC131 \uACA9\uB9AC\uC5D0 \uB300\uD574\uC11C\uB3C4 \uC54C\uC544\uBCF4\uC790</p><div class="custom-container info"><p class="custom-container-title">\uC9C1\uB82C \uC131 \uACA9\uB9AC</p><p>\uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uAC00 \uC5EC\uB7EC \uD2B8\uB79C\uC7AD\uC158\uB4E4\uC774 \uC9C1\uB82C\uC801\uC73C\uB85C \uC2E4\uD589\uB418\uB294 \uAC83(\uC989 \uB3D9\uC2DC\uC131 \uC5C6\uC774 \uD55C \uBC88\uC5D0 \uD2B8\uB79C\uC7AD\uC158 \uD558\uB098\uB9CC \uC2E4\uD589)\uACFC \uB3D9\uC77C\uD55C \uACB0\uACFC\uAC00 \uB098\uC624\uB3C4\uB85D \uBCF4\uC7A5\uD55C\uB2E4\uB294 \uAC83\uC744 \uC758\uBBF8.</p></div><h2 id="\u110B\u116A\u11AB\u1112\u116A\u1103\u116C\u11AB-\u1100\u1167\u11A8\u1105\u1175-\u1109\u116E\u110C\u116E\u11AB" tabindex="-1"><a class="header-anchor" href="#\u110B\u116A\u11AB\u1112\u116A\u1103\u116C\u11AB-\u1100\u1167\u11A8\u1105\u1175-\u1109\u116E\u110C\u116E\u11AB" aria-hidden="true">#</a> \uC644\uD654\uB41C \uACA9\uB9AC \uC218\uC900</h2><h3 id="_1-\u110F\u1165\u1106\u1175\u11BA-\u1112\u116E-\u110B\u1175\u11B0\u1100\u1175-read-committed" tabindex="-1"><a class="header-anchor" href="#_1-\u110F\u1165\u1106\u1175\u11BA-\u1112\u116E-\u110B\u1175\u11B0\u1100\u1175-read-committed" aria-hidden="true">#</a> 1. \uCEE4\uBC0B \uD6C4 \uC77D\uAE30(read committed)</h3><p>\uAC00\uC7A5 \uAE30\uBCF8\uC801\uC778 \uD2B8\uB79C\uC7AD\uC158 \uACA9\uB9AC \uC218\uC900\uC73C\uB85C \uB2E4\uC74C \uB450 \uAC00\uC9C0\uB97C \uBCF4\uC7A5\uD574\uC900\uB2E4.</p><p>\uB354\uD2F0 \uC77D\uAE30 \uBC29\uC9C0 : \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C \uC77D\uC744 \uB54C \uCEE4\uBC0B\uB41C \uB370\uC774\uD130\uB9CC \uBCF8\uB2E4.<br> \uB354\uD2F0 \uC4F0\uAE30 \uBC29\uC9C0 : \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC4F8 \uB54C \uCEE4\uBC0B\uB41C \uB370\uC774\uD130\uB9CC \uB36E\uC5B4\uC4F0\uAC8C \uB41C\uB2E4.</p><ul><li><p><strong>\uB354\uD2F0 \uC77D\uAE30 \uBC29\uC9C0</strong><br> \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uC544\uC9C1 \uCEE4\uBC0B\uB418\uC9C0 \uC54A\uC740 \uB370\uC774\uD130\uB97C \uBCF4\uB294 \uACBD\uC6B0\uB97C \uB354\uD2F0 \uC77D\uAE30\uB77C\uACE0 \uBD80\uB978\uB2E4. \uCEE4\uBC0B \uD6C4 \uC77D\uAE30 \uACA9\uB9AC \uC218\uC900\uC5D0\uC11C\uB294 \uB354\uD2F0 \uC77D\uAE30\uB97C \uB9C9\uC544\uC57C \uD55C\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC774 \uC4F4 \uB0B4\uC6A9\uC740 \uCEE4\uBC0B\uB41C \uD6C4\uC5D0\uC57C \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uAC8C \uBCF4\uC5EC\uC57C \uD55C\uB2E4. \uB354\uD2F0 \uC77D\uAE30\uB97C \uB9C9\uB294 \uAC8C \uC720\uC6A9\uD55C \uC774\uC720\uB294 \uB2E4\uC74C\uACFC \uAC19\uB2E4.</p><ul><li>\uD2B8\uB79C\uC7AD\uC158\uC774 \uC5EC\uB7EC \uAC1D\uCCB4\uB97C \uAC31\uC2E0\uD558\uB294\uB370 \uB354\uD2F0 \uC77D\uAE30\uAC00 \uC0DD\uAE30\uBA74 \uC77C\uBD80\uB294 \uAC31\uC2E0\uB41C \uAC12\uC744, \uC77C\uBD80\uB294 \uAC31\uC2E0\uB418\uC9C0 \uC54A\uC740 \uAC12\uC744 \uBCFC \uC218 \uC788\uAC8C \uB41C\uB2E4.</li><li>\uD2B8\uB79C\uC7AD\uC158\uC774 \uC5B4\uBCF4\uD2B8\uB418\uBA74 \uB864\uBC31\uD574\uC57C \uD558\uB294\uB370 \uB354\uD2F0 \uC77D\uAE30\uAC00 \uC0DD\uAE30\uBA74 \uC2E4\uC81C\uB85C \uCEE4\uBC0B\uB418\uC9C0 \uC54A\uC740 \uB864\uBC31\uB41C \uB370\uC774\uD130\uB97C \uBCFC \uC218 \uC788\uAC8C \uB41C\uB2E4.</li></ul></li><li><p><strong>\uB354\uD2F0 \uC4F0\uAE30 \uBC29\uC9C0</strong><br> \uC544\uC9C1 \uCEE4\uBC0B\uB418\uC9C0 \uC54A\uC740 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uC4F4 \uB370\uC774\uD130\uB97C \uC0C8\uB85C\uC6B4 \uD2B8\uB79C\uC7AD\uC158\uC774 \uB36E\uC5B4\uC4F0\uB294 \uACBD\uC6B0\uB97C \uB354\uD2F0 \uC4F0\uAE30\uB77C\uACE0 \uD55C\uB2E4. \uCEE4\uBC0B \uD6C4 \uC77D\uAE30 \uACA9\uB9AC \uC218\uC900\uC5D0\uC11C\uB294 \uB354\uD2F0 \uC4F0\uAE30\uB97C \uB9C9\uC544\uC57C \uD55C\uB2E4. \uBCF4\uD1B5 \uBA3C\uC800 \uC4F4 \uD2B8\uB79C\uC7AD\uC158\uC774 \uCEE4\uBC0B\uB418\uAC70\uB098 \uC5B4\uBCF4\uD2B8\uB420 \uB54C\uAE4C\uC9C0 \uB450 \uBC88\uC9F8 \uC4F0\uAE30\uB97C \uC9C0\uC5F0\uC2DC\uD0A8\uB2E4.</p><ul><li>\uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uCDA9\uB3CC\uD558\uB294 \uC4F0\uAE30\uB97C \uC2E4\uD589\uD560 \uB54C \uB354\uD2F0 \uC4F0\uAE30\uAC00 \uC788\uC73C\uBA74 \uB0B4\uC6A9\uC774 \uC11E\uC77C \uC218 \uC788\uB2E4</li></ul></li><li><p><strong>\uCEE4\uBC0B \uD6C4 \uC77D\uAE30 \uAD6C\uD604</strong></p><ul><li>\uB354\uD2F0 \uC4F0\uAE30 \uBC29\uC9C0 \uAD6C\uD604:<br> \uAC00\uC7A5 \uD754\uD55C \uBC29\uBC95\uC73C\uB85C \uB85C\uC6B0 \uC218\uC900 \uC7A0\uAE08\uC744 \uC0AC\uC6A9\uD574 \uB354\uD2F0 \uC4F0\uAE30\uB97C \uBC29\uC9C0\uD55C\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uD2B9\uC815 \uAC1D\uCCB4\uB97C \uBCC0\uACBD\uD558\uACE0 \uC2F6\uB2E4\uBA74 \uC7A0\uAE08\uC744 \uD68D\uB4DD\uD574\uC57C \uD558\uACE0 \uD2B8\uB79C\uC7AD\uC158\uC774 \uCEE4\uBC0B\uB418\uAC70\uB098 \uC5B4\uBCF4\uD2B8\uB420 \uB54C\uAE4C\uC9C0 \uC7A0\uAE08\uC744 \uBCF4\uC720\uD558\uACE0 \uC788\uC5B4\uC57C \uD55C\uB2E4. \uC624\uC9C1 \uD55C \uD2B8\uB79C\uC7AD\uC158\uB9CC \uC5B4\uB5A4 \uC8FC\uC5B4\uC9C4 \uAC1D\uCCB4\uC5D0 \uB300\uD574 \uC7A0\uAE08\uC744 \uBCF4\uC720\uD560 \uC218 \uC788\uB2E4.</li></ul><ul><li>\uB354\uD2F0 \uC77D\uAE30 \uBC29\uC9C0 \uAD6C\uD604:<br> \uB354\uD2F0 \uC77D\uAE30\uC758 \uACBD\uC6B0\uB294 \uC7A0\uAE08\uC744 \uC0AC\uC6A9\uD558\uBA74 \uC77D\uAE30\uB9CC \uC2E4\uD589\uD558\uB294 \uC5EC\uB7EC \uD2B8\uB79C\uC7AD\uC158\uB4E4\uC774 \uC624\uB7AB\uB3D9\uC548 \uC2E4\uD589\uB418\uB294 \uC4F0\uAE30 \uD2B8\uB79C\uC7AD\uC158 \uD558\uB098\uAC00 \uC644\uB8CC\uB420 \uB54C\uAE4C\uC9C0 \uAE30\uB2E4\uB824\uC57C \uD560 \uC218 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uC751\uB2F5 \uC2DC\uAC04\uC5D0 \uD574\uB97C \uB07C\uCE5C\uB2E4. \uADF8\uB807\uAE30 \uB54C\uBB38\uC5D0 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB294 \uC4F0\uC778 \uBAA8\uB4E0 \uAC1D\uCCB4\uC5D0 \uB300\uD574 \uACFC\uAC70\uC5D0 \uCEE4\uBC0B\uB41C \uAC12\uACFC \uD604\uC7AC \uC4F0\uAE30 \uC7A0\uAE08\uC744 \uAC00\uC9C4 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uC4F4 \uC0C8\uB85C\uC6B4 \uAC12\uC744 \uBAA8\uB450 \uAE30\uC5B5\uD55C\uB2E4. \uD55C \uAC1D\uCCB4\uC5D0 \uC4F0\uAE30 \uC7A0\uAE08\uC744 \uAC00\uC9C4 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC2E4\uD589 \uC911\uC778 \uB3D9\uC548 \uADF8 \uAC1D\uCCB4\uB97C \uC77D\uB294 \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC740 \uD574\uB2F9 \uAC1D\uCCB4\uAC00 \uCEE4\uBC0B\uD558\uAE30 \uC804\uAE4C\uC9C0\uB294 \uACFC\uAC70\uC5D0 \uCEE4\uBC0B\uB41C \uAC12\uC744 \uC77D\uAC8C \uB41C\uB2E4.</li></ul></li></ul><h3 id="_2-\u1109\u1173\u1102\u1162\u11B8\u1109\u116D\u11BA-\u1100\u1167\u11A8\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#_2-\u1109\u1173\u1102\u1162\u11B8\u1109\u116D\u11BA-\u1100\u1167\u11A8\u1105\u1175" aria-hidden="true">#</a> 2. \uC2A4\uB0C5\uC20F \uACA9\uB9AC</h3><ul><li><p>\uBE44\uBC18\uBCF5 \uC77D\uAE30(nonrepeatable read)<br> \uC740\uD589 \uACC4\uC88C A, B\uC5D0 \uAC01\uAC01 500\uB2EC\uB7EC\uC529 \uC788\uACE0 A \uACC4\uC88C\uC5D0\uC11C B\uACC4\uC88C\uB85C 100\uB2EC\uB7EC\uB97C \uC804\uC1A1\uD558\uB294 \uD2B8\uB79C\uC7AD\uC158\uC744 \uC2E4\uD589\uD55C\uB2E4\uACE0 \uD558\uC790. \uB450 \uACC4\uC88C\uC5D0\uC11C \uB370\uC774\uD130\uB97C \uBC1B\uC544\uC640\uC57C \uD558\uB294\uB370, b\uACC4\uC88C\uC758 \uAC80\uC0C9\uC740 \uD2B8\uB79C\uC7AD\uC158\uC774 \uCC98\uB9AC\uB418\uACE0 \uC788\uB294 \uC21C\uAC04\uC5D0 \uACC4\uC88C \uC794\uACE0\uB97C \uBCF4\uACE0 A \uACC4\uC88C\uB294 \uB294 \uCEE4\uBC0B\uB41C \uD6C4\uC5D0 \uACC4\uC88C\uC794\uACE0\uB97C \uBCF4\uAC8C \uB41C\uB2E4\uBA74 A \uACC4\uC88C\uB294 400\uB2EC\uB7EC, B\uACC4\uC88C\uB294 500\uB2EC\uB85C \uB098\uC640 \uCD1D 900\uB2EC\uB7EC\uAC00 \uC788\uB294 \uAC83\uCC98\uB7FC \uBCF4\uC77C \uC218\uB3C4 \uC788\uB2E4. \uC774\uB7F0 \uD604\uC0C1\uC744 \uBE44\uBC18\uBCF5 \uC77D\uAE30(nonrepeatable read) \uD639\uC740 \uC77D\uAE30 \uC2A4\uD050(read skew)\uB77C\uACE0 \uD558\uBA70 \uCEE4\uBC0B \uD6C4 \uC77D\uAE30\uC5D0\uC11C\uB294 \uC774\uB7F0 \uD604\uC0C1\uC744 \uB9C9\uC744 \uC218 \uC5C6\uB2E4. \uC2DC\uAC04\uC774 \uC9C0\uB098 \uD2B8\uB79C\uC7AD\uC158\uC774 \uCEE4\uBC0B\uB418\uBA74 \uC774\uB7F0 \uD604\uC0C1\uC740 \uC0AC\uB77C\uC9C0\uC9C0\uB9CC \uC774\uB7F0 \uC77C\uC2DC\uC801\uC778 \uBE44\uC77C\uAD00\uC131\uB3C4 \uAC10\uB0B4\uD560 \uC218 \uC5C6\uB294 \uACBD\uC6B0\uAC00 \uC788\uB2E4.(ex. \uBC31\uC5C5, \uBD84\uC11D \uC9C8\uC758, \uBB34\uACB0\uC131 \uD655\uC778)</p></li><li><p>\uC2A4\uB0C5\uC20F \uACA9\uB9AC<br> \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB294 \uC704\uC640 \uAC19\uC740 \uBB38\uC81C\uB97C \uD574\uACB0\uD560 \uC218 \uC788\uB294 \uAC00\uC7A5 \uD754\uD55C \uD574\uACB0\uCC45\uC774\uB2E4. <strong>\uAC01 \uD2B8\uB79C\uC7AD\uC158\uC740 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uC77C\uAD00\uB41C \uC2A4\uB0C5\uC20F\uC73C\uB85C\uBD80\uD130 \uC77D\uB294\uB2E4(\uD2B9\uC815 \uC2DC\uC810\uC5D0 \uACE0\uC815\uB41C DB). \uC989 \uD2B8\uB79C\uC7AD\uC158\uC740 \uC2DC\uC791\uD560 \uB54C \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uCEE4\uBC0B\uB41C \uC0C1\uD0DC\uC600\uB358 \uBAA8\uB4E0 \uB370\uC774\uD130\uB97C \uBCF8\uB2E4. \uB370\uC774\uD130\uAC00 \uB098\uC911\uC5D0 \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC5D0 \uC758\uD574 \uBC14\uB00C\uB354\uB77C\uB3C4 \uAC01 \uD2B8\uB79C\uC7AD\uC158\uC740 \uD2B9\uC815\uD55C \uC2DC\uC810\uC758 \uACFC\uAC70 \uB370\uC774\uD130\uB97C \uBCFC\uBFD0\uC774\uB2E4.</strong>(\uC2DC\uAC04\uC744 \uB9DE\uCD94\uB294 \uAC83) \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB294 PostgreSQL, InnoDB \uC800\uC7A5\uC18C \uC5D4\uC9C4\uC744 \uC4F0\uB294 MySQL, Oracle \uB4F1\uC5D0\uC11C \uC9C0\uC6D0\uD55C\uB2E4.</p></li><li><p>\uC2A4\uB0C5\uC20F \uACA9\uB9AC \uAD6C\uD604<br> \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uC5D0\uC11C\uB294 \uB354\uD2F0 \uC4F0\uAE30\uB97C \uBC29\uC9C0\uD558\uAE30 \uC704\uD574\uC11C\uB294 \uC4F0\uAE30 \uC7A0\uAE08\uC744 \uC0AC\uC6A9\uD55C\uB2E4. \uADF8\uB7EC\uB098 \uC77D\uC744 \uB54C\uB294 \uC544\uBB34 \uC7A0\uAE08\uB3C4 \uD544\uC694 \uC5C6\uB2E4. \uC131\uB2A5 \uAD00\uC810\uC5D0\uC11C \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uC758 \uD575\uC2EC \uC6D0\uB9AC\uB294 \uC77D\uB294 \uCABD\uC5D0\uC11C \uC4F0\uB294 \uCABD\uC744 \uACB0\uCF54 \uCC28\uB2E8\uD558\uC9C0 \uC54A\uACE0 \uC4F0\uB294 \uCABD\uC5D0\uC11C \uC77D\uB294 \uCABD\uC744 \uACB0\uCF54 \uCC28\uB2E8\uD558\uC9C0 \uC54A\uB294\uB2E4\uB294 \uAC83\uC774\uB2E4. \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB97C \uAD6C\uD604\uD558\uAE30 \uC704\uD574\uC11C\uB294 \uAC1D\uCCB4\uB9C8\uB2E4 \uCEE4\uBC0B\uB41C \uBC84\uC804 \uC5EC\uB7EC \uAC1C\uB97C \uC720\uC9C0\uD560 \uC218 \uC788\uC5B4\uC57C \uD55C\uB2E4. \uC9C4\uD589 \uC911\uC778 \uC5EC\uB7EC \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uC11C\uB85C \uB2E4\uB978 \uC2DC\uC810\uC758 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4 \uC0C1\uD0DC\uB97C \uBD10\uC57C \uD560 \uC218\uB3C4 \uC788\uAE30 \uB54C\uBB38\uC774\uB2E4. \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uAC00 \uAC1D\uCCB4\uC758 \uC5EC\uB7EC \uBC84\uC804\uC744 \uD568\uAED8 \uC720\uC9C0\uD558\uB294 \uAE30\uBC95\uC744 \uB2E4\uC911 \uBC84\uC804 \uB3D9\uC2DC\uC131 \uC81C\uC5B4(multi-version concurreny control, MVCC)\uB77C\uACE0 \uD55C\uB2E4. \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB97C \uC9C0\uC6D0\uD558\uB294 \uC800\uC7A5\uC18C \uC5D4\uC9C4\uC740 \uBCF4\uD1B5 \uCEE4\uBC0B \uD6C4 \uC77D\uAE30 \uACA9\uB9AC\uB97C \uC704\uD574\uC11C\uB3C4 MVCC\uB97C \uC0AC\uC6A9\uD55C\uB2E4. \uCEE4\uBC0B\uB41C \uBC84\uC804\uACFC \uC544\uC9C1 \uCEE4\uBC0B\uB418\uC9C0 \uC54A\uC740 \uBC84\uC804 2\uAC00\uC9C0\uB85C \uCEE4\uBC0B \uD6C4 \uC77D\uAE30 \uACA9\uB9AC\uB97C \uAD6C\uD604\uD560 \uC218 \uC788\uB2E4. \uC2A4\uB0C5\uC20F \uACA9\uB9AC \uAD6C\uD604\uC744 \uC704\uD574\uC11C\uB294 \uD2B8\uB79C\uC7AD\uC158 \uBCC4\uB85C \uACC4\uC18D \uC99D\uAC00\uD558\uB294 \uACE0\uC720\uD55C \uD2B8\uB79C\uC7AD\uC158 ID\uB97C \uD560\uB2F9\uBC1B\uB294\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC774 \uB370\uC774\uD130\uB97C \uC4F8 \uB54C\uB9C8\uB2E4 \uD574\uB2F9 \uD2B8\uB79C\uC7AD\uC158 ID\uC640 \uBC84\uC804\uBCC4 \uAC1D\uCCB4\uAC00 \uAE30\uB85D\uB418\uC5B4 \uD2B8\uB79C\uC7AD\uC158\uC5D0 \uB530\uB77C \uD2B9\uC815 \uBC84\uC804\uC758 \uAC1D\uCCB4\uB4E4\uC744 \uC77D\uC744 \uC218 \uC788\uB3C4\uB85D \uD55C\uB2E4. \uD14C\uC774\uBE14\uC758 \uAC01 \uB85C\uC6B0\uC5D0\uB294 \uADF8 \uB85C\uC6B0\uB97C \uD14C\uC774\uBE14\uC5D0 \uC0BD\uC785\uD55C \uD2B8\uB79C\uC7AD\uC158\uC758 ID\uB97C \uAC16\uB294 <code>created_by</code>\uD544\uB4DC\uAC00 \uC788\uB2E4 \uB610\uD55C <code>deleted_by</code>\uD544\uB4DC\uB3C4 \uC788\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC774 \uB85C\uC6B0\uB97C \uC0AD\uC81C\uD558\uBA74 \uC2E4\uC81C DB\uC5D0\uC11C\uB294 \uC9C0\uC6B0\uC9C0 \uC54A\uACE0 deleted_by\uD544\uB4DC\uB97C \uC0AD\uC81C\uC694\uCCAD \uD2B8\uB79C\uC7AD\uC158\uC758 ID\uB85C \uC124\uC815\uD55C\uB2E4. \uB098\uC911\uC5D0 \uC544\uBB34 \uD2B8\uB79C\uC7AD\uC158\uB3C4 \uC0AD\uC81C\uB41C \uB370\uC774\uD130\uC5D0 \uC811\uADFC\uD558\uC9C0 \uC54A\uB294 \uAC8C \uD655\uC2E4\uD574\uC9C0\uBA74 DB\uB0B4\uC758 \uAC00\uBE44\uC9C0 \uCEEC\uB809\uC158 \uD504\uB85C\uC138\uC2A4\uAC00 \uC9C0\uC6CC\uC84C\uB2E4\uACE0 \uD45C\uC2DC\uB41C \uB85C\uC6B0\uB4E4\uC744 \uC9C0\uC6B4\uB2E4. \uC989 \uB370\uC774\uD130 \uAC31\uC2E0\uC740 \uB0B4\uBD80\uC5D0\uC11C \uC0AD\uC81C\uC640 \uC0DD\uC131\uC73C\uB85C \uBCC0\uD658\uB41C\uB2E4 \uC704\uC758 \uACC4\uC88C \uBB38\uC81C\uC5D0\uC11C \uACC4\uC88C \uD14C\uC774\uBE14\uC5D0 \uC788\uB294 \uACC4\uC88C A\uC758 \uACBD\uC6B0 \uB450 \uAC1C\uC758 \uB85C\uC6B0\uB97C \uAC00\uC9C0\uAC8C \uB41C\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC774 \uC0AD\uC81C\uD55C \uAC83\uC73C\uB85C \uD45C\uC2DC\uB41C \uC794\uACE0 500\uB2EC\uB7EC \uB85C\uC6B0\uC640 \uD574\uB2F9 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC0DD\uC131\uD55C \uC794\uACE0 400\uB2EC\uB7EC\uC758 \uB85C\uC6B0\uC774\uB2E4. \uAC01 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC2DC\uC791\uB418\uB294 \uC2DC\uC810\uC5D0 \uC2E4\uD589 \uC911\uC778 \uD2B8\uB79C\uC7AD\uC158\uB4E4\uC774 \uC4F4 \uB370\uC774\uD130\uC640, \uC5B4\uBCF4\uD2B8 \uB41C \uD2B8\uB79C\uC7AD\uC158\uC774 \uC4F4 \uB370\uC774\uD130, \uD2B8\uB79C\uC7AD\uC158 ID\uAC00 \uB354 \uD070 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC4F4 \uB370\uC774\uD130\uB4E4\uC740 \uD6C4\uC5D0 \uCEE4\uBC0B \uC5EC\uBD80\uC640 \uAD00\uB828 \uC5C6\uC774 \uBAA8\uB450 \uBB34\uC2DC\uB41C\uB2E4.</p></li></ul><p>\uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB294 \uC720\uC6A9\uD55C \uACA9\uB9AC \uC218\uC900\uC774\uBA70 \uD2B9\uD788 \uC77D\uAE30 \uC804\uC6A9 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uC720\uC6A9\uD558\uB2E4. \uC624\uB77C\uD074\uC5D0\uC11C\uB294 \uC9C1\uB82C \uC131 PostgreSQL, MySQL\uC5D0\uC11C\uB294 \uBC18\uBCF5 \uC77D\uAE30(repeatable read)\uB77C\uACE0 \uD45C\uD604\uD55C\uB2E4.</p><h3 id="_3-\u1100\u1162\u11BC\u1109\u1175\u11AB-\u1109\u1169\u11AB\u1109\u1175\u11AF-\u1107\u1161\u11BC\u110C\u1175" tabindex="-1"><a class="header-anchor" href="#_3-\u1100\u1162\u11BC\u1109\u1175\u11AB-\u1109\u1169\u11AB\u1109\u1175\u11AF-\u1107\u1161\u11BC\u110C\u1175" aria-hidden="true">#</a> 3. \uAC31\uC2E0 \uC190\uC2E4 \uBC29\uC9C0</h3><p>\uCEE4\uBC0B \uD6C4 \uC77D\uAE30\uC640 \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB294 \uC8FC\uB85C \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uB418\uB294 \uC4F0\uAE30 \uC791\uC5C5\uC5D0\uC11C \uC77D\uAE30 \uC804\uC6A9 \uD2B8\uB79C\uC7AD\uC158\uC774 \uBB34\uC5C7\uC744 \uBCFC \uC218 \uC788\uB294\uC9C0\uC5D0 \uB300\uD55C \uBCF4\uC7A5\uACFC \uAD00\uB828\uB41C \uAC83\uC774\uB2E4.</p><p>\uB3D9\uC2DC \uC4F0\uAE30\uB97C \uC2E4\uD589\uD560 \uB54C \uBC1C\uC0DD\uD560 \uC218 \uC788\uB294 \uBB38\uC81C\uB294 \uBB34\uC5C7\uC774 \uC788\uC744\uAE4C. \uB354\uD2F0 \uC4F0\uAE30\uBFD0\uB9CC \uC544\uB2C8\uB77C \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uB418\uB294 \uC4F0\uAE30 \uD2B8\uB79C\uC7AD\uC158 \uC0AC\uC774\uC5D0 \uBC1C\uC0DD\uD560 \uC218 \uC788\uB294 \uCDA9\uB3CC \uC0C1\uD669\uB4E4\uC774 \uC874\uC7AC\uD558\uBA70 \uAC00\uC7A5 \uC720\uBA85\uD55C \uAC83\uC774 \uAC31\uC2E0 \uC190\uC2E4(lost update) \uBB38\uC81C\uC774\uB2E4.</p><p>\uAC31\uC2E0 \uC190\uC2E4 \uBB38\uC81C\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C \uAC12\uC744 \uC77D\uACE0 \uBCC0\uACBD\uD55C \uD6C4 \uBCC0\uACBD\uB41C \uAC12\uC744 \uB2E4\uC2DC \uC4F8 \uB54C \uBC1C\uC0DD\uD560 \uC218 \uC788\uB2E4.(read-modify-write \uC8FC\uAE30). \uB450 \uD2B8\uB79C\uC7AD\uC158\uC774 \uB3D9\uC2DC\uC5D0 \uD574\uB2F9 \uC791\uC5C5\uC740 \uC218\uD589\uD558\uB294 \uACBD\uC6B0 \uB450 \uBC88\uC9F8 \uC4F0\uAE30 \uC791\uC5C5\uC774 \uCCAB \uBC88\uC9F8 \uBCC0\uACBD\uC744 \uD3EC\uD568\uD558\uC9C0 \uC54A\uC73C\uBBC0\uB85C \uBC1C\uC0DD\uD560 \uC218 \uC788\uB2E4. \uC989 \uB450 \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uB3D9\uC2DC\uC5D0 read-modify-write \uC8FC\uAE30\uB97C \uC2E4\uD589\uD560 \uB54C \uD55C \uD2B8\uB79C\uC7AD\uC158\uC774 \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC758 \uBCC0\uACBD\uC744 \uD3EC\uD568\uD558\uC9C0 \uC54A\uC740 \uCC44\uB85C \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC4F4 \uB0B4\uC6A9\uC744 \uB36E\uC5B4\uC368\uC11C \uB370\uC774\uD130\uAC00 \uC190\uC2E4\uB418\uB294 \uBB38\uC81C\uC774\uB2E4.</p><ul><li>\uCE74\uC6B4\uD130 \uC99D\uAC00, \uACC4\uC88C\uC794\uACE0 \uAC31\uC2E0(\uD604\uC7AC \uAC12 \uC77D\uACE0 \uC0C8 \uAC12 \uACC4\uC0B0\uD558\uACE0 \uAC31\uC2E0\uB41C \uAC12 \uB2E4\uC2DC \uC368\uC57C \uD55C\uB2E4.)</li><li>json\uBB38\uC11C \uB0B4\uC5D0 \uC788\uB294 \uB9AC\uC2A4\uD2B8\uC5D0 \uC5D8\uB9AC\uBA3C\uD2B8 \uCD94\uAC00 \uB4F1 \uBCF5\uC7A1\uD55C \uAC12\uC744 \uC9C0\uC5ED\uC801 \uBCC0\uACBD</li><li>\uC0AC\uC6A9\uC790 \uD3B8\uC9D1 \uB0B4\uC6A9 \uC800\uC7A5 \uC2DC \uC804\uCCB4 \uB0B4\uC6A9 \uBCF4\uB0B4\uC11C \uB36E\uC5B4\uC4F0\uB294 \uB85C\uC9C1\uC73C\uB85C \uAD6C\uD604\uB41C \uC704\uD0A4\uC5D0\uC11C \uB450 \uBA85\uC758 \uC0AC\uC6A9\uC790 \uB3D9\uC2DC \uD3B8\uC9D1</li></ul><p>\uC774\uCC98\uB7FC \uAC31\uC2E0 \uC190\uC2E4 \uBB38\uC81C\uB294 \uD754\uD55C \uBB38\uC81C\uC774\uAE30 \uB54C\uBB38\uC5D0 \uB2E4\uC591\uD55C \uD574\uACB0\uCC45\uC774 \uAC1C\uBC1C\uB418\uC5C8\uB2E4.</p><ul><li><strong>\uC6D0\uC790\uC801 \uC4F0\uAE30 \uC5F0\uC0B0</strong><br> \uC5EC\uB7EC \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C \uC6D0\uC790\uC801 \uC4F0\uAE30 \uC5F0\uC0B0\uC744 \uC81C\uACF5\uD558\uAE30 \uB54C\uBB38\uC5D0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C read-modify-write \uC8FC\uAE30\uB97C \uAD6C\uD604\uD560 \uD544\uC694\uB97C \uC5C6\uC560 \uC900\uB2E4. UPDATE counters SET value = value + 1 WHERE key = &#39;foo&#39; \uC774 \uCFFC\uB9AC\uB294 \uB300\uBD80\uBD84\uC758 \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C \uB3D9\uC2DC\uC131 \uC548\uC804(concurrency-safe)\uD558\uB2E4. \uC774 \uBC29\uBC95\uC5D0\uC11C\uB294 \uBCF4\uD1B5 \uAC1D\uCCB4\uB97C \uC77D\uC744 \uB54C \uADF8 \uAC1D\uCCB4\uC5D0 \uB3C5\uC810\uC801\uC778 \uC7A0\uAE08\uC744 \uD68D\uB4DD\uD574\uC11C \uAD6C\uD604\uD55C\uB2E4. \uADF8\uB798\uC11C \uAC31\uC2E0\uC774 \uC801\uC6A9\uB420 \uB54C\uAE4C\uC9C0 \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC5D0\uC11C \uADF8 \uAC1D\uCCB4\uB97C \uC77D\uC9C0 \uBABB\uD558\uAC8C \uD55C\uB2E4.</li><li><strong>\uBA85\uC2DC\uC801\uC778 \uC7A0\uAE08</strong><br> \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C \uC6D0\uC790\uC801 \uC4F0\uAE30 \uC5F0\uC0B0\uC744 \uC81C\uACF5\uD558\uC9C0 \uC54A\uB294\uB2E4\uBA74 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C \uAC1D\uCCB4\uB97C \uBA85\uC2DC\uC801\uC73C\uB85C \uC7A0\uADF8\uB294 \uBC29\uC2DD\uC73C\uB85C \uD574\uACB0\uD560 \uC218 \uC788\uB2E4. \uD55C \uD2B8\uB79C\uC7AD\uC158\uC758 read-modify-write\uC758 \uC8FC\uAE30\uAC00 \uC644\uB8CC\uB420 \uB54C\uAE4C\uC9C0 \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC774 \uB3D9\uC2DC\uC5D0 \uAC19\uC740 \uAC1D\uCCB4\uB97C \uC77D\uC744 \uC218 \uC5C6\uACE0 \uAE30\uB2E4\uB824\uC57C \uD55C\uB2E4. \uC218\uB3D9 \uC7A0\uAE08\uC774 \uD544\uC694\uD558\uB2E4.(SELETE FOR UPDATE) \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uB85C\uC9C1\uC5D0 \uB300\uD574 \uC2E0\uC911\uD558\uAC8C \uC0DD\uAC01\uD574\uC57C \uD55C\uB2E4. \uC5B4\uB518\uAC00 \uB85C\uC9C1\uC758 \uC2E4\uC218\uB85C \uACBD\uC7C1\uC870\uAC74 \uC720\uBC1C\uD558\uAE30 \uC27D\uB2E4.</li></ul><ul><li><strong>\uAC31\uC2E0 \uC190\uC2E4 \uC790\uB3D9 \uAC10\uC9C0</strong><br> \uBCD1\uB82C \uC2E4\uD589\uC744 \uD5C8\uC6A9\uD558\uB3C4\uB85D \uD558\uACE0 \uD2B8\uB79C\uC7AD\uC158 \uAD00\uB9AC\uC790\uAC00 \uAC31\uC2E0 \uC190\uC2E4\uC744 \uBC1C\uACAC \uC2DC \uD2B8\uB79C\uC7AD\uC158\uC744 \uC5B4\uBCF4\uD2B8 \uC2DC\uD0A4\uACE0 read-modify-write \uC8FC\uAE30\uB97C \uC7AC\uC2DC\uB3C4\uD558\uB3C4\uB85D \uAC15\uC81C\uD558\uB294 \uBC29\uBC95\uC774 \uC788\uB2E4. \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uCF54\uB4DC\uC5D0\uC11C \uC5B4\uB5A4 \uD2B9\uBCC4\uD55C \uB370\uC774\uD130 \uBCA0\uC774\uC2A4 \uAE30\uB2A5\uB3C4 \uC4F8 \uD544\uC694\uAC00 \uC5C6\uAC8C \uB3C4\uC640\uC8FC\uBBC0\uB85C \uB9E4\uC6B0 \uC88B\uC740 \uAE30\uB2A5\uC774\uB2E4. \uBA85\uC2DC\uC801\uC778 \uC7A0\uAE08\uC744 \uC2E4\uC218\uB85C \uBE7C\uBA39\uC5C8\uC744 \uB54C \uC790\uB3D9\uC73C\uB85C \uAC31\uC2E0 \uC190\uC2E4\uC744 \uAC10\uC9C0\uD558\uC5EC \uD2B8\uB79C\uC7AD\uC158\uC744 \uC5B4\uBCF4\uD2B8 \uC2DC\uD0AC \uC218 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uC624\uB958\uB97C \uB35C \uBC29\uC0DD\uC2DC\uD0A8\uB2E4. \uC774 \uBC29\uBC95\uC758 \uC774\uC810\uC740 \uB370\uC774\uD130 \uBCA0\uC774\uC2A4\uAC00 \uC774 \uD655\uC778\uC744 \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uC640 \uACB0\uD569\uD574 \uD6A8\uC728\uC801\uC73C\uB85C \uC218\uD589\uD560 \uC218 \uC788\uB2E4\uB294 \uAC83\uC774\uB2E4. \uC2E4\uC81C\uB85C PostgreSQL, \uC624\uB77C\uD074, SQL \uC11C\uBC84\uC758 \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB294 \uAC31\uC2E0 \uC190\uC2E4\uC774 \uBC1C\uC0DD\uD558\uBA74 \uC790\uB3D9\uC73C\uB85C \uBC1C\uACAC\uD574\uC11C \uD574\uB2F9 \uD2B8\uB79C\uC7AD\uC158\uC744 \uC5B4\uBCF4\uD2B8 \uC2DC\uD0A4\uB294 \uC774 \uAE30\uB2A5\uC744 \uC81C\uACF5\uD558\uC9C0\uB9CC InnoDB \uAE30\uBC18\uC758 MySQL\uC740 \uC774 \uAE30\uB2A5\uC744 \uC81C\uACF5\uD558\uC9C0 \uC54A\uB294\uB2E4. ..</li></ul><h3 id="\u1104\u1169-\u1103\u1161\u1105\u1173\u11AB-\u1106\u116E\u11AB\u110C\u1166\u1103\u1173\u11AF" tabindex="-1"><a class="header-anchor" href="#\u1104\u1169-\u1103\u1161\u1105\u1173\u11AB-\u1106\u116E\u11AB\u110C\u1166\u1103\u1173\u11AF" aria-hidden="true">#</a> \uB610 \uB2E4\uB978 \uBB38\uC81C\uB4E4</h3><ul><li><p>\uC4F0\uAE30\uC4F0\uD050<br> \uD2B8\uB79C\uC7AD\uC158\uC774 \uBB34\uC5B8\uAC00\uB97C \uC77D\uACE0 \uC77D\uC740 \uAC12\uC744 \uAE30\uBC18\uC73C\uB85C \uC5B4\uB5A4 \uACB0\uC815\uC744 \uD558\uACE0 \uADF8 \uACB0\uC815\uC744 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC4F4\uB2E4. \uADF8\uB7EC\uB098 \uC4F0\uAE30\uB97C \uC2E4\uD589\uD558\uB294 \uC2DC\uC810\uC5D0\uB294 \uACB0\uC815\uC758 \uC804\uC81C\uAC00 \uB354 \uC774\uC0C1 \uCC38\uC774 \uC544\uB2C8\uB2E4. \uC9C1\uB82C \uC131 \uACA9\uB9AC\uB9CC \uC774\uB7F0 \uD604\uC0C1\uC744 \uB9C9\uC744 \uC218 \uC788\uB2E4.</p></li><li><p>\uD32C\uD140 \uC77D\uAE30<br> \uD2B8\uB79C\uC7AD\uC158\uC774 \uC5B4\uB5A4 \uAC80\uC0C9 \uC870\uAC74\uC5D0 \uBD80\uD569\uD558\uB294 \uAC1D\uCCB4\uB97C \uC77D\uB294\uB2E4. \uB2E4\uB978 \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uADF8 \uAC80\uC0C9 \uACB0\uACFC\uC5D0 \uC601\uD5A5\uC744 \uC8FC\uB294 \uC4F0\uAE30\uB97C \uC2E4\uD589\uD55C\uB2E4. \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB294 \uAC04\uB2E8\uD55C \uD32C\uD140 \uC77D\uAE30\uB294 \uB9C9\uC544\uC8FC\uC9C0\uB9CC \uC4F0\uAE30 \uC2A4\uD050 \uB9E5\uB77D\uC5D0\uC11C \uBC1C\uC0DD\uD558\uB294 \uD32C\uD140\uC740 \uC0C9\uC778 \uBC94\uC704 \uC7A0\uAE08\uCC98\uB7FC \uD2B9\uBCC4\uD55C \uCC98\uB9AC\uAC00 \uD544\uC694\uD558\uB2E4.</p></li></ul><h2 id="\u110C\u1175\u11A8\u1105\u1167\u11AF\u1109\u1165\u11BC-\u1100\u1167\u11A8\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#\u110C\u1175\u11A8\u1105\u1167\u11AF\u1109\u1165\u11BC-\u1100\u1167\u11A8\u1105\u1175" aria-hidden="true">#</a> \uC9C1\uB82C\uC131 \uACA9\uB9AC</h2><p>\uC644\uD654\uB41C \uACA9\uB9AC \uC218\uC900\uC740 \uBAA8\uB4E0 \uBB38\uC81C\uB97C \uB9C9\uC544\uC8FC\uC9C0 \uBABB\uD55C\uB2E4. \uC9C1\uB82C\uC131 \uACA9\uB9AC\uB9CC \uBAA8\uB4E0 \uBB38\uC81C\uB4E4\uC744 \uBCF4\uD638\uD574\uC900\uB2E4.</p><h3 id="_1-\u1110\u1173\u1105\u1162\u11AB\u110C\u1162\u11A8\u1109\u1167\u11AB-\u1109\u116E\u11AB\u1109\u1165\u1103\u1162\u1105\u1169-\u1109\u1175\u11AF\u1112\u1162\u11BC" tabindex="-1"><a class="header-anchor" href="#_1-\u1110\u1173\u1105\u1162\u11AB\u110C\u1162\u11A8\u1109\u1167\u11AB-\u1109\u116E\u11AB\u1109\u1165\u1103\u1162\u1105\u1169-\u1109\u1175\u11AF\u1112\u1162\u11BC" aria-hidden="true">#</a> 1. \uD2B8\uB79C\uC7AD\uC158 \uC21C\uC11C\uB300\uB85C \uC2E4\uD589</h3><p>\uD2B8\uB79C\uC7AD\uC158\uC758 \uC2E4\uD589 \uC2DC\uAC04\uC774 \uC544\uC8FC \uC9E7\uACE0 \uD2B8\uB79C\uC7AD\uC158 \uCC98\uB9AC\uB7C9\uC774 \uB2E8\uC77C CPU \uCF54\uC5B4\uC5D0\uC11C \uCC98\uB9AC\uD560 \uC218 \uC788\uC744\uC815\uB3C4\uB85C \uD2B8\uB79C\uC7AD\uC158 \uCC98\uB9AC\uB7C9\uC774 \uB0AE\uB2E4\uBA74 \uC544\uC8FC \uAC04\uB2E8\uD558\uACE0 \uD6A8\uACFC\uC801\uC774\uB2E4.</p><h3 id="_2-2\u1103\u1161\u11AB\u1100\u1168-\u110C\u1161\u11B7\u1100\u1173\u11B7" tabindex="-1"><a class="header-anchor" href="#_2-2\u1103\u1161\u11AB\u1100\u1168-\u110C\u1161\u11B7\u1100\u1173\u11B7" aria-hidden="true">#</a> 2. 2\uB2E8\uACC4 \uC7A0\uAE08</h3><p>\uC218\uC2ED \uB144 \uB3D9\uC548 \uC9C1\uB82C\uC131\uC744 \uAD6C\uD604\uD558\uB294 \uD45C\uC900\uC801\uC778 \uBC29\uBC95\uC774\uC5C8\uC9C0\uB9CC \uC131\uB2A5 \uD2B9\uC131 \uB54C\uBB38\uC5D0 \uC0AC\uC6A9\uC744 \uD53C\uD558\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uB9CE\uB2E4.</p><h3 id="_3-\u110C\u1175\u11A8\u1105\u1167\u11AF\u1109\u1165\u11BC-\u1109\u1173\u1102\u1162\u11B8\u1109\u116D\u11BA-\u1100\u1167\u11A8\u1105\u1175-ssl" tabindex="-1"><a class="header-anchor" href="#_3-\u110C\u1175\u11A8\u1105\u1167\u11AF\u1109\u1165\u11BC-\u1109\u1173\u1102\u1162\u11B8\u1109\u116D\u11BA-\u1100\u1167\u11A8\u1105\u1175-ssl" aria-hidden="true">#</a> 3. \uC9C1\uB82C\uC131 \uC2A4\uB0C5\uC20F \uACA9\uB9AC(SSL)</h3><p>\uC2A4\uB0C5\uC20F \uACA9\uB9AC\uC5D0 \uC9C1\uB82C\uC131\uC758 \uC694\uC18C\uB97C \uB354\uD55C \uAC83\uC73C\uB85C, \uC9C1\uB82C\uC131 \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB294 \uC644\uC804\uD55C \uC9C1\uB82C\uC131\uC744 \uC81C\uACF5\uD558\uC9C0\uB9CC \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uC5D0 \uBE44\uD574 \uC57D\uAC04\uC758 \uC131\uB2A5 \uC190\uD574\uB9CC \uC874\uC7AC\uD55C\uB2E4 \uC9C1\uB82C\uC131 \uC2A4\uB0C5\uC20F \uACA9\uB9AC \uC55E\uC5D0\uC11C \uC5B8\uAE09\uD55C \uACB0\uC810 \uC911 \uB300\uBD80\uBD84\uC744 \uD53C\uD558\uB294 \uC0C1\uB2F9\uD788 \uC0C8\uB85C\uC6B4 \uC54C\uACE0\uB9AC\uC998\uC774\uB2E4. 2\uB2E8\uACC4 \uC7A0\uAE08\uC740 \uBE44\uAD00\uC801 \uB3D9\uC2DC\uC131 \uC81C\uC5B4 \uBA54\uCEE4\uB2C8\uC998\uC774\uB77C\uBA74 \uC9C1\uB82C\uC131 \uC2A4\uB0C5\uC20F \uACA9\uB9AC\uB294 \uB099\uAD00\uC801 \uB3D9\uC2DC\uC131 \uC81C\uC5B4 \uAE30\uBC95\uC774\uB2E4. \uB099\uAD00\uC801\uC778 \uBC29\uBC95\uC744 \uC0AC\uC6A9\uD574\uC11C \uD2B8\uB79C\uC7AD\uC158\uC774 \uCC28\uB2E8\uB418\uC9C0 \uC54A\uACE0 \uC9C4\uD589\uD560 \uC218 \uC788\uAC8C \uD55C\uB2E4. \uD2B8\uB79C\uC7AD\uC158\uC774 \uCEE4\uBC0B\uC744 \uC6D0\uD560 \uB54C \uD2B8\uB79C\uC7AD\uC158\uC744 \uD655\uC778\uD574\uC11C \uC2E4\uD589\uC774 \uC9C1\uB82C\uC801\uC774\uC9C0 \uC54A\uC73C\uBA74 \uC5B4\uBCF4\uD2B8 \uC2DC\uD0A8\uB2E4. \uC704\uD5D8\uD55C \uC0C1\uD669\uC774 \uBC1C\uC0DD\uD560 \uC218 \uC788\uC744 \uB54C\uC5D0\uB3C4 \uC77C\uB2E8 \uC9C4\uD589\uD55C\uB2E4. \uB9C8\uC9C0\uB9C9\uC5D0 \uCEE4\uBC0B\uD560 \uB54C \uACA9\uB9AC \uC704\uBC18\uC744 \uD655\uC778\uD558\uACE0 \uC5B4\uBCF4\uD2B8\uD55C\uB2E4. \uC4F0\uAE30 \uC2A4\uD050\uCC98\uB7FC \uC9C8\uC758 \uACB0\uACFC(\uC804\uC81C \uC870\uAC74)\uC640 \uC4F0\uAE30 \uC791\uC5C5 \uC0AC\uC774 \uC778\uACFC\uC801 \uC758\uC874\uC131\uC774 \uC788\uC744 \uB54C, \uC804\uC81C \uC870\uAC74\uC774 \uCD5C\uC2E0 \uACB0\uACFC\uAC00 \uC544\uB2C8\uBA74, \uC774\uB97C \uAC10\uC9C0\uD574\uC11C \uD2B8\uB79C\uC7AD\uC158\uC744 \uC5B4\uBCF4\uD2B8\uC2DC\uCF1C\uC11C \uC9C1\uB82C\uC131 \uACA9\uB9AC\uB97C \uC81C\uACF5\uD55C\uB2E4. \uC5B4\uBCF4\uD2B8 \uBE44\uC728\uC740 SSI\uC758 \uC804\uCCB4\uC801\uC778 \uC131\uB2A5\uC5D0 \uD070 \uC601\uD5A5\uC744 \uBBF8\uCE5C\uB2E4. \uC774\uB97C\uD14C\uBA74 \uC624\uB79C \uC2DC\uAC04 \uB3D9\uC548 \uB370\uC774\uD130\uB97C \uC77D\uACE0 \uC4F0\uB294 \uD2B8\uB79C\uC7AD\uC158\uC740 \uCDA9\uB3CC\uC774 \uB098\uACE0 \uC5B4\uBCF4\uD2B8\uB418\uAE30 \uC26C\uC6CC\uC11C, SSI\uB294 \uC77D\uAE30 \uC4F0\uAE30 \uD2B8\uB79C\uC7AD\uC158\uC774 \uC0C1\uB2F9\uD788 \uC9E7\uAE30\uB97C \uC694\uAD6C\uD55C\uB2E4 \uC624\uB798 \uC2E4\uD589\uB418\uB294 \uC77D\uAE30 \uC804\uC6A9 \uD2B8\uB79C\uC7AD\uC158\uC740 \uAD1C\uCC2E\uB2E4. 2PL\uC774\uB098 \uC2F1\uAE00 \uC2A4\uB808\uB4DC \uC2E4\uD589\uBCF4\uB2E4\uB294 \uB35C \uBBFC\uAC10\uD558\uB2E4.</p>',31),t=[d];function n(o,s){return r(),a("div",null,t)}var p=e(l,[["render",n],["__file","ch7-transactions-2.html.vue"]]);export{p as default};
