import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as l,c as e,d as a}from"./app.d6b42b88.js";const t={},r=a('<div class="custom-container tip"><p class="custom-container-title">GOAL</p><p>Q. \uBCD1\uD589\uD504\uB85C\uC138\uC2A4\uB780 \uBB34\uC5C7\uC778\uAC00\uC694? Q. \uBBA4\uD14D\uC2A4\uC640 \uC138\uB9C8\uD3EC\uC5B4\uB780 \uBB34\uC5C7\uC774\uACE0 \uB458\uC758 \uCC28\uC774\uC810\uC740 \uBB34\uC5C7\uC778\uAC00\uC694?</p></div><p>\uD574\uB2F9\uD398\uC774\uC9C0\uB294 2022\uB144 3\uC6D4 \uB178\uC158\uC5D0\uC11C \uB9C8\uC774\uADF8\uB808\uC774\uC158 \uB418\uC5C8\uC2B5\uB2C8\uB2E4.</p><h2 id="\u1107\u1167\u11BC\u1112\u1162\u11BC\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173" tabindex="-1"><a class="header-anchor" href="#\u1107\u1167\u11BC\u1112\u1162\u11BC\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173" aria-hidden="true">#</a> \uBCD1\uD589\uD504\uB85C\uC138\uC2A4</h2><ul><li>\uBCD1\uD589\uD504\uB85C\uC138\uC2A4\uB780 \uC5EC\uB7EC \uD504\uB85C\uC138\uC2A4\uB97C \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uD558\uB294 \uAC83\uCC98\uB7FC \uBCF4\uC774\uB294 \uAC83\uC744 \uBCD1\uD589 \uD504\uB85C\uC138\uC2A4\uB77C\uACE0 \uD55C\uB2E4.<br> \uB3C5\uB9BD \uD504\uB85C\uC138\uC2A4 : \uB2E8\uC77C \uCC98\uB9AC \uC2DC\uC2A4\uD15C\uC5D0\uC11C \uC11C\uB85C \uB3C5\uB9BD\uC801\uC73C\uB85C \uC791\uC5C5\uC744 \uC218\uD589\uD558\uB294 \uBCD1\uD589\uD504\uB85C\uC138\uC2A4<br> \uD611\uB825 \uD504\uB85C\uC138\uC2A4 : \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC601\uD5A5\uC744 \uC8FC\uACE0 \uBC1B\uC73C\uBA70 \uC989 \uC0C1\uD638\uC791\uC6A9\uD558\uBA70 \uD2B9\uC815\uAE30\uB2A5\uC744 \uC218\uD589\uD558\uB294 \uBE44\uB3D9\uAE30\uC801 \uD504\uB85C\uC138\uC2A4 \uC774\uB2E4.<div class="custom-container info"><p class="custom-container-title">\uBCD1\uD589\uC131\uACFC \uBCD1\uB82C\uC131</p><p>\uBCD1\uD589\uC131\uACFC \uBCD1\uB82C\uC131\uC740 \uB458 \uB2E4 \uB300\uB7B5\uC801\uC73C\uB85C &#39;\uB3D9\uC77C\uD55C \uC2DC\uAC04\uB3D9\uC548 \uC5EC\uB7EC \uD504\uB85C\uC138\uC2A4\uB97C \uB3D9\uC2DC \uC2E4\uD589&#39; \uC774\uB77C\uB294 \uC758\uBBF8\uB97C \uB2F4\uACE0 \uC788\uB2E4. \uD558\uC9C0\uB9CC \uB458\uC740 \uC804\uD600 \uB2E4\uB974\uB2E4. \uBA3C\uC800 \uBCD1\uB82C\uC131\uC744 \uAE30\uBC18\uC73C\uB85C \uD55C \uBCD1\uB82C \uCEF4\uD4E8\uD305\uC740 \uB2E4\uC911 \uD504\uB85C\uC138\uC11C(CPU) \uC2DC\uC2A4\uD15C\uC5D0\uC11C \uB3D9\uC77C\uD55C \uC2DC\uAC04\uC5D0 \uBCC4\uB3C4\uC758 \uD504\uB85C\uC138\uC11C(CPU)\uC5D0\uC11C \uC2E4\uD589\uD558\uB294 \uAC83\uC774\uB2E4. \uBC18\uBA74\uC5D0 \uBCD1\uD589\uC131\uC744 \uAE30\uBC18\uC73C\uB85C\uD55C \uB3D9\uC2DC \uCEF4\uD4E8\uD305\uC740 \uD504\uB85C\uC138\uC2A4 \uC218\uBA85\uC774 \uACB9\uCE58\uB294 \uAC83\uC774\uC9C0 \uBAA8\uB450 \uB3D9\uC77C\uD55C \uC2DC\uAC04\uC5D0 \uC2E4\uD589\uD560 \uD544\uC694\uB294 \uC5C6\uB2E4. \uC608\uB97C \uB4E4\uC5B4 , \uBCD1\uD589 \uD504\uB85C\uC138\uC2A4\uB294 \uC2DC\uBD84\uD560\uB85C \uAC01 \uD504\uB85C\uC138\uC2A4\uC758 \uC2E4\uD589\uB2E8\uACC4\uB97C \uC804\uD658\uD558\uC5EC \uC5B4\uB290 \uD55C \uC21C\uAC04\uC5D0\uB294 \uD504\uB85C\uC138\uC2A4 \uD558\uB098\uB97C \uC2E4\uD589\uD558\uB3C4\uB85D \uD558\uB294 \uBC29\uBC95\uC73C\uB85C \uB2E8\uC77C \uD504\uB85C\uC138\uC11C(CPU) \uC2DC\uC2A4\uD15C\uC5D0\uC11C\uB3C4 \uAC00\uB2A5\uD558\uB2E4.</p></div></li><li>\uBCD1\uD589 \uD504\uB85C\uC138\uC2A4 \uD574\uACB0\uACFC\uC81C \uC2DC\uC2A4\uD15C\uC758 \uC2E0\uB8B0\uB3C4\uB97C \uB192\uC774\uAE30 \uC704\uD574\uC11C\uB294 \uACF5\uC720\uC790\uC6D0\uC744 \uC0C1\uD638 \uBC30\uD0C0\uC801\uC73C\uB85C \uC0AC\uC6A9\uD574\uC57C\uD55C\uB2E4. \uC5B4\uB5A4 \uD504\uB85C\uC138\uC2A4\uAC00 \uC791\uC5C5\uC744 \uC2E4\uD589 \uC911\uC77C \uB584 \uB098\uBA38\uC9C0 \uD504\uB85C\uC138\uC2A4\uB294 \uADF8 \uC791\uC5C5\uC5D0 \uAD00\uB828\uB418 \uC791\uC5C5\uC744 \uC218\uD589\uD560 \uC218 \uC5C6\uB3C4\uB85D \uC0C1\uD638\uBC30\uC81C\uC640 \uB3D9\uAE30\uD654\uB97C \uD574\uC57C\uD55C\uB2E4. \uD504\uB85C\uC138\uC2A4\uB294 \uB3D9\uC2DC\uC5D0 \uC218\uD589\uD558\uB294 \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC758 \uC2E4\uD589\uC18D\uB3C4\uC640 \uAD00\uACC4\uC5C6\uC774 \uD56D\uC0C1 \uC77C\uC815\uD55C \uC2E4\uD589 \uACB0\uACFC\uB97C \uBCF4\uC7A5\uD558\uB3C4\uB85D \uACB0\uC815\uC131\uC744 \uD655\uBCF4\uD574\uC57C\uD55C\uB2E4. \uD2B9\uD788 \uAD50\uCC29\uC0C1\uD0DC\uB97C \uD574\uACB0\uD558\uACE0 \uBCD1\uD589 \uD504\uB85C\uC138\uC2A4\uB4E4\uC758 \uBCD1\uB82C\uCC98\uB9AC \uB2A5\uB825\uC744 \uADF9\uB300\uD654\uC2DC\uCF1C\uC57C \uD55C\uB2E4</li></ul><h2 id="\u1109\u1161\u11BC\u1112\u1169\u1107\u1162\u110C\u1166\u110B\u116A-\u1103\u1169\u11BC\u1100\u1175\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#\u1109\u1161\u11BC\u1112\u1169\u1107\u1162\u110C\u1166\u110B\u116A-\u1103\u1169\u11BC\u1100\u1175\u1112\u116A" aria-hidden="true">#</a> \uC0C1\uD638\uBC30\uC81C\uC640 \uB3D9\uAE30\uD654</h2><p>\uC0C1\uD638\uBC30\uC81C : \uBCD1\uD589 \uD504\uB85C\uC138\uC2A4\uC5D0\uC11C \uD504\uB85C\uC138\uC2A4 \uD558\uB098\uAC00 \uACF5\uC720\uC790\uC6D0\uC744 \uC0AC\uC6A9\uD560 \uB54C \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uAC00 \uD574\uB2F9 \uB370\uC774\uD130\uC5D0 \uC811\uADFC \uD560 \uC218 \uC5C6\uAC8C \uD558\uB294 \uAC83<br> \uB3D9\uAE30\uD654 : \uACF5\uC720 \uC790\uC6D0\uC744 \uB3D9\uC2DC\uC5D0 \uC0AC\uC6A9\uD558\uC9C0 \uBABB\uD558\uAC8C \uC2E4\uD589\uC744 \uC81C\uC5B4\uD558\uB294 \uBC29\uBC95 *\uACBD\uC7C1\uC0C1\uD0DC(race condition) : \uC5EC\uB7EC\uD504\uB85C\uC138\uC2A4\uAC00 \uB3D9\uC2DC\uC5D0 \uACF5\uC720\uB370\uC774\uD130\uC5D0 \uC811\uADFC\uD560 \uB584 \uC811\uADFC \uC21C\uC11C\uC5D0 \uB530\uB77C \uC2E4\uD589\uACB0\uACFC\uAC00 \uB2EC\uB77C\uC9C0\uB294 \uC0C1\uD669\uC774\uB2E4.</p><h3 id="\u1107\u1161\u11AF\u1109\u1162\u11BC\u1112\u1161\u11AF-\u1109\u116E-\u110B\u1175\u11BB\u1102\u1173\u11AB-\u110B\u1175\u11B7\u1100\u1168\u1100\u116E\u110B\u1167\u11A8-\u1106\u116E\u11AB\u110C\u1166\u110B\u116A-\u1112\u1162\u1100\u1167\u11AF\u110B\u1161\u11AB\u110B\u1173\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1107\u1161\u11AF\u1109\u1162\u11BC\u1112\u1161\u11AF-\u1109\u116E-\u110B\u1175\u11BB\u1102\u1173\u11AB-\u110B\u1175\u11B7\u1100\u1168\u1100\u116E\u110B\u1167\u11A8-\u1106\u116E\u11AB\u110C\u1166\u110B\u116A-\u1112\u1162\u1100\u1167\u11AF\u110B\u1161\u11AB\u110B\u1173\u11AB" aria-hidden="true">#</a> \uBC1C\uC0DD\uD560 \uC218 \uC788\uB294 \uC784\uACC4\uAD6C\uC5ED \uBB38\uC81C\uC640 \uD574\uACB0\uC548\uC740?</h3><ul><li><p>\uC784\uACC4\uAD6C\uC5ED(critical section) :</p><ul><li>\uAC01 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC788\uB294, \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uB4E4\uACFC \uACF5\uC720\uD558\uB294 \uBCC0\uC218\uB97C \uBCC0\uACBD, \uB370\uC774\uD130 \uAC31\uC2E0, \uD30C\uC77C\uC4F0\uAE30 \uB4F1\uC758 \uC791\uC5C5\uC744 \uD558\uB294 \uCF54\uB4DC \uBD80\uBD84\uC774\uB2E4.</li><li>\uD504\uB85C\uADF8\uB7A8 \uCF54\uB4DC \uC0C1\uC5D0\uC11C \uACF5\uC720 \uC790\uC6D0\uC5D0 \uC811\uADFC\uD558\uB294 \uBD80\uBD84\uC744 \uC784\uACC4 \uAD6C\uC5ED\uC774\uB77C \uD55C\uB2E4.</li><li>\uBA40\uD2F0\uD504\uB85C\uC138\uC2A4\uAC00 \uB098\uC624\uBA74\uC11C \uB370\uC774\uD130\uB97C \uC5B4\uB290 \uD504\uB85C\uC138\uC2A4\uAC00 \uC870\uC791\uD558\uACE0 \uC788\uB294\uB370 \uC911\uAC04\uC5D0 \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uAC00 \uB07C\uC5B4\uB4E4\uC5B4\uC11C \uB370\uC774\uD130\uB97C \uC870\uC791\uD558\uBA74 \uC758\uB3C4\uD558\uC9C0 \uBABB\uD55C \uACB0\uACFC\uB97C \uAC00\uC838\uC62C \uC218\uC788\uB2E4 (\uC784\uACC4\uAD6C\uC5ED\uC5D0\uC11C \uB3D9\uC2DC\uC791\uC5C5\uC774 \uAC00\uB2A5\uD558\uB2E4\uBA74)</li></ul></li><li><p>\uD55C \uC21C\uAC04\uC5D0 \uD558\uB098\uC758 \uD504\uB85C\uC138\uC2A4\uB9CC \uC784\uACC4\uAD6C\uC5ED\uC744 \uCEE8\uD2B8\uB864 \uD560 \uC218 \uC788\uB3C4\uB85D \uC81C\uC5B4\uD55C\uB2E4. (e.counter \uC0AC\uC6A9)</p></li><li><p>\uC784\uACC4\uAD6C\uC5ED \uD574\uACB0\uC548\uC758 \uC694\uAD6C\uC0AC\uD56D \u21D2 \uC0C1\uD638\uBC30\uC81C \uC694\uAC74, \uC9C4\uD589, \uC81C\uD55C\uC774 \uC788\uB294 \uB300\uAE30\uC2DC\uAC04</p></li><li><p>\uD53C\uD130\uC2A8\uC758 \uD574\uACB0\uC548 : \uB450\uAC1C\uC758 \uD504\uB85C\uC138\uC2A4\uAC00 \uB450\uAC1C\uC758 \uB370\uC774\uD130 \uD56D\uBAA9 \uD134 \uD50C\uB798\uADF8\uBC30\uC5F4\uC774\uC6A9\uD574\uC11C \uC784\uACC4\uAD6C\uC5ED \uC73C\uB85C \uC9C4\uC785\uD560\uC218\uC788\uB294\uC9C0 \uC5EC\uBD80 \uD310\uBCC4</p></li><li><p>\uBBA4\uD14D\uC2A4\uC640 \uC138\uB9C8\uD3EC\uC5B4\uC758 \uAC1C\uB150\uC744 \uC774\uD574\uD55C\uB2E4.</p><ul><li>\uBBA4\uD14D\uC2A4 : \uC0C1\uD638\uBC30\uC81C\uB97C \uAC00\uB2A5\uD558\uB3C4\uB85D \uD558\uB294 \uB77D(lock) / \uBBA4\uD14D\uC2A4\uB77D <ul><li>\uC784\uACC4 \uAD6C\uC5ED\uC744 \uBCF4\uD638</li><li>\uD558\uB098\uC758 \uB370\uC774\uD130\uC5D0 \uD504\uB85C\uC138\uC2A4\uB4E4\uC774 \uB3D9\uC2DC\uC5D0 \uACBD\uC7C1\uD558\uC9C0 \uC54A\uB3C4\uB85D \uD574\uC900\uB2E4.</li></ul></li><li>\uC138\uB9C8\uD3EC\uC5B4 <ul><li>\uC815\uC218 \uBCC0\uC218 S(\uCE74\uC6B4\uD130 \uBCC0\uC218)</li><li>\uBBA4\uD14D\uC2A4\uC640 \uB2E4\uB974\uAC8C 1\uBCF4\uB2E4 \uD070 \uC815\uC218 \uC0AC\uC6A9 \uAC00\uB2A5, \uC811\uADFC\uAC00\uB2A5\uD55C \uD504\uB85C\uC138\uC2A4 \uAC1C\uC218\uB97C \uD1B5\uC81C \uAC00\uB2A5</li><li>wait / signal \uC5F0\uC0B0\uC744 \uC218\uD589\uD558\uC5EC \uB3D9\uAE30\uD654 \uBB38\uC81C\uB97C \uD574\uACB0</li></ul></li></ul><p>\u21D2 \uB458 \uB2E4 busy waiting \uB2E8\uC810 \uC874\uC7AC\uD55C\uB2E4<br> =&gt; \uBBA4\uD14D\uC2A4(Mutex)\uC640 \uC138\uB9C8\uD3EC\uC5B4\uC758 \uCC28\uC774\uC810</p><ul><li>\uBBA4\uD14D\uC2A4\uB294 \uC624\uC9C1 1\uAC1C\uC758 \uD504\uB85C\uC138\uC2A4 \uD639\uC740 \uC2A4\uB808\uB4DC\uB9CC\uC774 \uACF5\uC720 \uC790\uC6D0\uC5D0 \uC811\uADFC\uD560 \uC218 \uC788\uACE0, \uC138\uB9C8\uD3EC\uC5B4\uB294 \uC9C0\uC815\uB41C \uBCC0\uC218\uC758 \uAC12\uB9CC\uD07C \uC811\uADFC\uD560 \uC218 \uC788\uB2E4.</li><li>\uC138\uB9C8\uD3EC\uC5B4\uB294 \uC6B4\uC601\uCCB4\uC81C \uD639\uC740 \uCEE4\uB110 \uB2E8\uC704\uC5D0\uC11C \uD574\uB2F9 \uB9AC\uC18C\uC2A4 \uBCC0\uC218\uAC00 \uAD00\uB9AC\uB418\uC5B4 \uD604\uC7AC \uACF5\uC720 \uC790\uC6D0\uC744 \uC0AC\uC6A9 \uC911\uC778 \uB300\uC0C1 \uBFD0\uB9CC\uC544\uB2C8\uB77C \uB2E4\uB978 \uD504\uB85C\uC138\uC2A4 \uBC0F \uC2A4\uB808\uB4DC\uB3C4 \uC7A0\uAE08 \uC0C1\uD0DC\uB97C \uD574\uC81C\uD560 \uC218 \uC788\uC9C0\uB9CC \uBBA4\uD14D\uC2A4\uB294 \uD504\uB85C\uC138\uC2A4 \uB2E8\uC5D0\uC11C \uAD00\uB9AC\uB418\uACE0 \uD574\uB2F9 \uBCC0\uC218(Lock)\uC744 \uAC00\uC9C0\uACE0 \uC788\uAE30 \uB54C\uBB38\uC5D0 Lock\uC744 \uAC00\uC9C0\uACE0 \uC788\uB294 \uBCC0\uC218\uB9CC\uC774 Unlock\uC744 \uD560 \uC218 \uC788\uB2E4.</li></ul></li></ul><h2 id="\u1109\u1161\u11BC\u1112\u1169\u1107\u1162\u110C\u1166-\u1107\u1161\u11BC\u1107\u1165\u11B8" tabindex="-1"><a class="header-anchor" href="#\u1109\u1161\u11BC\u1112\u1169\u1107\u1162\u110C\u1166-\u1107\u1161\u11BC\u1107\u1165\u11B8" aria-hidden="true">#</a> \uC0C1\uD638\uBC30\uC81C \uBC29\uBC95</h2><ul><li>\uB370\uCEE4\uC758 \uC54C\uACE0\uB9AC\uC998: \uBCD1\uD589 \uD504\uB85C\uADF8\uB798\uBC0D\uC758 \uC0C1\uD638\uBC30\uC81C\uB97C \uD480 \uC218 \uC788\uB294 \uCCAB\uBC88\uC9F8 \uD574\uACB0\uCC45\uC73C\uB85C, \uB450 \uD504\uB85C\uC138\uC2A4\uAC00 \uC11C\uB85C \uD1B5\uC2E0\uD558\uB824\uACE0 \uACF5\uC720 \uBA54\uBAA8\uB9AC\uB97C \uC0AC\uC6A9\uD558\uC5EC \uCDA9\uB3CC \uC5C6\uC774 \uB2E8\uC77C \uC790\uC6D0\uC744 \uACF5\uC720 \uD560 \uC218 \uC788\uB3C4\uB85D \uD5C8\uC6A9\uD55C\uB2E4.</li><li>TestAndSet(TAS,\uD14C\uC2A4) \uBA85\uB839\uC5B4 : \uC18C\uD504\uD2B8\uC6E8\uC5B4\uC801\uC778 \uD574\uACB0\uCC45\uC740 \uB354 \uBCF5\uC7A1\uD558\uACE0, \uD504\uB85C\uC138\uC2A4\uAC00 2\uAC1C \uC774\uC0C1\uC77C\uB54C\uB294 \uB354 \uB9CE\uC774 \uB300\uAE30 \uD560 \uC218 \uC788\uB2E4. \uC774\uB54C \uAE30\uACC4\uB97C \uBE44\uAD50\uD558\uAC70\uB098 \uB2E8\uC5B4 \uB0B4\uC694\u3155\u3147\uC744 \uAC80\uC0AC.\uC218\uC815\uD558\uB294 \uD2B9\uBCC4\uD55C \uD558\uB4DC\uC6E8\uC5B4 \uBA85\uB839\uC5B4 TestAndSet\uC744 \uC0AC\uC6A9\uD558\uC5EC \uAC04\uB2E8\uD55C \uBC29\uBC95\uC73C\uB85C \uC784\uACC4\uC601\uC5ED \uBB38\uC81C\uB97C \uD574\uACB0\uD560 \uC218 \uC788\uB2E4</li><li>\uC138\uB9C8\uD3EC: \uC0C1\uD638\uBC30\uC81C \uD574\uACB0\uCC45\uC5D0\uB294 \uBC14\uC05C \uB300\uAE30\uB85C \uD504\uB85C\uC138\uC2A4\uB97C \uB0AD\uBE44\uD55C\uB2E4\uB294 \uB2E8\uC810\uC774 \uC788\uB2E4. \uB2E4\uC775\uC2A4\uD2B8\uB77C\uAC00 \uC9C4\uC785\uC870\uAC74\uC744 \uBC18\uBCF5 \uC870\uC0AC\uD558\uC9C0 \uC54A\uACE0 true \uC77C\uB54C \uD504\uB85C\uC138\uC2A4\uC0C1\uD0DC\uB97C \uD655\uC778\uD558\uB294 \uC0C8\uB85C\uC6B4 \uB3D9\uAE30\uD654 \uB3C4\uAD6C\uC778 \uC138\uB9C8\uD3EC\uB97C \uC81C\uC548\uD558\uC5EC \uC774\uBB38\uC81C\uB97C \uD574\uACB0\uD588\uB2E4. \uC138\uB9C8\uD3EC\uB294 \uC0C1\uD638\uBC30\uC81C\uC5D0 \uC0AC\uC6A9\uD560\uBFD0\uB9CC \uC544\uB2C8\uB77C \uB2E4\uC591\uD55C \uC5F0\uC0B0\uC758 \uC21C\uC11C\uB3C4 \uC81C\uACF5\uD55C\uB2E4.</li><li>\uBAA8\uB2C8\uD130 : \uC138\uB9C8\uD3EC\uB97C \uC798\uBABB \uC0AC\uC6A9\uD558\uBA74 \uC5EC\uB7EC\uAC00\uC9C0 \uC624\uB958\uAC00 \uC27D\uAC8C \uBC1C\uC0DD\uD558\uC5EC \uD504\uB85C\uADF8\uB7A8\uC744 \uC791\uC131\uD558\uAE30\uAC00 \uC5B4\uB835\uB2E4. \uC774\uB7F0 \uB2E8\uC810\uC744 \uADF9\uBCF5\uD558\uB824\uACE0 \uBAA8\uB2C8\uD130\uAC00 \uB4F1\uC7A5\uD588\uB2E4. \uBAA8\uB2C8\uD130\uB294 \uACF5\uC720\uC790\uC6D0\uACFC \uC774\uAC83\uC758 \uC784\uACC4\uC601\uC5ED\uC744 \uAD00\uB9AC\uD558\uB294 \uC18C\uD504\uD2B8\uC6E8\uC5B4 \uAD6C\uC131\uCCB4\uC774\uB2E4. \uB2E4\uC2DC\uB9D0\uD574 \uC790\uC6D0\uC5D0 \uBC30\uD0C0\uC801\uC73C\uB85C \uC811\uADFC\uD560 \uC218 \uC788\uB3C4\uB85D \uD504\uB85C\uC138\uC2A4\uAC00 \uC0AC\uC6A9\uD558\uB294 \uBCD1\uD589 \uD504\uB85C\uADF8\uB798\uBC0D \uAD6C\uC870\uC774\uB2E4.</li></ul><h3 id="\u110B\u1167\u1105\u1165-\u110C\u1165\u11AB\u1110\u1169\u11BC\u110C\u1165\u11A8\u110B\u1175\u11AB-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1103\u1169\u11BC\u1100\u1175\u1112\u116A-\u1106\u116E\u11AB\u110C\u1166" tabindex="-1"><a class="header-anchor" href="#\u110B\u1167\u1105\u1165-\u110C\u1165\u11AB\u1110\u1169\u11BC\u110C\u1165\u11A8\u110B\u1175\u11AB-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1103\u1169\u11BC\u1100\u1175\u1112\u116A-\u1106\u116E\u11AB\u110C\u1166" aria-hidden="true">#</a> \uC5EC\uB7EC \uC804\uD1B5\uC801\uC778 \uD504\uB85C\uC138\uC2A4 \uB3D9\uAE30\uD654 \uBB38\uC81C</h3><ul><li>\uC0DD\uC0B0\uC790.\uC18C\uBE44\uC790 \uBB38\uC81C : \uC6B4\uC601\uCCB4\uC81C\uC5D0\uC11C \uBE44\uB3D9\uAE30\uC801\uC73C\uB85C \uC218\uD589\uD558\uB294 \uBAA8\uB378\uB85C, \uC0DD\uC0B0\uC790 \uD504\uB85C\uC138\uC2A4\uAC00 \uC0DD\uC0B0\uD55C \uC815\uBCF4\uB97C \uC18C\uBE44\uC790 \uD504\uB85C\uC138\uC2A4\uAC00 \uC18C\uBE44\uD558\uB294 \uD615\uD0DC\uC774\uB2E4. \uC0DD\uC0B0\uC790\uC640 \uC18C\uBE44\uC790\uAC00 \uBD88\uD544\uC694\uD558\uAC8C \uACF5\uD68C\uC804\uD558\uC9C0 \uC54A\uB3C4\uB85D \uC0DD\uC0B0\uC790\uC640 \uC18C\uBE44\uC790\uB97C \uB3D9\uAE30\uD654 \uD574\uC57C\uD55C\uB2E4.</li><li>\uC720\uD55C \uBC84\uD37C \uBB38\uC81C</li><li>Readers, Writers \uBB38\uC81C</li><li>\uC2DD\uC0AC\uD558\uB294 \uCCA0\uD558\uC790\uB294 \uBB38\uC81C</li></ul>',12),c=[r];function n(s,d){return l(),e("div",null,c)}var u=i(t,[["render",n],["__file","ch8-\uBCD1\uD589-\uD504\uB85C\uC138\uC2A4\uC640-\uC0C1\uD638\uBC30\uC81C.html.vue"]]);export{u as default};
