import{_ as o}from"./plugin-vue_export-helper.21dcd24c.js";import{r as t,o as n,c,a as i,b as a,d as s,e}from"./app.d6b42b88.js";const r={},p=s('<p>AWS-SAA \uC790\uACA9\uC99D \uD56D\uBAA9\uC744 \uAE30\uC900\uC73C\uB85C AWS\uB97C \uACF5\uBD80 \uD569\uB2C8\uB2E4. \uAC01 \uD56D\uBAA9\uC758 \uC758\uBBF8\uB97C \uC0B4\uD3B4\uBCF4\uACE0 \uC911\uC810\uC0AC\uD56D\uC744 \uD30C\uC545\uD558\uB294 \uAC83\uC744 \uBAA9\uD45C\uB85C \uD569\uB2C8\uB2E4.\uAC01 \uD56D\uBAA9\uBCC4 \uC774\uB860/\uC2E4\uC2B5\uC73C\uB85C \uD559\uC2B5\uD569\uB2C8\uB2E4.</p><details class="custom-container details"><summary>\uC0C1\uC138 \uC601\uC5ED ( \uCD9C\uCC98: aws certification \uD648\uD398\uC774\uC9C0 )</summary><ul><li><p>2.1 \uC6CC\uD06C\uB85C\uB4DC\uB97C \uC704\uD55C \uD0C4\uB825\uC801\uC774\uACE0 \uD655\uC7A5 \uAC00\uB2A5\uD55C \uCEF4\uD4E8\uD305 \uC194\uB8E8\uC158 \uC2DD\uBCC4 \u{1F448}\u{1F448}\u{1F448}\u{1F448}\u{1F448} <strong>\uC9C0\uAE08 \uC5EC\uAE30</strong></p><ul><li>\uCEF4\uD4E8\uD305, \uC2A4\uD1A0\uB9AC\uC9C0 \uBC0F \uB124\uD2B8\uC6CC\uD0B9 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uC801\uC808\uD55C \uC778\uC2A4\uD134\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC131\uB2A5 \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB3C4\uB85D \uD655\uC7A5 \uAC00\uB2A5\uD55C \uC801\uC808\uD55C \uC544\uD0A4\uD14D\uCC98 \uBC0F \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC194\uB8E8\uC158\uC758 \uC131\uB2A5\uC744 \uBAA8\uB2C8\uD130\uB9C1\uD558\uB294 \uC9C0\uD45C\uB97C \uC2DD\uBCC4\uD569\uB2C8\uB2E4.</li></ul></li><li><p>2.2 \uC6CC\uD06C\uB85C\uB4DC\uB97C \uC704\uD55C \uD655\uC7A5 \uAC00\uB2A5\uD55C \uACE0\uC131\uB2A5 \uC2A4\uD1A0\uB9AC\uC9C0 \uC194\uB8E8\uC158 \uC120\uD0DD</p><ul><li>\uC131\uB2A5 \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB294 \uC2A4\uD1A0\uB9AC\uC9C0 \uC11C\uBE44\uC2A4 \uBC0F \uAD6C\uC131\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uD5A5\uD6C4 \uC694\uAD6C \uC0AC\uD56D\uC744 \uC218\uC6A9\uD558\uB3C4\uB85D \uD655\uC7A5 \uAC00\uB2A5\uD55C \uC2A4\uD1A0\uB9AC\uC9C0 \uC11C\uBE44\uC2A4\uB97C \uACB0\uC815\uD569\uB2C8\uB2E4.</li></ul></li><li><p>2.3 \uC6CC\uD06C\uB85C\uB4DC\uB97C \uC704\uD55C \uACE0\uC131\uB2A5 \uB124\uD2B8\uC6CC\uD0B9 \uC194\uB8E8\uC158 \uC120\uD0DD</p><ul><li>\uC131\uB2A5 \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB294 \uB370 \uC801\uC808\uD55C AWS \uC5F0\uACB0 \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>AWS \uD37C\uBE14\uB9AD \uC11C\uBE44\uC2A4\uC5D0 \uB300\uD55C \uC5F0\uACB0\uC744 \uCD5C\uC801\uD654\uD558\uB294 \uB370 \uC801\uC808\uD55C \uAE30\uB2A5\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC131\uB2A5 \uC774\uC810\uC744 \uC81C\uACF5\uD558\uB294 \uC5E3\uC9C0 \uCE90\uC2F1 \uC804\uB7B5\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uB9C8\uC774\uADF8\uB808\uC774\uC158 \uBC0F/\uB610\uB294 \uC218\uC9D1\uC5D0 \uC801\uD569\uD55C \uB370\uC774\uD130 \uC804\uC1A1 \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li><li><p>2.4 \uC6CC\uD06C\uB85C\uB4DC\uB97C \uC704\uD55C \uACE0\uC131\uB2A5 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC194\uB8E8\uC158 \uC120\uD0DD</p><ul><li>\uC801\uC808\uD55C \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC870\uC815 \uC804\uB7B5\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC131\uB2A5 \uD5A5\uC0C1\uC744 \uC704\uD574 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uCE90\uC2F1\uC774 \uD544\uC694\uD55C \uC2DC\uC810\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC131\uB2A5 \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB294 \uC801\uD569\uD55C \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li></ul></details><h2 id="domain-2-\u1100\u1169\u1109\u1165\u11BC\u1102\u1173\u11BC-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#domain-2-\u1100\u1169\u1109\u1165\u11BC\u1102\u1173\u11BC-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> Domain 2 \uACE0\uC131\uB2A5 \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4</h2><h2 id="\u110B\u116F\u110F\u1173\u1105\u1169\u1103\u1173\u1105\u1173\u11AF-\u110B\u1171\u1112\u1161\u11AB-\u1110\u1161\u11AB\u1105\u1167\u11A8\u110C\u1165\u11A8\u110B\u1175\u1100\u1169-\u1112\u116A\u11A8\u110C\u1161\u11BC-\u1100\u1161\u1102\u1173\u11BC\u1112\u1161\u11AB-\u110F\u1165\u11B7\u1111\u1172\u1110\u1175\u11BC-\u1109\u1169\u11AF\u1105\u116E\u1109\u1167\u11AB-\u1109\u1175\u11A8\u1107\u1167\u11AF" tabindex="-1"><a class="header-anchor" href="#\u110B\u116F\u110F\u1173\u1105\u1169\u1103\u1173\u1105\u1173\u11AF-\u110B\u1171\u1112\u1161\u11AB-\u1110\u1161\u11AB\u1105\u1167\u11A8\u110C\u1165\u11A8\u110B\u1175\u1100\u1169-\u1112\u116A\u11A8\u110C\u1161\u11BC-\u1100\u1161\u1102\u1173\u11BC\u1112\u1161\u11AB-\u110F\u1165\u11B7\u1111\u1172\u1110\u1175\u11BC-\u1109\u1169\u11AF\u1105\u116E\u1109\u1167\u11AB-\u1109\u1175\u11A8\u1107\u1167\u11AF" aria-hidden="true">#</a> \uC6CC\uD06C\uB85C\uB4DC\uB97C \uC704\uD55C \uD0C4\uB825\uC801\uC774\uACE0 \uD655\uC7A5 \uAC00\uB2A5\uD55C \uCEF4\uD4E8\uD305 \uC194\uB8E8\uC158 \uC2DD\uBCC4</h2><p>\uC194\uB8E8\uC158\uC758 \uD655\uC7A5\uC131 \uB610\uB294 \uD0C4\uB825\uC131\uC740 \uC544\uD0A4\uD14D\uCC98\uC5D0\uC11C \uC0AC\uC6A9\uD558\uAE30\uB85C \uC120\uD0DD\uD55C AWS \uC11C\uBE44\uC2A4\uC640 \uD568\uAED8 \uC791\uB3D9\uD558\uB3C4\uB85D \uD574\uB2F9 \uC11C\uBE44\uC2A4\uB97C \uAD6C\uC131\uD55C \uBC29\uBC95\uC5D0 \uB530\uB77C \uB2EC\uB77C\uC9C8 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uC608\uB97C \uB4E4\uC5B4 AWS Lambda\uC640 \uAC19\uC740 \uC11C\uBE44\uC2A4\uB294 \uC544\uBB34 \uAC83\uB3C4 \uD560 \uD544\uC694 \uC5C6\uC774 \uC790\uB3D9\uC73C\uB85C \uD0C4\uB825\uC801\uC785\uB2C8\uB2E4. \uD655\uC7A5 \uAC00\uB2A5\uD558\uACE0 \uD0C4\uB825\uC801\uC778 \uC11C\uBE44\uC2A4\uC785\uB2C8\uB2E4. Lambda \uD568\uC218\uB97C \uCD08\uB2F9 \uD55C \uBC88 \uB610\uB294 100\uBC88 \uD638\uCD9C\uD574\uC57C \uD558\uB294 \uACBD\uC6B0 \uAC04\uB2E8\uD558\uAC8C \uC218\uD589\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. AWS Lambda\uB97C \uD655\uC7A5\uD558\uAE30 \uC704\uD574 \uC11C\uBE44\uC2A4\uB97C \uD655\uC7A5\uD558\uAC70\uB098 \uC544\uD0A4\uD14D\uCC98\uB97C \uC124\uACC4\uD560 \uD544\uC694\uAC00 \uC5C6\uC2B5\uB2C8\uB2E4.</p><p>Amazon EC2\uB294 \uBCF8\uC9C8\uC801\uC73C\uB85C \uD655\uC7A5 \uAC00\uB2A5\uD558\uC9C0 \uC54A\uC740 \uC11C\uBE44\uC2A4\uC774\uC9C0\uB9CC Amazon EC2\uB97C \uD655\uC7A5 \uAC00\uB2A5\uD558\uACE0 \uD0C4\uB825\uC801\uC73C\uB85C \uB9CC\uB4DC\uB294 \uBC29\uBC95\uC5D0\uB294 \uC5EC\uB7EC \uAC00\uC9C0\uAC00 \uC788\uC2B5\uB2C8\uB2E4. Amazon EC2\uB97C \uC0AC\uC6A9\uD558\uC5EC \uC194\uB8E8\uC158\uC744 \uC124\uACC4\uD558\uAE30 \uC2DC\uC791\uD560 \uB54C \uAC00\uC7A5 \uBA3C\uC800 \uACE0\uB824\uD574\uC57C \uD560 \uC0AC\uD56D\uC740 \uC5B4\uB5A4 Amazon EC2 \uC778\uC2A4\uD134\uC2A4 \uC720\uD615\uC744 \uC0AC\uC6A9\uD560 \uAC83\uC785\uB2C8\uAE4C?</p><ul><li><p>\uCEF4\uD4E8\uD305, \uC2A4\uD1A0\uB9AC\uC9C0 \uBC0F \uB124\uD2B8\uC6CC\uD0B9 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uC801\uC808\uD55C \uC778\uC2A4\uD134\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</p><ul><li>\uC62C\uBC14\uB978 \uC778\uC2A4\uD134\uC2A4 \uC720\uD615\uC744 \uC120\uD0DD\uD558\uB824\uBA74 \uBA3C\uC800 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0 \uD544\uC694\uD55C \uAC83\uC774 \uBB34\uC5C7\uC778\uC9C0 \uC54C\uC544\uC57C \uD569\uB2C8\uB2E4.</li><li>\uCEF4\uD4E8\uD305, \uC2A4\uD1A0\uB9AC\uC9C0 \uBC0F \uB124\uD2B8\uC6CC\uD0B9 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB300\uD55C \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uB9AC\uC18C\uC2A4 \uC694\uAD6C \uC0AC\uD56D\uC744 \uC54C\uACE0 \uC788\uB294 \uACBD\uC6B0 Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uC801\uD569\uD55C \uC778\uC2A4\uD134\uC2A4 \uD328\uBC00\uB9AC\uB97C \uC120\uD0DD\uD560 \uC218 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4.</li><li>\uBAA8\uB4E0 \uB2E8\uC77C \uC778\uC2A4\uD134\uC2A4 \uC720\uD615\uC744 \uC678\uC6B8 \uD544\uC694\uB294 \uC5C6\uC9C0\uB9CC \uC6CC\uD06C\uB85C\uB4DC\uC5D0 \uB300\uD55C \uC778\uC2A4\uD134\uC2A4 \uD328\uBC00\uB9AC\uB97C \uC120\uD0DD\uD558\uB294 \uAC83\uC774 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC131\uB2A5\uACFC \uD655\uC7A5\uC131\uC5D0 \uC5B4\uB5A4 \uC601\uD5A5\uC744 \uBBF8\uCE60 \uC218 \uC788\uB294\uC9C0 \uD655\uC2E4\uD788 \uC774\uD574\uD574\uC57C \uD569\uB2C8\uB2E4.</li></ul></li><li><p>\uC131\uB2A5 \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB3C4\uB85D \uD655\uC7A5 \uAC00\uB2A5\uD55C \uC801\uC808\uD55C \uC544\uD0A4\uD14D\uCC98 \uBC0F \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</p><ul><li><p>\uC0AC\uC6A9 \uC0AC\uB840\uB97C \uAE30\uBC18\uC73C\uB85C \uC815\uC758\uB41C \uC0C1\uD669\uC5D0\uC11C \uC5B4\uB5A4 AWS \uC11C\uBE44\uC2A4\uAC00 \uAC00\uC7A5 \uD655\uC7A5 \uAC00\uB2A5\uD558\uACE0 \uC131\uB2A5\uC774 \uC88B\uC744\uC9C0 \uACB0\uC815\uD560 \uC218 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4.</p></li><li><p>\uC608\uB97C \uB4E4\uC5B4 Amazon EC2\uC5D0\uC11C \uBC31\uC5D4\uB4DC \uC6F9 \uC11C\uBE44\uC2A4\uB97C \uD638\uC2A4\uD305\uD574\uC57C \uD558\uB294 \uC870\uC9C1\uC774 \uC788\uACE0 \uC774 \uC6F9 \uC11C\uBE44\uC2A4\uC758 \uC0AC\uC6A9\uB7C9\uC774 \uD558\uB8E8 \uC885\uC77C \uD06C\uAC8C \uB2EC\uB77C\uC9C4\uB2E4\uACE0 \uAC00\uC815\uD574 \uBCF4\uACA0\uC2B5\uB2C8\uB2E4. \uC774 \uC194\uB8E8\uC158\uC740 \uAC00\uC6A9\uC131\uC774 \uB192\uACE0 \uD0C4\uB825\uC801\uC774\uC5B4\uC57C \uD569\uB2C8\uB2E4. \uC774 \uC6CC\uD06C\uB85C\uB4DC\uC5D0 \uB300\uD574 \uC5B4\uB5A4 \uC720\uD615\uC758 \uC544\uD0A4\uD14D\uCC98\uAC00 \uD655\uC7A5 \uAC00\uB2A5\uD55C \uC194\uB8E8\uC158\uC774 \uB420\uAE4C\uC694? \uC774 \uACBD\uC6B0 Elastic Load Balancing \uBC0F Amazon EC2 Auto Scaling\uACFC \uD568\uAED8 Amazon EC2\uB97C \uC0AC\uC6A9\uD558\uB294 \uBC29\uBC95\uC744 \uC0B4\uD3B4\uBCF4\uACE0 \uC2F6\uC744 \uAC83\uC785\uB2C8\uB2E4. \uCEF4\uD4E8\uD305 \uC6CC\uD06C\uB85C\uB4DC \uD655\uC7A5\uC5D0 \uC801\uD569\uD55C \uC544\uD0A4\uD14D\uCC98\uB97C \uC120\uD0DD\uD558\uB294 \uBC29\uBC95\uC744 \uC544\uB294 \uAC83\uC740 \uC911\uC694\uD569\uB2C8\uB2E4.</p></li><li><p>Amazon EC2\uAC00 \uC720\uC77C\uD55C \uCEF4\uD4E8\uD305 \uC635\uC158\uC774 \uC544\uB2C8\uB77C\uB294 \uC810\uB3C4 \uC911\uC694\uD569\uB2C8\uB2E4. Amazon EC2\uC640 AWS Lambda, \uCEE8\uD14C\uC774\uB108 \uC11C\uBE44\uC2A4\uB97C \uC5B8\uC81C \uC5B4\uB5A4 \uCEF4\uD4E8\uD305 \uC11C\uBE44\uC2A4\uB97C \uC0AC\uC6A9\uD560\uC9C0, \uAC01 \uCEF4\uD4E8\uD305 \uC11C\uBE44\uC2A4\uC5D0 \uC5B4\uB5A4 \uC774\uC810\uACFC \uC81C\uD55C\uC774 \uC788\uB294\uC9C0 \uC120\uD0DD\uD558\uB294 \uB370 \uB9E4\uC6B0 \uC775\uC219\uD574\uC57C \uD569\uB2C8\uB2E4.</p></li><li><p>\uCEF4\uD4E8\uD305 \uC81C\uD55C \uC0AC\uD56D\uB3C4 \uC788\uC2B5\uB2C8\uB2E4. \uD604\uC7AC AWS Lambda \uD568\uC218\uAC00 \uD55C \uBC88\uC5D0 \uCD5C\uB300 15\uBD84 \uB3D9\uC548\uB9CC \uC2E4\uD589\uD560 \uC218 \uC788\uB2E4\uB294 \uAC83\uACFC \uAC19\uC740 \uC0AC\uD56D \uC54C\uACE0 \uC788\uC5B4\uC57C \uD55C\uB2E4.</p></li></ul></li><li><p>\uC194\uB8E8\uC158\uC758 \uC131\uB2A5\uC744 \uBAA8\uB2C8\uD130\uB9C1\uD558\uB294 \uC9C0\uD45C\uB97C \uC2DD\uBCC4\uD569\uB2C8\uB2E4.</p><ul><li>\uC2A4\uCF00\uC77C\uB9C1 \uC8FC\uC81C\uB97C \uBCF4\uBA74. \uC77C\uBC18\uC801\uC73C\uB85C Amazon CloudWatch\uC5D0\uC11C \uC0DD\uC131\uB41C \uACBD\uBCF4\uC5D0 \uB300\uD574 \uC870\uC815 \uC774\uBCA4\uD2B8\uB97C \uD638\uCD9C\uD569\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uACBD\uBCF4\uB294 \uBA54\uD2B8\uB9AD\uC774 \uC815\uC758\uB41C \uC2DC\uAC04 \uB3D9\uC548 \uD2B9\uC815 \uC784\uACC4\uAC12\uC744 \uCD08\uACFC\uD560 \uB54C \uD638\uCD9C\uB429\uB2C8\uB2E4. \uC870\uC815\uC744 \uC704\uD574 \uBAA8\uB2C8\uD130\uB9C1\uD560 \uBA54\uD2B8\uB9AD\uC744 \uC120\uD0DD\uD558\uB294 \uBC29\uBC95\uC744 \uC544\uB294 \uAC83\uC740 \uC911\uC694\uD569\uB2C8\uB2E4.</li><li>CPU \uC0AC\uC6A9\uB960\uC740 \uC870\uC815\uC744 \uC704\uD574 \uBAA8\uB2C8\uD130\uB9C1\uD558\uB294 \uC77C\uBC18\uC801\uC778 Amazon EC2 \uC9C0\uD45C\uC785\uB2C8\uB2E4. \uC778\uC2A4\uD134\uC2A4 \uB610\uB294 \uC778\uC2A4\uD134\uC2A4 \uADF8\uB8F9\uC5D0 \uBE44\uD574 CPU \uC0AC\uC6A9\uB960\uC774 \uB108\uBB34 \uB192\uC73C\uBA74 \uC778\uC2A4\uD134\uC2A4\uAC00 \uD604\uC7AC \uC218\uC694\uB97C \uB530\uB77C\uAC08 \uC218 \uC5C6\uB2E4\uB294 \uC2E0\uD638\uC774\uBA70 \uC870\uC815 \uC774\uBCA4\uD2B8\uAC00 \uBC1C\uC0DD\uD574\uC57C \uD569\uB2C8\uB2E4.</li><li>\uC0AC\uC6A9\uC790 \uC9C0\uC815 \uC9C0\uD45C \uB610\uB294 HealthyHostCount \uB610\uB294 SurgeQueueLength\uC640 \uAC19\uC740 Elastic Load Balancing\uACFC \uAD00\uB828\uB41C \uC9C0\uD45C\uC640 \uAC19\uC774 \uC870\uC815\uC5D0 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uB2E4\uB978 \uC720\uD615\uC758 \uC9C0\uD45C\uB3C4 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uAC83\uC740 \uC2E4\uC81C\uB85C \uC0C1\uD669\uC5D0 \uB530\uB77C \uB2E4\uB974\uBA70, \uCC99\uB3C4 \uCE21\uC815\uC774 \uAC00\uC7A5 \uC758\uBBF8\uAC00 \uC788\uC2B5\uB2C8\uB2E4.</li></ul></li><li><p>\uC2A4\uD130\uB514</p></li><li><p>VPC\uB294 \uD55C\uB9AC\uC804\uC5D0 \uC5EC\uB7EC \uAC00\uC6A9\uC601\uC5ED, \uC11C\uBE0C\uB137\uAD88\uC131\uC740 \uB290\uB08C\uC0C1 \uD55C \uAC00\uC6A9</p></li><li><p>vpc\uC548\uC5D0\uC11C \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC740 elb \uD504\uB77C\uC774\uBE57 \uC548\uC5D0 \uC778\uC2A4\uD134\uC2A4</p></li><li><p>\uC778\uC2A4\uD134\uC2A4 \uADF8\uB8F9 \uACFC \uC624\uD1A0\uC2A4\uCF00\uC77C\uB9C1 \uADF8\uB8F9\uC758 \uCC28\uC774</p></li></ul><h2 id="\u110F\u1175\u110B\u116F\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#\u110F\u1175\u110B\u116F\u1103\u1173" aria-hidden="true">#</a> \uD0A4\uC6CC\uB4DC</h2><div class="custom-container tip"><p class="custom-container-title">Lamda</p></div><div class="custom-container tip"><p class="custom-container-title">EC2</p></div><div class="custom-container tip"><p class="custom-container-title">CloudWatch</p></div><div class="custom-container tip"><p class="custom-container-title">SQS , SNS</p></div><div class="custom-container tip"><p class="custom-container-title">Tips</p><p>\uB514\uCEE4\uD50C\uB9C1 \uC11C\uBE44\uC2A4, Amazon SQS \uBC0F Amazon EC2 \uC870\uC815 \uC0AC\uB840\uC640 AWS Lambda \uAE30\uB2A5\uC5D0 \uB300\uD574 \uC790\uC138\uD788 \uC77D\uC5B4\uBCF4\uB294 \uAC83\uC774 \uC88B\uC2B5\uB2C8\uB2E4.</p></div><h2 id="question" tabindex="-1"><a class="header-anchor" href="#question" aria-hidden="true">#</a> Question</h2><p>\uD55C \uD68C\uC0AC\uC5D0\uC11C \uC0AC\uC9C4\uACFC \uBE44\uB514\uC624\uB97C \uCC98\uB9AC\uD558\uB294 \uC751\uC6A9 \uD504\uB85C\uADF8\uB7A8\uC744 \uAC1C\uBC1C\uD588\uC2B5\uB2C8\uB2E4. \uC0AC\uC6A9\uC790\uAC00 \uC0AC\uC9C4\uACFC \uBE44\uB514\uC624\uB97C \uC5C5\uB85C\uB4DC\uD558\uBA74 \uC791\uC5C5\uC5D0\uC11C \uD30C\uC77C\uC744 \uCC98\uB9AC\uD569\uB2C8\uB2E4. \uAE34 \uB3D9\uC601\uC0C1\uC744 \uCC98\uB9AC\uD558\uB294 \uB370 \uCD5C\uB300 1\uC2DC\uAC04\uC774 \uC18C\uC694\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uD68C\uC0AC\uB294 Amazon EC2 \uC628\uB514\uB9E8\uB4DC \uC778\uC2A4\uD134\uC2A4\uB97C \uC0AC\uC6A9\uD558\uC5EC \uC6F9 \uC11C\uBC84\uB97C \uC2E4\uD589\uD558\uACE0 \uC791\uC5C5\uC744 \uCC98\uB9AC\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uC6F9 \uACC4\uCE35\uACFC \uCC98\uB9AC \uACC4\uCE35\uC5D0\uB294 Application Load Balancer \uB4A4\uC758 Auto Scaling \uADF8\uB8F9\uC5D0\uC11C \uC2E4\uD589\uB418\uB294 \uC778\uC2A4\uD134\uC2A4\uAC00 \uC788\uC2B5\uB2C8\uB2E4. \uD53C\uD06C \uC2DC\uAC04 \uB3D9\uC548 \uC0AC\uC6A9\uC790\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uB290\uB9AC\uACE0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uC77C\uBD80 \uC694\uCCAD\uC744 \uC804\uD600 \uCC98\uB9AC\uD558\uC9C0 \uC54A\uB294\uB2E4\uACE0 \uBCF4\uACE0\uD569\uB2C8\uB2E4. \uC800\uB141 \uC2DC\uAC04\uC5D0\uB294 \uC2DC\uC2A4\uD15C\uC774 \uC720\uD734 \uC0C1\uD0DC\uC785\uB2C8\uB2E4. \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uBC29\uC2DD\uC73C\uB85C \uBAA8\uB4E0 \uC791\uC5C5\uC744 \uCC98\uB9AC\uD558\uB3C4\uB85D \uC194\uB8E8\uC158 \uC124\uACC4\uC790\uB294 \uBB34\uC5C7\uC744 \uD574\uC57C \uD569\uB2C8\uAE4C?</p><p>A. \uC6F9 \uACC4\uCE35\uACFC \uCC98\uB9AC \uACC4\uCE35\uC758 Auto Scaling \uADF8\uB8F9\uC5D0\uC11C \uB354 \uD070 \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uB97C \uC0AC\uC6A9.<br> B. \uC6F9 \uACC4\uCE35 \uBC0F \uCC98\uB9AC \uACC4\uCE35\uC758 Auto Scaling \uADF8\uB8F9\uC5D0 \uB300\uD574 Spot Instances\uB97C \uC0AC\uC6A9.<br> C. \uC6F9 \uACC4\uCE35\uACFC \uCC98\uB9AC \uACC4\uCE35 \uC0AC\uC774\uC5D0 Amazon Simple Queue Service \uD45C\uC900 \uB300\uAE30\uC5F4\uC744 \uC0AC\uC6A9. \uC0AC\uC6A9\uC790 \uC9C0\uC815 \uB300\uAE30\uC5F4 \uBA54\uD2B8\uB9AD\uC744 \uC0AC\uC6A9\uD558\uC5EC \uCC98\uB9AC \uACC4\uCE35\uC5D0\uC11C Auto Scaling \uADF8\uB8F9\uC744 \uD655\uC7A5.<br> D. EC2 \uC778\uC2A4\uD134\uC2A4 \uBC0F Auto Scaling \uADF8\uB8F9 \uB300\uC2E0 AWS Lambda \uD568\uC218\uB97C \uC0AC\uC6A9. \uCDA9\uBD84\uD55C \uB3D9\uC2DC \uAE30\uB2A5\uC774 \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uB420 \uC218 \uC788\uB3C4\uB85D \uC11C\uBE44\uC2A4 \uD560\uB2F9\uB7C9\uC744 \uB298\uB9BC</p><hr><ul><li><p>\uBB38\uC81C \uBD84\uC11D</p><ol><li>\uCC98\uB9AC\uD558\uB294 \uB370 \uCD5C\uB300 1\uC2DC\uAC04\uC774 \uC18C\uC694\uB420 \uC218 \uC788\uB294 \uC791\uC5C5\uC774 \uC788\uC2B5\uB2C8\uB2E4.</li><li>EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0\uC11C \uC2E4\uD589\uB418\uB294 \uCC98\uB9AC \uACC4\uCE35\uC5D0 \uB300\uD55C \uC138\uBD80 \uC815\uBCF4\uB97C \uC81C\uACF5\uD569\uB2C8\uB2E4</li><li>\uD574\uACB0\uD558\uB824\uB294 \uBB38\uC81C\uB294 \uC0AC\uC6A9\uB7C9\uC774 \uB9CE\uC740 \uC2DC\uAC04\uC5D0 \uC0AC\uC6A9\uC790\uAC00 \uC751\uC6A9 \uD504\uB85C\uADF8\uB7A8\uC774 \uB290\uB9AC\uAC70\uB098 \uB54C\uB54C\uB85C \uC791\uC5C5\uC744 \uCC98\uB9AC\uD558\uC9C0 \uC54A\uB294\uB2E4\uB294 \uAC83\uC785\uB2C8\uB2E4.</li><li>\uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uB2F5\uBCC0\uC744 \uC694\uAD6C\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4</li></ol></li><li><p>\uC815\uB2F5 C</p><ul><li>\uC774 \uC9C8\uBB38\uC740 Auto Scaling \uADF8\uB8F9\uC774 \uC788\uB294 \uCC98\uB9AC \uACC4\uCE35\uC774 \uD53C\uD06C \uC2DC\uAC04\uC5D0 \uC791\uC5C5\uC744 \uC644\uB8CC\uD560 \uB9CC\uD07C \uCDA9\uBD84\uD788 \uBE60\uB974\uAC8C \uD655\uC7A5\uB418\uC9C0 \uC54A\uB294 \uC2DC\uB098\uB9AC\uC624\uB97C \uC124\uC815\uD569\uB2C8\uB2E4. \uC989, \uC778\uC2A4\uD134\uC2A4\uAC00 \uB4E4\uC5B4\uC624\uB294 \uC694\uCCAD \uBCFC\uB968\uACFC \uB3D9\uAE30\uD654\uD558\uC5EC \uD655\uC7A5\uD560 \uC218 \uC788\uB3C4\uB85D \uD655\uC7A5 \uC694\uAD6C \uC0AC\uD56D\uC744 \uC870\uC815\uD574\uC57C \uD569\uB2C8\uB2E4. Auto Scaling \uADF8\uB8F9\uC740 SQS \uB300\uAE30\uC5F4\uC758 \uC2DC\uC2A4\uD15C \uB85C\uB4DC \uBCC0\uACBD\uC5D0 \uB530\uB77C \uD655\uC7A5\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C SQS \uB300\uAE30\uC5F4\uC744 \uC544\uD0A4\uD14D\uCC98\uC5D0 \uB3C4\uC785\uD558\uBA74 \uB300\uAE30\uC5F4\uC774 \uD2B9\uC815 \uAE4A\uC774\uC5D0 \uB3C4\uB2EC\uD558\uBA74 \uC0AC\uC6A9\uC790 \uC9C0\uC815 \uBA54\uD2B8\uB9AD\uC744 \uC0AC\uC6A9\uD558\uC5EC \uCC98\uB9AC \uC778\uC2A4\uD134\uC2A4\uB97C \uD655\uC7A5\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 \uBA54\uC2DC\uC9C0\uAC00 \uB300\uAE30\uC5F4\uC5D0\uC11C \uBC31\uC5C5\uB418\uAE30 \uC2DC\uC791\uD55C\uB2E4\uB294 \uC2E0\uD638\uC785\uB2C8\uB2E4. \uC774 \uC194\uB8E8\uC158\uC740 \uB610\uD55C \uB54C\uB54C\uB85C \uCD5C\uB300 \uC0AC\uC6A9\uB7C9\uC5D0\uC11C \uC778\uC2A4\uD134\uC2A4\uAC00 \uC694\uCCAD\uC744 \uC0AD\uC81C\uD558\uACE0 \uC791\uC5C5\uC744 \uC804\uD600 \uCC98\uB9AC\uD558\uC9C0 \uC54A\uB294 \uBB38\uC81C\uB97C \uD574\uACB0\uD569\uB2C8\uB2E4. SQS \uB300\uAE30\uC5F4\uC758 \uB3C4\uC785\uC73C\uB85C Auto Scaling \uADF8\uB8F9\uC774 \uCD5C\uB300 \uC6A9\uB7C9\uC5D0 \uB3C4\uB2EC\uD558\uB354\uB77C\uB3C4 \uC791\uC5C5\uC740 \uB300\uAE30\uC5F4\uC5D0 \uC800\uC7A5\uB418\uACE0 \uCEF4\uD4E8\uD305 \uB9AC\uC18C\uC2A4\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC788\uAC8C \uB418\uBA74 \uCC98\uB9AC\uB429\uB2C8\uB2E4.</li></ul></li><li><p>\uC624\uB2F5\uC774\uC720</p><ul><li>A: \uC6F9 \uACC4\uCE35\uACFC \uCC98\uB9AC \uACC4\uCE35\uC758 Auto Scaling \uADF8\uB8F9\uC5D0\uC11C \uB354 \uD070 \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uB97C \uC0AC\uC6A9\uD569\uB2C8\uB2E4.<br> \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uAC00 \uD074\uC218\uB85D \uCC98\uB9AC \uC778\uC2A4\uD134\uC2A4\uC758 \uC131\uB2A5\uC774 \uD5A5\uC0C1\uB429\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uBE44\uC6A9\uB3C4 \uC99D\uAC00\uD569\uB2C8\uB2E4. \uD070 \uC778\uC2A4\uD134\uC2A4\uAC00 \uC720\uD734 \uC0C1\uD0DC\uC77C \uB54C \uC791\uC740 \uC778\uC2A4\uD134\uC2A4\uBCF4\uB2E4 \uB354 \uB9CE\uC740 \uBE44\uC6A9\uC774 \uBC1C\uC0DD\uD558\uAE30 \uB54C\uBB38\uC5D0 \uC774\uB294 \uB300\uBD80\uBD84 \uBB38\uC81C\uAC00 \uB429\uB2C8\uB2E4. \uADF8\uB9AC\uACE0 \uC6B0\uB9AC\uB294 \uC774 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uD1B5\uD574 \uBC24\uC5D0 \uB9AC\uC18C\uC2A4\uAC00 \uC720\uD734 \uC0C1\uD0DC\uB77C\uB294 \uAC83\uC744 \uC54C\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uC989, \uC774 \uC194\uB8E8\uC158\uC744 \uC0AC\uC6A9\uD558\uC5EC \uBC1C\uC0DD\uD558\uB294 \uCD94\uAC00 \uBE44\uC6A9\uC73C\uB85C \uC778\uD574 \uC62C\uBC14\uB978 \uC751\uB2F5\uC73C\uB85C \uBD80\uC801\uD569\uD569\uB2C8\uB2E4. \uB610\uD55C \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uC758 \uBCC0\uACBD\uC744 \uD558\uB354\uB77C\uB3C4 \uCC98\uB9AC\uB97C \uBCF4\uC7A5\uD560 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4.</li><li>B: \uC6F9 \uB808\uC774\uC5B4\uC640 \uD504\uB85C\uC138\uC2F1 \uB808\uC774\uC5B4\uC758 Auto Scaling \uADF8\uB8F9\uC5D0 \uC2A4\uD31F \uC778\uC2A4\uD134\uC2A4\uB97C \uC0AC\uC6A9\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4. \uC2A4\uD31F \uC778\uC2A4\uD134\uC2A4\uB294 \uC628\uB514\uB9E8\uB4DC \uC778\uC2A4\uD134\uC2A4\uBCF4\uB2E4 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC785\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uC774 \uC751\uB2F5\uB3C4 \uC124\uC815\uD55C \uBB38\uC81C\uAC00 \uC2E4\uC81C\uB85C \uD574\uACB0\uB418\uC9C0\uB294 \uC54A\uC2B5\uB2C8\uB2E4. \uC2A4\uD31F \uC778\uC2A4\uD134\uC2A4\uB97C \uC0AC\uC6A9\uD55C\uB2E4\uACE0 \uD574\uC11C \uBCF8\uC9C8\uC801\uC73C\uB85C \uC544\uD0A4\uD14D\uCC98\uB97C \uB354 \uD655\uC7A5\uD560 \uC218 \uC788\uB294 \uAC83\uC740 \uC544\uB2D9\uB2C8\uB2E4. \uAC8C\uB2E4\uAC00 \uC2A4\uD31F \uC778\uC2A4\uD134\uC2A4\uC640 \uC628\uB514\uB9E8\uB4DC \uC778\uC2A4\uD134\uC2A4\uB294 \uBD80\uD305 \uBC0F \uD655\uC7A5\uC5D0 \uB3D9\uC77C\uD55C \uC2DC\uAC04\uC774 \uAC78\uB9BD\uB2C8\uB2E4. \uB530\uB77C\uC11C \uD558\uB098\uB97C \uB2E4\uB978 \uAC83\uC73C\uB85C \uAD50\uCCB4\uD558\uB294 \uAC83\uC740 \uC6B0\uB9AC\uAC00 \uC5EC\uAE30\uC11C \uACAA\uACE0 \uC788\uB294 \uBB38\uC81C\uB97C \uC2E4\uC81C\uB85C \uBCC0\uACBD\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uB610\uD55C \uC2A4\uD31F \uC778\uC2A4\uD134\uC2A4\uB97C \uD56D\uC0C1 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB2E4\uB294 \uBCF4\uC7A5\uB3C4 \uC5C6\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC2A4\uD31F \uC778\uC2A4\uD134\uC2A4\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC5C6\uACE0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uACC4\uC18D\uD574\uC11C \uC694\uCCAD\uC744 \uC0AD\uC81C\uD560 \uAC00\uB2A5\uC131\uC774 \uC788\uC2B5\uB2C8\uB2E4.</li><li>D: EC2 \uC778\uC2A4\uD134\uC2A4 \uBC0F Auto Scaling \uADF8\uB8F9 \uB300\uC2E0 AWS Lambda \uD568\uC218 \uC0AC\uC6A9\uC774 \uC788\uC2B5\uB2C8\uB2E4. \uCDA9\uBD84\uD55C \uB3D9\uC2DC \uAE30\uB2A5\uC774 \uB3D9\uC2DC\uC5D0 \uC2E4\uD589\uB420 \uC218 \uC788\uB3C4\uB85D \uC11C\uBE44\uC2A4 \uD560\uB2F9\uB7C9\uC744 \uB298\uB9BD\uB2C8\uB2E4. \uCC98\uB9AC \uC791\uC5C5\uC744 \uC2E4\uD589\uD558\uB294 \uB370 \uCD5C\uB300 1\uC2DC\uAC04\uC774 \uAC78\uB9B4 \uC218 \uC788\uC9C0\uB9CC AWS Lambda \uD568\uC218\uC758 \uB7F0\uD0C0\uC784 \uC81C\uD55C\uC740 15\uBD84\uC785\uB2C8\uB2E4.</li></ul></li></ul><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',20),u={href:"https://www.coursera.org/learn/aws-certified-solutions-architect-associate",target:"_blank",rel:"noopener noreferrer"},d=e("coursera\uAC15\uC758"),m=i("br",null,null,-1),A={href:"https://aws.amazon.com/ko/faqs/",target:"_blank",rel:"noopener noreferrer"},h=e("aws FAQ");function S(_,C){const l=t("ExternalLinkIcon");return n(),c("div",null,[p,i("p",null,[i("a",u,[d,a(l)]),m,i("a",A,[h,a(l)])])])}var E=o(r,[["render",S],["__file","domain2-1-elastic-and-scalable-compute-solutions-for-a-workload.html.vue"]]);export{E as default};
