import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{r as t,o as s,c as l,a as e,b as r,d as n,e as o}from"./app.d6b42b88.js";const c={},p=n('<p>AWS-SAA \uC790\uACA9\uC99D \uD56D\uBAA9\uC744 \uAE30\uC900\uC73C\uB85C AWS\uB97C \uACF5\uBD80 \uD569\uB2C8\uB2E4. \uAC01 \uD56D\uBAA9\uC758 \uC758\uBBF8\uB97C \uC0B4\uD3B4\uBCF4\uACE0 \uC911\uC810\uC0AC\uD56D\uC744 \uD30C\uC545\uD558\uB294 \uAC83\uC744 \uBAA9\uD45C\uB85C \uD569\uB2C8\uB2E4.\uAC01 \uD56D\uBAA9\uBCC4 \uC774\uB860/\uC2E4\uC2B5\uC73C\uB85C \uD559\uC2B5\uD569\uB2C8\uB2E4.</p><details class="custom-container details"><summary>\uC0C1\uC138 \uC601\uC5ED ( \uCD9C\uCC98: aws certification \uD648\uD398\uC774\uC9C0 )</summary><ul><li>3.1 AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uBCF4\uC548 \uC561\uC138\uC2A4 \uC124\uACC4 \u{1F448}\u{1F448}\u{1F448}\u{1F448}\u{1F448} <strong>\uC9C0\uAE08 \uC5EC\uAE30</strong><ul><li>\uC0AC\uC6A9\uC790, \uADF8\uB8F9, \uC5ED\uD560 \uAC04\uC5D0 \uC120\uD0DD\uD560 \uC2DC\uC810\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC9C0\uC815\uB41C \uC561\uC138\uC2A4 \uC815\uCC45\uC758 \uC21C \uD6A8\uACFC\uB97C \uD574\uC11D\uD569\uB2C8\uB2E4.</li><li>\uB8E8\uD2B8 \uACC4\uC815\uC744 \uBCF4\uD638\uD558\uB294 \uB370 \uC801\uC808\uD55C \uAE30\uC220\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>AWS IAM \uC758 \uAE30\uB2A5\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC790\uACA9 \uC99D\uBA85\uC744 \uBCF4\uD638\uD558\uB294 \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 AWS API \uC5D0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uC548\uC804\uD55C \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4 \uCD94\uC801 \uAC00\uB2A5\uC131\uC744 \uC0DD\uC131\uD558\uB294 \uB370 \uC801\uC808\uD55C \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li><li>3.2 \uBCF4\uC548 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uACC4\uCE35 \uC124\uACC4 <ul><li>\uC8FC\uC5B4\uC9C4 \uD2B8\uB798\uD53D \uC81C\uC5B4 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uBCF4\uC548 \uADF8\uB8F9 \uBC0F \uB124\uD2B8\uC6CC\uD06C ACL \uC744 \uC0AC\uC6A9\uD560 \uC2DC\uC810 \uBC0F \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uACFC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC0AC\uC6A9\uD558\uC5EC \uB124\uD2B8\uC6CC\uD06C \uC138\uBD84\uD654 \uC804\uB7B5\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>Amazon VPC \uC5D0\uC11C AWS \uC11C\uBE44\uC2A4 \uC5D4\uB4DC\uD3EC\uC778\uD2B8 \uB610\uB294 \uC778\uD130\uB137 \uAE30\uBC18 \uB9AC\uC18C\uC2A4\uC5D0 \uC548\uC804\uD558\uAC8C \uC561\uC138\uC2A4\uD558\uB294 \uB370 \uC801\uC808\uD55C \uB77C\uC6B0\uD305 \uBA54\uCEE4\uB2C8\uC998\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC678\uBD80 \uC704\uD611\uC73C\uB85C\uBD80\uD130 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uBCF4\uD638\uD558\uB294 \uB370 \uC801\uC808\uD55C AWS \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li><li>3.3 \uC801\uC808\uD55C \uB370\uC774\uD130 \uBCF4\uC548 \uC635\uC158 \uC120\uD0DD <ul><li>\uC561\uC138\uC2A4 \uD328\uD134\uC744 \uAE30\uBC18\uC73C\uB85C \uAC1C\uCCB4\uC5D0 \uC801\uC6A9\uD574\uC57C \uD558\uB294 \uC815\uCC45\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uC11C\uBE44\uC2A4\uC758 \uC800\uC7A5 \uB370\uC774\uD130 \uBC0F \uC804\uC1A1 \uC911 \uB370\uC774\uD130\uC5D0 \uC801\uC808\uD55C \uC554\uD638\uD654 \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uC801\uC808\uD55C \uD0A4 \uAD00\uB9AC \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li></ul></details><h2 id="domain-3-\u110B\u1161\u11AB\u110C\u1165\u11AB\u1112\u1161\u11AB-\u110B\u1162\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB-\u1106\u1175\u11BE-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#domain-3-\u110B\u1161\u11AB\u110C\u1165\u11AB\u1112\u1161\u11AB-\u110B\u1162\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB-\u1106\u1175\u11BE-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> Domain 3 \uC548\uC804\uD55C \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBC0F \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4</h2><h2 id="aws-\u1105\u1175\u1109\u1169\u1109\u1173\u110B\u1166-\u1103\u1162\u1112\u1161\u11AB-\u1107\u1169\u110B\u1161\u11AB-\u110B\u1162\u11A8\u1109\u1166\u1109\u1173-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#aws-\u1105\u1175\u1109\u1169\u1109\u1173\u110B\u1166-\u1103\u1162\u1112\u1161\u11AB-\u1107\u1169\u110B\u1161\u11AB-\u110B\u1162\u11A8\u1109\u1166\u1109\u1173-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uBCF4\uC548 \uC561\uC138\uC2A4 \uC124\uACC4</h2><p><strong>\uC138\uBD80\uC601\uC5ED</strong></p><ul><li>\uC0AC\uC6A9\uC790, \uADF8\uB8F9, \uC5ED\uD560 \uAC04\uC5D0 \uC120\uD0DD\uD560 \uC2DC\uC810\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC9C0\uC815\uB41C \uC561\uC138\uC2A4 \uC815\uCC45\uC758 \uC21C \uD6A8\uACFC\uB97C \uD574\uC11D\uD569\uB2C8\uB2E4.</li><li>\uB8E8\uD2B8 \uACC4\uC815\uC744 \uBCF4\uD638\uD558\uB294 \uB370 \uC801\uC808\uD55C \uAE30\uC220\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>AWS IAM \uC758 \uAE30\uB2A5\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC790\uACA9 \uC99D\uBA85\uC744 \uBCF4\uD638\uD558\uB294 \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 AWS API \uC5D0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uC548\uC804\uD55C \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4 \uCD94\uC801 \uAC00\uB2A5\uC131\uC744 \uC0DD\uC131\uD558\uB294 \uB370 \uC801\uC808\uD55C \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul><p>\uC544\uD0A4\uD14D\uCC98\uB97C \uC124\uACC4\uD560 \uB54C \uAC00\uC7A5 \uCD08\uAE30\uC5D0 \uACE0\uB824\uD558\uB294 \uAC83 \uC911 \uD558\uB098\uAC00 \uAD6C\uCD95\uD558\uB294 \uC0AC\uB78C\uC774\uB098 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uD544\uC694\uD55C AWS \uC11C\uBE44\uC2A4\uC5D0 \uC561\uC138\uC2A4\uD558\uB294 \uBC29\uBC95\uC785\uB2C8\uB2E4. \uC5EC\uAE30\uC5D0\uB294 \uB9AC\uC18C\uC2A4\uB97C \uC2DC\uC791\uD558\uAC70\uB098 \uC885\uB8CC\uD560 \uC218 \uC788\uB294 \uB300\uC0C1\uC744 \uACB0\uC815\uD558\uB294 \uAC83\uBFD0\uB9CC \uC544\uB2C8\uB77C \uC561\uC138\uC2A4 \uAD8C\uD55C\uC774 \uBD80\uC5EC\uB418\uB294 \uBC29\uBC95\uACFC \uC2DC\uAE30\uC5D0 \uB300\uD55C \uAD00\uB9AC, \uC6B4\uC601 \uAD8C\uD55C \uBC0F \uC11C\uBE44\uC2A4 \uD638\uCD9C\uACFC \uAD00\uB828\uB41C \uBAA8\uB4E0 \uAC83\uC774 \uD3EC\uD568\uB429\uB2C8\uB2E4.</p><p>\uC774 \uC601\uC5ED\uC5D0\uC11C AWS IAM(Identity and Access Management)\uC740 \uC911\uC694\uD569\uB2C8\uB2E4. \uC0AC\uC6A9\uC790, \uADF8\uB8F9, \uC5ED\uD560 \uC774 3\uAC00\uC9C0\uAC00 \uBB34\uC5C7\uC744 \uC758\uBBF8\uD558\uB294\uC9C0 \uC0B4\uD3B4\uBCF4\uACE0 \uACB0\uC815\uD558\uB294 \uB370 \uD544\uC694\uD55C \uC0AC\uD56D\uACFC \uC774\uB4E4\uC744 \uACB0\uD569\uD558\uB294 \uBC29\uBC95\uC744 \uC0B4\uD3B4\uBD10\uC57C \uD569\uB2C8\uB2E4.</p><p>\uC5B4\uB5BB\uAC8C \uC0DD\uC131\uD558\uB294\uC9C0,\uAC01\uAC01\uC758 \uAC15\uC810\uACFC \uD55C\uACC4\uB294 \uBB34\uC5C7\uC778\uC9C0 \uADF8\uB9AC\uACE0 \uB2E4\uC591\uD55C \uC0AC\uC6A9\uC790, \uADF8\uB8F9 \uBC0F \uC5ED\uD560 \uAE30\uBC18 \uAD8C\uD55C \uAC04\uC5D0 \uC804\uD658\uD560 \uC218 \uC788\uB294 \uC2DC\uB098\uB9AC\uC624\uB294 \uBB34\uC5C7\uC778\uC9C0 \uC54C\uC544\uC57C \uD569\uB2C8\uB2E4.</p><p>\uC774\uB7EC\uD55C \uC790\uACA9 \uC99D\uBA85\uACFC \uD568\uAED8 IAM \uBC0F \uAE30\uD0C0 AWS \uC11C\uBE44\uC2A4\uB97C \uD1B5\uD574 \uD544\uC694\uD55C \uC790\uACA9 \uC99D\uBA85\uC744 \uBCF4\uD638\uD558\uB294 \uBC29\uBC95\uACFC \uC774\uB7EC\uD55C \uC790\uACA9 \uC99D\uBA85\uC744 \uCC98\uB9AC\uD558\uAE30 \uC704\uD55C \uBAA8\uBC94 \uC0AC\uB840\uB97C \uC54C\uACE0 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4. \uC774\uAC83\uC740 \uB8E8\uD2B8 \uC0AC\uC6A9\uC790\uB97C \uBCF4\uD638\uD558\uB294 \uAE30\uC220\uBFD0\uB9CC \uC544\uB2C8\uB77C \uC0DD\uC131\uD560 \uC218 \uC788\uB294 ID\uC5D0\uB3C4 \uC801\uC6A9\uB429\uB2C8\uB2E4. AWS API\uC5D0 \uB300\uD55C \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC561\uC138\uC2A4 \uC81C\uC5B4\uC640 \uAD00\uB828\uB41C \uBAA8\uBC94 \uC0AC\uB840\uB97C \uC0B4\uD3B4\uBD10\uC57C \uD569\uB2C8\uB2E4.</p><p>API \uC561\uC138\uC2A4\uB97C \uD65C\uC131\uD654\uD558\uB294 \uB2E4\uB978 \uBC29\uBC95\uC778 \uC815\uCC45 \uBB38\uC11C\uB97C \uC791\uC131\uD558\uACE0 \uD574\uC11D\uD558\uB294 \uBC29\uBC95\uB3C4 \uC54C\uC544\uC57C \uD569\uB2C8\uB2E4. \uC815\uCC45 \uC124\uBA85\uC758 \uC8FC\uC694 \uBD80\uBD84, \uD544\uC694\uD55C \uC0AC\uD56D, \uC815\uCC45\uC774 \uAD8C\uD55C\uC5D0 \uB300\uD55C \uC138\uBD84\uC131\uC744 \uC81C\uACF5\uD558\uB294 \uBC29\uBC95\uC740 \uBB34\uC5C7\uC778\uC9C0 \uD655\uC778\uD558\uC2ED\uC2DC\uC624. \uB610\uD55C \uC815\uCC45\uC744 \uD3C9\uAC00\uD560 \uB54C IAM\uC758 \uACB0\uC815 \uB17C\uB9AC\uC640 \uC774\uAC83\uC774 \uC5F0\uACB0\uB41C \uC5EC\uB7EC \uC815\uCC45\uC774 \uC788\uB294 ID\uC5D0 \uC5B4\uB5A4 \uC601\uD5A5\uC744 \uBBF8\uCE58\uB294\uC9C0 \uC774\uD574\uD574\uC57C \uD569\uB2C8\uB2E4.</p><p>\uB9C8\uC9C0\uB9C9\uC73C\uB85C AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4\uB97C \uCD94\uC801\uD558\uB294 \uB370 \uB3C4\uC6C0\uC774 \uB418\uB294 \uBC29\uBC95, \uC11C\uBE44\uC2A4 \uBC0F \uB3C4\uAD6C\uB97C \uC774\uD574\uD558\uC2ED\uC2DC\uC624. \uACC4\uC815 \uB9AC\uC18C\uC2A4\uC5D0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uC0AC\uB78C\uACFC \uB300\uC0C1\uC5D0 \uB300\uD55C \uD1B5\uCC30\uB825\uC774 \uD544\uC694\uD569\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uAC00\uC2DC\uC131\uC744 \uD655\uBCF4\uD558\uB294 \uBC29\uBC95, \uBCF4\uC548 \uD45C\uC900\uC744 \uC2DC\uD589\uD558\uB294 \uBC29\uBC95, \uD574\uB2F9 \uB370\uC774\uD130\uB97C \uAE30\uBC18\uC73C\uB85C \uACBD\uACE0 \uBC0F \uC790\uB3D9\uD654\uD558\uB294 \uBC29\uBC95\uC744 \uC54C\uACE0 \uC788\uC5B4\uC57C\uD569\uB2C8\uB2E4</p><h2 id="\u110F\u1175\u110B\u116F\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#\u110F\u1175\u110B\u116F\u1103\u1173" aria-hidden="true">#</a> \uD0A4\uC6CC\uB4DC</h2><div class="custom-container tip"><p class="custom-container-title">I AM</p></div><div class="custom-container tip"><p class="custom-container-title">root \uACC4\uC815</p></div><div class="custom-container tip"><p class="custom-container-title">\uC815\uCC45</p></div><h2 id="question" tabindex="-1"><a class="header-anchor" href="#question" aria-hidden="true">#</a> Question</h2><p>\uD68C\uC0AC\uC758 CIO\uB294 AWS \uACC4\uC815\uC758 \uB8E8\uD2B8 \uC0AC\uC6A9\uC790 \uBCF4\uC548\uC5D0 \uB300\uD574 \uC6B0\uB824\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. CIO\uAC00 \uB8E8\uD2B8 \uACC4\uC815\uC774 \uC548\uC804\uD55C \uB85C\uADF8\uC778\uC744 \uC704\uD55C \uBAA8\uBC94 \uC0AC\uB840\uB97C \uB530\uB974\uB3C4\uB85D \uD558\uB824\uBA74 \uC5B4\uB5BB\uAC8C \uD574\uC57C \uD569\uB2C8\uAE4C? (2\uAC1C \uC751\uB2F5)</p><p>A. Enforce the use of an access key ID and secret access key for the root user logins.<br> -\uB8E8\uD2B8 \uC0AC\uC6A9\uC790 \uB85C\uADF8\uC778\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4 \uD0A4 ID \uBC0F \uBCF4\uC548 \uC561\uC138\uC2A4 \uD0A4 \uC0AC\uC6A9\uC744 \uC2DC\uD589\uD569\uB2C8\uB2E4.</p><p>B. Enforce the use of MFA for the root user logins.<br> -\uB8E8\uD2B8 \uC0AC\uC6A9\uC790 \uB85C\uADF8\uC778\uC5D0 \uB300\uD574 MFA \uC0AC\uC6A9\uC744 \uC2DC\uD589\uD569\uB2C8\uB2E4.</p><p>C. Enforce the root user to assume a role to access the root user&#39;s own resources.<br> -\uB8E8\uD2B8 \uC0AC\uC6A9\uC790\uAC00 \uB8E8\uD2B8 \uC0AC\uC6A9\uC790 \uC790\uC2E0\uC758 \uB9AC\uC18C\uC2A4\uC5D0 \uC561\uC138\uC2A4\uD558\uB294 \uC5ED\uD560\uC744 \uB9E1\uB3C4\uB85D \uC2DC\uD589\uD569\uB2C8\uB2E4.</p><p>D. Enforce the use of complex passwords for member account root user logins.<br> -\uAD6C\uC131\uC6D0 \uACC4\uC815 \uB8E8\uD2B8 \uC0AC\uC6A9\uC790 \uB85C\uADF8\uC778\uC5D0 \uBCF5\uC7A1\uD55C \uC554\uD638 \uC0AC\uC6A9\uC744 \uC2DC\uD589\uD569\uB2C8\uB2E4.</p><p>E. Enforce the deletion of the root account so that it can&#39;t be used.<br> -root \uACC4\uC815\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC5C6\uB3C4\uB85D \uAC15\uC81C\uB85C \uC2DC\uD589\uD569\uB2C8\uB2E4.</p><hr><ul><li><p>\uC815\uB2F5 B,D</p><ul><li>\uB450 \uAC00\uC9C0 \uBC29\uBC95 \uBAA8\uB450 AWS \uACC4\uC815\uC758 \uB8E8\uD2B8 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uCD94\uAC00 \uBCF4\uC548\uC744 \uC81C\uACF5\uD569\uB2C8\uB2E4. \uC774\uB294 \uBCF4\uC548 \uBAA8\uBC94 \uC0AC\uB840\uC640\uB3C4 \uC77C\uCE58\uD569\uB2C8\uB2E4.</li></ul></li><li><p>\uC624\uB2F5\uC774\uC720</p><ul><li><p>A. AWS \uACC4\uC815 \uB8E8\uD2B8 \uC0AC\uC6A9\uC790 \uC561\uC138\uC2A4 \uD0A4\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC548 \uB429\uB2C8\uB2E4. \uC774 \uBC29\uBC95\uC740 \uB8E8\uD2B8 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uACB0\uC81C \uC815\uBCF4\uB97C \uD3EC\uD568\uD558\uC5EC \uACC4\uC815\uC758 \uBAA8\uB4E0 AWS \uC11C\uBE44\uC2A4\uC5D0 \uB300\uD55C \uBAA8\uB4E0 \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uC804\uCCB4 \uC561\uC138\uC2A4 \uAD8C\uD55C\uC744 \uBD80\uC5EC\uD558\uBA70 \uC774 \uC561\uC138\uC2A4 \uBC29\uBC95\uC5D0 \uB300\uD55C \uAD8C\uD55C\uC740 \uCD95\uC18C\uD560 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4.</p></li><li><p>C. \uB8E8\uD2B8 \uC0AC\uC6A9\uC790\uB294 \uC790\uC2E0\uC758 \uACC4\uC815\uC5D0 \uB300\uD55C \uC5ED\uD560\uC744 \uB9E1\uC744 \uC218 \uC5C6\uAE30 \uB54C\uBB38\uC5D0 \uC774\uAC83\uC740 \uC62C\uBC14\uB974\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uC5ED\uD560\uC740 \uACC4\uC815 \uB0B4\uC758 \uC11C\uBE44\uC2A4\uC640 \uB2E4\uB978 AWS \uACC4\uC815\uC5D0 \uB300\uD55C \uD0C0\uC0AC \uC561\uC138\uC2A4\uC5D0 \uC0AC\uC6A9\uB429\uB2C8\uB2E4.</p></li><li><p>E. \uB8E8\uD2B8 \uACC4\uC815\uC774 \uC0AD\uC81C\uB418\uBA74 \uBAA8\uB4E0 \uB9AC\uC18C\uC2A4\uAC00 \uC0AD\uC81C\uB418\uAE30 \uB54C\uBB38\uC5D0 \uC791\uB3D9\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uC774 \uACBD\uC6B0 \uB354 \uC774\uC0C1 AWS\uC5D0\uC11C \uD658\uACBD\uC744 \uC2E4\uD589\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</p></li></ul></li></ul><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',26),u={href:"https://www.coursera.org/learn/aws-certified-solutions-architect-associate",target:"_blank",rel:"noopener noreferrer"},d=o("coursera\uAC15\uC758"),h=e("br",null,null,-1),A={href:"https://aws.amazon.com/ko/faqs/",target:"_blank",rel:"noopener noreferrer"},f=o("aws FAQ");function m(_,S){const i=t("ExternalLinkIcon");return s(),l("div",null,[p,e("p",null,[e("a",u,[d,r(i)]),h,e("a",A,[f,r(i)])])])}var b=a(c,[["render",m],["__file","domain3-1-secure-resource-access.html.vue"]]);export{b as default};
