import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";import{r,o as n,c as o,a as i,b as e,d as s,e as a}from"./app.d6b42b88.js";const c={},p=s('<p>AWS-SAA \uC790\uACA9\uC99D \uD56D\uBAA9\uC744 \uAE30\uC900\uC73C\uB85C AWS\uB97C \uACF5\uBD80 \uD569\uB2C8\uB2E4. \uAC01 \uD56D\uBAA9\uC758 \uC758\uBBF8\uB97C \uC0B4\uD3B4\uBCF4\uACE0 \uC911\uC810\uC0AC\uD56D\uC744 \uD30C\uC545\uD558\uB294 \uAC83\uC744 \uBAA9\uD45C\uB85C \uD569\uB2C8\uB2E4.\uAC01 \uD56D\uBAA9\uBCC4 \uC774\uB860/\uC2E4\uC2B5\uC73C\uB85C \uD559\uC2B5\uD569\uB2C8\uB2E4.</p><details class="custom-container details"><summary>\uC0C1\uC138 \uC601\uC5ED ( \uCD9C\uCC98: aws certification \uD648\uD398\uC774\uC9C0 )</summary><ul><li>3.1 AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uBCF4\uC548 \uC561\uC138\uC2A4 \uC124\uACC4 <ul><li>\uC0AC\uC6A9\uC790, \uADF8\uB8F9, \uC5ED\uD560 \uAC04\uC5D0 \uC120\uD0DD\uD560 \uC2DC\uC810\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC9C0\uC815\uB41C \uC561\uC138\uC2A4 \uC815\uCC45\uC758 \uC21C \uD6A8\uACFC\uB97C \uD574\uC11D\uD569\uB2C8\uB2E4.</li><li>\uB8E8\uD2B8 \uACC4\uC815\uC744 \uBCF4\uD638\uD558\uB294 \uB370 \uC801\uC808\uD55C \uAE30\uC220\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>AWS IAM \uC758 \uAE30\uB2A5\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC790\uACA9 \uC99D\uBA85\uC744 \uBCF4\uD638\uD558\uB294 \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 AWS API \uC5D0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uC548\uC804\uD55C \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4 \uCD94\uC801 \uAC00\uB2A5\uC131\uC744 \uC0DD\uC131\uD558\uB294 \uB370 \uC801\uC808\uD55C \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li><li>3.2 \uBCF4\uC548 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uACC4\uCE35 \uC124\uACC4 \u{1F448}\u{1F448}\u{1F448}\u{1F448}\u{1F448} <strong>\uC9C0\uAE08 \uC5EC\uAE30</strong><ul><li>\uC8FC\uC5B4\uC9C4 \uD2B8\uB798\uD53D \uC81C\uC5B4 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uBCF4\uC548 \uADF8\uB8F9 \uBC0F \uB124\uD2B8\uC6CC\uD06C ACL \uC744 \uC0AC\uC6A9\uD560 \uC2DC\uC810 \uBC0F \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uACFC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC0AC\uC6A9\uD558\uC5EC \uB124\uD2B8\uC6CC\uD06C \uC138\uBD84\uD654 \uC804\uB7B5\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>Amazon VPC \uC5D0\uC11C AWS \uC11C\uBE44\uC2A4 \uC5D4\uB4DC\uD3EC\uC778\uD2B8 \uB610\uB294 \uC778\uD130\uB137 \uAE30\uBC18 \uB9AC\uC18C\uC2A4\uC5D0 \uC548\uC804\uD558\uAC8C \uC561\uC138\uC2A4\uD558\uB294 \uB370 \uC801\uC808\uD55C \uB77C\uC6B0\uD305 \uBA54\uCEE4\uB2C8\uC998\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC678\uBD80 \uC704\uD611\uC73C\uB85C\uBD80\uD130 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uBCF4\uD638\uD558\uB294 \uB370 \uC801\uC808\uD55C AWS \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li><li>3.3 \uC801\uC808\uD55C \uB370\uC774\uD130 \uBCF4\uC548 \uC635\uC158 \uC120\uD0DD <ul><li>\uC561\uC138\uC2A4 \uD328\uD134\uC744 \uAE30\uBC18\uC73C\uB85C \uAC1C\uCCB4\uC5D0 \uC801\uC6A9\uD574\uC57C \uD558\uB294 \uC815\uCC45\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uC11C\uBE44\uC2A4\uC758 \uC800\uC7A5 \uB370\uC774\uD130 \uBC0F \uC804\uC1A1 \uC911 \uB370\uC774\uD130\uC5D0 \uC801\uC808\uD55C \uC554\uD638\uD654 \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uC801\uC808\uD55C \uD0A4 \uAD00\uB9AC \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li></ul></details><h2 id="domain-3-\u110B\u1161\u11AB\u110C\u1165\u11AB\u1112\u1161\u11AB-\u110B\u1162\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB-\u1106\u1175\u11BE-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#domain-3-\u110B\u1161\u11AB\u110C\u1165\u11AB\u1112\u1161\u11AB-\u110B\u1162\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB-\u1106\u1175\u11BE-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> Domain 3 \uC548\uC804\uD55C \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBC0F \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4</h2><h2 id="aws-\u1107\u1169\u110B\u1161\u11AB-\u110B\u1162\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB-\u1100\u1168\u110E\u1173\u11BC-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#aws-\u1107\u1169\u110B\u1161\u11AB-\u110B\u1162\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB-\u1100\u1168\u110E\u1173\u11BC-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> AWS \uBCF4\uC548 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uACC4\uCE35 \uC124\uACC4</h2><p><strong>\uC138\uBD80\uC601\uC5ED</strong></p><ul><li><p>\uC8FC\uC5B4\uC9C4 \uD2B8\uB798\uD53D \uC81C\uC5B4 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uBCF4\uC548 \uADF8\uB8F9 \uBC0F \uB124\uD2B8\uC6CC\uD06C ACL \uC744 \uC0AC\uC6A9\uD560 \uC2DC\uC810 \uBC0F \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</p><ul><li>\uB300\uBD80\uBD84\uC758 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC544\uD0A4\uD14D\uCC98\uB294 \uB2E4\uC911 \uACC4\uCE35\uC774 \uB420 \uAC00\uB2A5\uC131\uC774 \uB192\uC2B5\uB2C8\uB2E4. \uBAA8\uB4E0 \uC218\uC900\uC5D0\uC11C \uBCF4\uC548\uC744 \uACE0\uB824\uD574\uC57C \uD558\uBA70, \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uACC4\uCE35 \uBCF4\uC548\uC5D0 \uB300\uD574 \uACF5\uBD80\uD560 \uB54C \uBCF4\uC548 \uADF8\uB8F9 \uBC0F \uB124\uD2B8\uC6CC\uD06C ACL\uC758 \uC0AC\uC6A9\uACFC \uAE30\uB2A5\uC5D0 \uC0C1\uB2F9\uD55C \uC8FC\uC758\uB97C \uAE30\uC6B8\uC5EC\uC57C \uD569\uB2C8\uB2E4. \uD2B8\uB798\uD53D \uC81C\uC5B4 \uC694\uAD6C\uC0AC\uD56D\uC5D0 \uB300\uD574 \uAD00\uB9AC\uB97C \uC81C\uACF5\uD558\uACE0 \uD544\uC694\uD55C \uADDC\uCE59, \uC81C\uD55C \uBC0F \uD5C8\uC6A9\uCE58\uB97C \uC54C\uC544\uC57C \uD569\uB2C8\uB2E4.</li><li>\uD568\uAED8 \uB610\uB294 \uAC1C\uBCC4\uC801\uC73C\uB85C \uC791\uB3D9\uD558\uB294 \uBC29\uC2DD\uC744 \uC774\uD574\uD569\uB2C8\uB2E4. \uB610\uD55C \uADDC\uCE59\uC744 \uC791\uC131\uD558\uB294 \uBC29\uBC95, \uD53C\uD574\uC57C \uD560 \uD568\uC815, \uADDC\uCE59 \uCC98\uB9AC \uB17C\uB9AC \uBC0F \uB354 \uB098\uC740 \uACB0\uD569 \uAE30\uB2A5\uC744 \uC704\uD574 \uC0AC\uC6A9\uD560 \uBC29\uBC95\uC744 \uC774\uD574\uD558\uC2ED\uC2DC\uC624.</li></ul></li><li><p>\uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uACFC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC0AC\uC6A9\uD558\uC5EC \uB124\uD2B8\uC6CC\uD06C \uC138\uBD84\uD654 \uC804\uB7B5\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</p><ul><li>\uB124\uD2B8\uC6CC\uD06C \uC138\uBD84\uD654\uB3C4 \uC911\uC694\uD569\uB2C8\uB2E4. \uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uACFC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC0AC\uC6A9\uD558\uB294 \uC2DC\uAE30\uC640 \uC804\uB7B5, \uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uACFC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC758 \uCC28\uC774\uC810, \uC774\uB7EC\uD55C \uC11C\uBE0C\uB137 \uC0AC\uC6A9\uC5D0 \uB300\uD55C \uC77C\uBC18\uC801\uC778 \uAD00\uD589\uC744 \uC774\uD574\uD558\uC2ED\uC2DC\uC624.</li><li>VPC \uB0B4\uC758 \uB77C\uC6B0\uD305 \uBA54\uCEE4\uB2C8\uC998\uC744 \uC774\uD574\uD574\uC57C \uD569\uB2C8\uB2E4. \uC608, \uC5EC\uAE30\uC5D0\uB294 \uB77C\uC6B0\uD305 \uD14C\uC774\uBE14\uC758 \uAD6C\uD604, \uC0AC\uC6A9 \uBC0F \uAE30\uB2A5\uC774 \uD3EC\uD568\uB418\uC9C0\uB9CC AWS \uC11C\uBE44\uC2A4 \uC5D4\uB4DC\uD3EC\uC778\uD2B8, VPN \uC5F0\uACB0 \uBC0F \uAE30\uD0C0 \uB124\uD2B8\uC6CC\uD06C \uC5F0\uACB0 \uB3C4\uAD6C\uC640 \uAC19\uC740 \uAE30\uD0C0 \uAD6C\uC131 \uC694\uC18C\uB97C \uC801\uC808\uD558\uAC8C \uC120\uD0DD\uD558\uACE0 \uBC30\uD3EC\uD558\uB294 \uBC29\uBC95\uB3C4 \uC54C\uC544\uC57C \uD569\uB2C8\uB2E4.</li></ul></li><li><p>Amazon VPC \uC5D0\uC11C AWS \uC11C\uBE44\uC2A4 \uC5D4\uB4DC\uD3EC\uC778\uD2B8 \uB610\uB294 \uC778\uD130\uB137 \uAE30\uBC18 \uB9AC\uC18C\uC2A4\uC5D0 \uC548\uC804\uD558\uAC8C \uC561\uC138\uC2A4\uD558\uB294 \uB370 \uC801\uC808\uD55C \uB77C\uC6B0\uD305 \uBA54\uCEE4\uB2C8\uC998\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</p><ul><li><p>\uBC30\uD3EC\uC640 \uC0AC\uC6A9\uB3C4 \uC911\uC694\uD55C \uC8FC\uC81C\uC774\uC9C0\uB9CC \uC774 \uC601\uC5ED\uC740 \uBCF4\uC548\uC5D0 \uAD00\uD55C \uAC83\uC785\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uB124\uD2B8\uC6CC\uD0B9 \uACC4\uCE35\uC5D0 \uB300\uD55C \uBCF4\uC548\uC744 \uC5B4\uB5BB\uAC8C \uAD6C\uCD95\uD569\uB2C8\uAE4C? \uC774\uB7EC\uD55C \uACC4\uCE35\uC5D0\uC11C \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC0AC\uC6A9\uC744 \uC5B4\uB5BB\uAC8C \uBCF4\uD638\uD569\uB2C8\uAE4C? \uADF8\uB9AC\uACE0 \uC774\uB7EC\uD55C \uBCF4\uC548 \uAD6C\uC131 \uC694\uC18C\uC758 \uAD00\uB9AC\uB294 \uC5B4\uB5A4 \uBAA8\uC2B5\uC785\uB2C8\uAE4C?</p></li><li><p>\uC774\uB7EC\uD55C \uC8FC\uC81C\uB97C \uB2E8\uB3C5\uC73C\uB85C \uACF5\uBD80\uD558\uAE30\uB294 \uC5B4\uB835\uC2B5\uB2C8\uB2E4. \uBCF4\uC548 \uADF8\uB8F9 \uBC0F \uB124\uD2B8\uC6CC\uD06C ACL\uC758 \uC0AC\uC6A9\uC744 \uC774\uD574\uD558\uB824\uBA74 \uACF5\uC6A9 \uBC0F \uC0AC\uC124 \uC11C\uBE0C\uB137\uC758 \uC0AC\uC6A9\uC744 \uC774\uD574\uD574\uC57C \uD569\uB2C8\uB2E4. \uD37C\uBE14\uB9AD \uBC0F \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC774\uD574\uD558\uB824\uBA74 \uB77C\uC6B0\uD305 \uBA54\uCEE4\uB2C8\uC998\uACFC \uC11C\uBE0C\uB137\uC5D0\uC11C \uC774\uB97C \uD65C\uC6A9\uD558\uB294 \uBC29\uBC95\uC5D0 \uB300\uD574 \uC54C\uC544\uC57C \uD569\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4, \uD2B9\uC815 \uC720\uD615\uC758 \uD2B8\uB798\uD53D\uC774 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84\uC5D0 \uC561\uC138\uC2A4\uD558\uB3C4\uB85D \uD5C8\uC6A9\uD574\uC57C \uD558\uC9C0\uB9CC \uD2B8\uB798\uD53D\uC774 \uC628\uD504\uB808\uBBF8\uC2A4 \uC704\uCE58\uC5D0\uC11C \uC640\uC11C VPN\uC744 \uD1B5\uACFC\uD558\uACE0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84\uAC00 \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC5D0 \uC788\uB294 \uACBD\uC6B0 \uC5B4\uB5BB\uAC8C \uD558\uC2DC\uACA0\uC2B5\uB2C8\uAE4C? \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84\uAC00 \uACF5\uC6A9 \uC778\uD130\uB137\uC5D0\uC11C \uC624\uB294 \uC561\uC138\uC2A4\uB85C\uBD80\uD130 \uC548\uC804\uD558\uC9C0\uB9CC VPN\uC744 \uD1B5\uD574 \uC5F0\uACB0\uB41C \uC628\uD504\uB808\uBBF8\uC2A4\uC5D0\uC11C \uC624\uB294 \uC694\uCCAD\uC758 VPC, \uC11C\uBE0C\uB137 \uB610\uB294 \uC778\uC2A4\uD134\uC2A4 \uC218\uC900\uC5D0 \uBB38\uC81C\uAC00 \uC5C6\uB3C4\uB85D \uD558\uB824\uBA74 \uBB34\uC5C7\uC744 \uD560 \uC218 \uC788\uC2B5\uB2C8\uAE4C? \uC774\uB7F0 \uC9C8\uBB38\uC5D0 \uB300\uB2F5\uD560 \uC218 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4.</p></li></ul></li><li><p>\uC678\uBD80 \uC704\uD611\uC73C\uB85C\uBD80\uD130 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uBCF4\uD638\uD558\uB294 \uB370 \uC801\uC808\uD55C AWS \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</p><ul><li>\uAE30\uBCF8\uC801\uC73C\uB85C \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uB300\uC0C1\uC73C\uB85C \uD560 \uC218 \uC788\uB294 \uB2E4\uC591\uD55C \uC720\uD615\uC758 \uACF5\uACA9\uC744 \uC644\uD654\uD558\uACE0 \uBC29\uC9C0\uD558\uAE30 \uC704\uD574 \uC5B4\uB5A4 \uC11C\uBE44\uC2A4\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uAE4C? \uC5B4\uB5A4 \uAD00\uB9AC \uC635\uC158\uC774 \uC788\uC2B5\uB2C8\uAE4C? \uBCF4\uC548 \uC11C\uBE44\uC2A4\uB294 \uD5C8\uC6A9\uB420 \uC218 \uC788\uB294 \uD2B8\uB798\uD53D \uC720\uD615\uC5D0 \uB300\uD574 \uC5B4\uB5A4 \uC81C\uC5B4\uB97C \uC81C\uACF5\uD569\uB2C8\uAE4C? \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0 \uB300\uD55C \uD2B8\uB798\uD53D \uD750\uB984\uC758 \uC5B4\uB290 \uC9C0\uC810\uC5D0 \uC11C\uBE44\uC2A4\uAC00 \uBC30\uCE58\uB429\uB2C8\uAE4C? \uC5E3\uC9C0 \uB85C\uCF00\uC774\uC158\uACFC VPC \uB0B4\uC5D0\uC11C \uC791\uB3D9\uD558\uB294 \uAC83\uC740 \uBB34\uC5C7\uC785\uB2C8\uAE4C?</li></ul></li></ul><p>\uBAA8\uB4E0 \uC218\uC900\uC5D0\uC11C \uBCF4\uC548\uC5D0 \uB300\uD574 \uACF5\uBD80\uD558\uACE0 \uBAA8\uB4E0 \uAC83\uC774 \uC5F0\uACB0\uB418\uC5B4 \uC788\uC74C\uC744 \uAE30\uC5B5\uD574\uC57C\uD569\uB2C8\uB2E4.</p><h2 id="\u110F\u1175\u110B\u116F\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#\u110F\u1175\u110B\u116F\u1103\u1173" aria-hidden="true">#</a> \uD0A4\uC6CC\uB4DC</h2><div class="custom-container tip"><p class="custom-container-title">Tips</p><p>stateless : stateful : networkACL: \uB4E4\uC5B4\uC624\uB294\uAC70 \uB098\uAC00\uB294\uAC70 \uB458\uB2E4 securgroup :</p></div><div class="custom-container tip"><p class="custom-container-title">VPC \uC640 VPN</p></div><h2 id="question" tabindex="-1"><a class="header-anchor" href="#question" aria-hidden="true">#</a> Question</h2><p>\uC194\uB8E8\uC158 \uC124\uACC4\uC790\uB294 VPC\uC758 Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uBC30\uD3EC\uB418\uB294 AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uBCF4\uC548 \uD658\uACBD\uC744 \uC124\uACC4\uD574\uC57C \uD569\uB2C8\uB2E4. \uC194\uB8E8\uC158\uC740 \uC6F9 \uC11C\uBC84, \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD074\uB7EC\uC2A4\uD130\uB85C \uAD6C\uC131\uB41C 3\uACC4\uCE35 \uC544\uD0A4\uD14D\uCC98\uB97C \uC9C0\uC6D0\uD574\uC57C \uD569\uB2C8\uB2E4. VPC\uB294 HTTPS \uD504\uB85C\uD1A0\uCF5C\uC744 \uD1B5\uD574\uC11C\uB9CC \uC778\uD130\uB137\uC5D0\uC11C \uC6F9 \uACC4\uCE35\uC758 \uB9AC\uC18C\uC2A4\uC5D0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB3C4\uB85D \uD5C8\uC6A9\uD574\uC57C \uD569\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB294 \uC791\uC5C5 \uC870\uD569\uC740 \uBB34\uC5C7\uC785\uB2C8\uAE4C? (2\uAC00\uC9C0 \uC751\uB2F5)</p><p>A. Amazon API Gateway\uB97C VPC\uC5D0 \uC5F0\uACB0\uD558\uACE0 \uC6F9 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uACC4\uCE35\uC5D0 \uB300\uD55C \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC0DD\uC131\uD569\uB2C8\uB2E4. B. \uC778\uD130\uB137 \uAC8C\uC774\uD2B8\uC6E8\uC774\uB97C VPC\uC5D0 \uC5F0\uACB0\uD569\uB2C8\uB2E4. \uC6F9 \uACC4\uCE35\uC5D0 \uB300\uD55C \uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uC744 \uC0DD\uC131\uD558\uACE0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uACC4\uCE35\uC5D0 \uB300\uD55C \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC0DD\uC131\uD569\uB2C8\uB2E4. C. \uAC00\uC0C1 \uD504\uB77C\uC774\uBE57 \uAC8C\uC774\uD2B8\uC6E8\uC774\uB97C VPC\uC5D0 \uC5F0\uACB0\uD569\uB2C8\uB2E4. \uC6F9 \uBC0F \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uACC4\uCE35\uC5D0 \uB300\uD55C \uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uC744 \uC0DD\uC131\uD558\uACE0 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uACC4\uCE35\uC5D0 \uB300\uD55C \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC0DD\uC131\uD569\uB2C8\uB2E4. D. \uC778\uD130\uB137\uC758 \uBAA8\uB4E0 \uD2B8\uB798\uD53D\uC744 \uD5C8\uC6A9\uD558\uB294 \uC6F9 \uC11C\uBC84 \uBCF4\uC548 \uADF8\uB8F9\uC744 \uB9CC\uB4ED\uB2C8\uB2E4. \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uD3EC\uD2B8\uC5D0\uC11C Amazon API Gateway\uC758 \uC694\uCCAD\uB9CC \uD5C8\uC6A9\uD558\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBCF4\uC548 \uADF8\uB8F9\uC744 \uC0DD\uC131\uD558\uACE0, \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD3EC\uD2B8\uC5D0\uC11C\uB9CC \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBCF4\uC548 \uADF8\uB8F9\uC758 TCP \uC5F0\uACB0\uC744 \uD5C8\uC6A9\uD558\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD074\uB7EC\uC2A4\uD130 \uBCF4\uC548 \uADF8\uB8F9\uC744 \uC0DD\uC131\uD569\uB2C8\uB2E4. E. \uC778\uD130\uB137\uC5D0\uC11C HTTPS \uC694\uCCAD\uC744 \uD5C8\uC6A9\uD558\uB294 \uC6F9 \uC11C\uBC84 \uBCF4\uC548 \uADF8\uB8F9\uC744 \uC0DD\uC131\uD569\uB2C8\uB2E4. \uC6F9 \uBCF4\uC548 \uADF8\uB8F9\uC758 \uC694\uCCAD\uB9CC \uD5C8\uC6A9\uD558\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84 \uBCF4\uC548 \uADF8\uB8F9\uC744 \uC0DD\uC131\uD558\uACE0, \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD3EC\uD2B8\uC5D0\uC11C\uB9CC \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBCF4\uC548 \uADF8\uB8F9\uC758 TCP \uC5F0\uACB0\uC744 \uD5C8\uC6A9\uD558\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD074\uB7EC\uC2A4\uD130 \uBCF4\uC548 \uADF8\uB8F9\uC744 \uC0DD\uC131\uD55C\uB2E4.</p><hr><ul><li><p>\uBB38\uC81C \uBD84\uC11D</p><ol><li>\uD658\uACBD\uC774 \uC548\uC804\uD574\uC57C \uD569\uB2C8\uB2E4.</li><li>\uC6F9 \uBC0F \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84\uC640 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD074\uB7EC\uC2A4\uD130\uB97C \uC0AC\uC6A9\uD558\uC5EC 3\uACC4\uCE35 \uC544\uD0A4\uD14D\uCC98\uAC00 \uC9C0\uC6D0\uB418\uC5B4\uC57C \uD569\uB2C8\uB2E4.</li><li>\uC6F9 \uACC4\uCE35\uC740 \uC778\uD130\uB137\uC5D0\uC11C \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uC5B4\uC57C \uD558\uC9C0\uB9CC HTTPS\uB85C\uB9CC \uAC00\uB2A5\uD574\uC57C \uD569\uB2C8\uB2E4.</li></ol><p>\uC989, \uC6F9 \uBCF4\uC548 \uADF8\uB8F9\uC758 \uC694\uCCAD\uB9CC \uD5C8\uC6A9\uD558\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84 \uBCF4\uC548 \uADF8\uB8F9\uC744 \uC0DD\uC131\uD558\uACE0, \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD3EC\uD2B8\uC5D0\uC11C\uB9CC \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBCF4\uC548 \uADF8\uB8F9\uC758 TCP \uC5F0\uACB0\uC744 \uD5C8\uC6A9\uD558\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD074\uB7EC\uC2A4\uD130 \uBCF4\uC548 \uADF8\uB8F9\uC744 \uC0DD\uC131\uD574\uC57C \uD569\uB2C8\uB2E4.</p></li><li><p>\uC815\uB2F5 B, E</p><ul><li>\uC751\uB2F5 B\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC6F9 \uACC4\uCE35\uC5D0 \uACF5\uAC1C\uC801\uC73C\uB85C \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uB3D9\uC2DC\uC5D0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uACC4\uCE35\uC744 \uBE44\uACF5\uAC1C\uB85C \uC720\uC9C0\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</li><li>\uC751\uB2F5 E\uB294 \uC774\uB7EC\uD55C \uAC01 \uACC4\uCE35\uC5D0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uD2B8\uB798\uD53D\uC758 \uC720\uD615\uACFC \uCD9C\uCC98\uB97C \uC9C0\uC815\uD569\uB2C8\uB2E4.</li></ul></li><li><p>\uC624\uB2F5 \uC774\uC720</p><ul><li>A. API Gateway\uB294 API\uC758 \uC0DD\uC131, \uAD00\uB9AC, \uBCF4\uC548\uC744 \uC704\uD55C \uAC83\uC73C\uB85C \uC6F9 \uD2F0\uC5B4\uC5D0 \uB300\uD55C \uC811\uADFC\uC131\uC744 \uC81C\uACF5\uD558\uB294 \uC635\uC158\uC740 \uC5C6\uC2B5\uB2C8\uB2E4.</li><li>C. VPN \uC5F0\uACB0\uC744 \uC704\uD574 \uD2B9\uBCC4\uD788 \uD65C\uC6A9\uB418\uB294 VGW\uC758 \uC0AC\uC6A9\uC744 \uC81C\uC548\uD569\uB2C8\uB2E4. \uC774\uAC83\uC740 \uC6F9 \uACC4\uCE35\uC774 \uC778\uD130\uB137\uC5D0\uC11C \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uBC29\uBC95\uC744 \uC81C\uACF5\uD558\uC9C0\uB9CC \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uACC4\uCE35\uC744 \uBCF4\uD638\uD558\uC9C0\uB294 \uC54A\uC2B5\uB2C8\uB2E4.</li><li>D. \uC5B8\uB73B \uBCF4\uAE30\uC5D0\uB294 E\uC640 \uB9E4\uC6B0 \uC720\uC0AC\uD574 \uBCF4\uC774\uC9C0\uB9CC \uADF8\uB807\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uC81C\uC548\uB41C \uBCF4\uC548 \uADF8\uB8F9\uC740 \uC694\uAD6C \uC0AC\uD56D\uC758 \uC694\uCCAD\uBCF4\uB2E4 \uB354 \uC5F4\uB824 \uC788\uC2B5\uB2C8\uB2E4. \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBCF4\uC548 \uADF8\uB8F9\uC740 API Gateway\uB97C \uB2E4\uC2DC \uD65C\uC6A9\uD558\uB824\uACE0 \uD558\uBA70 \uC55E\uC11C \uB9D0\uC500\uB4DC\uB9B0 \uAC83\uCC98\uB7FC \uC774 \uC11C\uBE44\uC2A4\uB294 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB300\uD55C \uC194\uB8E8\uC158\uC774 \uC544\uB2D9\uB2C8\uB2E4.</li></ul></li></ul><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',16),u={href:"https://www.coursera.org/learn/aws-certified-solutions-architect-associate",target:"_blank",rel:"noopener noreferrer"},d=a("coursera\uAC15\uC758"),h=i("br",null,null,-1),A={href:"https://aws.amazon.com/ko/faqs/",target:"_blank",rel:"noopener noreferrer"},m=a("aws FAQ");function P(_,f){const l=r("ExternalLinkIcon");return n(),o("div",null,[p,i("p",null,[i("a",u,[d,e(l)]),h,i("a",A,[m,e(l)])])])}var S=t(c,[["render",P],["__file","domain3-2-secure-application-tiers.html.vue"]]);export{S as default};
