import{_ as r}from"./plugin-vue_export-helper.21dcd24c.js";import{r as t,o as n,c as o,a as e,b as a,d as s,e as l}from"./app.d6b42b88.js";const c={},S=s('<p>AWS-SAA \uC790\uACA9\uC99D \uD56D\uBAA9\uC744 \uAE30\uC900\uC73C\uB85C AWS\uB97C \uACF5\uBD80 \uD569\uB2C8\uB2E4. \uAC01 \uD56D\uBAA9\uC758 \uC758\uBBF8\uB97C \uC0B4\uD3B4\uBCF4\uACE0 \uC911\uC810\uC0AC\uD56D\uC744 \uD30C\uC545\uD558\uB294 \uAC83\uC744 \uBAA9\uD45C\uB85C \uD569\uB2C8\uB2E4.\uAC01 \uD56D\uBAA9\uBCC4 \uC774\uB860/\uC2E4\uC2B5\uC73C\uB85C \uD559\uC2B5\uD569\uB2C8\uB2E4.</p><details class="custom-container details"><summary>\uC0C1\uC138 \uC601\uC5ED ( \uCD9C\uCC98: aws certification \uD648\uD398\uC774\uC9C0 )</summary><ul><li>3.1 AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uBCF4\uC548 \uC561\uC138\uC2A4 \uC124\uACC4 <ul><li>\uC0AC\uC6A9\uC790, \uADF8\uB8F9, \uC5ED\uD560 \uAC04\uC5D0 \uC120\uD0DD\uD560 \uC2DC\uC810\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC9C0\uC815\uB41C \uC561\uC138\uC2A4 \uC815\uCC45\uC758 \uC21C \uD6A8\uACFC\uB97C \uD574\uC11D\uD569\uB2C8\uB2E4.</li><li>\uB8E8\uD2B8 \uACC4\uC815\uC744 \uBCF4\uD638\uD558\uB294 \uB370 \uC801\uC808\uD55C \uAE30\uC220\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>AWS IAM \uC758 \uAE30\uB2A5\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC790\uACA9 \uC99D\uBA85\uC744 \uBCF4\uD638\uD558\uB294 \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 AWS API \uC5D0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uC548\uC804\uD55C \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4 \uCD94\uC801 \uAC00\uB2A5\uC131\uC744 \uC0DD\uC131\uD558\uB294 \uB370 \uC801\uC808\uD55C \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li><li>3.2 \uBCF4\uC548 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uACC4\uCE35 \uC124\uACC4 <ul><li>\uC8FC\uC5B4\uC9C4 \uD2B8\uB798\uD53D \uC81C\uC5B4 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uBCF4\uC548 \uADF8\uB8F9 \uBC0F \uB124\uD2B8\uC6CC\uD06C ACL \uC744 \uC0AC\uC6A9\uD560 \uC2DC\uC810 \uBC0F \uBC29\uBC95\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uACFC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC744 \uC0AC\uC6A9\uD558\uC5EC \uB124\uD2B8\uC6CC\uD06C \uC138\uBD84\uD654 \uC804\uB7B5\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>Amazon VPC \uC5D0\uC11C AWS \uC11C\uBE44\uC2A4 \uC5D4\uB4DC\uD3EC\uC778\uD2B8 \uB610\uB294 \uC778\uD130\uB137 \uAE30\uBC18 \uB9AC\uC18C\uC2A4\uC5D0 \uC548\uC804\uD558\uAC8C \uC561\uC138\uC2A4\uD558\uB294 \uB370 \uC801\uC808\uD55C \uB77C\uC6B0\uD305 \uBA54\uCEE4\uB2C8\uC998\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC678\uBD80 \uC704\uD611\uC73C\uB85C\uBD80\uD130 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uBCF4\uD638\uD558\uB294 \uB370 \uC801\uC808\uD55C AWS \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li><li>3.3 \uC801\uC808\uD55C \uB370\uC774\uD130 \uBCF4\uC548 \uC635\uC158 \uC120\uD0DD \u{1F448}\u{1F448}\u{1F448}\u{1F448}\u{1F448} <strong>\uC9C0\uAE08 \uC5EC\uAE30</strong><ul><li>\uC561\uC138\uC2A4 \uD328\uD134\uC744 \uAE30\uBC18\uC73C\uB85C \uAC1C\uCCB4\uC5D0 \uC801\uC6A9\uD574\uC57C \uD558\uB294 \uC815\uCC45\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uC11C\uBE44\uC2A4\uC758 \uC800\uC7A5 \uB370\uC774\uD130 \uBC0F \uC804\uC1A1 \uC911 \uB370\uC774\uD130\uC5D0 \uC801\uC808\uD55C \uC554\uD638\uD654 \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uC801\uC808\uD55C \uD0A4 \uAD00\uB9AC \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li></ul></li></ul></details><h2 id="\u110B\u1161\u11AB\u110C\u1165\u11AB\u1112\u1161\u11AB-\u110B\u1162\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB-\u1106\u1175\u11BE-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#\u110B\u1161\u11AB\u110C\u1165\u11AB\u1112\u1161\u11AB-\u110B\u1162\u1111\u1173\u11AF\u1105\u1175\u110F\u1166\u110B\u1175\u1109\u1167\u11AB-\u1106\u1175\u11BE-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> \uC548\uC804\uD55C \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uBC0F \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4</h2><h2 id="\u110C\u1165\u11A8\u110C\u1165\u11AF\u1112\u1161\u11AB-\u1103\u1166\u110B\u1175\u1110\u1165-\u1107\u1169\u110B\u1161\u11AB-\u110B\u1169\u11B8\u1109\u1167\u11AB-\u1109\u1165\u11AB\u1110\u1162\u11A8" tabindex="-1"><a class="header-anchor" href="#\u110C\u1165\u11A8\u110C\u1165\u11AF\u1112\u1161\u11AB-\u1103\u1166\u110B\u1175\u1110\u1165-\u1107\u1169\u110B\u1161\u11AB-\u110B\u1169\u11B8\u1109\u1167\u11AB-\u1109\u1165\u11AB\u1110\u1162\u11A8" aria-hidden="true">#</a> \uC801\uC808\uD55C \uB370\uC774\uD130 \uBCF4\uC548 \uC635\uC158 \uC120\uD0DD</h2><p><strong>\uC138\uBD80\uC601\uC5ED</strong></p><ul><li><p>\uC561\uC138\uC2A4 \uD328\uD134\uC744 \uAE30\uBC18\uC73C\uB85C \uAC1C\uCCB4\uC5D0 \uC801\uC6A9\uD574\uC57C \uD558\uB294 \uC815\uCC45\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4. \uB9C8\uC9C0\uB9C9 \uC601\uC5ED\uC740 \uC561\uC138\uC2A4 \uD328\uD134\uC744 \uAE30\uBC18\uC73C\uB85C \uBCF4\uD638\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4. S3\uC640 \uAC19\uC740 \uD2B9\uC815 \uC11C\uBE44\uC2A4\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC804\uCCB4 \uBC84\uD0B7\uC5D0 \uB300\uD55C \uBCF4\uC548\uC744 \uAD00\uB9AC\uD560 \uC218 \uC788\uC744 \uBFD0\uB9CC \uC544\uB2C8\uB77C \uD2B9\uC815 \uACBD\uB85C \uB610\uB294 \uAC1D\uCCB4\uB97C \uAE30\uBC18\uC73C\uB85C \uC81C\uC5B4\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4. \uC5B4\uB5A4 \uC11C\uBE44\uC2A4\uAC00 \uC774 \uC218\uC900\uC758 \uC138\uBD84\uC131\uC744 \uC81C\uACF5\uD558\uB294\uC9C0 \uC54C\uACE0, \uB2E4\uC591\uD55C \uC561\uC138\uC2A4 \uD328\uD134\uACFC \uC694\uAD6C \uC0AC\uD56D\uC744 \uAE30\uBC18\uC73C\uB85C \uC815\uCC45\uC744 \uC77D\uACE0 \uAD6C\uCD95\uD558\uBA70, \uC11C\uBE44\uC2A4 \uBC31\uC5D4\uB4DC\uC5D0\uC11C \uC774\uB7EC\uD55C \uC815\uCC45\uC744 \uD3C9\uAC00\uD558\uB294 \uBC29\uBC95\uC744 \uC774\uD574\uD560 \uC218 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4.</p></li><li><p>AWS \uC11C\uBE44\uC2A4\uC758 \uC800\uC7A5 \uB370\uC774\uD130 \uBC0F \uC804\uC1A1 \uC911 \uB370\uC774\uD130\uC5D0 \uC801\uC808\uD55C \uC554\uD638\uD654 \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</p></li><li><p>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uC801\uC808\uD55C \uD0A4 \uAD00\uB9AC \uC635\uC158\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</p></li></ul><p>\uADF8\uAC83\uC740 \uC2E4\uC81C\uB85C \uB2F9\uC2E0\uC774 \uC9D1\uC911\uD574\uC57C \uD560 \uC601\uC5ED \uC911 \uD558\uB098\uC785\uB2C8\uB2E4. \uC800\uC7A5 \uB370\uC774\uD130\uC640 \uC804\uC1A1 \uC911 \uB370\uC774\uD130\uB97C \uBCF4\uD638\uD558\uAE30 \uC704\uD574 AWS \uB0B4\uC5D0\uC11C \uC5B4\uB5A4 \uBC29\uBC95\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uAE4C? \uBB3C\uB860 \uADF8 \uC911 \uB9CE\uC740 \uBD80\uBD84\uC740 \uC0AC\uC6A9 \uC911\uC778 \uB3C4\uAD6C\uC640 \uC11C\uBE44\uC2A4\uC5D0 \uB530\uB77C \uB2E4\uB974\uC9C0\uB9CC \uC774\uB7EC\uD55C \uC2DC\uB098\uB9AC\uC624\uB97C \uC0B4\uD3B4\uBCF4\uC2ED\uC2DC\uC624. \uC608\uB97C \uB4E4\uC5B4, \uC778\uD130\uB137\uC744 \uD1B5\uD574 VPN\uC744 \uC0AC\uC6A9\uD560 \uB54C \uB370\uC774\uD130 \uBCF4\uD638\uB294 \uC5B4\uB5A4 \uBAA8\uC2B5\uC77C\uAE4C\uC694? AWS Direct Connect\uB97C \uD1B5\uD55C \uBE44\uACF5\uAC1C \uC5F0\uACB0 VPC \uAC04\uC758 \uC5F0\uACB0 Amazon Simple Storage Service\uC640 \uAC19\uC740 \uC11C\uBE44\uC2A4\uC640 VPC \uAC04\uC758 \uC804\uC1A1 \uACF5\uC6A9 \uC778\uD130\uB137\uC744 \uD1B5\uD574 \uCD5C\uC885 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uB3C4\uB2EC\uD560 \uB54C \uC804\uC1A1 \uC911\uC778 \uB370\uC774\uD130\uB97C \uBCF4\uD638\uD569\uB2C8\uAE4C? \uB610\uD55C \uB2E4\uC591\uD55C \uB370\uC774\uD130 \uAD00\uB9AC \uBC0F \uC2A4\uD1A0\uB9AC\uC9C0 \uC11C\uBE44\uC2A4\uB294 \uB370\uC774\uD130 \uBCF4\uD638\uB97C \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uD569\uB2C8\uAE4C? \uC608\uB97C \uB4E4\uC5B4, S3\uC640 Elastic Block Store\uB97C \uBE44\uAD50\uD588\uC744 \uB54C \uCC28\uC774\uC810\uC740 \uBB34\uC5C7\uC774\uBA70 \uC774\uB7EC\uD55C \uBCF4\uD638 \uAE30\uB2A5\uC744 \uC0AC\uC6A9\uD558\uBA74 \uC11C\uBE44\uC2A4 \uC131\uB2A5\uC774 \uBCC0\uACBD\uB429\uB2C8\uAE4C? \uADF8\uB9AC\uACE0 \uC774\uB7EC\uD55C \uBCF4\uC548 \uC8FC\uC81C\uC5D0 \uB300\uD574 \uC0DD\uAC01\uD560 \uB54C \uC554\uD638\uD654\uB97C \uBCF4\uB294 \uAC83\uC740 \uB2F9\uC5F0\uD558\uBA70 \uC554\uD638\uD654\uB294 \uD0A4 \uAD00\uB9AC\uB85C \uC774\uC5B4\uC9D1\uB2C8\uB2E4. \uC774 \uB450 \uC601\uC5ED \uBAA8\uB450 \uC2DC\uD5D8\uC5D0 \uC911\uC694\uD569\uB2C8\uB2E4.</p><p>\uC554\uD638\uD654\uC758 \uACBD\uC6B0 \uC5B4\uB5A4 \uC635\uC158\uC774 \uC788\uB294\uC9C0 \uD655\uC778\uD558\uC2ED\uC2DC\uC624. \uB124\uD2B8\uC6CC\uD0B9 \uC11C\uBE44\uC2A4\uB294 \uC804\uC1A1 \uC911\uC778 \uB370\uC774\uD130\uC758 \uC554\uD638\uD654\uB97C \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uD558\uBA70 \uB2E4\uC591\uD55C \uC11C\uBE44\uC2A4\uC5D0 \uB300\uD574 \uC5B4\uB5A4 \uC635\uC158\uC774 \uC788\uC2B5\uB2C8\uAE4C? \uC608\uB97C \uB4E4\uC5B4 \uD574\uB2F9 \uC554\uD638\uD654\uB294 AWS \uB0B4\uC758 \uB2E4\uC591\uD55C \uC720\uD615\uC758 \uB85C\uB4DC \uBC38\uB7F0\uC11C\uC5D0\uC11C \uC5B4\uB5BB\uAC8C \uBCF4\uC774\uACE0 \uC778\uC99D\uC11C\uB294 \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uB429\uB2C8\uAE4C? \uC2A4\uD1A0\uB9AC\uC9C0 \uC11C\uBE44\uC2A4\uB294 \uC554\uD638\uD654\uB97C \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uD569\uB2C8\uAE4C? S3, EBS\uC640 \uAC19\uC740 \uC11C\uBE44\uC2A4\uC640 \uD574\uB2F9 \uC11C\uBE44\uC2A4 \uB0B4\uC758 \uB2E4\uC591\uD55C \uC635\uC158 \uAC04\uC5D0 \uCC28\uC774\uAC00 \uC788\uC2B5\uB2C8\uAE4C?</p><p>\uADF8\uB9AC\uACE0 \uC554\uD638\uD654\uB97C \uACE0\uB824\uD560 \uB54C \uD0A4\uB294 \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uB429\uB2C8\uAE4C? \uC774\uB97C \uC5F0\uAD6C\uD558\uB824\uBA74 \uC11C\uBE44\uC2A4\uC5D0\uC11C \uD0A4\uB97C \uCC98\uB9AC\uD558\uB294 \uBC29\uC2DD, \uD0A4 \uAD00\uB9AC \uC11C\uBE44\uC2A4\uAC00 \uC791\uB3D9\uD558\uB294 \uBC29\uC2DD, S3\uC640 \uAC19\uC740 \uC11C\uBE44\uC2A4\uAC00 AWS Key Management Service\uC640 \uAC19\uC740 \uC11C\uBE44\uC2A4\uC640 \uC0C1\uD638 \uC791\uC6A9\uD558\uB294 \uBC29\uC2DD\uC744 \uBAA8\uB450 \uC5F0\uAD6C\uD574\uC57C \uD569\uB2C8\uB2E4. EBS \uBCFC\uB968\uC744 \uC0AC\uC6A9\uD558\uB294 \uC778\uC2A4\uD134\uC2A4\uC5D0\uC11C \uB370\uC774\uD130\uAC00 \uC0DD\uC131\uB418\uACE0 \uC788\uC73C\uBA70 \uB370\uC774\uD130\uB97C \uBCF4\uD638\uD558\uBA74\uC11C \uB0B4\uAD6C\uC131\uC744 \uC720\uC9C0\uD574\uC57C \uD558\uB294 \uC2DC\uB098\uB9AC\uC624\uB97C \uC0DD\uAC01\uD574 \uBCF4\uC2ED\uC2DC\uC624. \uC554\uD638\uD654\uB41C EBS \uBCFC\uB968\uC5D0 \uB370\uC774\uD130\uB97C \uC800\uC7A5\uD558\uC2DC\uACA0\uC2B5\uB2C8\uAE4C, \uC544\uB2C8\uBA74 \uC554\uD638\uD654\uB41C S3 \uBC84\uD0B7\uC73C\uB85C \uB370\uC774\uD130\uB97C \uC804\uC1A1\uD558\uC2DC\uACA0\uC2B5\uB2C8\uAE4C? \uC554\uD638\uD654\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC131\uB2A5\uC5D0 \uC601\uD5A5\uC744 \uC90D\uB2C8\uAE4C? \uADF8\uB807\uB2E4\uBA74 \uC5B4\uB5BB\uAC8C? \uB8E8\uD2B8 \uD0A4\uB97C \uC5B4\uB5BB\uAC8C \uCC98\uB9AC\uD558\uACE0 \uB370\uC774\uD130 \uD0A4\uC5D0 \uB300\uD574 \uADF8 \uBC29\uBC95\uC774 \uB2E4\uB97C\uAE4C\uC694? \uB370\uC774\uD130 \uBCF4\uC548\uC744 \uBCF4\uD638, \uD3C9\uAC00 \uBC0F \uAC10\uC0AC\uD558\uB294 \uB370 \uB3C4\uC6C0\uC774 \uB418\uB294 \uAD00\uB9AC \uC11C\uBE44\uC2A4\uAC00 \uC788\uC2B5\uB2C8\uAE4C?</p><p>\uB370\uC774\uD130 \uBCF4\uC548 \uC635\uC158\uC744 \uC774\uD574\uD558\uB824\uBA74 \uC11C\uBE44\uC2A4\uAC00 \uBCF4\uC548 \uC635\uC158\uC744 \uC791\uB3D9\uD558\uB294 \uBC29\uC2DD\uACFC \uC11C\uBE44\uC2A4\uAC00 \uC0C1\uD638 \uC791\uC6A9\uD558\uB294 \uBC29\uC2DD\uC744 \uC774\uD574\uD574\uC57C \uD569\uB2C8\uB2E4</p><p>\uC55E\uC11C \uB9D0\uD588\uB4EF\uC774 \uB370\uC774\uD130 \uBCF4\uC548\uC740 \uC2DC\uD5D8\uC5D0 \uC811\uADFC\uD560 \uB54C \uB9E4\uC6B0 \uC911\uC694\uD569\uB2C8\uB2E4. \uCD5C\uC0C1\uC758 \uC194\uB8E8\uC158\uC740 \uC885\uC885 \uAC00\uC7A5 \uC548\uC804\uD55C \uC194\uB8E8\uC158\uC774 \uB418\uAE30 \uB54C\uBB38\uC785\uB2C8\uB2E4. \uBAA8\uB4E0 \uC218\uC900\uC5D0\uC11C \uBCF4\uC548\uC744 \uD65C\uC131\uD654\uD560 \uB54C \uB370\uC774\uD130 \uBCF4\uD638\uB294 \uC2DC\uD5D8\uACFC \uC2E4\uC81C \uD658\uACBD \uBAA8\uB450\uC5D0\uC11C \uC194\uB8E8\uC158\uC744 \uC124\uACC4\uD558\uACE0 \uAD6C\uD604\uD558\uB294 \uB370 \uC911\uC694\uD569\uB2C8\uB2E4. \uBAA8\uB4E0 \uACC4\uCE35\uC5D0\uC11C \uBCF4\uC548\uC758 \uC6B0\uC120 \uC21C\uC704\uB97C \uC9C0\uC815\uD558\uACE0 \uBCF4\uC548\uC744 \uC5FC\uB450\uC5D0 \uB450\uACE0 \uC5F0\uAD6C\uD558\uBA70 \uC6D0\uB798 \uC124\uACC4\uC640 \uAE30\uC874 \uBC30\uD3EC \uAC15\uD654 \uBAA8\uB450\uC5D0 \uBCF4\uD638 \uAE30\uB2A5\uC744 \uCD94\uAC00\uD560 \uC218 \uC788\uB294 \uBC29\uBC95\uC744 \uD3C9\uAC00\uD558\uC2ED\uC2DC\uC624. \uC9C0\uAE08\uC740 \uADF8\uAC8C \uB2E4\uC57C. \uB098\uC911\uC5D0 \uC774\uC57C\uAE30\uD558\uC790.</p><h2 id="\u110F\u1175\u110B\u116F\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#\u110F\u1175\u110B\u116F\u1103\u1173" aria-hidden="true">#</a> \uD0A4\uC6CC\uB4DC</h2><div class="custom-container tip"><p class="custom-container-title">Tips</p><p>openSSL: http? SSH : \uC6D0\uACA9\uC811\uC18D \uD328\uD0B7</p></div><h2 id="question" tabindex="-1"><a class="header-anchor" href="#question" aria-hidden="true">#</a> Question</h2><p>\uD68C\uC0AC\uB294 \uADDC\uC815 \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uAE30 \uC704\uD574 \uBCF4\uC548 \uB370\uC774\uD130 \uC554\uD638\uD654 \uC194\uB8E8\uC158\uC744 \uAD6C\uD604\uD574\uC57C \uD569\uB2C8\uB2E4. \uC194\uB8E8\uC158\uC740 \uC554\uD638\uD654 \uB370\uC774\uD130 \uD0A4\uB97C \uC0DD\uC131, \uC800\uC7A5 \uBC0F \uC81C\uC5B4\uD560 \uB54C \uBCF4\uC548\uACFC \uB0B4\uAD6C\uC131\uC744 \uC81C\uACF5\uD574\uC57C \uD569\uB2C8\uB2E4. \uAC00\uC7A5 \uC548\uC804\uD55C \uC194\uB8E8\uC158\uC744 \uC81C\uACF5\uD558\uB824\uBA74 \uC5B4\uB5A4 \uC870\uCE58\uB97C \uCDE8\uD574\uC57C \uD569\uB2C8\uAE4C?</p><p>A. AWS Key Management Service\uB97C \uC0AC\uC6A9\uD558\uC5EC KMS \uD0A4 \uBC0F \uB370\uC774\uD130 \uD0A4 \uC0DD\uC131\uC774 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uB7F0 \uB2E4\uC74C AWS KMS \uD0A4 \uC815\uCC45\uC744 \uC0AC\uC6A9\uD558\uC5EC KMS \uD0A4\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4\uB97C \uC81C\uC5B4\uD569\uB2C8\uB2E4.<br> B. AWS Key Management Service\uB97C \uC0AC\uC6A9\uD558\uC5EC \uC554\uD638\uD654 \uD0A4\uB97C \uC0DD\uC131\uD558\uACE0 \uD0A4\uB97C AWS Certificate Manager\uB85C \uAC00\uC838\uC635\uB2C8\uB2E4. \uADF8\uB7F0 \uB2E4\uC74C IAM \uC815\uCC45\uC744 \uC0AC\uC6A9\uD558\uC5EC \uD0A4\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4\uB97C \uC81C\uC5B4\uD569\uB2C8\uB2E4.<br> C. AWS Marketplace\uC758 \uD0C0\uC0AC \uC194\uB8E8\uC158\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC554\uD638\uD654 \uD0A4\uB97C \uC0DD\uC131\uD558\uACE0 \uC554\uD638\uD654\uB41C \uC778\uC2A4\uD134\uC2A4 \uC2A4\uD1A0\uC5B4 \uBCFC\uB968\uC5D0 \uC800\uC7A5\uD569\uB2C8\uB2E4. \uADF8\uB7F0 \uB2E4\uC74C IAM \uC815\uCC45\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC554\uD638\uD654 \uD0A4 API\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4\uB97C \uC81C\uC5B4\uD569\uB2C8\uB2E4.<br> D. OpenSSL\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC554\uD638\uD654 \uD0A4\uB97C \uC0DD\uC131\uD558\uACE0 \uC554\uD638\uD654\uAC00 \uD65C\uC131\uD654\uB41C Amazon S3 \uBC84\uD0B7\uC5D0 \uD0A4\uB97C \uC5C5\uB85C\uB4DC\uD569\uB2C8\uB2E4. \uADF8\uB7F0 \uB2E4\uC74C AWS Key Management Service \uD0A4 \uC815\uCC45\uC744 \uC801\uC6A9\uD558\uC5EC \uD0A4\uC5D0 \uB300\uD55C \uC561\uC138\uC2A4\uB97C \uC81C\uC5B4\uD569\uB2C8\uB2E4.</p><hr><ul><li>\uC815\uB2F5 A <ul><li>\uC774 \uC194\uB8E8\uC158\uC740 \uB370\uC774\uD130 \uD0A4\uB97C \uC0DD\uC131, \uC800\uC7A5 \uBC0F \uC81C\uC5B4\uD558\uB294 \u200B\u200B\uC548\uC804\uD55C \uBC29\uBC95\uC744 \uC81C\uACF5\uD558\uB294 \uB3D9\uC2DC\uC5D0 \uD0A4 \uB0B4\uAD6C\uC131\uB3C4 \uC81C\uACF5\uD558\uBBC0\uB85C \uBCF4\uAE30\uC5D0 \uC88B\uC2B5\uB2C8\uB2E4. \uC9C1\uC811\uC801\uC778 \uACB0\uD568\uC744 \uCC3E\uC744 \uC218\uB294 \uC5C6\uC9C0\uB9CC \uC904\uAE30\uB294 \uAC00\uC7A5 \uC548\uC804\uD55C \uC194\uB8E8\uC158\uC744 \uCC3E\uC73C\uB77C\uACE0 \uC694\uCCAD\uD569\uB2C8\uB2E4.</li></ul></li><li>\uC624\uB2F5 \uC774\uC720 <ul><li>B. AWS Certificate Manager\uB294 \uB370\uC774\uD130 \uD0A4\uAC00 \uC544\uB2C8\uB77C SSL \uD0A4\uB97C \uC800\uC7A5\uD558\uB294 \uB370 \uC0AC\uC6A9\uB418\uAE30 \uB54C\uBB38\uC5D0 \uC774\uAC83\uC740 \uC815\uB2F5\uC774 \uC544\uB2D9\uB2C8\uB2E4</li><li>C. \uC774 \uB2F5\uC548\uC758 \uBB38\uC81C\uB294 \uC778\uC2A4\uD134\uC2A4 \uC2A4\uD1A0\uC5B4 \uBCFC\uB968\uC758 \uC0AC\uC6A9\uC785\uB2C8\uB2E4. \uADF8\uAC83\uB4E4\uC740 \uC784\uC2DC\uB85C \uD2B9\uBCC4\uD788 \uC124\uACC4\uB418\uC5C8\uC73C\uBA70 \uC774\uB294 \uB0B4\uAD6C\uC131 \uC694\uAD6C \uC0AC\uD56D\uC774 \uCDA9\uC871\uB418\uC9C0 \uC54A\uC74C\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4.</li><li>D. S3\uC5D0\uC11C \uC9C1\uC811 \uD0A4 \uAD00\uB9AC\uB97C \uD560 \uC218 \uC788\uB294 \uBC29\uBC95\uC774 \uC5C6\uACE0, KMS \uC815\uCC45\uC774 KMS\uC5D0 \uC800\uC7A5\uB41C \uD0A4\uB85C\uB9CC \uC791\uB3D9\uD558\uAE30 \uB54C\uBB38\uC5D0 \uC774\uAC83\uB3C4 \uC624\uB2F5\uC774\uB2E4.</li></ul></li></ul><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',19),d={href:"https://www.coursera.org/learn/aws-certified-solutions-architect-associate",target:"_blank",rel:"noopener noreferrer"},p=l("coursera\uAC15\uC758"),h=e("br",null,null,-1),u={href:"https://aws.amazon.com/ko/faqs/",target:"_blank",rel:"noopener noreferrer"},A=l("aws FAQ");function m(_,f){const i=t("ExternalLinkIcon");return n(),o("div",null,[S,e("p",null,[e("a",d,[p,a(i)]),h,e("a",u,[A,a(i)])])])}var g=r(c,[["render",m],["__file","domain3-3-data-security-options.html.vue"]]);export{g as default};
