import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{r as o,o as s,c as t,a,b as r,d as n,e as l}from"./app.d6b42b88.js";var c="/blog/assets/1.6e5b6b91.png",d="/blog/assets/2.5d7d178a.png",m="/blog/assets/3.696a80a9.png",g="/blog/assets/4.df0dc1e6.png",_="/blog/assets/5.35c4c3b9.png",p="/blog/assets/6.aad88e16.png",h="/blog/assets/7.797ec8e2.png",D="/blog/assets/8.708f3e17.png",b="/blog/assets/9.7572efe2.png",y="/blog/assets/10.87e75f17.png",f="/blog/assets/11.ec320b89.png",u="/blog/assets/12.c57d4ae2.png",B="/blog/assets/13.b9594a0c.png",v="/blog/assets/14.39c2f6d9.png",z="/blog/assets/15.5f21da58.png",A="/blog/assets/16.a20b5770.png",x="/blog/assets/17.3c6ec6ac.png";const C={},w=n('<h2 id="\u1109\u1175\u11AF\u1109\u1173\u11B8-\u1106\u1169\u11A8\u1111\u116D-aws-database-service-\u1107\u1175\u1100\u116D" tabindex="-1"><a class="header-anchor" href="#\u1109\u1175\u11AF\u1109\u1173\u11B8-\u1106\u1169\u11A8\u1111\u116D-aws-database-service-\u1107\u1175\u1100\u116D" aria-hidden="true">#</a> \uC2E4\uC2B5 \uBAA9\uD45C : AWS database service \uBE44\uAD50</h2><p><strong>\uC0AC\uC6A9\uD558\uB294 \uAE30\uC220 \uBC0F \uAC1C\uB150</strong></p><ul><li>Amazon Relational Database Service</li><li>Amazon DynamoDB</li><li>Caches</li></ul><h2 id="_1-rds-\u110B\u1175\u11AB\u1109\u1173\u1110\u1165\u11AB\u1109\u1173-\u1109\u1162\u11BC\u1109\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#_1-rds-\u110B\u1175\u11AB\u1109\u1173\u1110\u1165\u11AB\u1109\u1173-\u1109\u1162\u11BC\u1109\u1165\u11BC" aria-hidden="true">#</a> 1. RDS \uC778\uC2A4\uD134\uC2A4 \uC0DD\uC131</h2><ol><li>engine \uC120\uD0DD <img src="'+c+'" alt="rds \uC0DD\uC131" loading="lazy"></li><li>free tier \uC120\uD0DD <img src="'+d+'" alt="rds \uC0DD\uC131" loading="lazy"></li><li>DB instance \uC774\uB984\uACFC \uC811\uC18D\uC2DC \uC0AC\uC6A9\uD560 username, password\uB97C \uC124\uC815 <img src="'+m+'" alt="rds \uC0DD\uC131" loading="lazy"></li><li>VPC\uC640 sebnet group\uC744 \uC120\uD0DD\uD574\uC900\uB2E4. \uC774 \uC2E4\uC2B5\uC5D0\uC11C\uB294 default \uAC12\uB4E4\uC744 \uC0AC\uC6A9\uD55C\uB2E4. <img src="'+g+'" alt="rds \uC0DD\uC131" loading="lazy"></li></ol><h2 id="_2-dynamodb-table-\u1109\u1162\u11BC\u1109\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#_2-dynamodb-table-\u1109\u1162\u11BC\u1109\u1165\u11BC" aria-hidden="true">#</a> 2. DynamoDB table \uC0DD\uC131</h2><ol><li>create table\uB20C\uB7EC\uC11C \uC0DD\uC131 \uCC3D \uC811\uC18D</li><li>\uC774\uB984(\uB9AC\uC804\uC5D0\uC11C \uC720\uB2C8\uD06C)\uACFC \uD30C\uD2F0\uC158\uD0A4 \uC124\uC815 \uD6C4 \uC0DD\uC131 <img src="'+_+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li></ol><h2 id="_3-\u1109\u1162\u11BC\u1109\u1165\u11BC\u1109\u1169\u11A8\u1103\u1169-\u1107\u1175\u1100\u116D" tabindex="-1"><a class="header-anchor" href="#_3-\u1109\u1162\u11BC\u1109\u1165\u11BC\u1109\u1169\u11A8\u1103\u1169-\u1107\u1175\u1100\u116D" aria-hidden="true">#</a> 3. \uC0DD\uC131\uC18D\uB3C4 \uBE44\uAD50</h2><ul><li>DynamoDB \uD14C\uC774\uBE14\uC774 RDS \uC778\uC2A4\uD134\uC2A4\uBCF4\uB2E4 \uD6E8\uC52C \uBE60\uB974\uAC8C \uC0DD\uC131\uB41C\uB2E4. RDS DB \uC778\uC2A4\uD134\uC2A4\uB294 \uBCF8\uC9C8\uC801\uC73C\uB85C EC2 \uAE30\uBC18 \uC720\uD615\uACFC \uC720\uC0AC\uD55C \uAC83\uC73C\uB85C \uBD80\uD305\uB41C\uB2E4. \uADF8\uB7EC\uB098 DynamoDB\uB294 \uC11C\uBC84\uB9AC\uC2A4\uC785\uB2C8\uB2E4. \uADF8\uB798\uC11C \uD14C\uC774\uBE14 \uC0DD\uC131\uBC84\uD2BC\uC744 \uB204\uB974\uBA74 \uAC70\uC758 \uBC14\uB85C active \uC0C1\uD0DC\uAC00 \uB41C \uD14C\uC774\uBE14\uC744 \uD655\uC77C \uD560 \uC218 \uC788\uB2E4.</li></ul><h2 id="_4-dynamodb-accelerator-dax-\u1112\u116A\u11AF\u1109\u1165\u11BC\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#_4-dynamodb-accelerator-dax-\u1112\u116A\u11AF\u1109\u1165\u11BC\u1112\u116A" aria-hidden="true">#</a> 4. DynamoDB Accelerator DAX \uD65C\uC131\uD654</h2><ol><li>\uC67C\uCABD\uD0D0\uC0C9\uBC14\uB97C \uC0B4\uD3B4\uBCF4\uBA74 DAX\uB77C\uB294 \uC139\uC158\uC774 \uC788\uB2E4. \uC774\uAC8C DynamoDB Accelerator\uC774\uB2E4. \uD074\uB7EC\uC2A4\uD130\uC5D0 \uB4E4\uC5B4\uAC00\uC11C \uD074\uB7EC\uC2A4\uD130 \uC0DD\uC131 \uD074\uB9AD *\uC8FC\uC758 \uD55C\uAD6D \uB9AC\uC804\uC5D0\uC11C\uB294 DAX\uAE30\uB2A5 \uD14C\uC2A4\uD2B8 \uBABB\uD568!<br><img src="'+p+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>\uD074\uB7EC\uC2A4\uD130 \uC774\uB984\uC744 \uC801\uACE0<br><img src="'+h+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>node \uC720\uD615\uC744 \uC120\uD0DD\uD574\uC57C \uD558\uB294\uB370 \uC5EC\uAE30\uC11C\uB294 \uAE30\uBCF8 \uAC12\uC778 \uADF8\uB300\uB85C \uAC04\uB2E4.<br> \uC774 \uBD80\uBD84\uC740 \uD544\uC694\uD55C \uC131\uB2A5 \uC720\uD615, \uD544\uC694\uD55C \uBA54\uBAA8\uB9AC \uC591, \uD544\uC694\uD55C CPU \uC591 \uB4F1\uC5D0 \uB530\uB77C \uB2EC\uB77C\uC9C4\uB2E4. \uC815\uB9D0 \uC9E7\uC740 \uB300\uAE30 \uC2DC\uAC04\uC774\uB098 \uB0AE\uC740 \uCC98\uB9AC\uB7C9\uC774 \uD544\uC694\uD55C \uACBD\uC6B0\uC5D0\uB294 T type family\uC744 \uC0AC\uC6A9 \uD560 \uC218 \uB3C4 \uC788\uB2E4. \uADF8\uB7EC\uB2C8 \uBB38\uC11C\uB97C \uC798 \uC77D\uACE0 \uD30C\uC545\uD55C \uD6C4\uC5D0 \uC801\uD569\uD55C \uC81C\uD488\uC744 \uC120\uD0DD\uD558\uC790. <img src="'+D+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>\uB178\uB4DC \uC218 \uC120\uD0DD, \uCD94\uD6C4 \uB178\uB4DC\uB97C \uD655\uC7A5\uD558\uAC70\uB098 \uCD95\uC18C \uAC00\uB2A5\uD558\uB2E4. <img src="'+b+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>\uC11C\uBE0C\uB137 \uADF8\uB8F9\uC0DD\uC131\uC2DC \uC11C\uBE0C\uB137 \uADF8\uB8F9\uC744 \uC0DD\uC131\uD574\uC57C\uD55C\uB2E4. DAX\uB294 \uC774 \uADF8\uB8F9\uC5D0 \uD3EC\uD568\uB41C \uC11C\uBE0C\uB137\uC5D0\uC11C \uD074\uB7EC\uC2A4\uD130 \uB178\uB4DC\uC5D0 \uB124\uD2B8\uC6CC\uD06C \uC8FC\uC18C\uB97C \uD560\uB2F9\uD55C\uB2E4. <img src="'+y+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>vpc \uC640 \uC11C\uBE0C\uB137 \uC120\uD0EF \uC0AC\uC6A9\uD560 vpc\uC640 \uC11C\uBE0C\uB137\uC5D0 \uB300\uD574 \uD2B9\uC815 \uC11C\uBE0C\uB137\uC744 \uC120\uD0DD\uD55C\uB2E4. \uC5EC\uAE30\uC11C \uB9CE\uC740 \uC11C\uBE0C\uB137\uC744 \uC120\uD0DD\uD560 \uC218 \uC788\uC73C\uBA70 \uD574\uB2F9 \uC11C\uBE0C\uB137\uC5D0 \uD074\uB7EC\uC2A4\uD130 \uC885\uB958\uAC00 \uBD84\uC0B0\uB418\uB3C4\uB85D \uD560 \uC218 \uC788\uB2E4 <img src="'+f+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>\uC561\uC138\uC2A4\uC5D0 \uAD00\uC5EC\uD558\uB294 \uAE30\uBCF8 \uBCF4\uC548 \uADF8\uB8F9, \uC77C\uB2E8 \uADF8\uB300\uB85C \uB454\uB2E4. <img src="'+u+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>AWS Service \uC5ED\uD560\uC744 \uC120\uD0DD\uD55C\uB2E4.(IAM) create\uB97C \uC120\uD0DD\uD558\uACE0 \uB2E4 \uAE30\uBCF8\uC73C\uB85C \uB454\uB2E4. <img src="'+B+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>option \uC0AC\uD56D \uB2E4\uC591\uD55C \uC124\uC815\uC744 \uC9C0\uC815\uD560 \uC218 \uC788\uB2E4 ( \uCE90\uC2DC \uC9C0\uC18D\uC2DC\uAC04, \uC720\uC9C0 \uAD00\uB9AC \uAE30\uAC04\uB4F1) \uC6B0\uC120 \uB514\uD3F4\uD2B8\uB85C \uB450\uACE0 \uD074\uB7EC\uC2A4\uD130\uB97C \uC0DD\uC131\uD55C\uB2E4.<br><img src="'+v+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li></ol><h2 id="_5-read-replica-\u110B\u1175\u11B0\u1100\u1175-\u110C\u1165\u11AB\u110B\u116D\u11BC-\u1107\u1169\u11A8\u110C\u1166-\u1109\u1162\u11BC\u1109\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#_5-read-replica-\u110B\u1175\u11B0\u1100\u1175-\u110C\u1165\u11AB\u110B\u116D\u11BC-\u1107\u1169\u11A8\u110C\u1166-\u1109\u1162\u11BC\u1109\u1165\u11BC" aria-hidden="true">#</a> 5. read replica(\uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C) \uC0DD\uC131</h2><p>\uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8\uC740 \uC77D\uAE30 \uC791\uC5C5\uB7C9\uC774 \uB9CE\uC744 \uB54C \uC0AC\uC6A9\uD55C\uB2E4.</p><ol><li>read replica \uC0DD\uC131\uC744 \uD074\uB9AD <img src="'+z+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>\uBCF5\uC81C\uBCF8\uC758 \uC774\uB984\uC744 \uC815\uD558\uACE0 \uB098\uBA38\uC9C0 \uAC12\uB4E4\uC740 \uAE30\uBCF8\uAC12\uC73C\uB85C \uB450\uACE0 \uC0DD\uC131\uD55C\uB2E4. <img src="'+A+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li><li>\uC0DD\uC131\uB41C \uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8\uC744 \uD074\uB9AD \uC0C1\uC138 \uC815\uBCF4\uB97C \uBCFC \uC218 \uC788\uB2E4. \uD604\uC7AC \uC5F0\uACB0\uC774 \uC804\uD600 \uC5C6\uC74C\uC744 \uC54C \uC218 \uC788\uC9C0\uB9CC \uC6D0\uD560 \uACBD\uC6B0 \uC774 \uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8\uC744 \uAC00\uB9AC\uD0A4\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uB9CC\uB4E4\uACE0 \uC774 \uBCF5\uC81C\uBCF8\uC5D0\uC11C \uB370\uC774\uD130\uB97C \uC77D\uC744 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC8FC\uC758\uD560 \uC810\uC740 \uC774\uAC83\uC740 \uCE90\uC2DC\uC774\uBBC0\uB85C \uC77D\uAE30 \uC694\uCCAD(read request)\uB9CC \uC774\uACF3\uC744 \uAC00\uB9AC\uD0A4\uAC8C \uB41C\uB2E4. \uADF8 \uC678\uB3C4 \uBAA8\uB2C8\uD130\uB9C1, \uB85C\uADF8, \uC774\uBCA4\uD2B8, \uC801\uC6A9\uD560 \uC218 \uC788\uB294 \uB2E4\uC591\uD55C \uAD6C\uC131 \uB4F1\uC744 \uD655\uC778 \uD560 \uC218 \uC788\uB2E4. <img src="'+x+'" alt="DynamoDB \uC0DD\uC131" loading="lazy"></li></ol><h2 id="_6-dynamodb-accelerator-cluster-\u110B\u116A-read-replica-\u110C\u1161\u11A8\u1103\u1169\u11BC\u1107\u1161\u11BC\u1109\u1175\u11A8-\u1107\u1175\u1100\u116D" tabindex="-1"><a class="header-anchor" href="#_6-dynamodb-accelerator-cluster-\u110B\u116A-read-replica-\u110C\u1161\u11A8\u1103\u1169\u11BC\u1107\u1161\u11BC\u1109\u1175\u11A8-\u1107\u1175\u1100\u116D" aria-hidden="true">#</a> 6. DynamoDB Accelerator Cluster \uC640 read replica \uC791\uB3D9\uBC29\uC2DD \uBE44\uAD50</h2><p>\uC791\uB3D9 \uBC29\uC2DD\uC744 \uC0B4\uD3B4\uBCF4\uBA74 EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0\uC11C \uC2E4\uD589 \uC911\uC778 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uC788\uACE0 DynamoDB Accelerator \uD074\uB7EC\uC2A4\uD130\uC640 DynamoDB \uD14C\uC774\uBE14\uC774 \uC788\uB2E4\uACE0 \uAC00\uC815\uD574\uBCF4\uB2E4. DAX \uD074\uB7EC\uC2A4\uD130\uB294 \uADF8 \uC0AC\uC774\uC5D0 \uC788\uB2E4. \uB530\uB77C\uC11C \uBAA8\uB4E0 \uC77D\uAE30 \uC694\uCCAD, \uC4F0\uAE30 \uC694\uCCAD, \uBAA8\uB4E0 \uC694\uCCAD\uC774 \uC774 \uD074\uB7EC\uC2A4\uD130\uB85C \uC774\uB3D9\uD55C\uB2E4. \uADF8\uB7F0 \uB2E4\uC74C DynamoDB Accelerator\uAC00 \uD074\uB7EC\uC2A4\uD130\uC640 \uC2E4\uC81C \uD14C\uC774\uBE14 \uAC04\uC758 \uD1B5\uC2E0\uC744 \uCC98\uB9AC\uD55C\uB2E4. \uB530\uB77C\uC11C \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0\uC11C Accelerator Cluster \uC0AC\uC6A9\uD558\uB824 \uD560\uB54C\uB294 \uAE30\uBCF8\uC801\uC73C\uB85C DynamoDB\uC6A9 \uD074\uB77C\uC774\uC5B8\uD2B8\uB97C \uAD50\uCCB4\uD558\uACE0 DynamoDB Accelerator Client\uB97C \uBC30\uCE58\uD558\uAE30\uB9CC \uD558\uBA74 \uB41C\uB2E4.</p><p>\uBC18\uBA74\uC5D0 \uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8\uC744 \uBCF4\uBA74 \uC870\uAE08 \uB2E4\uB974\uAC8C \uC124\uC815\uC774 \uB41C\uB2E4 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84\uAC00 \uC788\uACE0 Primary \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uAC00 \uC788\uC73C\uBA70 \uB450 \uBC88\uC9F8 \uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8\uC774 \uC788\uB2E4. \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC11C\uBC84\uB294 \uAE30\uBCF8\uC801\uC73C\uB85C Primary \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC77D\uACE0/\uC4F0\uAE30 \uC791\uC5C5\uC744 \uD560 \uC218 \uC788\uB2E4. \uADF8\uB7EC\uB098 \uB370\uC774\uD130 \uBCF4\uACE0\uB098 \uB370\uC774\uD130 \uC9D1\uACC4 \uB610\uB294 \uB9E4\uC6B0 \uAE34 \uCFFC\uB9AC \uC218\uD589\uACFC \uAC19\uC774 read-heaby \uC694\uCCAD\uC774 \uC788\uC744 \uB54C \uC774\uB7EC\uD55C \uC791\uC5C5\uC740 \uC804\uC6A9 \uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8\uC73C\uB85C \uC694\uCCAD\uC744 \uBCF4\uB0B8\uB2E4\uBA74 \uD6A8\uC728\uC801\uC778 \uC544\uD0A4\uD14D\uCCD0\uAC00 \uB420 \uAC83\uC774\uB2E4.</p><p>DynamoDB Accelerator Cluster \uC640 read replica \uC774\uAC83\uB4E4\uC740 \uC77C\uC885\uC758 \uB2E4\uB978 \uC544\uD0A4\uD14D\uCC98\uC774\uB2E4.</p><h2 id="\u1106\u1161\u1106\u116E\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#\u1106\u1161\u1106\u116E\u1105\u1175" aria-hidden="true">#</a> \uB9C8\uBB34\uB9AC</h2><p>RDS\uC758 Read replica\uC5D0\uC11C\uB294 \uAE30\uBCF8\uC5D0\uC11C \uC77D\uACE0 \uC4F0\uAE30\uB97C \uC218\uD589\uD55C \uB2E4\uC74C \uC77D\uAE30 \uC804\uC6A9\uCFFC\uB9AC\uAC00 \uC788\uB2E4\uBA74 \uC774 \uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8\uC73C\uB85C \uC774\uB3D9\uD55C\uB2E4. \uB530\uB77C\uC11C \uC694\uCCAD\uC744 \uC9C0\uC2DC\uD560 \uC704\uCE58\uB97C \uC54C\uACE0 \uC788\uC5B4\uC57C \uD55C\uB2E4. \uBC18\uBA74 DynamoDB Accelerator\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uD074\uB7EC\uC2A4\uD130\uC640 \uD1B5\uC2E0\uD558\uACE0 \uADF8\uB7F0 \uB2E4\uC74C \uBAA8\uB4E0 \uD1B5\uC2E0\uC774 \uB4A4\uC5D0\uC11C \uCC98\uB9AC\uB41C\uB2E4</p><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',21),S={href:"https://www.coursera.org/learn/aws-certified-solutions-architect-associate",target:"_blank",rel:"noopener noreferrer"},k=l("coursera\uAC15\uC758"),E=a("br",null,null,-1),R={href:"https://aws.amazon.com/ko/faqs/",target:"_blank",rel:"noopener noreferrer"},V=l("aws FAQ");function X(N,P){const e=o("ExternalLinkIcon");return s(),t("div",null,[w,a("p",null,[a("a",S,[k,r(e)]),E,a("a",R,[V,r(e)])])])}var L=i(C,[["render",X],["__file","domain3-Exercise.html.vue"]]);export{L as default};
