import{_ as n}from"./plugin-vue_export-helper.21dcd24c.js";import{r as o,o as t,c as r,a,b as i,d as s,e as l}from"./app.d6b42b88.js";const c={},d=s('<p>AWS-SAA \uC790\uACA9\uC99D \uD56D\uBAA9\uC744 \uAE30\uC900\uC73C\uB85C AWS\uB97C \uACF5\uBD80 \uD569\uB2C8\uB2E4. \uAC01 \uD56D\uBAA9\uC758 \uC758\uBBF8\uB97C \uC0B4\uD3B4\uBCF4\uACE0 \uC911\uC810\uC0AC\uD56D\uC744 \uD30C\uC545\uD558\uB294 \uAC83\uC744 \uBAA9\uD45C\uB85C \uD569\uB2C8\uB2E4.\uAC01 \uD56D\uBAA9\uBCC4 \uC774\uB860/\uC2E4\uC2B5\uC73C\uB85C \uD559\uC2B5\uD569\uB2C8\uB2E4.</p><details class="custom-container details"><summary>\uC0C1\uC138 \uC601\uC5ED ( \uCD9C\uCC98: aws certification \uD648\uD398\uC774\uC9C0 )</summary><ul><li><p>4.1 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC2A4\uD1A0\uB9AC\uC9C0 \uC194\uB8E8\uC158 \uC2DD\uBCC4</p><ul><li>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uB370\uC774\uD130 \uC2A4\uD1A0\uB9AC\uC9C0 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uBE44\uC6A9\uC744 \uCD5C\uC18C\uD654\uD558\uAE30 \uC704\uD574 \uC2DC\uAC04 \uACBD\uACFC\uC5D0 \uB530\uB77C \uB370\uC774\uD130\uB97C \uC2A4\uD1A0\uB9AC\uC9C0 \uACC4\uCE35\uC5D0 \uC800\uC7A5\uD558\uB294 \uC790\uB3D9\uD654\uB41C \uD504\uB85C\uC138\uC2A4\uB97C \uC801\uC6A9\uD569\uB2C8\uB2E4.</li></ul></li><li><p>4.2 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uCEF4\uD4E8\uD305 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC11C\uBE44\uC2A4 \uC2DD\uBCC4 \u{1F448}\u{1F448}\u{1F448}\u{1F448}\u{1F448} <strong>\uC9C0\uAE08 \uC5EC\uAE30</strong></p><ul><li>\uC6CC\uD06C\uB85C\uB4DC\uC758 \uAC01 \uCE21\uBA74\uC5D0 \uB300\uD574 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 Amazon EC2 \uACB0\uC81C \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uBE44\uC6A9 \uAD00\uC810\uC5D0\uC11C \uC801\uC808\uD55C \uC870\uC815 \uC804\uB7B5\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC6CC\uD06C\uB85C\uB4DC\uC5D0 \uAC00\uC7A5 \uC801\uD569\uD55C \uCEF4\uD4E8\uD305 \uB9AC\uC18C\uC2A4\uB97C \uC120\uD0DD\uD558\uACE0 \uD06C\uAE30\uB97C \uC870\uC815\uD569\uB2C8\uB2E4.</li><li>\uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4 \uBC0F \uC11C\uBC84\uB9AC\uC2A4 \uC544\uD0A4\uD14D\uCC98\uB97C \uD1B5\uD574 TCO(\uCD1D \uC18C\uC720 \uBE44\uC6A9)\uB97C \uCD5C\uC18C\uD654\uD558\uB294 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li></ul></li><li><p>4.3 \uBE44\uC6A9\uC5D0 \uCD5C\uC801\uD654\uB41C \uB124\uD2B8\uC6CC\uD06C \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4</p><ul><li>\uBE44\uC6A9\uC744 \uC808\uAC10\uD558\uAE30 \uC704\uD574 \uCF58\uD150\uCE20 \uC804\uC1A1\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uC2DC\uC810\uC744 \uD30C\uC545\uD569\uB2C8\uB2E4.</li><li>AWS \uB0B4\uC5D0\uC11C \uB370\uC774\uD130 \uC804\uC1A1 \uBE44\uC6A9\uC744 \uC808\uAC10\uD558\uAE30 \uC704\uD55C \uC804\uB7B5\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uD658\uACBD\uACFC \uC628\uD504\uB808\uBBF8\uC2A4 \uD658\uACBD \uAC04\uC5D0 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC5F0\uACB0 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li></ul></li></ul></details><h2 id="domain-4-\u1107\u1175\u110B\u116D\u11BC\u110B\u1166-\u110E\u116C\u110C\u1165\u11A8\u1112\u116A\u1103\u116C\u11AB-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#domain-4-\u1107\u1175\u110B\u116D\u11BC\u110B\u1166-\u110E\u116C\u110C\u1165\u11A8\u1112\u116A\u1103\u116C\u11AB-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> Domain 4 : \uBE44\uC6A9\uC5D0 \uCD5C\uC801\uD654\uB41C \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4</h2><h2 id="\u1107\u1175\u110B\u116D\u11BC-\u1112\u116D\u110B\u1172\u11AF\u110C\u1165\u11A8\u110B\u1175\u11AB-\u110F\u1165\u11B7\u1111\u1172\u1110\u1175\u11BC-\u1106\u1175\u11BE-\u1103\u1166\u110B\u1175\u1110\u1165\u1107\u1166\u110B\u1175\u1109\u1173-\u1109\u1165\u1107\u1175\u1109\u1173-\u1109\u1175\u11A8\u1107\u1167\u11AF" tabindex="-1"><a class="header-anchor" href="#\u1107\u1175\u110B\u116D\u11BC-\u1112\u116D\u110B\u1172\u11AF\u110C\u1165\u11A8\u110B\u1175\u11AB-\u110F\u1165\u11B7\u1111\u1172\u1110\u1175\u11BC-\u1106\u1175\u11BE-\u1103\u1166\u110B\u1175\u1110\u1165\u1107\u1166\u110B\u1175\u1109\u1173-\u1109\u1165\u1107\u1175\u1109\u1173-\u1109\u1175\u11A8\u1107\u1167\u11AF" aria-hidden="true">#</a> \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uCEF4\uD4E8\uD305 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC11C\uBE44\uC2A4 \uC2DD\uBCC4</h2><p><strong>\uC138\uBD80\uD56D\uBAA9</strong></p><ul><li>\uC6CC\uD06C\uB85C\uB4DC\uC758 \uAC01 \uCE21\uBA74\uC5D0 \uB300\uD574 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 Amazon EC2 \uACB0\uC81C \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uBE44\uC6A9 \uAD00\uC810\uC5D0\uC11C \uC801\uC808\uD55C \uC870\uC815 \uC804\uB7B5\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC6CC\uD06C\uB85C\uB4DC\uC5D0 \uAC00\uC7A5 \uC801\uD569\uD55C \uCEF4\uD4E8\uD305 \uB9AC\uC18C\uC2A4\uB97C \uC120\uD0DD\uD558\uACE0 \uD06C\uAE30\uB97C \uC870\uC815\uD569\uB2C8\uB2E4.</li><li>\uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4 \uBC0F \uC11C\uBC84\uB9AC\uC2A4 \uC544\uD0A4\uD14D\uCC98\uB97C \uD1B5\uD574 TCO(\uCD1D \uC18C\uC720 \uBE44\uC6A9)\uB97C \uCD5C\uC18C\uD654\uD558\uB294 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li></ul><p>\uB2E4\uC74C\uC73C\uB85C \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uCEF4\uD4E8\uD305 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC11C\uBE44\uC2A4\uB97C \uC2DD\uBCC4\uD569\uB2C8\uB2E4. Amazon EC2\uC5D0\uB294 \uC5EC\uB7EC \uAC00\uC9C0 \uB2E4\uC591\uD55C \uC694\uAE08 \uC635\uC158\uC774 \uC788\uC2B5\uB2C8\uB2E4. EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0\uB294 \uC628\uB514\uB9E8\uB4DC(\uC774\uBC1C), \uC608\uC57D, \uC804\uC6A9 \uBC0F \uC2A4\uD31F \uC694\uAE08\uC774 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uB7F0 \uB2E4\uC74C Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uBFD0\uB9CC \uC544\uB2C8\uB77C \uB2E4\uB978 \uCEF4\uD4E8\uD305 \uC11C\uBE44\uC2A4\uC5D0\uB3C4 \uC801\uC6A9\uD560 \uC218 \uC788\uB294 Savings Plans\uAC00 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uAC01 \uAC00\uACA9 \uCC45\uC815 \uBAA8\uB378\uC740 \uBE44\uC6A9\uC5D0 \uC11C\uB85C \uB2E4\uB978 \uC601\uD5A5\uC744 \uBBF8\uCE58\uBA70 \uD2B9\uC815 \uC2DC\uB098\uB9AC\uC624\uC5D0\uC11C \uAC00\uC7A5 \uC798 \uC0AC\uC6A9\uB429\uB2C8\uB2E4.</p><p>\uC2DC\uAC04\uC774 \uC9C0\uB0A8\uC5D0 \uB530\uB77C \uC0AC\uC6A9\uB7C9\uC774 \uC77C\uC815\uD558\uACE0 \uD558\uB8E8 \uC885\uC77C \uC0AC\uC6A9\uB7C9\uC774 \uC57D\uAC04 \uAE09\uC99D\uD558\uC9C0\uB9CC \uC2E4\uC81C\uB85C\uB294 \uD2B9\uC815 \uC0AC\uC6A9\uB7C9 \uC218\uC900 \uC544\uB798\uB85C \uB5A8\uC5B4\uC9C0\uC9C0 \uC54A\uB294 \uCEF4\uD4E8\uD305 \uC6CC\uD06C\uB85C\uB4DC\uC5D0 \uAC00\uC7A5 \uC801\uD569\uD55C \uAC00\uACA9 \uCC45\uC815 \uBAA8\uB378\uC744 \uD30C\uC545\uD558\uB824\uACE0 \uD55C\uB2E4\uACE0 \uAC00\uC815\uD574 \uBCF4\uACA0\uC2B5\uB2C8\uB2E4. \uC628\uB514\uB9E8\uB4DC, \uC608\uC57D \uB610\uB294 \uC2A4\uD31F \uC911 \uC774 \uC2DC\uB098\uB9AC\uC624\uC5D0 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uAC00\uACA9 \uCC45\uC815 \uBAA8\uB378\uC740 \uBB34\uC5C7\uC785\uB2C8\uAE4C? \uC774 \uACBD\uC6B0 \uC2DC\uAC04\uC774 \uC9C0\uB0A8\uC5D0 \uB530\uB77C \uC77C\uC815 \uC218\uC900\uC758 \uC77C\uAD00\uB41C \uC0AC\uC6A9\uB7C9\uC774 \uC788\uB2E4\uB294 \uAC83\uC744 \uC54C\uACE0 \uC608\uC57D \uC778\uC2A4\uD134\uC2A4\uB97C \uC0AC\uC6A9\uD558\uC5EC \uC774 \uC77C\uAD00\uB41C \uC0AC\uC6A9\uB7C9\uC5D0 \uB300\uD574 \uD560\uC778\uB41C \uCCAD\uAD6C \uC694\uAE08\uC744 \uBC1B\uC744 \uC218 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uC608\uC57D \uC778\uC2A4\uD134\uC2A4\uB97C \uC120\uD0DD\uD558\uACA0\uC2B5\uB2C8\uB2E4. \uB610\uD55C \uC628\uB514\uB9E8\uB4DC \uC778\uC2A4\uD134\uC2A4\uB97C \uC0AC\uC6A9\uD558\uC5EC \uAE09\uC99D\uD558\uB294 \uC0AC\uC6A9\uB7C9\uC744 \uCC98\uB9AC\uD55C \uB2E4\uC74C \uC2A4\uD31F\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC624\uB798 \uC9C0\uC18D\uB418\uC9C0 \uC54A\uB294 \uC0AC\uC6A9\uB7C9\uC758 \uC815\uC810\uC744 \uCC98\uB9AC\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 \uAC00\uACA9 \uCC45\uC815\uC5D0 \uB300\uD55C \uD63C\uD569 \uC811\uADFC \uBC29\uC2DD\uC73C\uB85C, \uC6CC\uD06C\uB85C\uB4DC \uD655\uC7A5\uC5D0 \uB300\uD55C \uBE44\uC6A9 \uCD5C\uC801\uD654 \uC811\uADFC \uBC29\uC2DD\uC744 \uC81C\uACF5\uD569\uB2C8\uB2E4. \uAC01 Amazon EC2 \uC694\uAE08 \uBAA8\uB378\uC774 \uBE44\uC6A9\uC5D0 \uBBF8\uCE58\uB294 \uC601\uD5A5\uC744 \uC798 \uC774\uD574\uD558\uBA74 \uC2DC\uD5D8\uC744 \uC900\uBE44\uD558\uB294 \uB370 \uB3C4\uC6C0\uC774 \uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC608\uC57D \uC778\uC2A4\uD134\uC2A4\uB294 Amazon RDS \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC778\uC2A4\uD134\uC2A4\uC5D0\uB3C4 \uC0AC\uC6A9\uD560 \uC218 \uC788\uC73C\uBBC0\uB85C \uCD94\uAC00 \uBE44\uC6A9 \uCD5C\uC801\uD654\uAC00 \uAC00\uB2A5\uD558\uB2E4\uB294 \uC810\uC744 \uAE30\uC5B5\uD558\uB294 \uAC83\uB3C4 \uC911\uC694\uD569\uB2C8\uB2E4.</p><p>EC2\uC758 \uC694\uAE08 \uBAA8\uB378 \uC678\uC5D0\uB3C4 \uBE44\uC6A9 \uCD5C\uC801\uD654\uB97C \uC704\uD574 \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uC640 \uC81C\uD488\uAD70\uC744 \uACE0\uB824\uD574\uC57C \uD569\uB2C8\uB2E4. \uC801\uC808\uD55C \uD06C\uAE30 \uC870\uC815\uC774\uB77C\uB294 \uAC1C\uB150\uC740 \uC6CC\uD06C\uB85C\uB4DC\uC758 \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB294 \uC778\uC2A4\uD134\uC2A4 \uC720\uD615\uC744 \uC120\uD0DD\uD55C \uB2E4\uC74C \uC801\uC808\uD55C \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uB97C \uC120\uD0DD\uD558\uB294 \uAC83\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4. \uC774\uB97C \uC704\uD574\uC11C\uB294 \uC2DC\uAC04 \uACBD\uACFC\uC5D0 \uB530\uB978 \uC778\uC2A4\uD134\uC2A4 \uC0AC\uC6A9\uB960\uC744 \uD655\uC778\uD560 \uC218 \uC788\uB3C4\uB85D \uBAA8\uB2C8\uD130\uB9C1\uC774 \uD544\uC694\uD569\uB2C8\uB2E4. \uADF8\uB9AC\uACE0 \uC774\uB7EC\uD55C \uC778\uC2A4\uD134\uC2A4\uC758 \uC0AC\uC6A9\uB960\uC774 \uB9E4\uC6B0 \uB0AE\uC740 \uACBD\uC6B0 \uB354 \uC791\uC740 \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uB85C \uBCC0\uACBD\uD560 \uC218 \uC788\uB294 \uC635\uC158\uC774 \uC788\uC73C\uBBC0\uB85C AWS \uCCAD\uAD6C\uC11C \uBE44\uC6A9\uC744 \uC808\uC57D\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4 Amazon RDS\uC640 \uAC19\uC740 \uC77C\uBD80 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC11C\uBE44\uC2A4\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uB97C \uC120\uD0DD\uD560 \uC218 \uC788\uC73C\uBBC0\uB85C \uB9AC\uC18C\uC2A4 \uD06C\uAE30\uB97C \uC801\uC808\uD558\uAC8C \uC870\uC815\uD55C\uB2E4\uB294 \uC544\uC774\uB514\uC5B4\uB294 \uCEF4\uD4E8\uD305 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC801\uC6A9\uB429\uB2C8\uB2E4. \uCEF4\uD4E8\uD305 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uBE44\uC6A9\uC744 \uCD5C\uC801\uD654\uD558\uB294 \uB610 \uB2E4\uB978 \uBC29\uBC95\uC740 \uC8FC\uC5B4\uC9C4 \uC2DC\uB098\uB9AC\uC624\uC5D0\uC11C \uC801\uC808\uD55C \uD655\uC7A5 \uC804\uB7B5\uC744 \uC120\uD0DD\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uB192\uC740 CPU \uC0AC\uC6A9\uB960\uB85C \uC778\uD574 \uC131\uB2A5 \uBB38\uC81C\uAC00 \uBC1C\uC0DD\uD558\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uAC00 \uC788\uC744 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774 \uACBD\uC6B0 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB300\uD55C \uC77D\uAE30 \uC694\uCCAD\uC774 \uB9CE\uC740 \uAE30\uAC04\uC774 \uC788\uC744 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uD655\uC7A5\uD558\uAE30 \uC704\uD574 \uC778\uC2A4\uD134\uC2A4 \uD06C\uAE30\uB97C \uB298\uB9B4 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uC774\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC640 \uAD00\uB828\uB41C \uBE44\uC6A9\uC744 \uC99D\uAC00\uC2DC\uD0B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uBE44\uC6A9\uC744 \uCD5C\uC801\uD654\uD558\uAE30 \uC704\uD574 \uC77D\uAE30 \uC694\uCCAD\uC758 \uC77C\uBD80\uB97C \uD761\uC218\uD558\uACE0 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC778\uC2A4\uD134\uC2A4\uC758 \uC694\uCCAD\uC744 \uC624\uD504\uB85C\uB4DC\uD558\uB294 \uB370 \uB3C4\uC6C0\uC774 \uB418\uB3C4\uB85D \uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8 \uB610\uB294 \uC77C\uC885\uC758 \uCE90\uC2DC\uB97C \uCD94\uAC00\uD558\uB294 \uAC83\uC744 \uB300\uC2E0 \uACE0\uB824\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB97C \uD1B5\uD574 \uC131\uB2A5 \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB294 \uB3D9\uC2DC\uC5D0 \uBE44\uC6A9\uC744 \uCD5C\uC801\uD654\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC77D\uAE30 \uC804\uC6A9 \uBCF5\uC81C\uBCF8\uC744 \uB3C4\uC785\uD558\uB294 \uAC83\uC774 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC778\uC2A4\uD134\uC2A4\uB97C \uD655\uC7A5\uD558\uB294 \uAC83\uBCF4\uB2E4 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC77C \uC218 \uC788\uAE30 \uB54C\uBB38\uC785\uB2C8\uB2E4. \uC2A4\uD1A0\uB9AC\uC9C0 Auto Scaling\uC744 \uC0AC\uC6A9\uD558\uC5EC Amazon RDS \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uC2A4\uD1A0\uB9AC\uC9C0 \uD06C\uAE30\uB97C \uC801\uC808\uD558\uAC8C \uC870\uC815\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4. \uC870\uC815\uACFC \uAD00\uB828\uD558\uC5EC \uC774\uB7EC\uD55C \uC885\uB958\uC758 \uACB0\uC815\uC744 \uB0B4\uB9AC\uB824\uBA74 AWS \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC11C\uBE44\uC2A4\uC640 AWS \uCEF4\uD4E8\uD305 \uC11C\uBE44\uC2A4 \uBAA8\uB450\uC5D0 \uC874\uC7AC\uD558\uB294 \uB2E4\uC591\uD55C \uC870\uC815 \uBA54\uCEE4\uB2C8\uC998\uC5D0 \uB300\uD55C \uAE4A\uC740 \uC9C0\uC2DD\uC774 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC5EC\uAE30\uC5D0 \uACF5\uBC31\uC774 \uC788\uB294 \uACBD\uC6B0 \uAD00\uB828 \uC11C\uBE44\uC2A4\uC5D0 \uB300\uD55C \uC124\uBA85\uC11C\uB97C \uC77D\uB294 \uAC83\uC774 \uC88B\uC2B5\uB2C8\uB2E4.</p><p>\uBE44\uC6A9\uC744 \uCD5C\uC801\uD654\uD558\uB294 \uB610 \uB2E4\uB978 \uBC29\uBC95\uC740 \uC0AC\uC6A9 \uC0AC\uB840\uC5D0 \uB9DE\uB294 \uC62C\uBC14\uB978 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC11C\uBE44\uC2A4\uB97C \uC120\uD0DD\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uC0AC\uC6A9 \uC0AC\uB840 \uBC0F \uC0AC\uC6A9 \uD328\uD134\uC5D0 \uB530\uB77C Amazon Aurora\uBCF4\uB2E4 Amazon Aurora Serverless\uB97C \uC0AC\uC6A9\uD558\uB294 \uAC83\uC774 \uB354 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC77C \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uBAA8\uB450 \uC0AC\uC6A9 \uC0AC\uB840\uC5D0 \uB530\uB77C \uB2E4\uB974\uBBC0\uB85C \uAC01 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC640 \uD574\uB2F9 \uC0AC\uC6A9 \uC0AC\uB840\uB97C \uC798 \uC544\uB294 \uAC83\uC774 \uC911\uC694\uD569\uB2C8\uB2E4. \uC774\uC81C \uCEF4\uD4E8\uD305 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uBE44\uC6A9 \uCD5C\uC801\uD654\uB97C \uC704\uD55C \uB9C8\uC9C0\uB9C9 \uD301\uC740 \uAC00\uB2A5\uD55C \uACBD\uC6B0 \uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4\uB97C \uC0AC\uC6A9\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4. \uD074\uB77C\uC6B0\uB4DC\uC5D0\uC11C \uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC2E4\uD589 \uB610\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uAD00\uB9AC\uC640 \uAC19\uC740 \uC791\uC5C5\uC744 \uC704\uD574 \uC11C\uBC84\uB97C \uC720\uC9C0 \uAD00\uB9AC\uD558\uB294 \uC6B4\uC601 \uBD80\uB2F4\uC744 \uC81C\uAC70\uD569\uB2C8\uB2E4. \uADF8\uB9AC\uACE0 \uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4\uB294 \uD074\uB77C\uC6B0\uB4DC \uADDC\uBAA8\uC5D0\uC11C \uC6B4\uC601\uB418\uAE30 \uB54C\uBB38\uC5D0 \uD2B8\uB79C\uC7AD\uC158 \uB610\uB294 \uC11C\uBE44\uC2A4\uB2F9 \uB354 \uB0AE\uC740 \uBE44\uC6A9\uC744 \uC81C\uACF5\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC989, \uCEE8\uD14C\uC774\uB108\uC6A9 AWS Fargate, \uC11C\uBC84\uB9AC\uC2A4 \uCEF4\uD4E8\uD305\uC6A9 AWS Lambda \uB610\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC6A9 Amazon DynamoDB\uC640 \uAC19\uC740 \uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4 \uC0AC\uC6A9\uC744 \uACE0\uB824\uD574\uC57C \uD569\uB2C8\uB2E4. AWS \uCCAD\uAD6C\uC11C\uC5D0 \uBE44\uC6A9\uC774 \uC870\uAE08 \uB354 \uB4E4 \uC218 \uC788\uB294 \uC11C\uBE44\uC2A4\uC758 \uACBD\uC6B0\uC5D0\uB3C4 \uC774\uB7EC\uD55C \uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4\uB294 DBA\uC640 \uC2DC\uC2A4\uD15C \uAD00\uB9AC\uC790\uAC00 \uC77C\uC0C1\uC801\uC778 \uC791\uC5C5\uC5D0 \uC2DC\uAC04\uC744 \uBCF4\uB0B4\uB294 \uB300\uC2E0 \uBC31\uC5C5 \uBC0F \uBCF5\uAD6C\uC640 \uAC19\uC740 \uD575\uC2EC \uBE44\uC988\uB2C8\uC2A4 \uBC0F \uAC1C\uBC1C\uC5D0 \uC9D1\uC911\uD560 \uC218 \uC788\uB3C4\uB85D \uD569\uB2C8\uB2E4.</p><h2 id="\u110F\u1175\u110B\u116F\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#\u110F\u1175\u110B\u116F\u1103\u1173" aria-hidden="true">#</a> \uD0A4\uC6CC\uB4DC</h2><div class="custom-container tip"><p class="custom-container-title">Tips</p><p>\uD655\uC7A5 \uBA54\uCEE4\uB2C8\uC998, DDoS \uBCF4\uD638 \uBA54\uCEE4\uB2C8\uC998 \uBC0F \uAD00\uB828\uB41C \uBAA8\uB4E0 \uC11C\uBE44\uC2A4\uC758 \uAC00\uACA9 \uCC45\uC815\uC5D0 \uB300\uD574 \uC77D\uC5B4\uBCF4\uC790</p></div><h2 id="question" tabindex="-1"><a class="header-anchor" href="#question" aria-hidden="true">#</a> Question</h2><p>\uD55C \uC2E0\uC0DD \uAE30\uC5C5\uC774 \uC778\uD504\uB77C \uAD00\uB9AC\uC758 \uC6B4\uC601 \uC624\uBC84\uD5E4\uB4DC \uC5C6\uC774 \uB9C8\uC774\uD06C\uB85C\uC11C\uBE44\uC2A4\uB97C \uBE44\uC6A9 \uD6A8\uC728\uC801\uC73C\uB85C \uC2E4\uD589\uD558\uACE0 \uC561\uC138\uC2A4\uD560 \uC218 \uC788\uB294 \uC194\uB8E8\uC158\uC744 \uCC3E\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uC194\uB8E8\uC158\uC740 \uC694\uCCAD \uBCFC\uB968\uC758 \uAE09\uACA9\uD55C \uBCC0\uD654\uB97C \uC218\uC6A9\uD558\uACE0 \uC77C\uBC18\uC801\uC778 DDoS \uACF5\uACA9\uC73C\uB85C\uBD80\uD130 \uBCF4\uD638\uD560 \uC218 \uC788\uB3C4\uB85D \uC2E0\uC18D\uD558\uAC8C \uD655\uC7A5\uD560 \uC218 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD558\uB294 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC194\uB8E8\uC158\uC740 \uBB34\uC5C7\uC785\uB2C8\uAE4C?</p><p>A. Run the microservices in containers using AWS Elastic Beanstalk.<br> -AWS Elastic Beanstalk\uB97C \uC0AC\uC6A9\uD558\uC5EC \uCEE8\uD14C\uC774\uB108\uC5D0\uC11C \uB9C8\uC774\uD06C\uB85C\uC11C\uBE44\uC2A4\uB97C \uC2E4\uD589\uD569\uB2C8\uB2E4.<br> B. Run the microservices in AWS Lambda behind an Amazon API Gateway.<br> -Amazon API Gateway \uB4A4\uC5D0\uC11C AWS Lambda\uC5D0\uC11C \uB9C8\uC774\uD06C\uB85C\uC11C\uBE44\uC2A4\uB97C \uC2E4\uD589\uD569\uB2C8\uB2E4.<br> C. Run the microservices on Amazon EC2 instances in an Auto Scaling group.<br> -Auto Scaling \uADF8\uB8F9\uC758 Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0\uC11C \uB9C8\uC774\uD06C\uB85C\uC11C\uBE44\uC2A4\uB97C \uC2E4\uD589\uD569\uB2C8\uB2E4. D. Run the microservices in containers using Amazon Elastic Container Service backed by EC2 instances.<br> -EC2 \uC778\uC2A4\uD134\uC2A4\uAC00 \uC9C0\uC6D0\uD558\uB294 Amazon Elastic Container Service\uB97C \uC0AC\uC6A9\uD558\uC5EC \uCEE8\uD14C\uC774\uB108\uC5D0\uC11C \uB9C8\uC774\uD06C\uB85C\uC11C\uBE44\uC2A4\uB97C \uC2E4\uD589\uD569\uB2C8\uB2E4.</p><hr><ul><li>\uBB38\uC81C \uBD84\uC11D</li></ul><ol><li>\uD68C\uC0AC\uAC00 \uB9C8\uC774\uD06C\uB85C\uC11C\uBE44\uC2A4\uB97C \uC2E4\uD589\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</li><li>\uC778\uD504\uB77C \uAD00\uB9AC \uC624\uBC84\uD5E4\uB4DC \uC5C6\uC774 \uB9C8\uC774\uD06C\uB85C\uC11C\uBE44\uC2A4\uB97C \uC2E4\uD589\uD558\uB294 \uBC29\uBC95\uC744 \uCC3E\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</li><li>\uC194\uB8E8\uC158\uC774 \uBE60\uB974\uAC8C \uD655\uC7A5\uB418\uACE0 DDoS\uB85C\uBD80\uD130 \uBCF4\uD638\uD574\uC57C \uD569\uB2C8\uB2E4.</li><li>\uD68C\uC0AC\uAC00 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC194\uB8E8\uC158\uC744 \uCC3E\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</li></ol><ul><li><p>\uC815\uB2F5 B</p><ul><li>AWS Lambda\uB294 \uC11C\uBC84\uB9AC\uC2A4 \uC11C\uBE44\uC2A4\uC774\uAE30 \uB54C\uBB38\uC5D0 \uC778\uD504\uB77C \uAD00\uB9AC\uC758 \uC6B4\uC601 \uC624\uBC84\uD5E4\uB4DC \uC5C6\uC774 \uBC31\uC5D4\uB4DC \uB9C8\uC774\uD06C\uB85C\uC11C\uBE44\uC2A4\uB97C \uD638\uC2A4\uD305\uD560 \uC218 \uC788\uB294 \uD6CC\uB96D\uD55C \uC194\uB8E8\uC158\uC785\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC774 \uC194\uB8E8\uC158\uC740 \uC774\uB7EC\uD55C \uC694\uAD6C \uC0AC\uD56D\uC744 \uCDA9\uC871\uD569\uB2C8\uB2E4.</li><li>API Gateway \uBC0F AWS Lambda\uB294 \uD655\uC7A5\uC744 \uAD00\uB9AC\uD560 \uD544\uC694 \uC5C6\uC774 \uC694\uCCAD \uBCFC\uB968\uC758 \uAE09\uACA9\uD55C \uBCC0\uD654\uC5D0 \uB9DE\uAC8C \uD655\uC7A5\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uD655\uC7A5 \uBA54\uCEE4\uB2C8\uC998\uC774 \uB0B4\uC7A5\uB418\uC5B4 \uC788\uACE0 \uB9E4\uC6B0 \uBE60\uB974\uBBC0\uB85C \uC774\uB7EC\uD55C \uC694\uAD6C \uC0AC\uD56D\uB3C4 \uCDA9\uC871\uD569\uB2C8\uB2E4.</li><li>DDoS \uBC29\uC5B4\uC5D0 \uB300\uD574 \uC0DD\uAC01\uD574\uC57C \uD569\uB2C8\uB2E4. API \uAC8C\uC774\uD2B8\uC6E8\uC774\uC640 \uB78C\uB2E4 \uC11C\uBE44\uC2A4\uB294 \uBAA8\uB450 \uB300\uADDC\uBAA8 \uD655\uC7A5 \uAE30\uB2A5\uC744 \uAC16\uCD98 \uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4\uC785\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC774\uB7EC\uD55C \uC11C\uBE44\uC2A4\uB97C \uC555\uB3C4\uD558\uB294 \uAC83\uC740 \uC2E4\uC81C\uB85C \uB9E4\uC6B0 \uC5B4\uB835\uC2B5\uB2C8\uB2E4.</li><li>\uB610\uD55C API Gateway\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC0DD\uC131\uD558\uB294 \uAC01 \uBA54\uC11C\uB4DC\uC5D0 \uB300\uD574 \uC870\uC808\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB807\uAC8C \uD558\uBA74 \uBC31\uC5D4\uB4DC\uC5D0 \uC694\uCCAD\uC774 \uB118\uCCD0\uB098\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uB300\uC2E0 API \uAC8C\uC774\uD2B8\uC6E8\uC774 \uC218\uC900\uC5D0\uC11C \uC0AD\uC81C\uB429\uB2C8\uB2E4. \uB610\uD55C API Gateway\uB294 \uACC4\uCE35 7\uC778 \uC704\uC870 \uC694\uCCAD\uC73C\uB85C \uACF5\uACA9\uC744 \uBC1B\uB4E0 \uBD84\uC0B0 \uC11C\uBE44\uC2A4 \uAC70\uBD80 \uACF5\uACA9 \uB610\uB294 DDoS \uACF5\uACA9\uC73C\uB85C\uBD80\uD130 \uBC31\uC5D4\uB4DC \uC2DC\uC2A4\uD15C\uC744 \uC790\uB3D9\uC73C\uB85C \uBCF4\uD638\uD569\uB2C8\uB2E4.</li><li>API Gateway\uC640 Lambda\uB294 \uBAA8\uB450 \uC11C\uBC84\uB9AC\uC2A4 \uC11C\uBE44\uC2A4\uC774\uBBC0\uB85C \uC720\uD734 \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD574 \uBE44\uC6A9\uC744 \uC9C0\uBD88\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC694\uAD6C \uC0AC\uD56D\uACFC \uC694\uCCAD \uBCFC\uB968\uC758 \uAE09\uACA9\uD55C \uC99D\uAC00 \uBC0F \uAC10\uC18C\uB97C \uAC10\uC548\uD560 \uB54C \uB54C\uB54C\uB85C \uD558\uB294 \uAC83\uCC98\uB7FC \uC720\uD734 \uB9AC\uC18C\uC2A4\uC5D0 \uB300\uD574 \uBE44\uC6A9\uC744 \uC9C0\uBD88\uD558\uC9C0 \uC54A\uACE0 \uC8FC\uC5B4\uC9C4 \uC2DC\uAC04\uC5D0 \uC0AC\uC6A9\uD55C \uB9CC\uD07C\uB9CC \uBE44\uC6A9\uC744 \uC9C0\uBD88\uD558\uACE0 \uC788\uC74C\uC744 \uD655\uC2E0\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uAC83\uC774 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC194\uB8E8\uC158\uC785\uB2C8\uB2E4.</li></ul></li><li><p>\uC624\uB2F5\uC774\uC720</p><ul><li>A, \uC774 \uB2F5\uBCC0\uC740 \uBA87 \uAC00\uC9C0 \uC774\uC720 \uB54C\uBB38\uC5D0 \uB2F5\uC774 \uB420 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4. \uADF8 \uC911 \uD558\uB098\uB294 \uBE60\uB978 \uD655\uC7A5 \uC694\uAD6C \uC0AC\uD56D\uC785\uB2C8\uB2E4. AWS Elastic Beanstalk\uB97C \uC0AC\uC6A9\uD558\uBA74 Elastic Load Balancer\uAC00 \uC804\uBA74\uC5D0 \uC788\uB294 Auto Scaling \uADF8\uB8F9 \uB0B4\uBD80\uC758 Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0\uC11C \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uC815\uB9D0 \uC27D\uAC8C \uD638\uC2A4\uD305\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774 \uC194\uB8E8\uC158\uC740 \uD655\uC7A5 \uAC00\uB2A5\uD558\uC9C0\uB9CC \uC870\uC815 \uC774\uBCA4\uD2B8\uAC00 \uBC1C\uC0DD\uD560 \uB54C Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uB97C \uBD80\uD305\uD574\uC57C \uD558\uBBC0\uB85C \uC870\uC815\uC774 \uBC1C\uC0DD\uD558\uB294 \uB370 \uBA87 \uBD84 \uC815\uB3C4 \uAC78\uB9B4 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC194\uB8E8\uC158\uC774 \uC798\uBABB\uB41C \uB2E4\uC74C \uC774\uC720\uB294 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC194\uB8E8\uC158\uC774 \uC544\uB2C8\uAE30 \uB54C\uBB38\uC785\uB2C8\uB2E4. EC2 \uAE30\uBC18 \uC11C\uBE44\uC2A4\uC774\uBBC0\uB85C \uBCFC\uB968\uC774 \uB0AE\uC740 \uB3D9\uC548 \uB9AC\uC18C\uC2A4\uAC00 \uC720\uD734 \uC0C1\uD0DC\uB85C \uC788\uC744 \uC218 \uC788\uC9C0\uB9CC \uC5EC\uC804\uD788 \uBE44\uC6A9\uC774 \uBC1C\uC0DD\uD569\uB2C8\uB2E4. \uC989, AWS Lambda\uBCF4\uB2E4 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC194\uB8E8\uC158\uC785\uB2C8\uB2E4.</li><li>C, \uC774\uAC83\uC740 AWS Elastic Beanstalk\uC640 \uAC19\uC740 \uC774\uC720\uB85C \uC62C\uBC14\uB974\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uC774\uAC83\uC740 \uBE60\uB974\uAC8C \uD655\uC7A5\uB418\uC9C0 \uC54A\uC73C\uBA70 \uC7A0\uC7AC\uC801\uC73C\uB85C \uC720\uD734 \uB9AC\uC18C\uC2A4\uC758 \uBB38\uC81C\uB3C4 \uC788\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC774 \uB2F5\uBCC0\uC740 \uC798\uBABB\uB41C \uAC83\uC785\uB2C8\uB2E4.</li><li>D, \uB9C8\uC774\uD06C\uB85C \uC11C\uBE44\uC2A4\uAC00 \uCEE8\uD14C\uC774\uB108\uC5D0 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uAC1C\uBCC4 \uC11C\uBE44\uC2A4\uC5D0 \uB300\uD55C \uD655\uC7A5\uC740 \uB9E4\uC6B0 \uBE60\uB985\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uC694\uCCAD \uBCFC\uB968\uC774 \uD06C\uAC8C \uBCC0\uACBD\uB420 \uAC00\uB2A5\uC131\uC774 \uC788\uB294 \uD074\uB7EC\uC2A4\uD130\uB97C \uD655\uC7A5\uD574\uC57C \uD558\uB294 \uACBD\uC6B0 \uD074\uB7EC\uC2A4\uD130\uB97C \uD655\uC7A5\uD558\uB294 \uB370 \uBA87 \uBD84\uC774 \uAC78\uB9BD\uB2C8\uB2E4. \uC774\uAC83\uC740 \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uCDA9\uBD84\uD788 \uBE60\uB974\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uC774 \uC194\uB8E8\uC158\uC740 \uB610\uD55C Amazon EC2 \uAE30\uBC18 \uC11C\uBE44\uC2A4\uC774\uAE30 \uB54C\uBB38\uC5D0 \uB9AC\uC18C\uC2A4\uB97C \uC720\uD734 \uC0C1\uD0DC\uB85C \uB9CC\uB4E4 \uAC00\uB2A5\uC131\uC774 \uB3D9\uC77C\uD569\uB2C8\uB2E4. \uC989, API Gateway \uBC0F AWS Lambda\uB9CC\uD07C \uBE44\uC6A9 \uD6A8\uC728\uC801\uC774\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</li></ul></li></ul><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',20),A={href:"https://www.coursera.org/learn/aws-certified-solutions-architect-associate",target:"_blank",rel:"noopener noreferrer"},m=l("coursera\uAC15\uC758"),u=a("br",null,null,-1),h={href:"https://aws.amazon.com/ko/faqs/",target:"_blank",rel:"noopener noreferrer"},p=l("aws FAQ");function S(b,_){const e=o("ExternalLinkIcon");return t(),r("div",null,[d,a("p",null,[a("a",A,[m,i(e)]),u,a("a",h,[p,i(e)])])])}var C=n(c,[["render",S],["__file","domain4-2-cost-effective-compute-and-database-services.html.vue"]]);export{C as default};
