import{_ as n}from"./plugin-vue_export-helper.21dcd24c.js";import{r as o,o as s,c as r,a as e,b as i,d as l,e as a}from"./app.d6b42b88.js";const c={},p=l('<p>AWS-SAA \uC790\uACA9\uC99D \uD56D\uBAA9\uC744 \uAE30\uC900\uC73C\uB85C AWS\uB97C \uACF5\uBD80 \uD569\uB2C8\uB2E4. \uAC01 \uD56D\uBAA9\uC758 \uC758\uBBF8\uB97C \uC0B4\uD3B4\uBCF4\uACE0 \uC911\uC810\uC0AC\uD56D\uC744 \uD30C\uC545\uD558\uB294 \uAC83\uC744 \uBAA9\uD45C\uB85C \uD569\uB2C8\uB2E4.\uAC01 \uD56D\uBAA9\uBCC4 \uC774\uB860/\uC2E4\uC2B5\uC73C\uB85C \uD559\uC2B5\uD569\uB2C8\uB2E4.</p><details class="custom-container details"><summary>\uC0C1\uC138 \uC601\uC5ED ( \uCD9C\uCC98: aws certification \uD648\uD398\uC774\uC9C0 )</summary><ul><li><p>4.1 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC2A4\uD1A0\uB9AC\uC9C0 \uC194\uB8E8\uC158 \uC2DD\uBCC4</p><ul><li>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uB370\uC774\uD130 \uC2A4\uD1A0\uB9AC\uC9C0 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uBE44\uC6A9\uC744 \uCD5C\uC18C\uD654\uD558\uAE30 \uC704\uD574 \uC2DC\uAC04 \uACBD\uACFC\uC5D0 \uB530\uB77C \uB370\uC774\uD130\uB97C \uC2A4\uD1A0\uB9AC\uC9C0 \uACC4\uCE35\uC5D0 \uC800\uC7A5\uD558\uB294 \uC790\uB3D9\uD654\uB41C \uD504\uB85C\uC138\uC2A4\uB97C \uC801\uC6A9\uD569\uB2C8\uB2E4.</li></ul></li><li><p>4.2 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uCEF4\uD4E8\uD305 \uBC0F \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC11C\uBE44\uC2A4 \uC2DD\uBCC4</p><ul><li>\uC6CC\uD06C\uB85C\uB4DC\uC758 \uAC01 \uCE21\uBA74\uC5D0 \uB300\uD574 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 Amazon EC2 \uACB0\uC81C \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uC694\uAD6C \uC0AC\uD56D\uC5D0 \uB530\uB77C \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>\uBE44\uC6A9 \uAD00\uC810\uC5D0\uC11C \uC801\uC808\uD55C \uC870\uC815 \uC804\uB7B5\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4.</li><li>\uC6CC\uD06C\uB85C\uB4DC\uC5D0 \uAC00\uC7A5 \uC801\uD569\uD55C \uCEF4\uD4E8\uD305 \uB9AC\uC18C\uC2A4\uB97C \uC120\uD0DD\uD558\uACE0 \uD06C\uAE30\uB97C \uC870\uC815\uD569\uB2C8\uB2E4.</li><li>\uAD00\uB9AC\uD615 \uC11C\uBE44\uC2A4 \uBC0F \uC11C\uBC84\uB9AC\uC2A4 \uC544\uD0A4\uD14D\uCC98\uB97C \uD1B5\uD574 TCO(\uCD1D \uC18C\uC720 \uBE44\uC6A9)\uB97C \uCD5C\uC18C\uD654\uD558\uB294 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li></ul></li><li><p>4.3 \uBE44\uC6A9\uC5D0 \uCD5C\uC801\uD654\uB41C \uB124\uD2B8\uC6CC\uD06C \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4 \u{1F448}\u{1F448}\u{1F448}\u{1F448}\u{1F448} <strong>\uC9C0\uAE08 \uC5EC\uAE30</strong></p><ul><li>\uBE44\uC6A9\uC744 \uC808\uAC10\uD558\uAE30 \uC704\uD574 \uCF58\uD150\uCE20 \uC804\uC1A1\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uC2DC\uC810\uC744 \uD30C\uC545\uD569\uB2C8\uB2E4.</li><li>AWS \uB0B4\uC5D0\uC11C \uB370\uC774\uD130 \uC804\uC1A1 \uBE44\uC6A9\uC744 \uC808\uAC10\uD558\uAE30 \uC704\uD55C \uC804\uB7B5\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li><li>AWS \uD658\uACBD\uACFC \uC628\uD504\uB808\uBBF8\uC2A4 \uD658\uACBD \uAC04\uC5D0 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC5F0\uACB0 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4.</li></ul></li></ul></details><h2 id="domain-4-\u1107\u1175\u110B\u116D\u11BC\u110B\u1166-\u110E\u116C\u110C\u1165\u11A8\u1112\u116A\u1103\u116C\u11AB-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#domain-4-\u1107\u1175\u110B\u116D\u11BC\u110B\u1166-\u110E\u116C\u110C\u1165\u11A8\u1112\u116A\u1103\u116C\u11AB-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> Domain 4 : \uBE44\uC6A9\uC5D0 \uCD5C\uC801\uD654\uB41C \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4</h2><h2 id="\u1107\u1175\u110B\u116D\u11BC\u110B\u1166-\u110E\u116C\u110C\u1165\u11A8\u1112\u116A\u1103\u116C\u11AB-\u1102\u1166\u1110\u1173\u110B\u116F\u110F\u1173-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#\u1107\u1175\u110B\u116D\u11BC\u110B\u1166-\u110E\u116C\u110C\u1165\u11A8\u1112\u116A\u1103\u116C\u11AB-\u1102\u1166\u1110\u1173\u110B\u116F\u110F\u1173-\u110B\u1161\u110F\u1175\u1110\u1166\u11A8\u110E\u1165-\u1109\u1165\u11AF\u1100\u1168" aria-hidden="true">#</a> \uBE44\uC6A9\uC5D0 \uCD5C\uC801\uD654\uB41C \uB124\uD2B8\uC6CC\uD06C \uC544\uD0A4\uD14D\uCC98 \uC124\uACC4</h2><p><strong>\uC138\uBD80\uC601\uC5ED</strong></p><ul><li><p>\uBE44\uC6A9\uC744 \uC808\uAC10\uD558\uAE30 \uC704\uD574 \uCF58\uD150\uCE20 \uC804\uC1A1\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uC2DC\uC810\uC744 \uD30C\uC545\uD569\uB2C8\uB2E4. S3\uC758 \uACBD\uC6B0 \uC800\uC7A5 \uBE44\uC6A9 \uBFD0\uC544\uB2CC S3 \uBC84\uD0B7\uC5D0 \uC800\uC7A5\uB41C \uB370\uC774\uD130 \uC591\uC5D0 \uB300\uD55C \uC694\uAE08\uC774 \uBC1C\uC0DD\uD558\uC9C0\uB9CC S3\uC5D0 \uB300\uD55C API \uD638\uCD9C \uBC0F \uBC84\uD0B7 \uC678\uBD80 \uB370\uC774\uD130 \uC804\uC1A1\uC5D0 \uB300\uD55C \uBE44\uC6A9\uB3C4 \uBC1C\uC0DD\uD55C\uB2E4. \uBE44\uC6A9\uC744 \uCD5C\uC801\uD654\uD558\uB294 \uC88B\uC740 \uBC29\uBC95 \uC911 \uD558\uB098\uB294 Amazon CloudFront\uC640 \uAC19\uC740 \uCF58\uD150\uCE20 \uC804\uC1A1 \uC11C\uBE44\uC2A4\uB97C \uC0AC\uC6A9\uD558\uC5EC API \uD638\uCD9C \uBC0F \uB370\uC774\uD130 \uC804\uC1A1\uC758 \uC591\uC744 \uC904\uC774\uB294 \uAC83\uC774\uB2E4.</p></li><li><p>AWS \uB0B4\uC5D0\uC11C \uB370\uC774\uD130 \uC804\uC1A1 \uBE44\uC6A9\uC744 \uC808\uAC10\uD558\uAE30 \uC704\uD55C \uC804\uB7B5\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4. AWS \uB9AC\uC804\uAC04\uC758 \uB370\uC774\uD130\uB97C \uC804\uC1A1 : VPC \uD53C\uC5B4\uB9C1\uACFC \uAC19\uC740 \uAC83\uC744 \uC0AC\uC6A9\uD558\uB294 \uAC83\uC774 AWS Transit Gateway\uC640 \uAC19\uC740 \uAC83\uC744 \uC0AC\uC6A9\uD558\uB294 \uAC83\uBCF4\uB2E4 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC77C \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uB124\uD2B8\uC6CC\uD06C \uC5F0\uACB0 \uC11C\uBE44\uC2A4\uC640 \uAC00\uACA9\uC744 \uC544\uB294 \uAC83\uC774 \uB3C4\uC6C0\uC774 \uB420 \uAC83\uC785\uB2C8\uB2E4.</p></li><li><p>AWS \uD658\uACBD\uACFC \uC628\uD504\uB808\uBBF8\uC2A4 \uD658\uACBD \uAC04\uC5D0 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC5F0\uACB0 \uC635\uC158\uC744 \uACB0\uC815\uD569\uB2C8\uB2E4 \uC628\uD504\uB808\uBBF8\uC2A4 \uB370\uC774\uD130 \uC13C\uD130\uC640 AWS \uAC04\uC5D0 \uC5F0\uACB0\uC774 \uC774\uB8E8\uC5B4\uC9D1\uB2C8\uB2E4. \uC77C\uBC18\uC801\uC73C\uB85C Direct Connect\uB97C \uC0AC\uC6A9\uD558\uB294 \uC194\uB8E8\uC158\uC740 VPN \uC5F0\uACB0\uC744 \uC0AC\uC6A9\uD558\uB294 \uAC83\uBCF4\uB2E4 \uBE44\uC6A9\uC774 \uB354 \uB9CE\uC774 \uB4ED\uB2C8\uB2E4. \uB530\uB77C\uC11C AWS Direct Connect\uAC00 \uC81C\uACF5\uD558\uB294 \uCC98\uB9AC\uB7C9\uC774\uB098 \uBCF4\uC548\uC774 \uC815\uB9D0\uB85C \uD544\uC694\uD558\uC9C0 \uC54A\uB294 \uD55C VPN \uC5F0\uACB0\uC744 \uC0AC\uC6A9\uD558\uB294 \uAC83\uC774 \uB354 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC774\uAE30 \uB54C\uBB38\uC5D0 \uB300\uC2E0 \uC0AC\uC6A9\uD574\uC57C \uD569\uB2C8\uB2E4.</p></li></ul><ul><li>AWS\uC5D0 \uB300\uD55C \uB610 \uB2E4\uB978 \uC5F0\uACB0 \uC720\uD615\uC740 AWS \uB9AC\uC18C\uC2A4\uC5D0 \uC5F0\uACB0\uD558\uC5EC \uB9AC\uC18C\uC2A4\uB97C \uAD00\uB9AC\uD558\uB294 \uBC29\uBC95\uC785\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uC77C\uBD80 \uB85C\uADF8\uB97C \uD655\uC778\uD558\uAC70\uB098 \uBB38\uC81C \uD574\uACB0\uC744 \uC218\uD589\uD558\uAE30 \uC704\uD574 Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB85C\uADF8\uC628\uD574\uC57C \uD558\uB294 \uACBD\uC6B0 \uC774\uB97C \uB2EC\uC131\uD560 \uC218 \uC788\uB294 \uC5EC\uB7EC \uAC00\uC9C0 \uBC29\uBC95\uC774 \uC788\uC2B5\uB2C8\uB2E4. SSH \uB610\uB294 RDP\uC758 \uACE0\uC804\uC801\uC778 \uBC29\uBC95\uC774 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uB9AC\uACE0 AWS Systems Manager Session Manager\uC640 Amazon EC2 Instance Connect\uAC00 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uC5F0\uACB0 \uBC29\uBC95 \uAC01\uAC01\uC758 \uBE44\uC6A9 \uC601\uD5A5\uC744 \uC54C\uACE0 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4. \uB9C8\uC9C0\uB9C9\uC73C\uB85C NAT \uAC8C\uC774\uD2B8\uC6E8\uC774 \uB610\uB294 \uC804\uC1A1 \uAC8C\uC774\uD2B8\uC6E8\uC774\uC640 \uAC19\uC740 \uB2E4\uB978 \uB124\uD2B8\uC6CC\uD0B9 \uC11C\uBE44\uC2A4\uC758 \uBE44\uC6A9 \uC601\uD5A5\uC744 \uC54C\uACE0 \uC788\uC5B4\uC57C \uD569\uB2C8\uB2E4.</li></ul><h2 id="\u110F\u1175\u110B\u116F\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#\u110F\u1175\u110B\u116F\u1103\u1173" aria-hidden="true">#</a> \uD0A4\uC6CC\uB4DC</h2><div class="custom-container tip"><p class="custom-container-title">SSH / RDP\uC758 \uACE0\uC804\uC801\uC778 \uBC29\uBC95\uC774 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uB9AC\uACE0 AWS Systems Manager Session Manager\uC640 Amazon EC2 Instance Connect</p></div><div class="custom-container tip"><p class="custom-container-title">AWS Transit Gateway vs VPC \uD53C\uC5B4\uB9C1</p><p>AWS \uB9AC\uC804\uAC04 \uB370\uC774\uD130 \uC804\uC1A1</p></div><div class="custom-container tip"><p class="custom-container-title">Direct Connect vs VPN \uC5F0\uACB0</p><p>\uC628\uD504\uB808\uBBF8\uC2A4 \uB370\uC774\uD130 \uC13C\uD130 AWS \uAC04\uC5D0 \uC5F0\uACB0</p></div><div class="custom-container tip"><p class="custom-container-title">Tips</p><p>\uC989, Amazon S3\uB97C \uC0AC\uC6A9\uD560 \uB54C \uBE44\uC6A9\uC744 \uCD5C\uC801\uD654\uD558\uB294 \uC88B\uC740 \uBC29\uBC95 \uC911 \uD558\uB098\uB294 \uBC1C\uC0DD\uD558\uB294 API \uD638\uCD9C \uBC0F \uB370\uC774\uD130 \uC804\uC1A1\uC758 \uC591\uC744 \uC904\uC774\uB294 \uAC83\uC785\uB2C8\uB2E4. \uC774\uB97C \uC218\uD589\uD560 \uC218 \uC788\uB294 \uD55C \uAC00\uC9C0 \uBC29\uBC95\uC740 Amazon CloudFront\uC640 \uAC19\uC740 \uCF58\uD150\uCE20 \uC804\uC1A1 \uC11C\uBE44\uC2A4\uB97C \uC0AC\uC6A9\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4. \uC561\uC138\uC2A4\uB418\uB294 \uB370\uC774\uD130\uC758 \uC591\uACFC \uC561\uC138\uC2A4 \uD328\uD134\uC5D0 \uB530\uB77C CloudFront\uB97C \uC0AC\uC6A9\uD558\uC5EC S3 \uBC84\uD0B7\uC744 \uC804\uBA74\uC5D0 \uBC30\uCE58\uD558\uB294 \uAC83\uC774 \uB354 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC77C \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB807\uAC8C \uD558\uBA74 \uB204\uAD70\uAC00\uAC00 \uAC1D\uCCB4\uC5D0 \uB300\uD55C \uC694\uCCAD\uC744 \uC81C\uCD9C\uD558\uBA74 \uC694\uCCAD\uC774 \uBA3C\uC800 CloudFront \uBC30\uD3EC\uB85C \uB77C\uC6B0\uD305\uB429\uB2C8\uB2E4. \uAC1C\uCCB4\uAC00 \uBC1C\uACAC\uB418\uBA74 \uBC18\uD658\uB429\uB2C8\uB2E4. \uCE90\uC2DC\uC5D0\uC11C \uCC3E\uC744 \uC218 \uC5C6\uC73C\uBA74 S3\uC5D0\uC11C \uAC80\uC0C9\uB429\uB2C8\uB2E4. \uADF8\uB7F0 \uB2E4\uC74C \uD574\uB2F9 \uAC1C\uCCB4\uB294 \uB2E4\uC74C \uC694\uCCAD\uC774 \uB4E4\uC5B4\uC62C \uC900\uBE44\uAC00 \uB418\uB3C4\uB85D \uC77C\uC815 \uC2DC\uAC04 \uB3D9\uC548 \uCE90\uC2DC\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uCF58\uD150\uCE20 \uC804\uC1A1 \uB124\uD2B8\uC6CC\uD06C\uC778 CloudFront\uB97C \uC0AC\uC6A9\uD558\uC5EC \uD6A8\uACFC\uC801\uC778 \uCE90\uC2F1 \uC194\uB8E8\uC158\uC744 \uCC3E\uB294 \uAC83\uC740 S3\uC5D0\uC11C \uB370\uC774\uD130 \uC804\uC1A1\uC744 \uC904\uC5EC \uBE44\uC6A9\uC744 \uCD5C\uC801\uD654\uD558\uB294 \uC88B\uC740 \uBC29\uBC95\uC774 \uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC2DC\uD5D8\uC5D0\uC11C\uB294 CloudFront\uB97C \uC0AC\uC6A9\uD558\uC5EC \uBE44\uC6A9\uC744 \uCD5C\uC801\uD654\uD558\uB294 \uC2DC\uAE30\uC640 \uBC29\uBC95\uC744 \uC798 \uC0B4\uD3B4\uBCF4\uACE0 CloudFront \uBC30\uD3EC\uB85C Amazon S3 \uBC84\uD0B7\uC744 \uC804\uBA74\uC5D0 \uBC30\uCE58\uD558\uB294 \uAC83 \uC774\uC0C1\uC758 \uC0AC\uC6A9 \uC0AC\uB840\uB97C \uD655\uC778\uD558\uB294 \uAC83\uC774 \uC88B\uC2B5\uB2C8\uB2E4.</p></div><div class="custom-container tip"><p class="custom-container-title">Tips</p><p>AWS\uC758 \uC5F0\uACB0 \uC11C\uBE44\uC2A4 \uBC0F \uAE30\uB2A5\uC774 \uBE44\uC6A9\uC5D0 \uBBF8\uCE58\uB294 \uC601\uD5A5\uC744 \uC77D\uC5B4 \uBCF4\uC2ED\uC2DC\uC624.</p></div><h2 id="question" tabindex="-1"><a class="header-anchor" href="#question" aria-hidden="true">#</a> Question</h2><p>\uD68C\uC0AC\uB294 \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC758 Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0\uC11C \uC2E4\uD589\uB418\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uAC1C\uBC1C\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. EC2 \uC778\uC2A4\uD134\uC2A4\uB294 NAT \uAC8C\uC774\uD2B8\uC6E8\uC774\uB97C \uC0AC\uC6A9\uD558\uC5EC \uC778\uD130\uB137\uC5D0 \uC561\uC138\uC2A4\uD569\uB2C8\uB2E4. \uC194\uB8E8\uC158 \uC124\uACC4\uC790\uB294 \uAC1C\uBC1C\uC790\uAC00 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB85C\uADF8\uC778\uD560 \uC218 \uC788\uB3C4\uB85D \uBCF4\uC548 \uC635\uC158\uC744 \uC81C\uACF5\uD574\uC57C \uD569\uB2C8\uB2E4. \uC5B4\uB5A4 \uC194\uB8E8\uC158\uC774 \uC774\uB7EC\uD55C \uC694\uAD6C \uC0AC\uD56D\uC744 \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC73C\uB85C \uCDA9\uC871\uD569\uB2C8\uAE4C?</p><p>A. Configure AWS Systems Manager Session Manager for the EC2 instances to enable login.<br> -\uB85C\uADF8\uC778\uC744 \uD65C\uC131\uD654\uD558\uB3C4\uB85D EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB300\uD574 AWS Systems Manager Session Manager\uB97C \uAD6C\uC131\uD569\uB2C8\uB2E4.<br> B. Configure a bastion host in a public subnet to log in to the EC2 instances in a private subnet.<br> -\uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uC758 \uBC30\uC2A4\uCC9C \uD638\uC2A4\uD2B8\uB97C \uAD6C\uC131\uD558\uC5EC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC758 EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB85C\uADF8\uC778\uD569\uB2C8\uB2E4.<br> C. Use the existing NAT gateway to log in to the EC2 instances in a private subnet.<br> -\uAE30\uC874 NAT \uAC8C\uC774\uD2B8\uC6E8\uC774\uB97C \uC0AC\uC6A9\uD558\uC5EC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC758 EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB85C\uADF8\uC778\uD569\uB2C8\uB2E4.<br> D. Configure AWS Site-to-Site VPN to log in directly to the EC2 instances. Taking another pause here<br> -EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uC9C1\uC811 \uB85C\uADF8\uC778\uD558\uB3C4\uB85D AWS Site-to-Site VPN\uC744 \uAD6C\uC131\uD569\uB2C8\uB2E4.</p><hr><ul><li><p>\uC815\uB2F5 A</p><ul><li>Session Manager\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC778\uBC14\uC6B4\uB4DC \uD3EC\uD2B8\uB97C \uC5F4\uAC70\uB098, \uBC30\uC2A4\uCC9C \uD638\uC2A4\uD2B8\uB97C \uC720\uC9C0\uD558\uAC70\uB098, SSH \uD0A4\uB97C \uAD00\uB9AC\uD560 \uD544\uC694 \uC5C6\uC774 \uC548\uC804\uD558\uACE0 \uAC10\uC0AC \uAC00\uB2A5\uD55C \uC778\uC2A4\uD134\uC2A4 \uAD00\uB9AC\uB97C \uC124\uC815\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. Session Manager\uB97C \uC0AC\uC6A9\uD558\uC5EC Amazon EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uC561\uC138\uC2A4\uD558\uB294 \uB370\uB294 \uCD94\uAC00 \uBE44\uC6A9\uC774 \uC5C6\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uD3EC\uD2B8\uB97C \uC5F4 \uD544\uC694\uAC00 \uC5C6\uACE0 \uCD94\uAC00 \uBE44\uC6A9\uC774 \uC5C6\uAE30 \uB54C\uBB38\uC5D0 \uB9E4\uC6B0 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC785\uB2C8\uB2E4.</li></ul></li><li><p>\uC624\uB2F5 \uC774\uC720</p><ul><li>B. \uD37C\uBE14\uB9AD \uC11C\uBE0C\uB137\uC758 \uBC30\uC2A4\uCC9C \uD638\uC2A4\uD2B8\uB97C \uAD6C\uC131\uD558\uC5EC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC758 EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB85C\uADF8\uC778\uD569\uB2C8\uB2E4. \uC774\uB294 \uC778\uC2A4\uD134\uC2A4 \uBC0F \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC5D0 \uB300\uD55C \uC5F0\uACB0\uC744 \uD65C\uC131\uD654\uD558\uB294 \uACE0\uC804\uC801\uC778 \uBC29\uBC95\uC785\uB2C8\uB2E4. \uC774\uAC83\uC740 \uC791\uB3D9\uD560 \uC218 \uC788\uC9C0\uB9CC \uBC30\uC2A4\uCC9C \uD638\uC2A4\uD2B8\uB294 EC2 \uC778\uC2A4\uD134\uC2A4\uB85C \uC694\uAE08\uC744 \uBD80\uACFC\uD558\uBBC0\uB85C \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC194\uB8E8\uC158\uC774 \uC544\uB2D9\uB2C8\uB2E4.</li><li>C.\uAE30\uC874 NAT \uAC8C\uC774\uD2B8\uC6E8\uC774\uB97C \uC0AC\uC6A9\uD558\uC5EC \uD504\uB77C\uC774\uBE57 \uC11C\uBE0C\uB137\uC758 EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB85C\uADF8\uC778\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4. \uC774\uAC83\uC740 \uB354 \uAE30\uC220\uC801\uC778 \uC774\uC720\uB85C \uC62C\uBC14\uB974\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. NAT \uAC8C\uC774\uD2B8\uC6E8\uC774\uB294 \uC218\uC2E0\uC774 \uC544\uB2CC \uC1A1\uC2E0 \uB124\uD2B8\uC6CC\uD06C \uD2B8\uB798\uD53D\uB9CC \uD5C8\uC6A9\uD569\uB2C8\uB2E4. \uC774\uB294 \uC774\uB7EC\uD55C \uBC29\uC2DD\uC73C\uB85C \uC778\uC2A4\uD134\uC2A4\uC5D0 \uB300\uD55C \uC778\uBC14\uC6B4\uB4DC \uC5F0\uACB0\uC744 \uC124\uC815\uD560 \uC218 \uC5C6\uC74C\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4. \uB530\uB77C\uC11C NAT \uAC8C\uC774\uD2B8\uC6E8\uC774\uC758 \uC791\uB3D9 \uBC29\uC2DD\uACFC \uD574\uB2F9 \uAE30\uB2A5\uC5D0 \uB300\uD55C \uC9C0\uC2DD\uC744 \uC544\uB294 \uAC83\uC774 \uC911\uC694\uD558\uBA70 \uC774 \uB2F5\uBCC0\uC744 \uBC30\uC81C\uD558\uB294 \uB370 \uB3C4\uC6C0\uC774 \uB429\uB2C8\uB2E4.</li><li>D. EC2 \uC778\uC2A4\uD134\uC2A4\uC5D0 \uC9C1\uC811 \uB85C\uADF8\uC778\uD558\uB3C4\uB85D AWS Site-to-Site VPN \uAD6C\uC131\uC774 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uC81C VPN \uC5F0\uACB0 \uC124\uC815\uC774 \uC774 \uC0AC\uC6A9 \uC0AC\uB840\uC5D0 \uC801\uD569\uD558\uAE30 \uB54C\uBB38\uC5D0 \uC774\uAC83\uC740 \uAC15\uB825\uD55C \uBC29\uD574 \uC694\uC18C\uC785\uB2C8\uB2E4. \uADF8\uB7EC\uB098 VPN \uC5F0\uACB0\uC5D0\uB294 \uCD94\uAC00 \uBE44\uC6A9\uC774 \uBC1C\uC0DD\uD558\uBBC0\uB85C \uAC00\uC7A5 \uBE44\uC6A9 \uD6A8\uC728\uC801\uC778 \uC194\uB8E8\uC158\uC774 \uC544\uB2C8\uBBC0\uB85C \uC798\uBABB\uB41C \uC751\uB2F5\uC774 \uB429\uB2C8\uB2E4.</li></ul></li></ul><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',19),d={href:"https://www.coursera.org/learn/aws-certified-solutions-architect-associate",target:"_blank",rel:"noopener noreferrer"},u=a("coursera\uAC15\uC758"),h=e("br",null,null,-1),S={href:"https://aws.amazon.com/ko/faqs/",target:"_blank",rel:"noopener noreferrer"},A=a("aws FAQ");function m(C,_){const t=o("ExternalLinkIcon");return s(),r("div",null,[p,e("p",null,[e("a",d,[u,i(t)]),h,e("a",S,[A,i(t)])])])}var W=n(c,[["render",m],["__file","domain4-3-cost-optimized-network-architectures.html.vue"]]);export{W as default};
